INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010101000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01', '分子动理论', '', NULL),
('01010101010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01', '分子动理论的基本内容', '', '01010101000000'),
('01010101010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01', '物体是由大量分子组成的', '', '01010101010000'),
('01010101010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP01', '人们用肉眼无法直接看到分子，就是用高倍的光学显微镜也看不到。', '', '01010101010100'),
('01010101010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP02', '直至1982年，人们研制了能放大几亿倍的扫描隧道显微镜③，才观察到物质表面原子的排列。', '', '01010101010100'),
('01010101010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02', '分子热运动', '', '01010101010000'),
('01010101010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP01', '扩散现象是物质分子永不停息地做无规则运动的证据之一。', '', '01010101010200'),
('01010101010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP02', '扩散现象在科学技术中有很多应用。', '', '01010101010200'),
('01010101010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP03', '19世纪初，一些人观察到，悬浮在液体中的小颗粒总在不停地运动。', '', '01010101010200'),
('01010101010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP04', '从实验结果可以看出，小炭粒的运动是无规则的，温度越高，小炭粒的运动越明显。', '', '01010101010200'),
('01010101010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP05', '在扩散现象中，温度越高，扩散得越快。', '', '01010101010200'),
('01010101010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP06', '观察布朗运动，温度越高，悬浮微粒的运动就越明显。', '', '01010101010200'),
('01010101010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP07', '温度是分子热运动剧烈程度的标志。', '', '01010101010200'),
('01010101010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03', '分子间的作用力', '', '01010101010000'),
('01010101010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP01', '水和酒精混合后的总体积变小了。', '', '01010101010300'),
('01010101010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP02', '当用力拉伸物体时，物体内各部分之间要产生反抗拉伸的作用力，此时分子间的作用力表现为引力。', '', '01010101010300'),
('01010101010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP03', '当用力压缩物体时，物体内各部分之间会产生反抗压缩的作用力，此时分子间的作用力表现为斥力。', '', '01010101010300'),
('01010101010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP04', '原子内部有带正电的原子核和带负电的电子。', '', '01010101010300'),
('01010101010400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP04', '分子动理论', '', '01010101010000'),
('01010101020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02', '实验：用油膜法估测油酸分子的大小', '', '01010101000000'),
('01010101020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP01', '实验思路', '', '01010101020000'),
('01010101020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP02', '物理量的测量', '', '01010101020000'),
('01010101020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP03', '数据分析', '', '01010101020000'),
('01010101030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03', '分子运动速率分布规律', '', '01010101000000'),
('01010101030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01', '气体分子运动的特点', '', '01010101030000'),
('01010101030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP01', '热现象与大量分子热运动的统计规律有关。', '', '01010101030100'),
('01010101030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP02', '要研究气体的热现象，就要了解气体分子运动的特点。', '', '01010101030100'),
('01010101030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP03', '液体变为气体后，体积要增大上千倍，可见，气体分子间距离大约是分子直径的10倍。', '', '01010101030100'),
('01010101030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP04', '分子的大小相对分子间的空隙来说很小，所以，可以把气体分子视为质点。', '', '01010101030100'),
('01010101030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP05', '分子的运动杂乱无章，在某一时刻，向着任何一个方向运动的分子都有，而且向各个方向运动的分子数目几乎相等。', '', '01010101030100'),
('01010101030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02', '分子运动速率分布图像', '', '01010101030000'),
('01010101030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP01', '尽管分子做无规则运动，速率有大有小，但大量分子的速率却按一定的规律分布。', '', '01010101030200'),
('01010101030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP02', '100℃的氧气，速率大的分子比例较多，其分子的平均速率比0℃的大。', '', '01010101030200'),
('01010101030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03', '气体压强的微观解释', '', '01010101030000'),
('01010101030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP01', '器壁单位面积上受到的压力，就是气体的压强。', '', '01010101030300'),
('01010101030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP02', '从微观角度来看，气体分子的数量是巨大的。', '', '01010101030300'),
('01010101040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04', '分子动能和分子势能', '', '01010101000000'),
('01010101040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01', '分子动能', '', '01010101040000'),
('01010101040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP01', '物体中分子热运动的速率大小不一，所以各个分子的动能也有大有小，而且在不断改变。', '', '01010101040100'),
('01010101040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP02', '温度升高时，分子的热运动加剧，温度越高，分子热运动的平均动能越大。', '', '01010101040100'),
('01010101040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP03', '温度越低，分子热运动的平均动能越小。', '', '01010101040100'),
('01010101040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02', '分子势能', '', '01010101040000'),
('01010101040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP01', '分子间存在着相互作用力，可以证明分子间的作用力所做的功与路径无关，分子组成的系统具有分子势能。', '', '01010101040200'),
('01010101040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP02', '物体的体积变化时，分子间距离将发生变化，因而分子势能随之改变。', '', '01010101040200'),
('01010101040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03', '物体的内能', '', '01010101040000'),
('01010101040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP01', '任何物体都具有内能。', '', '01010101040300'),
('01010101040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP02', '分子热运动的平均动能与温度有关，分子势能与物体的体积有关。', '', '01010101040300');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010102000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02', '气体、固体和液体', '', NULL),
('01010102010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01', '温度和温标', '', '01010102000000'),
('01010102010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01', '状态参量与平衡态', '', '01010102010000'),
('01010102010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01.OP01', '要定量地描述系统的状态往往很难，因为有时系统的参量会随时间变化。', '', '01010102010100'),
('01010102010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP02', '热平衡与温度', '', '01010102010000'),
('01010102010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03', '温度计与温标', '', '01010102010000'),
('01010102010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03.OP01', '现代科学中用得更多的是热力学温标。', '', '01010102010300'),
('01010102020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC02', '气体的等温变化', '', '01010102000000'),
('01010102030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03', '气体的等压变化和等容变化', '', '01010102000000'),
('01010102030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP01', '气体的等压变化', '', '01010102030000'),
('01010102030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02', '气体的等容变化', '', '01010102030000'),
('01010102030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02.OP01', '法国科学家查理在分析了实验事实后发现，当一定质量的气体体积一定时，各种气体的压强与温度之间都有线性关系。', '', '01010102030200'),
('01010102030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03', '理想气体', '', '01010102030000'),
('01010102030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP01', '当压强很大、温度很低时，由上述规律计算的结果与实际测量结果有很大的差别。', '', '01010102030300'),
('01010102030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP02', '实际气体的分子之间有相互作用力，但是作用力很小；分子也有大小，但气体分子之间的间距比分子直径大得多；气体分子与器壁碰撞几乎是弹性的，动能损失也很小。', '', '01010102030300'),
('01010102030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP03', '在温度不低于零下几十摄氏度、压强不超过大气压的几倍时，把实际气体当成理想气体来处理，误差很小。', '', '01010102030300'),
('01010102030400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04', '气体实验定律的微观解释', '', '01010102030000'),
('01010102030501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP01', '用分子动理论可以定性解释气体的实验定律。', '', '01010102030400'),
('01010102030502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP02', '一定质量的某种理想气体，温度保持不变时，分子的平均动能是一定的。', '', '01010102030400'),
('01010102030503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP03', '一定质量的某种理想气体，温度升高时，分子的平均动能增大；只有气体的体积同时增大，使分子的数密度减小，才能保持压强不变。', '', '01010102030400'),
('01010102030504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP04', '一定质量的某种理想气体，体积保持不变时，分子的数密度保持不变。', '', '01010102030400'),
('01010102040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04', '固体', '', '01010102000000'),
('01010102040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01', '晶体和非晶体', '', '01010102040000'),
('01010102040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP01', '固体可以分为晶体和非晶体两类。', '', '01010102040100'),
('01010102040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP02', '石英、云母①、明矾、食盐、硫酸铜、味精等是晶体。', '', '01010102040100'),
('01010102040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP03', '玻璃、蜂蜡、松香、沥青、橡胶等是非晶体。', '', '01010102040100'),
('01010102040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP04', '非晶体没有规则的外形。', '', '01010102040100'),
('01010102040205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP05', '除了形状是否规则外，晶体和非晶体在物理性质上也有所不同。', '', '01010102040100'),
('01010102040206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP06', '从实验结果可以看出，玻璃片上石蜡熔化区域的形状近似于圆形，表明玻璃沿各个方向的导热性能相同；云母片上石蜡熔化区域的形状呈椭圆形，表明云母沿不同方向的导热性能不同。', '', '01010102040100'),
('01010102040207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP07', '蔗糖受潮后粘在一起形成的糖块，看起来没有确定的几何形状。', '', '01010102040100'),
('01010102040208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP08', '粘在一起的糖块也是多晶体，单个的蔗糖晶体颗粒是单晶体。', '', '01010102040100'),
('01010102040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02', '晶体的微观结构', '', '01010102040000'),
('01010102040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP01', '在20世纪70年代，人们又用电子显微镜观察到了铀、钍原子的像。', '', '01010102040200'),
('01010102040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP02', '1982年，人们用扫描隧道显微镜观察到物质表面原子的排列。', '', '01010102040200'),
('01010102040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP03', '在各种晶体中，原子（或分子、离子）都是按照一定的规则排列的，具有空间上的周期性。', '', '01010102040200'),
('01010102040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP04', '石墨是层状结构，层与层之间距离较大，原子间的作用力比较弱，所以石墨质地松软，可以用来制作粉状润滑剂；金刚石中碳原子间的作用力很强，所以金刚石有很大的硬度，可以用来切割玻璃。', '', '01010102040200'),
('01010102040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP05', '同种物质也可能以晶体和非晶体两种不同的形态出现，也就是说，物质是晶体还是非晶体，并不是绝对的。', '', '01010102040200'),
('01010102040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP06', '有些非晶体在一定条件下也可以转化为晶体。', '', '01010102040200'),
('01010102050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05', '液体', '', '01010102000000'),
('01010102050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP01', '液体的表面张力', '', '01010102050000'),
('01010102050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02', '浸润和不浸润', '', '01010102050000'),
('01010102050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP01', '管的内径越小，上述现象就越明显。', '', '01010102050200'),
('01010102050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP02', '有些物体内部有许多细小的孔道，孔道起到了毛细管的作用。', '', '01010102050200'),
('01010102050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP03', '当液体和与之接触的固体的相互作用比液体分子之间的相互作用强时，液体能够浸润固体。', '', '01010102050200'),
('01010102050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP04', '如果把水银滴到干净的铅板上，水银就会附着在铅板上，很难擦去。', '', '01010102050200'),
('01010102050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03', '液晶', '', '01010102050000'),
('01010102050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP01', '液晶在现代生活中扮演着重要角色，广泛应用于手机屏幕、平板电视等显示设备中。', '', '01010102050300'),
('01010102050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP02', '液晶态既具有液体的流动性，又在一定程度上具有晶体分子的规则排列的性质。', '', '01010102050300'),
('01010102050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP03', '有些物质在特定的温度范围之内具有液晶态；另一些物质，在适当的溶剂中溶解时，在一定的浓度范围具有液晶态。', '', '01010102050300'),
('01010102050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP04', '分子取向排列的液晶具有光学各向异性，具体地说，沿分子长轴方向上的折射率不同于沿短轴的。', '', '01010102050300'),
('01010102050405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP05', '入射光的偏振方向与液晶分子长轴的方向成不同夹角时，液晶对光的折射率不同。', '', '01010102050300'),
('01010102050406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP06', '液晶的研究在生命科学中也有应用。', '', '01010102050300');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010103000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03', '热力学定律', '', NULL),
('01010103010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01', '功、热和内能的改变', '', '01010103000000'),
('01010103010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01', '焦耳的实验', '', '01010103010000'),
('01010103010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP01', '在焦耳研究功与热等效性的许多实验中，有两个最具代表性。', '', '01010103010100'),
('01010103010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP02', '一个是让重物下落带动叶片搅拌容器中的水，引起水温上升；另一个是通过电流的热效应给水加热。', '', '01010103010100'),
('01010103010203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP03', '焦耳的多次实验测量表明，尽管各次悬挂重物的质量不同，下落的高度也不一样，但只要重力所做的功相同，容器内水温上升的数值都是相同的，即系统状态的变化是相同的。', '', '01010103010100'),
('01010103010204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP04', '多次实验结果表明，对于同一个系统，如果过程是绝热的，那么不管通过电阻丝的电流或大或小、通电时间或长或短，只要所做的电功相等，则系统温度上升的数值是相同的，即系统的状态就发生了同样的变化。', '', '01010103010100'),
('01010103010205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP05', '两个位置间移动时重力所做的功。', '', '01010103010100'),
('01010103010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02', '功与内能的改变', '', '01010103010000'),
('01010103010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP01', '当温度达到易燃物的燃点时，易燃物发生自燃。', '', '01010103010200'),
('01010103010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP02', '当橡胶塞跳出时，瓶内出现白雾。', '', '01010103010200'),
('01010103010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP03', '以瓶内气体为研究对象，橡胶塞跳出后，瓶内的气体迅速膨胀，系统对外做功，因此，气体的内能迅速减少，瓶内气体温度迅速下降，瓶内水蒸气液化，就会出现大量的雾状小水滴。', '', '01010103010200'),
('01010103010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03', '热与内能的改变', '', '01010103010000'),
('01010103010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03.OP01', '外界对系统做功可以改变系统的热力学状态，而单纯地对系统传热也能改变系统的热力学状态。', '', '01010103010300'),
('01010103020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02', '热力学第一定律', '', '01010103000000'),
('01010103020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01', '热力学第一定律', '', '01010103020000'),
('01010103020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01.OP01', '（1）求上述压缩过程中气体内能的变化量。', '', '01010103020100'),
('01010103020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP02', '热力学第一定律的应用', '', '01010103020000'),
('01010103030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03', '能量守恒定律', '', '01010103000000'),
('01010103030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01', '探索能量守恒的足迹', '', '01010103030000'),
('01010103030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP01', '能量的概念是人类在对物质运动规律进行长期探索中建立起来的。', '', '01010103030100'),
('01010103030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP02', '所有自然现象都涉及能量，人类的任何活动都离不开能量。', '', '01010103030100'),
('01010103030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP03', '能量具有不同的形式，有描述热运动的内能、描述机械运动的机械能、描述光辐射的光能，等等。', '', '01010103030100'),
('01010103030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP04', '不同形式的运动都可以用能量来描述。', '', '01010103030100'),
('01010103030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP05', '在认识自然的进程中，科学家慢慢知道了要用联系的观点去观察自然。', '', '01010103030100'),
('01010103030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP06', '由于能量既抽象又以各种不同的形式广泛存在，所以能量守恒思想的萌芽及产生并不是一蹴而就的。', '', '01010103030100'),
('01010103030207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP07', '从各种实验的验证，再到实验结果的被质疑，科学家们付出了大量的努力。', '', '01010103030100'),
('01010103030208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP08', '从18世纪末到19世纪中叶，不同领域的科学家从不同角度都提出过能量守恒的思想。', '', '01010103030100'),
('01010103030209', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP09', '人类对能量守恒的认识经历了一个由浅入深、由含糊到清晰的过程。', '', '01010103030100'),
('01010103030210', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP10', '俄国化学家盖斯的研究发现，任何一个化学反应，不论是一步完成，还是分几步完成，放出的总热量相同。', '', '01010103030100'),
('01010103030211', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP11', '焦耳的实验精确地测量了做功与传热之间的等价关系，从而为能量守恒定律奠定了牢固的实验基础，也为能量守恒的定量描述迈出了重要的一步。', '', '01010103030100'),
('01010103030212', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP12', '德国医生迈尔通过比对不同地区人血颜色的差异，认识到食物中化学能与内能的等效性，即生物体内能量的输入和输出是平衡的。', '', '01010103030100'),
('01010103030213', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP13', '半个多世纪人类对能量以及能量守恒研究的历史，预示着人们把各分立的领域连成一体的时刻已经到来，也就是到了建立能量转化与守恒定律的时候了。', '', '01010103030100'),
('01010103030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02', '能量守恒定律', '', '01010103030000'),
('01010103030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP01', '在热力学领域内，做功和传热可以改变系统的内能，即系统内能与系统外的能量会发生转化或转移，但能的总量不会改变。', '', '01010103030200'),
('01010103030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP02', '能量守恒是自然界的普遍规律。', '', '01010103030200'),
('01010103030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP03', '根据能量守恒定律，物理学发现和解释了很多科学现象。', '', '01010103030200'),
('01010103030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP04', '在20世纪30年代初，奥地利物理学家泡利根据能量守恒定律预言了中微子。', '', '01010103030200'),
('01010103030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP05', '能量守恒定律经受住了新的检验。', '', '01010103030200'),
('01010103030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP06', '能量守恒定律的发现是科学史上的重大事件。', '', '01010103030200'),
('01010103030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03', '永动机不可能制成', '', '01010103030000'),
('01010103030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP01', '“问题”栏目中的饮水小鸭不是永动机。', '', '01010103030300'),
('01010103030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP02', '小鸭的头和身由两个玻璃球构成，并通过细玻璃管相连。', '', '01010103030300'),
('01010103030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP03', '下球盛有一些乙醚，上球连同尖嘴被吸水毛毡包起来。', '', '01010103030300'),
('01010103030404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP04', '永动机的思想违背了能量守恒定律，所以是不可能制成的。', '', '01010103030300'),
('01010103030405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP05', '应该指出的是，制造永动机的千万次失败使人们的头脑冷静下来，开始在更深层次寻找失败的原因。', '', '01010103030300'),
('01010103030406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP06', '国科学院宣布“不再审查有关永动机的一切设计”。', '', '01010103030300'),
('01010103040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04', '热力学第二定律', '', '01010103000000'),
('01010103040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01', '热力学第二定律', '', '01010103040000'),
('01010103040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP01', '在电冰箱的实例中，热量的确从低温物体——冰箱内的食品，传到了高温物体——冰箱外的空气。', '', '01010103040100'),
('01010103040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP02', '从能量的角度看，热机的工作分为两个阶段：第一个阶段是燃烧燃料，把燃料中的化学能变成工作物质的内能；第二个阶段是工作物质对外做功，把自己的内能变成机械能。', '', '01010103040100'),
('01010103040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP03', '热力学第二定律的开尔文表述阐述了机械能与内能转化的方向性：机械能可以全部转化为内能，而内能无法全部用来做功以转化成机械能。', '', '01010103040100'),
('01010103040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP04', '可以证明，热力学第二定律的克劳修斯表述和开尔文表述是等价的。', '', '01010103040100'),
('01010103040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02', '能源是有限的', '', '01010103040000'),
('01010103040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP01', '自然界的一切变化，人类社会的所有活动，都伴随着能量的转移和转化，能量是一切物质运动的源泉，是一切生命活动的基础。', '', '01010103040200'),
('01010103040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP02', '能量在数量上虽然守恒，但其转移和转化却具有方向性。', '', '01010103040200'),
('01010103040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP03', '核能、生物能……最终都转化成内能，流散到周围的环境中。', '', '01010103040200'),
('01010103040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP04', '周围环境中的内能很难再利用，而机械能、光能、化学能、电能相对于周围环境中的内能来说，可利用的品质要高。', '', '01010103040200'),
('01010103040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP05', '能源的使用过程中虽然能的总量保持守恒，但能量的品质下降了。', '', '01010103040200'),
('01010103040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP06', '虽然能量总量不会减少，但能源会逐步减少，因此能源是有限的资源。', '', '01010103040200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010104000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04', '原子结构和波粒二象性', '', NULL),
('01010104010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01', '普朗克黑体辐射理论', '', '01010104000000'),
('01010104010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01', '黑体与黑体辐射', '', '01010104010000'),
('01010104010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP01', '19世纪，由于冶金、星体测温等需求，人们对热辐射进行了大量的研究。', '', '01010104010100'),
('01010104010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP02', '研究表明，对于一般材料的物体，辐射电磁波的情况除了与温度有关，还与材料的种类及表面状况有关，而黑体辐射电磁波的强度按波长的分布只与黑体的温度有关。', '', '01010104010100'),
('01010104010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02', '黑体辐射的实验规律', '', '01010104010000'),
('01010104010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP01', '利用分光技术和热电偶等设备，可以测出黑体辐射电磁波的强度按波长分布的情况。', '', '01010104010200'),
('01010104010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP02', '从中可以看出，随着温度的升高，一方面，各种波长的辐射强度都有增加，另一方面，辐射强度的极大值向波长较短的方向移动。', '', '01010104010200'),
('01010104010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP03', '德国物理学家维恩在1896年、英国物理学家瑞利在1900年，分别提出了辐射强度按波长分布的理论公式。', '', '01010104010200'),
('01010104010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP04', '维恩公式在短波区与实验非常接近，在长波区则与实验偏离较大；瑞利公式在长波区与实验基本一致，但在短波区与实验严重不符。', '', '01010104010200'),
('01010104010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP05', '为了得出同全部实验相符的黑体辐射公式，德国物理学家普朗克做了多种尝试。', '', '01010104010200'),
('01010104010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03', '能量子', '', '01010104010000'),
('01010104010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP01', '弹簧振子的能量不一定是某个最小值的整数倍。', '', '01010104010300'),
('01010104010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP02', '而普朗克的假设则认为微观粒子的能量是量子化的，或者说微观粒子的能量是分立的。', '', '01010104010300'),
('01010104010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP03', '普朗克本人因此获得了1918年的诺贝尔物理学奖。', '', '01010104010300'),
('01010104020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02', '光电效应', '', '01010104000000'),
('01010104020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01', '光电效应的实验规律', '', '01010104020000'),
('01010104020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP01', '1887年，赫兹在研究电磁波的实验中偶尔发现，接收电路的间隙如果受到光照，就更容易产生电火花。', '', '01010104020100'),
('01010104020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP02', '科学家通过实验发现了以下规律。', '', '01010104020100'),
('01010104020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP03', '实验表明，不同金属的截止频率不同。', '', '01010104020100'),
('01010104020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP04', '在光照条件不变的情况下，随着所加电压的增大，光电流趋于一个饱和值。', '', '01010104020100'),
('01010104020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP05', '实验表明，在光的频率不变的情况下，入射光越强，饱和电流越大。', '', '01010104020100'),
('01010104020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02', '光电效应经典解释中的疑难', '', '01010104020000'),
('01010104020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP01', '当光照射金属表面时，电子会吸收光的能量。', '', '01010104020200'),
('01010104020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP02', '光越强，逸出的电子数越多，光电流也就越大。', '', '01010104020200'),
('01010104020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP03', '●不管光的频率如何，只要光足够强，电子都可以获得足够能量从而逸出表面，不应存在截止频率。', '', '01010104020200'),
('01010104020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03', '爱因斯坦的光电效应理论', '', '01010104020000'),
('01010104020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP01', '对于光电效应的解释，爱因斯坦是在普朗克量子假说的基础上作出的。', '', '01010104020300'),
('01010104020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP02', '从下面的讨论可以看出，爱因斯坦光电效应方程可以很好地解释光电效应实验中的各种现象。', '', '01010104020300'),
('01010104020403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP03', '●电子一次性吸收光子的全部能量，不需要积累能量的时间，光电流自然几乎是瞬时产生的。', '', '01010104020300'),
('01010104020404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP04', '爱因斯坦由于提出了光电效应理论而获得1921年的诺贝尔物理学奖。', '', '01010104020300'),
('01010104020405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP05', '金属表面的光子数较多，照射金属时产生的光电子较多，因而饱和电流较大。', '', '01010104020300'),
('01010104020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04', '康普顿效应和光子的动量', '', '01010104020000'),
('01010104020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP01', '光可以与介质中的物质微粒发生散射，改变传播方向。', '', '01010104020400'),
('01010104020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP02', '康普顿效应让人们对光子有了更深入的认识。', '', '01010104020400'),
('01010104020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP03', '康普顿因此获得了1927年的诺贝尔物理学奖。', '', '01010104020400'),
('01010104020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05', '光的波粒二象性', '', '01010104020000'),
('01010104020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP01', '众所周知，在麦克斯韦的电磁理论建立之后，人们认识到光是一种电磁波，从而光的波动说被普遍接受，人们不再认为光是由粒子组成的。', '', '01010104020500'),
('01010104020602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP02', '而爱因斯坦的光电效应理论和康普顿效应理论表明，光在某些方面确实会表现得像是由一些粒子（即一个个有确定能量和动量的“光子”）组成的。', '', '01010104020500'),
('01010104020603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP03', '人们意识到，光既具有波动性，又具有粒子性。', '', '01010104020500'),
('01010104020604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP04', '从牛顿的微粒说、惠更斯和托马斯·杨的波动说，到麦克斯韦的电磁理论，再到爱因斯坦的光子理论乃至量子电动力学，人类对光的认识构成了一部科学史诗。', '', '01010104020500'),
('01010104030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03', '原子的核式结构模型', '', '01010104000000'),
('01010104030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01', '电子的发现', '', '01010104030000'),
('01010104030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP01', '为了找到有利于自己的证据，双方都做了许多实验。', '', '01010104030100'),
('01010104030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP02', '由实验测得的阴极射线粒子的比荷是氢离子①（也就是质子）比荷的近两千倍。', '', '01010104030100'),
('01010104030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP03', '金属受热还是放射性物质的自发辐射，都能发射同样的带电粒子——电子。', '', '01010104030100'),
('01010104030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02', '原子的核式结构模型', '', '01010104030000'),
('01010104030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP01', 'α粒子是从放射性物质（如铀和镭）中发射出来的快速运动的粒子，质量为氢原子质量的4倍、电子质量的7300倍。', '', '01010104030200'),
('01010104030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP02', '被散射的α粒子打在荧光屏上会有微弱的闪光产生。', '', '01010104030200'),
('01010104030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP03', '通过显微镜观察闪光就可以记录在某一时间内向某一方向散射的α粒子数。', '', '01010104030200'),
('01010104030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP04', 'α粒子有库仑力的作用，一些α粒子的运动方向改变，也就是发生了α粒子的散射。', '', '01010104030200'),
('01010104030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP05', 'α粒子所占的比例，可以推知原子中电荷的分布情况。', '', '01010104030200'),
('01010104030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP06', '卢瑟福分析了实验数据后认为，事实应该是：占原子质量绝大部分的带正电的物质集中在很小的空间范围。', '', '01010104030200'),
('01010104030307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP07', '1911年，卢瑟福提出了自己的原子结构模型。', '', '01010104030200'),
('01010104030308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP08', '因为正电体很小，当α粒子进入原子区域后，大部分离正电体很远，受到的库仑斥力很小，运动方向几乎不改变。', '', '01010104030200'),
('01010104030309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP09', '只有极少数α粒子在穿过时距离正电体很近，因此受到很强的库仑斥力，发生大角度散射。', '', '01010104030200'),
('01010104030310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP10', '按照卢瑟福的理论，正电体的尺度是很小的，称为原子核。', '', '01010104030200'),
('01010104030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03', '原子核的电荷与尺度', '', '01010104030000'),
('01010104030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP01', '又由于原子是电中性的，可以推算出原子内含有的电子数。', '', '01010104030300'),
('01010104030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP02', '通常用核半径描述核的大小。', '', '01010104030300'),
('01010104040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04', '氢原子光谱和玻尔的原子模型', '', '01010104000000'),
('01010104040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01', '光谱', '', '01010104040000'),
('01010104040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP01', '有的光谱看起来不是一条条分立的谱线，而是连在一起的光带，叫作连续谱。', '', '01010104040100'),
('01010104040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP02', '气体中中性原子的发光光谱都是线状谱，说明原子只发出几种特定频率的光。', '', '01010104040100'),
('01010104040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02', '氢原子光谱的实验规律', '', '01010104040000'),
('01010104040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP01', '光谱的结果显示氢原子只能发出一系列特定波长的光。', '', '01010104040200'),
('01010104040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP02', '巴耳末公式以简洁的形式反映了氢原子的线状光谱的特征。', '', '01010104040200'),
('01010104040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03', '经典理论的困难', '', '01010104040000'),
('01010104040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP01', '卢瑟福的核式结构模型正确地指出了原子核的存在，很好地解释了α粒子散射实验，但跟经典电磁理论发生了矛盾。', '', '01010104040300'),
('01010104040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP02', '核外电子受到原子核的库仑引力的作用，却没有被吸引到原子核上，而是在以一定的速度绕核运动。', '', '01010104040300'),
('01010104040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP03', '随着绕核运动轨道半径的不断变化，电子运动的频率也要不断变化，因此原子辐射电磁波的频率也要不断变化。', '', '01010104040300'),
('01010104040400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04', '玻尔原子理论的基本假设', '', '01010104040000'),
('01010104040501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP01', '丹麦物理学家玻尔意识到了经典理论在解释原子结构方面的困难。', '', '01010104040400'),
('01010104040502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP02', '玻尔的原子结构假说包括以下两方面的内容。', '', '01010104040400'),
('01010104040503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP03', '玻尔认为，原子中的电子在库仑引力的作用下，绕原子核做圆周运动，服从经典力学的规律。', '', '01010104040400'),
('01010104040504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP04', '在玻尔理论中，电子的轨道半径只可能是某些分立的数值。', '', '01010104040400'),
('01010104040505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP05', '当电子在不同的轨道上运动时，原子处于不同的状态，具有不同的能量。', '', '01010104040400'),
('01010104040506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP06', '根据玻尔理论，电子只能在特定轨道上运动，因此，原子的能量也只能取一系列特定的值。', '', '01010104040400'),
('01010104040507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP07', '按照玻尔的观点，电子在一系列定态轨道上运动，不会发生电磁辐射。', '', '01010104040400'),
('01010104040500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05', '玻尔理论对氢光谱的解释', '', '01010104040000'),
('01010104040601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP01', '从玻尔的基本假设出发，运用经典电磁学和经典力学的理论，可以计算氢原子中电子的可能轨道半径及相应的能量。', '', '01010104040500'),
('01010104040602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP02', '处于基态的原子非常稳定。', '', '01010104040500'),
('01010104040603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP03', '处于激发态的原子是不稳定的，会自发地向能量较低的能级跃迁，放出光子，最终回到基态。', '', '01010104040500'),
('01010104040604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP04', '由于原子的能级是分立的，所以放出的光子的能量也是分立的。', '', '01010104040500'),
('01010104040605', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP05', '由于不同的原子具有不同的结构，能级各不相同，因此辐射（或吸收）的光子频率也不相同。', '', '01010104040500'),
('01010104040606', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP06', '各种气体原子的能级不同，跃迁时发射光子的能量各异。', '', '01010104040500'),
('01010104040607', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP07', '街道上的霓虹灯、试电笔中的氖管，都是由灯管内的气体原子从高能级向低能级跃迁而发光的。', '', '01010104040500'),
('01010104040608', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP08', '食盐被灼烧时发的光，也主要是由食盐蒸气中钠原子的能级跃迁而造成的。', '', '01010104040500'),
('01010104040600', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06', '玻尔理论的局限性', '', '01010104040000'),
('01010104040701', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP01', '玻尔的原子理论第一次将量子观念引入原子领域，提出了定态和跃迁的概念。', '', '01010104040600'),
('01010104040702', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP02', '玻尔理论成功地解释了氢原子光谱的实验规律。', '', '01010104040600'),
('01010104040703', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP03', '①玻尔理论的不足之处在于保留了经典粒子的观念，仍然把电子的运动看作经典力学描述下的轨道运动。', '', '01010104040600'),
('01010104040704', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP04', '实际上，根据量子力学，原子中电子的坐标没有确定的值。', '', '01010104040600'),
('01010104040705', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP05', '当原子处于不同的状态时，电子在各处出现的概率是不一样的。', '', '01010104040600'),
('01010104050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05', '粒子的波动性和量子力学的建立', '', '01010104000000'),
('01010104050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01', '粒子的波动性', '', '01010104050000'),
('01010104050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01.OP01', '1924年，法国物理学家德布罗意在对光的波粒二象性、玻尔氢原子理论以及相对论的深入研究的基础上，把波粒二象性推广到实物粒子，如电子、质子等。', '', '01010104050100'),
('01010104050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02', '物质波的实验验证', '', '01010104050000'),
('01010104050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP01', '德布罗意关于实物粒子具有波动性的假说在当时来看是难以理解的。', '', '01010104050200'),
('01010104050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP02', '1929年，德布罗意因提出物质波的假说获得了诺贝尔物理学奖。', '', '01010104050200'),
('01010104050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP03', '除了电子以外，后来还陆续证实了中子、质子以及原子、分子的波动性。', '', '01010104050200'),
('01010104050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP04', '德布罗意提出物质波的观念被实验证实，表明电子、质子、原子等粒子不但具有粒子的性质，而且具有波动的性质。', '', '01010104050200'),
('01010104050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03', '量子力学的建立', '', '01010104050000'),
('01010104050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP01', '19、20世纪之交，人们在黑体辐射、光电效应、氢原子光谱等许多类问题中，都发现了经典物理学无法解释的现象。', '', '01010104050300'),
('01010104050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP02', '普朗克黑体辐射理论、爱因斯坦光电效应理论、康普顿散射理论、玻尔氢原子理论以及德布罗意物质波假说等一系列理论在解释实验方面都取得了成功。', '', '01010104050300'),
('01010104050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP03', '1925年，德国物理学家海森堡和玻恩等人对玻尔的氢原子理论进行了推广和改造，使之可以适用于更普遍的情况。', '', '01010104050300'),
('01010104050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04', '量子力学的应用', '', '01010104050000'),
('01010104050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP01', '量子力学被应用到众多具体物理系统中，得到了与实验符合得很好的结果，获得了极大的成功。', '', '01010104050400'),
('01010104050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP02', '借助量子力学，人们深入认识了微观世界的组成、结构和属性。', '', '01010104050400'),
('01010104050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP03', '量子力学推动了核物理和粒子物理的发展。', '', '01010104050400'),
('01010104050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP04', '人们认识了原子、原子核、基本粒子等各个微观层次的物质结构。', '', '01010104050400'),
('01010104050505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP05', '而粒子物理学的发展又促进了天文学和宇宙学的研究。', '', '01010104050400'),
('01010104050506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP06', '从整个宇宙到微小的粒子，人类探索自然的视野前所未有的宽广。', '', '01010104050400'),
('01010104050507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP07', '人们惊讶地发现，世界具有奇妙的结构，最微观层次和最宏观层次的规律，竟有着紧密的联系。', '', '01010104050400'),
('01010104050508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP08', '核物理的发展，还让人们成功地认识并利用了原子核反应堆所释放的能量——核能。', '', '01010104050400'),
('01010104050509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP09', '”量子力学推动了原子、分子物理和光学的发展。', '', '01010104050400'),
('01010104050510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP10', '人们认识了原子的结构，以及原子、分子和电磁场相互作用的方式。', '', '01010104050400'),
('01010104050511', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP11', '在此基础上，发展了各式各样的对原子和电磁场进行精确操控和测量的技术，如激光、核磁共振、原子钟，等等。', '', '01010104050400'),
('01010104050512', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP12', '以利用振荡的磁场测量材料中原子的性质，因此，被广泛地用于化学、生物研究和医学诊断。', '', '01010104050400'),
('01010104050513', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP13', '原子钟利用原子为电磁波校准频率，从而实现了对时间的高精度测量。', '', '01010104050400'),
('01010104050514', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP14', '在日常生活和国家安全中发挥巨大作用的卫星定位技术，其核心部件就是原子钟。', '', '01010104050400'),
('01010104050515', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP15', '量子力学推动了固体物理的发展。', '', '01010104050400'),
('01010104050516', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP16', '人们了解了固体中电子运行的规律，并弄清了为什么固体有导体、绝缘体和半导体之分。', '', '01010104050400'),
('01010104050517', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP17', '量子力学的应用还有很多。', '', '01010104050400'),
('01010104050518', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP18', '毫不夸张地说，在过去的近一百年中，量子力学极大地推动了人类的进步。', '', '01010104050400');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010105000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05', '原子核', '', NULL),
('01010105010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01', '原子核的组成', '', '01010105000000'),
('01010105010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01', '天然放射现象', '', '01010105010000'),
('01010105010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP01', '受到贝克勒尔的发现的鼓舞，波兰裔法国物理学家玛丽·居里和她的丈夫皮埃尔·居里对铀和含铀的各种矿石进行了深入研究。', '', '01010105010100'),
('01010105010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP02', '放射性元素自发地发出射线的现象，叫作天然放射现象。', '', '01010105010100'),
('01010105010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02', '射线的本质', '', '01010105010000'),
('01010105010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP01', '把放射源铀、钋或镭放入用铅做成的容器中，射线只能从容器的小孔射出，成为细细的一束。', '', '01010105010200'),
('01010105010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP02', '物理学家经过多方面的研究后确认α射线、β射线和γ射线具有以下特征。', '', '01010105010200'),
('01010105010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP03', 'α粒子带正电，电荷量是电子的2倍，质量是氢原子的4倍，其组成与氦原子核相同。', '', '01010105010200'),
('01010105010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP04', '由于α粒子带电，质量又比较大，通过气体时很容易把气体分子中的电子剥离，使气体电离。', '', '01010105010200'),
('01010105010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP05', '是电子流，速度可以接近光速。', '', '01010105010200'),
('01010105010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP06', 'β射线的电离作用较弱，穿透能力较强，很容易穿透黑纸，也能穿透几毫米厚的铝板。', '', '01010105010200'),
('01010105010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP07', 'γ射线的电离作用更弱，穿透能力更强，甚至能穿透几厘米厚的铅板和几十厘米厚的混凝土。', '', '01010105010200'),
('01010105010308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP08', '放射性的强度也不受温度、外界压强的影响。', '', '01010105010200'),
('01010105010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03', '原子核的组成', '', '01010105010000'),
('01010105010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP01', '绝大多数原子核的质量与电荷量之比都大于质子的相应比值。', '', '01010105010300'),
('01010105010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP02', '质子带正电荷，电荷量与一个电子的电荷量相等。', '', '01010105010300'),
('01010105010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP03', '由于中子不带电，原子核所带的电荷等于核内质子电荷的总和。', '', '01010105010300'),
('01010105010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP04', '元素的性质与原子核外的电子有密切的关系。', '', '01010105010300'),
('01010105020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02', '放射性元素的衰变', '', '01010105000000'),
('01010105020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01', '原子核的衰变', '', '01010105020000'),
('01010105020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP01', '铀238放出一个α粒子后，质量数减少4，电荷数减少2，成为新核。', '', '01010105020100'),
('01010105020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP02', '研究还表明，在一定条件下，原子核中的两个中子和两个质子会结合起来形成α粒子，并被释放出来。', '', '01010105020100'),
('01010105020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP03', '原子核的能量也跟原子的能量一样，其变化是不连续的，也只能取一系列不连续的数值，因此，也存在着能级，同样是能级越低越稳定。', '', '01010105020100'),
('01010105020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP04', '当放射性物质连续衰变时，原子核中有的发生α衰变，有的发生β衰变，同时伴随着γ射线辐射。', '', '01010105020100'),
('01010105020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02', '半衰期', '', '01010105020000'),
('01010105020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP01', '放射性同位素衰变的快慢有一定的规律。', '', '01010105020200'),
('01010105020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP02', '不同的放射性元素，半衰期不同，甚至差别非常大。', '', '01010105020200'),
('01010105020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP03', '放射性元素衰变的快慢是由核内部自身的因素决定的，跟原子所处的化学状态和外部条件没有关系。', '', '01010105020200'),
('01010105020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03', '核反应', '', '01010105020000'),
('01010105020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP01', '衰变是原子核的自发变化，科学家更希望人工控制原子核的变化。', '', '01010105020300'),
('01010105020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP02', '与衰变过程一样，在核反应中，质量数守恒、电荷数守恒。', '', '01010105020300'),
('01010105020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04', '放射性同位素及其应用', '', '01010105020000'),
('01010105020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP01', '天然放射性同位素不过40多种，而今天通过核反应生成的人工放射性同位素已达3000多种，每种元素都有了自己的放射性同位素。', '', '01010105020400'),
('01010105020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP02', '丰富的放射性同位素资源在国民经济和科学研究的各个领域中得到了广泛的应用。', '', '01010105020400'),
('01010105020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP03', '工业部门可以使用放射性同位素发出的射线来测厚度。', '', '01010105020400'),
('01010105020504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP04', '像癌细胞那样，不断迅速繁殖的、无法控制的细胞组织，在射线照射下破坏得比健康细胞快。', '', '01010105020400'),
('01010105020505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP05', '利用γ射线照射种子，会使种子的遗传基因发生变异，经过筛选，可以培育出新品种。', '', '01010105020400'),
('01010105020506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP06', '棉花在开花、结桃的时候需要较多的磷肥，把磷肥喷在棉花叶子上，磷肥也能被吸收。', '', '01010105020400'),
('01010105020507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP07', '如果用磷的放射性同位素制成肥料喷在棉花的叶面上，然后每隔一定时间用探测器测量棉株各部位的放射性强度，上面的问题就解决了。', '', '01010105020400'),
('01010105020508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP08', '人体甲状腺的工作需要碘，碘被吸收后聚集在甲状腺内。', '', '01010105020400'),
('01010105020509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP09', '定时用探测器测量甲状腺及邻近组织的放射强度，有助于诊断甲状腺的疾病。', '', '01010105020400'),
('01010105020510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP10', '近年来，有关生物大分子的结构及其功能的研究，几乎都要借助于示踪原子。', '', '01010105020400'),
('01010105020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05', '辐射与安全', '', '01010105020000'),
('01010105020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05.OP01', '人类一直生活在放射性的环境中。', '', '01010105020500'),
('01010105030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03', '核力与结合能', '', '01010105000000'),
('01010105030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01', '核力与四种基本相互作用', '', '01010105030000'),
('01010105030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP01', '现代物理学认为，自然界存在着四种基本相互作用，即引力相互作用、电磁相互作用、强相互作用和弱相互作用。', '', '01010105030100'),
('01010105030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP02', '17世纪下半叶，人们发现了万有引力。', '', '01010105030100'),
('01010105030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP03', '引力是自然界的一种基本相互作用，地面物体所受的重力只是引力在地球表面附近的一种表现。', '', '01010105030100'),
('01010105030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP04', '20世纪，物理学家发现原子核是由若干带正电荷的质子和不带电的中子组成的。', '', '01010105030100'),
('01010105030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP05', '19世纪末20世纪初，物理学家发现了放射现象。', '', '01010105030100'),
('01010105030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP06', '①弱相互作用是引起原子核β衰变的原因，即引起中子—质子转变的原因。', '', '01010105030100'),
('01010105030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02', '结合能', '', '01010105030000'),
('01010105030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP01', '相距很远的两个物体，由于万有引力而相互接近，运动速度越来越大，引力势能转化为动能。', '', '01010105030200'),
('01010105030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP02', '最后撞在一起，一部分动能变成热并散失掉了。', '', '01010105030200'),
('01010105030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP03', '比结合能越大，原子核中核子结合得越牢固，原子核越稳定。', '', '01010105030200'),
('01010105030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03', '质量亏损', '', '01010105030000'),
('01010105030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP01', '原子核的结合能很难直接测量。', '', '01010105030300'),
('01010105030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP02', '质量亏损表明，的确存在着原子核的结合能。', '', '01010105030300'),
('01010105040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04', '核裂变与核聚变', '', '01010105000000'),
('01010105040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01', '核裂变的发现', '', '01010105040000'),
('01010105040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP01', '以铀235为例，核裂变时产生两或三个中子。', '', '01010105040100'),
('01010105040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP02', '原子核的体积非常小，原子内部的空隙很大，如果铀块不够大，中子在铀块中通过时，很有可能碰不到铀核而跑到铀块外面去，链式反应不能继续。', '', '01010105040100'),
('01010105040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP03', '只有当铀块足够大时，核裂变产生的中子才有足够大的概率打中某个铀核，使链式反应进行下去。', '', '01010105040100'),
('01010105040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP04', '铀核裂变时如果生成物不同，释放的能量也有差异。', '', '01010105040100'),
('01010105040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02', '反应堆与核电站', '', '01010105040000'),
('01010105040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP01', '原子核的链式反应也可以在人工控制下进行。', '', '01010105040200'),
('01010105040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP02', '其实，在第一个原子弹制成以前，科学家们已经实现了核能的可控释放。', '', '01010105040200'),
('01010105040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP03', '1942年，美籍意大利物理学家费米就主持建立了世界上第一个称为“核反应堆”的装置，首次通过可控制的链式反应实现了核能的释放。', '', '01010105040200'),
('01010105040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP04', '常用的慢化剂有石墨、重水和普通水（也叫轻水）。', '', '01010105040200'),
('01010105040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP05', '为了调节中子数目以控制反应速度，还需要在铀棒之间插进一些镉棒。', '', '01010105040200'),
('01010105040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP06', '核燃料发生核裂变释放的能量使反应区温度升高。', '', '01010105040200'),
('01010105040307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP07', '水或液态的金属钠等流体在反应堆内外循环流动，把反应堆内的热量传输出去，用于发电，同时也使反应堆冷却。', '', '01010105040200'),
('01010105040308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP08', '在热交换器内，第二回路中的水被加热生成高温高压蒸汽，驱动汽轮机运转。', '', '01010105040200'),
('01010105040309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP09', '建造核电站时需要特别注意防止放射线和放射性物质的泄漏，以避免射线对人体的伤害和放射性物质对水源、空气和工作场所造成放射性污染。', '', '01010105040200'),
('01010105040310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP10', '核反应堆中的核废料具有很强的放射性，需要装入特制的容器，深埋地下。', '', '01010105040200'),
('01010105040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03', '核聚变', '', '01010105040000'),
('01010105040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP01', '当物质的温度达到几百万开尔文时，剧烈的热运动使得一部分原子核具有足够的动能，可以克服库仑斥力，碰撞时十分接近，发生核聚变。', '', '01010105040300'),
('01010105040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP02', '太阳的主要成分是氢。', '', '01010105040300'),
('01010105040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP03', '现在地球上消耗的能量，追根溯源，绝大部分还是来自太阳，即太阳内部核聚变时释放的核能。', '', '01010105040300'),
('01010105040404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP04', '太阳在“核燃烧”的过程中“体重”不断减轻。', '', '01010105040300'),
('01010105040405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP05', '太阳的寿命已经有50亿年了。', '', '01010105040300'),
('01010105040406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP06', '目前，人工产生的热核反应主要用在核武器上，那就是氢弹。', '', '01010105040300'),
('01010105040407', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP07', '与核裂变相比，核聚变有很多优点。', '', '01010105040300'),
('01010105040408', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP08', '利用核聚变物质的惯性进行约束。', '', '01010105040300'),
('01010105040409', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP09', '总的来说，实现受控核聚变还有一段很长的路要走。', '', '01010105040300'),
('01010105050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05', '“基本”粒子', '', '01010105000000'),
('01010105050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01', '发现新粒子', '', '01010105050000'),
('01010105050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP01', '从20世纪30年代以来，人们在对宇宙线的研究中陆续发现了一些新的粒子。', '', '01010105050100'),
('01010105050202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP02', '1932年发明的粒子加速器，能使带电粒子加速到很高的能量。', '', '01010105050100'),
('01010105050203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP03', '利用加速器和对撞机，人们发现了更多的粒子。', '', '01010105050100'),
('01010105050204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP04', '粒子物理的研究不再单纯依赖宇宙线。', '', '01010105050100'),
('01010105050205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP05', '高能量的对撞机往往极为庞大和复杂。', '', '01010105050100'),
('01010105050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02', '粒子的分类', '', '01010105050000'),
('01010105050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP01', '现在已经发现的粒子达400多种。', '', '01010105050200'),
('01010105050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP02', '强子是参与强相互作用的粒子。', '', '01010105050200'),
('01010105050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP03', '质子和中子都是强子。', '', '01010105050200'),
('01010105050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP04', '轻子不参与强相互作用。', '', '01010105050200'),
('01010105050305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP05', '最早发现的轻子是电子，后来发现的轻子有电子中微子、μ子、μ子中微子以及τ子和τ子中微子。', '', '01010105050200'),
('01010105050306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP06', '每种轻子都有对应的反粒子。', '', '01010105050200'),
('01010105050307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP07', '现代实验还没有发现轻子的内部结构。', '', '01010105050200'),
('01010105050308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP08', '光子传递电磁相互作用，中间玻色子传递弱相互作用，胶子传递强相互作用。', '', '01010105050200'),
('01010105050309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP09', '希格斯玻色子是希格斯场的量子激发。', '', '01010105050200'),
('01010105050310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP10', '基本粒子因与希格斯场耦合而获得质量。', '', '01010105050200'),
('01010105050311', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP11', '2012年，欧洲核子研究中心利用大型强子对撞机发现了希格斯玻色子。', '', '01010105050200'),
('01010105050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03', '研究样例', '', '01010105050000'),
('01010105050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP01', '①燃气灶烧水时的能量转化和转移的情况如下。', '', '01010105050300'),
('01010105050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP02', '记录数据时室温为26℃，基本上保证了实验环境温度的恒定。', '', '01010105050300'),
('01010105050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP03', '为减小水在高温时的放热损失，应缩短水在高温时的加热时间。', '', '01010105050300'),
