INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010101000000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01', '运动的描述', '', NULL),
('01010101010000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01', '质点   参考系', '', '01010101000000'),
('01010101010100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP01', '物体和质点', '', '01010101010000'),
('01010101010201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP01.OP01', '可以把物体看成一个点，什么样的问题不能把物体看成一个点。', '', '01010101010100'),
('01010101010202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP01.OP02', '人类居住的地球在绕太阳公转，同时又在自转。', '', '01010101010100'),
('01010101010203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP01.OP03', '还有一种情况，虽然不能忽略物体的大小和形状，但是，物体上各点的运动情况完全相同。', '', '01010101010100'),
('01010101010204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP01.OP04', '从前面的讨论可以看出，一个物体能否看成质点是由所要研究的问题决定的。', '', '01010101010100'),
('01010101010205', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP01.OP05', '同一个物体，由于所要研究的问题不同，有时可以看成质点，有时不能看成质点。', '', '01010101010100'),
('01010101010200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP02', '参考系', '', '01010101010000'),
('01010101010301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP02.OP01', '自然界的一切物体都处于永恒的运动中，绝对静止的物体是不存在的。', '', '01010101010200'),
('01010101010302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP02.OP02', '在描述一个物体的运动时，参考系可以任意选择。', '', '01010101010200'),
('01010101010303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP02.OP03', '参考系选取得当，会使问题的研究变得简洁、方便。', '', '01010101010200'),
('01010101010304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP02.OP04', '通常情况下，在讨论地面上物体的运动时，都以地面为参考系。', '', '01010101010200'),
('01010101020000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02', '时间   位移', '', '01010101000000'),
('01010101020100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP01', '时刻和时间间隔', '', '01010101020000'),
('01010101020201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP01.OP01', '要描述物体位置随时间的变化，首先要清楚“时间”一词的含义。', '', '01010101020100'),
('01010101020202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP01.OP02', '说到时间，不能不说时刻和时间间隔。', '', '01010101020100'),
('01010101020203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP01.OP03', '时刻和时间间隔既有联系又有区别。', '', '01010101020100'),
('01010101020204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP01.OP04', '在表示时间的数轴上，时刻用点表示，时间间隔用线段表示。', '', '01010101020100'),
('01010101020200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP02', '位置和位移', '', '01010101020000'),
('01010101020301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP02.OP01', '既可以乘火车，也可以乘飞机，还可以先乘火车到武汉，然后乘轮船沿长江而上。', '', '01010101020200'),
('01010101020300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP03', '直线运动的位移', '', '01010101020000'),
('01010101020400', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP04', '位移—时间图像', '', '01010101020000'),
('01010101020500', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP05', '位移和时间的测量', '', '01010101020000'),
('01010101020601', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP05.OP01', '学校实验室中常用打点计时器来记录时间和位移。', '', '01010101020500'),
('01010101020602', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP05.OP02', '如果把纸带和运动的物体连在一起，纸带上各点之间的距离就表示相应时间间隔中物体的位移大小。', '', '01010101020500'),
('01010101030000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03', '位置变化快慢的描述——速度', '', '01010101000000'),
('01010101030100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP01', '速度', '', '01010101030000'),
('01010101030201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP01.OP01', '不同的运动，位置变化的快慢往往不同，也就是说，运动的快慢不同。', '', '01010101030100'),
('01010101030200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP02', '平均速度和瞬时速度', '', '01010101030000'),
('01010101030301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP02.OP01', '一般来说，物体在某一段时间内，运动的快慢通常是变化的。', '', '01010101030200'),
('01010101030302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP02.OP02', '匀速直线运动是瞬时速度保持不变的运动。', '', '01010101030200'),
('01010101030303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP02.OP03', '在匀速直线运动中，平均速度与瞬时速度相等。', '', '01010101030200'),
('01010101030304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP02.OP04', '日常生活中说到的“速度”，有时是指速率，要根据上下文判断。', '', '01010101030200'),
('01010101030300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP03', '速度—时间图像', '', '01010101030000'),
('01010101040000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04', '速度变化快慢的描述——加速度', '', '01010101000000'),
('01010101040100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP01', '加速度', '', '01010101040000'),
('01010101040201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP01.OP01', '看来“速度变化的快慢”是一个不同于“速度”的概念。', '', '01010101040100'),
('01010101040202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP01.OP02', '两个物体速度变化相同，所用时间短的当然速度变化得快。', '', '01010101040100'),
('01010101040203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP01.OP03', '要比较位置变化的快慢，可以用位移除以时间。', '', '01010101040100'),
('01010101040204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP01.OP04', '同理，要比较速度变化的快慢，可以用速度的变化量除以时间。', '', '01010101040100'),
('01010101040205', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP01.OP05', '现在讨论做直线运动的物体加速度的方向。', '', '01010101040100'),
('01010101040200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP02', '加速度的方向', '', '01010101040000'),
('01010101040300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP03', '从', '', '01010101040000'),
('01010101040400', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP04', '-', '', '01010101040000'),
('01010101040500', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP05', '图像看加速度', '', '01010101040000'),
('01010101040601', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP05.OP01', '比为加速度，其比值为该直线的斜率。', '', '01010101040500'),
('01010101040602', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP05.OP02', '学习是一个不断探究、积累和总结的过程。', '', '01010101040500'),
('01010101040603', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP05.OP03', '学习的每一阶段都应该适时地总结，把学到的知识、方法、过程和体会等及时地加以梳理，以便更好地理解和把握。', '', '01010101040500');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010102000000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02', '匀变速直线运动的研究', '', NULL),
('01010102010000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01', '实验：探究小车速度随时间变化的规律', '', '01010102000000'),
('01010102010100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP01', '实验思路', '', '01010102010000'),
('01010102010201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP01.OP01', '要研究小车速度随时间变化的规律，就要想办法测量小车在不同时刻的瞬时速度，而打点计时器具有此功能。', '', '01010102010100'),
('01010102010202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP01.OP02', '小车在槽码的牵引下运动，通过研究纸带上的信息，就可以知道小车运动的速度是怎样随时间变化的。', '', '01010102010100'),
('01010102010200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP02', '数据分析', '', '01010102010000'),
('01010102010300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP03', '进行实验', '', '01010102010000'),
('01010102010401', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP03.OP01', '把小车停在靠近打点计时器的位置。', '', '01010102010300'),
('01010102010402', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP03.OP02', '随后，立即关闭电源。', '', '01010102010300'),
('01010102010403', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP03.OP03', '增减所挂的槽码（或在小车上放置重物），更换纸带，再做两次实验。', '', '01010102010300'),
('01010102010400', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP04', '数据记录', '', '01010102010000'),
('01010102010501', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP04.OP01', '为了便于测量，舍掉纸带开头一些过于密集的点，找一个适当的点作为计时起点。', '', '01010102010400'),
('01010102020000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC02', '匀变速直线运动的速度与时间的关系', '', '01010102000000'),
('01010102020100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC02.KP01', '匀变速直线运动', '', '01010102020000'),
('01010102020200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC02.KP02', '速度与时间的关系', '', '01010102020000'),
('01010102020301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC02.KP02.OP01', '（1）汽车做匀加速直线运动。', '', '01010102020200'),
('01010102030000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03', '匀变速直线运动的位移与时间的关系', '', '01010102000000'),
('01010102030100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03.KP01', '匀变速直线运动的位移', '', '01010102030000'),
('01010102030201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03.KP01.OP01', '（2）飞机在航母上降落时，需用阻拦索使飞机迅速停下来。', '', '01010102030100'),
('01010102030202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03.KP01.OP02', '某同学乘坐动车时，通过观察里程碑和车厢内电子屏上显示的动车速度来估算动车减速进站时的加速度大小。', '', '01010102030100'),
('01010102030200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03.KP02', '速度与位移的关系', '', '01010102030000'),
('01010102030301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03.KP02.OP01', '沿动车运动方向为正方向建立一维坐标系。', '', '01010102030200'),
('01010102030302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03.KP02.OP02', '从第2节和第3节的例题可以看到，只有建立了坐标系，速度、加速度等物理量的正负号才能确定。', '', '01010102030200'),
('01010102040000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04', '自由落体运动', '', '01010102000000'),
('01010102040100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01', '自由落体运动', '', '01010102040000'),
('01010102040201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01.OP01', '伽利略认为，根据亚里士多德“重的物体下落得快”的论断，会推出相互矛盾的结论。', '', '01010102040100'),
('01010102040202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01.OP02', '根据仔细的分析，伽利略认为物体下落的运动只有一种可能性：重的物体与轻的物体应该下落得同样快。', '', '01010102040100'),
('01010102040203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01.OP03', '由实验可以看到，将玻璃管里的空气抽出去后，没有了空气阻力的影响，轻的物体和重的物体下落得同样快。', '', '01010102040100'),
('01010102040204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01.OP04', '在现实生活中人们之所以看到物体下落的快慢不同，是因为空气阻力的影响。', '', '01010102040100'),
('01010102040205', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01.OP05', '如果没有空气阻力，所有物体下落的快慢都一样。', '', '01010102040100'),
('01010102040206', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01.OP06', '在有空气的空间，如果空气阻力的作用比较小，可以忽略，物体的下落可以近似看作自由落体运动。', '', '01010102040100'),
('01010102040200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP02', '自由落体加速度', '', '01010102040000'),
('01010102040301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP02.OP01', '自由落体运动是初速度为0的匀加速直线运动，所以匀变速直线运动的基本公式及其推论都适用于自由落体运动。', '', '01010102040200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010103000000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03', '相互作用——力', '', NULL),
('01010103010000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01', '重力与弹力', '', '01010103000000'),
('01010103010100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01', '重力', '', '01010103010000'),
('01010103010201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01.OP01', '重力的施力物体是地球，方向竖直向下。', '', '01010103010100'),
('01010103010202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01.OP02', '做自由落体运动的物体只受到重力的作用。', '', '01010103010100'),
('01010103010203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01.OP03', '质量均匀分布的物体，重心的位置只跟物体的形状有关。', '', '01010103010100'),
('01010103010204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01.OP04', '质量分布不均匀的物体，重心的位置除了跟物体的形状有关外，还跟物体内质量的分布有关。', '', '01010103010100'),
('01010103010205', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01.OP05', '力可以用有向线段表示。', '', '01010103010100'),
('01010103010206', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01.OP06', '有向线段的长短表示力的大小，箭头表示力的方向，箭尾（或箭头）表示力的作用点。', '', '01010103010100'),
('01010103010200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP02', '弹力', '', '01010103010000'),
('01010103010301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP02.OP01', '有时物体的形变很小，不易被觉察。', '', '01010103010200'),
('01010103010300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP03', '胡克定律', '', '01010103010000'),
('01010103010401', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP03.OP01', '弹簧在形变时产生的弹力与弹簧的伸长量是有关系的。', '', '01010103010300'),
('01010103020000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02', '摩擦力', '', '01010103000000'),
('01010103020100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02.KP01', '滑动摩擦力', '', '01010103020000'),
('01010103020201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02.KP01.OP01', '滑动摩擦力的方向总是沿着接触面，并且跟物体相对运动的方向相反。', '', '01010103020100'),
('01010103020200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02.KP02', '静摩擦力', '', '01010103020000'),
('01010103020301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02.KP02.OP01', '静摩擦力的方向总是跟物体相对运动趋势的方向相反。', '', '01010103020200'),
('01010103020302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02.KP02.OP02', '只要沙发与地面间没有产生相对运动，静摩擦力的大小就随着推力的增大而增大，并与推力保持大小相等。', '', '01010103020200'),
('01010103020303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02.KP02.OP03', '皮带运输机能把货物送往高处，也是静摩擦力作用的结果。', '', '01010103020200'),
('01010103030000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03', '牛顿第三定律', '', '01010103000000'),
('01010103030100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP01', '作用力和反作用力', '', '01010103030000'),
('01010103030201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP01.OP01', '力是物体对物体的作用。', '', '01010103030100'),
('01010103030202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP01.OP02', '只要谈到力，就一定存在着受力物体和施力物体。', '', '01010103030100'),
('01010103030203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP01.OP03', '观察和实验的结果表明，两个物体之间的作用总是相互的。', '', '01010103030100'),
('01010103030204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP01.OP04', '当一个物体对另一个物体施加了力，后一个物体一定同时对前一个物体也施加了力。', '', '01010103030100'),
('01010103030205', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP01.OP05', '作用力和反作用力总是互相依赖、同时存在的。', '', '01010103030100'),
('01010103030200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP02', '牛顿第三定律', '', '01010103030000'),
('01010103030301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP02.OP01', '在生活和生产中应用牛顿第三定律的例子是很多的。', '', '01010103030200'),
('01010103030302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP02.OP02', '陷在泥泞中的汽车，尽管车轮飞转，但是如果泥和车轮之间太滑，车轮得不到足够的摩擦力，车也是出不来的。', '', '01010103030200'),
('01010103030303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP02.OP03', '许多越野车可以按需要分别由前轮或后轮驱动，必要时甚至可以四轮同时驱动，以便根据车轮与地面接触的不同情况来获得足够的驱动力。', '', '01010103030200'),
('01010103030300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP03', '物体受力的初步分析', '', '01010103030000'),
('01010103030401', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP03.OP01', '分析物体的受力，有两条思路。', '', '01010103030300'),
('01010103040000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04', '力的合成和分解', '', '01010103000000'),
('01010103040100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP01', '合力和分力', '', '01010103040000'),
('01010103040200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP02', '力的合成和分解', '', '01010103040000'),
('01010103040301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP02.OP01', '由于各个力的数据都没有改变，因此，力的分解也遵从平行四边形定则。', '', '01010103040200'),
('01010103040302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP02.OP02', '一个已知力究竟应该怎样分解，要根据具体问题来确定。', '', '01010103040200'),
('01010103040300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP03', '矢量和标量', '', '01010103040000'),
('01010103040401', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP03.OP01', '力的合成，可以认为是力的相加。', '', '01010103040300'),
('01010103040402', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP03.OP02', '二力相加时，不能简单地把两个力的大小相加，而要按平行四边形定则来确定合力的大小和方向。', '', '01010103040300'),
('01010103040403', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP03.OP03', '除了力和位移以外，速度、加速度都是矢量。', '', '01010103040300'),
('01010103050000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC05', '共点力的平衡', '', '01010103000000'),
('01010103050100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC05.KP01', '共点力平衡的条件', '', '01010103050000'),
('01010103050201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC05.KP01.OP01', '桌上的书、屋顶的灯、随传送带匀速运送的物体、沿直线公路匀速前进的汽车，都处于平衡状态。', '', '01010103050100'),
('01010103050202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC05.KP01.OP02', '二力平衡时物体所受的合力为0。', '', '01010103050100'),
('01010103050203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC05.KP01.OP03', '方法1用两个力的合力和第三个力平衡的方法求解。', '', '01010103050100');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010104000000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04', '运动和力的关系', '', NULL),
('01010104010000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01', '牛顿第一定律', '', '01010104000000'),
('01010104010100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP01', '理想实验的魅力', '', '01010104010000'),
('01010104010201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP01.OP01', '伽利略认为，将人们引入歧途的是摩擦，而物体在通常情况下运动时，摩擦又是难以避免的。', '', '01010104010100'),
('01010104010202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP01.OP02', '若没有摩擦，球将永远运动下去。', '', '01010104010100'),
('01010104010203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP01.OP03', '如果没有摩擦，小球将到达原来的高度。', '', '01010104010100'),
('01010104010204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP01.OP04', '如果第二个斜面倾角减小，小球仍将到达原来的高度，但是运动的距离更长。', '', '01010104010100'),
('01010104010200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP02', '牛顿第一定律', '', '01010104010000'),
('01010104010301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP02.OP01', '牛顿第一定律也叫作惯性定律。', '', '01010104010200'),
('01010104010302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP02.OP02', '任何物体都和周围的物体有相互作用，不受力作用的物体是不存在的。', '', '01010104010200'),
('01010104010303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP02.OP03', '牛顿第一定律揭示了运动和力的关系：力不是维持物体运动状态的原因，而是改变物体运动状态的原因。', '', '01010104010200'),
('01010104010304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP02.OP04', '牛顿第一定律为力学的发展奠定了坚实的基础。', '', '01010104010200'),
('01010104010300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP03', '惯性与质量', '', '01010104010000'),
('01010104010401', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP03.OP01', '①牛顿第一定律涉及两个重要的物理概念：力和惯性。', '', '01010104010300'),
('01010104010402', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP03.OP02', '质量大的石块需要的力大。', '', '01010104010300'),
('01010104010403', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP03.OP03', '大量事例说明，不同质量的物体，惯性的大小是不一样的。', '', '01010104010300'),
('01010104010404', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP03.OP04', '质量只有大小，没有方向，是标量。', '', '01010104010300'),
('01010104020000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02', '实验：探究加速度与力、质量的关系', '', '01010104000000'),
('01010104020100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01', '实验思路', '', '01010104020000'),
('01010104020201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01.OP01', '将小车置于水平木板上，通过滑轮与槽码相连。', '', '01010104020100'),
('01010104020202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01.OP02', '小车可以在槽码的牵引下运动。', '', '01010104020100'),
('01010104020203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01.OP03', '保持小车质量不变，通过改变槽码的个数改变小车所受的拉力。', '', '01010104020100'),
('01010104020204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01.OP04', '小车所受的拉力可认为与槽码所受的重力相等。', '', '01010104020100'),
('01010104020205', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01.OP05', '测得不同拉力下小车运动的加速度，分析加速度与拉力的变化情况，找出二者之间的定量关系。', '', '01010104020100'),
('01010104020206', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01.OP06', '保持小车所受的拉力不变，通过在小车上增加重物改变小车的质量。', '', '01010104020100'),
('01010104020200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP02', '物理量的测量', '', '01010104020000'),
('01010104020301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP02.OP01', '可以用天平测量质量。', '', '01010104020200'),
('01010104020302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP02.OP02', '为了改变小车的质量，可以在小车中增减砝码的数量。', '', '01010104020200'),
('01010104020303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP02.OP03', '方法2将打点计时器的纸带连在小车上，根据纸带上打出的点来测量加速度（参考第一章的实验方法）。', '', '01010104020200'),
('01010104020304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP02.OP04', '现实中，仅受一个力作用的物体几乎不存在。', '', '01010104020200'),
('01010104020300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP03', '进行实验', '', '01010104020000'),
('01010104020400', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP04', '数据分析', '', '01010104020000'),
('01010104020501', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP04.OP01', '根据各组实验数据在坐标纸上描点。', '', '01010104020400'),
('01010104030000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03', '牛顿第二定律', '', '01010104000000'),
('01010104030100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP01', '牛顿第二定律的表达式', '', '01010104030000'),
('01010104030201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP01.OP01', '牛顿第二定律不仅阐述了力、质量和加速度三者数量间的关系，还明确了加速度的方向与力的方向一致。', '', '01010104030100'),
('01010104030200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP02', '力的单位', '', '01010104030000'),
('01010104030301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP02.OP01', '假定试车过程中汽车受到的阻力不变。', '', '01010104030200'),
('01010104030302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP02.OP02', '取消动力后，汽车做匀减速直线运动。', '', '01010104030200'),
('01010104030303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP02.OP03', '在某次测定中，悬线与竖直方向的夹角为θ，求列车的加速度。', '', '01010104030200'),
('01010104030304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP02.OP04', '方法1选择小球为研究对象。', '', '01010104030200'),
('01010104040000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04', '力学单位制', '', '01010104000000'),
('01010104040100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP01', '基本单位', '', '01010104040000'),
('01010104040201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP01.OP01', '的单位（米、秒）就是基本单位。', '', '01010104040100'),
('01010104040200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP02', '国际单位制', '', '01010104040000'),
('01010104040301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP02.OP01', '如果采用不同的物理量作为基本量，或者虽然采用相同的基本量，但采用的基本单位不同，导出单位自然随之不同，从而产生不同的单位制。', '', '01010104040200'),
('01010104040302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP02.OP02', '不同的地区使用不同的单位制，不方便交流。', '', '01010104040200'),
('01010104040303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP02.OP03', '以静止的物体为研究对象。', '', '01010104040200'),
('01010104040304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP02.OP04', '既然如此，在统一已知量的单位后，计算过程中就不必写出各量后面的单位，只在数字计算式后面写出正确的单位就可以了。', '', '01010104040200'),
('01010104050000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05', '牛顿运动定律的应用', '', '01010104000000'),
('01010104050100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP01', '从受力确定运动情况', '', '01010104050000'),
('01010104050201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP01.OP01', '如果已知物体的受力情况，可以由牛顿第二定律求出物体的加速度，再通过运动学的规律确定物体的运动情况。', '', '01010104050100'),
('01010104050202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP01.OP02', '按比赛规则，投掷冰壶运动员的队友，可以用毛刷在冰壶滑行前方来回摩擦冰面，减小冰面的动摩擦因数以调节冰壶的运动。', '', '01010104050100'),
('01010104050203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP01.OP03', '（1）选择滑行的冰壶为研究对象。', '', '01010104050100'),
('01010104050200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP02', '从运动情况确定受力', '', '01010104050000'),
('01010104050301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP02.OP01', '如果已知物体的运动情况，可以根据运动学规律求出物体的加速度，结合受力分析，再根据牛顿第二定律求出力。', '', '01010104050200'),
('01010104050302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP02.OP02', '以滑雪者为研究对象。', '', '01010104050200'),
('01010104050303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP02.OP03', '滑雪者沿山坡向下做匀加速直线运动。', '', '01010104050200'),
('01010104060000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06', '超重和失重', '', '01010104000000'),
('01010104060100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP01', '重力的测量', '', '01010104060000'),
('01010104060201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP01.OP01', '在地球表面附近，物体由于地球的吸引而受到重力。', '', '01010104060100'),
('01010104060200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02', '超重和失重', '', '01010104060000'),
('01010104060301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP01', '体重计的示数称为视重，反映了人对体重计的压力。', '', '01010104060200'),
('01010104060302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP02', '设竖直向下方向为坐标轴正方向。', '', '01010104060200'),
('01010104060303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP03', '设竖直向上方向为坐标轴正方向。', '', '01010104060200'),
('01010104060304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP04', '实际中有许多领域涉及超重和失重现象。', '', '01010104060200'),
('01010104060305', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP05', '如果是载人航天，在火箭发射阶段，航天员要承受数倍于自身体重的压力。', '', '01010104060200'),
('01010104060306', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP06', '只有很好地研究材料、机械结构、人体自身所能承受的压力问题，才能使火箭成功发射、航天员顺利飞向太空。', '', '01010104060200'),
('01010104060307', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP07', '课堂上的学习是一种学习方式。', '', '01010104060200'),
('01010104060308', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP08', '本栏目中提供了一个“研究样例”和一个“参考选题”。', '', '01010104060200'),
('01010104060309', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP09', '通过阅读样例，大致了解应该如何进行课题研究，包括：课题研究的选题是怎样提出的，研究的证据是怎样收集的，怎样通过对证据的解释形成研究结论，如何撰写课题研究报告等。', '', '01010104060200'),
('01010104060310', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP10', '在学习样例后，同学们要自己选择一个课题进行研究，写出研究报告。', '', '01010104060200'),
('01010104060300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03', '研究样例', '', '01010104060000'),
('01010104060401', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP01', '人们散步时并不觉得空气有阻力，但跑步时就会明显感觉迎面有风阻挡。', '', '01010104060300'),
('01010104060402', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP02', '当人站在高速行驶的敞篷车上时，会感到迎面阻挡的风力很大。', '', '01010104060300'),
('01010104060403', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP03', '鸟在飞行中受到的阻力是很小的。', '', '01010104060300'),
('01010104060404', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP04', '本课题以气球为实验对象，并保持同一气球的体积不变，探究球形物体在空气中运动时空气阻力的大小跟速率的定量关系。', '', '01010104060300'),
('01010104060405', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP05', '本实验需要测量两个物理量：气球所受空气阻力的大小和气球运动的速率。', '', '01010104060300'),
('01010104060406', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP06', '如果气球下落时的空气阻力恰好等于重力，气球将做匀速运动。', '', '01010104060300'),
('01010104060407', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP07', '让气球从空中下落，随着下落速率的增大，气球所受的空气阻力也增大，当空气阻力增大到跟重力相等时，气球便做匀速运动。', '', '01010104060300'),
('01010104060408', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP08', '如果判断气球下落一段高度后已在做匀速运动，则此时空气阻力的大小就等于气球和重物所受的总重力（因重物的体积很小，作用在重物上的空气阻力可以忽略不计）。', '', '01010104060300'),
('01010104060409', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP09', '气球的速率可以用数码相机测量。', '', '01010104060300'),
('01010104060410', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP10', '尺上的具体读数就可以计算气球的速率。', '', '01010104060300'),
('01010104060411', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP11', '之后，请一名同学在刻度尺的上方释放气球，另一名同学用数码相机在刻度尺前拍摄气球下落的视频，并得到分帧照片。', '', '01010104060300'),
('01010104060400', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP04', '参考选题', '', '01010104060000'),
('01010104060501', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP04.OP01', '橡皮筋具有类似弹簧的力学性质，日常生活中常常用橡皮筋来代替弹簧。', '', '01010104060400'),
('01010104060502', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP04.OP02', '在弹性限度内，弹簧弹力与弹簧形变量是成正比的。', '', '01010104060400'),
('01010104060503', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP04.OP03', '实验或者引发了理论的建立，或者对新建立的理论进行检验。', '', '01010104060400'),
('01010104060504', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP04.OP04', '总之，物理学离不开实验，要学好物理就要做好实验。', '', '01010104060400'),
('01010104060500', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05', '实验推动了物理学的发展', '', '01010104060000'),
('01010104060601', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP01', '在电磁学研究中，法拉第在10年中坚持实验，为电磁感应定律的建立打下了基础。', '', '01010104060500'),
('01010104060602', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP02', '赫兹用实验证明了电磁波的存在。', '', '01010104060500'),
('01010104060603', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP03', '在粒子物理学中有一个物理量叫作宇称。', '', '01010104060500'),
('01010104060604', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP04', '物理学家们曾经认为粒子经历的各种过程中宇称都是守恒的。', '', '01010104060500'),
('01010104060605', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP05', '她除了用实验证实宇称不守恒外，还用实验证实了电磁相互作用与弱相互作用的密切关系，对后来电、弱统一理论的提出起了重要作用。', '', '01010104060500'),
('01010104060606', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP06', '今天，物理学中的许多学说还在等待实验的证实或否定。', '', '01010104060500'),
('01010104060607', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP07', '物体与物体之间有万有引力。', '', '01010104060500'),
('01010104060608', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP08', '20世纪70年代初，韦伯首创用铝棒做“天线”接收天体辐射的引力波。', '', '01010104060500'),
('01010104060609', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP09', '为了提高灵敏度，“天线”重达数吨，为了排除干扰，“天线”置于－270℃左右的超低温环境中。', '', '01010104060500'),
('01010104060610', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP10', '直到2016年，科学家用激光干涉仪代替铝棒作为“天线”，建成了引力波激光干涉仪探测装置，才首次探测到了来自于双黑洞合并的引力波的信号。', '', '01010104060500'),
('01010104060600', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06', '怎样做好物理实验', '', '01010104060000'),
('01010104060701', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP01', '下面是做实验大致要涉及的几个方面。', '', '01010104060600'),
('01010104060702', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP02', '做实验有利于透彻理解物理知识。', '', '01010104060600'),
('01010104060703', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP03', '不论做哪一类物理实验，都要搞清楚实验所根据的物理知识。', '', '01010104060600'),
('01010104060704', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP04', '同时，实验器材的工作原理也需要了解。', '', '01010104060600'),
('01010104060705', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP05', '首先，要保证人身安全。', '', '01010104060600'),
('01010104060706', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP06', '凡涉及用电的器材要小心处理。', '', '01010104060600'),
('01010104060707', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP07', '要注意保护眼睛，凡在高速转动、可能出现爆炸、可能有液体溅出的场合，最好戴上护目眼镜。', '', '01010104060600'),
('01010104060708', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP08', '其次，要保证器材的安全。', '', '01010104060600'),
('01010104060709', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP09', '各种仪器都必须按照要求使用。', '', '01010104060600'),
('01010104060710', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP10', '……最后，操作要谨慎。', '', '01010104060600'),
('01010104060711', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP11', '实验的每一阶段之后都要对其进行评估，也就是回想刚才的操作有没有什么问题，可能引起什么误差，等等。', '', '01010104060600'),
('01010104060712', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP12', '实验来不得半点粗心和虚假。', '', '01010104060600'),
('01010104060713', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP13', '实验数据从来不以人的意志而改变。', '', '01010104060600'),
('01010104060714', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP14', '要获得正确的实验结果，离不开严谨、求真的态度。', '', '01010104060600'),
('01010104060715', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP15', '不能随意更改记录去“凑数”。', '', '01010104060600'),
('01010104060716', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP16', '实验后要仔细分析数据，从中得出结论。', '', '01010104060600'),
('01010104060717', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP17', '把自己的实验成果告诉别人，听取别人的批评和质疑，同时，从别人的成果中受到启发。', '', '01010104060600'),
('01010104060700', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07', '误差和有效数字', '', '01010104060000'),
('01010104060801', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP01', '任何测量结果都不可能绝对准确。', '', '01010104060700'),
('01010104060802', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP02', '由仪器直接读出的数据叫作直接测量结果。', '', '01010104060700'),
('01010104060803', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP03', '直接测量结果的误差来源于两方面。', '', '01010104060700'),
('01010104060804', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP04', '又如，中学实验室中温度计的分度值为1℃，靠目测只能估计到分度值的101，肯定会有误差。', '', '01010104060700'),
('01010104060805', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP05', '通过改进仪器和谨慎操作，可以减小误差，但任何仪器的测量都不能完全消灭误差。', '', '01010104060700'),
('01010104060806', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP06', '人读仪表时，眼睛的位置可能稍向左偏或稍向右偏，电表指针由于轴上的摩擦，停下来的位置可能一次偏大、一次偏小。', '', '01010104060700'),
('01010104060807', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP07', '偶然误差的特点是，当多次重复测量时，偏大和偏小的机会比较接近，可以用取平均值的方法来减小偶然误差。', '', '01010104060700'),
('01010104060808', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP08', '系统误差的特点是，多次重复测量的结果总是大于（或小于）被测量的真实值，呈现单一倾向。', '', '01010104060700'),
('01010104060809', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP09', '限于刻度尺的精确度，测量有一定的误差。', '', '01010104060700'),
('01010104060810', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP10', '①相对误差是一个比值，没有单位，通常用百分数表示。', '', '01010104060700'),
('01010104060811', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP11', '小数中最前一位非零数字前面的0是表示小数点的位置的，不是有效数字。', '', '01010104060700'),
('01010104060812', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP12', '实验的结果往往是由若干直接测量值经过运算得到的，每一个直接测量值的误差都对最后结果产生影响，应当按一定的数学方法来确定运算结果的有效数字。', '', '01010104060700');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010201000000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01', '抛体运动', '', NULL),
('01010201010000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC01', '曲线运动', '', '01010201000000'),
('01010201010100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC01.KP01', '曲线运动的速度方向', '', '01010201010000'),
('01010201010201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC01.KP01.OP01', '运动员掷链球时，链球在手的牵引下做曲线运动，一旦运动员放手，链球即刻飞出。', '', '01010201010100'),
('01010201010202', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC01.KP01.OP02', '放手的时刻不同，链球飞出的方向也不一样。', '', '01010201010100'),
('01010201010203', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC01.KP01.OP03', '速度是矢量，既有大小，又有方向。', '', '01010201010100'),
('01010201010204', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC01.KP01.OP04', '由于曲线运动中速度的方向是变化的，所以曲线运动是变速运动。', '', '01010201010100'),
('01010201010200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC01.KP02', '物体做曲线运动的条件', '', '01010201010000'),
('01010201010301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC01.KP02.OP01', '物体做曲线运动时，由于速度方向时刻改变，物体的加速度一定不为0，因此，物体所受的合力一定不为0。', '', '01010201010200'),
('01010201010302', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC01.KP02.OP02', '由实验可以看出，当钢球受到的合力的方向与速度方向不在同一条直线上时，钢球做曲线运动。', '', '01010201010200'),
('01010201010303', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC01.KP02.OP03', '生活中也有大量类似的例子。', '', '01010201010200'),
('01010201010304', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC01.KP02.OP04', '当物体受力的方向与速度的方向不在同一直线上时，加速度的方向也就与速度的方向不在同一直线上了，于是物体的速度方向要发生变化，物体就做曲线运动。', '', '01010201010200'),
('01010201020000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC02', '运动的合成与分解', '', '01010201000000'),
('01010201020100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC02.KP01', '一个平面运动的实例', '', '01010201020000'),
('01010201020201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC02.KP01.OP01', '要想定量地研究蜡块的运动，就要建立坐标系，具体分析。', '', '01010201020100'),
('01010201020202', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC02.KP01.OP02', '研究物体的运动时，坐标系的选取很重要。', '', '01010201020100'),
('01010201020203', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC02.KP01.OP03', '研究物体在平面内的运动时，可以选择平面直角坐标系。', '', '01010201020100'),
('01010201020204', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC02.KP01.OP04', '要确定蜡块运动的轨迹，首先要确定任意时刻蜡块的位置。', '', '01010201020100'),
('01010201020200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC02.KP02', '运动的合成与分解', '', '01010201020000'),
('01010201020301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC02.KP02.OP01', '运动的合成与分解遵从矢量运算法则。', '', '01010201020200'),
('01010201020302', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC02.KP02.OP02', '运动的合成与分解是分析复杂运动时常用的方法。', '', '01010201020200'),
('01010201020303', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC02.KP02.OP03', '虽然本节实验中的两个分运动都是匀速运动，但运动合成与分解的思想和方法对分运动是变速运动的情况也是适用的。', '', '01010201020200'),
('01010201030000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC03', '实验：探究平抛运动的特点', '', '01010201000000'),
('01010201030100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC03.KP01', '实验思路', '', '01010201030000'),
('01010201030201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC03.KP01.OP01', '平抛运动是曲线运动，速度和位移的方向都在时刻变化。', '', '01010201030100'),
('01010201030200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC03.KP02', '进行实验', '', '01010201030000'),
('01010201030301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC03.KP02.OP01', '研究思路确定后，还需要设计可操作的方案以便进行实验。', '', '01010201030200'),
('01010201030302', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC03.KP02.OP02', '设法记录做平抛运动的物体经过相等时间间隔所到达的位置，获得水平方向和竖直方向的位移随时间变化的具体数据，便可以独立分析水平方向和竖直方向的运动规律。', '', '01010201030200'),
('01010201030303', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC03.KP02.OP03', '可以用频闪照相或者录制视频的方法，记录物体在不同时刻的位置。', '', '01010201030200'),
('01010201030304', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC03.KP02.OP04', '在水平和竖直两个方向中，先研究其中一个方向的运动规律，再设法分析另外一个方向的运动规律。', '', '01010201030200'),
('01010201030305', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC03.KP02.OP05', '后面参考案例给出的方案，是先研究物体在竖直方向的运动，再研究水平方向的运动。', '', '01010201030200'),
('01010201030306', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC03.KP02.OP06', '也可以反过来，先研究物体在水平方向的运动，再研究竖直方向的运动。', '', '01010201030200'),
('01010201040000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC04', '抛体运动的规律', '', '01010201000000'),
('01010201040100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC04.KP01', '平抛运动的速度', '', '01010201040000'),
('01010201040200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC04.KP02', '平抛运动的位移与轨迹', '', '01010201040000'),
('01010201040301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC04.KP02.OP01', '（1）求小球下落的时间。', '', '01010201040200'),
('01010201040302', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC04.KP02.OP02', '（2）求小球释放点与落地点之间的水平距离。', '', '01010201040200'),
('01010201040300', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC04.KP03', '一般的抛体运动', '', '01010201040000'),
('01010201040401', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH01.SEC04.KP03.OP01', '仿照平抛运动的处理方法也能得到描述斜抛运动的几个关系式。', '', '01010201040300');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010202000000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02', '圆周运动', '', NULL),
('01010202010000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC01', '圆周运动', '', '01010202000000'),
('01010202010100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC01.KP01', '线速度', '', '01010202010000'),
('01010202010200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC01.KP02', '角速度', '', '01010202010000'),
('01010202010301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC01.KP02.OP01', '由于匀速圆周运动是线速度大小不变的运动，物体在相等时间内通过的弧长相等，所以物体在相等时间内转过的角度也相等。', '', '01010202010200'),
('01010202010300', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC01.KP03', '周期', '', '01010202010000'),
('01010202010401', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC01.KP03.OP01', '圆周运动有其特殊性，物体运动一周后又会返回到初始位置，周而复始地运动着。', '', '01010202010300'),
('01010202010402', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC01.KP03.OP02', '技术中常用转速来描述物体做圆周运动的快慢。', '', '01010202010300'),
('01010202010400', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC01.KP04', '线速度与角速度的关系', '', '01010202010000'),
('01010202010501', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC01.KP04.OP01', '线速度的大小描述了做圆周运动的物体沿着圆弧运动的快慢，角速度的大小描述了物体与圆心连线扫过角度的快慢。', '', '01010202010400'),
('01010202020000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC02', '向心力', '', '01010202000000'),
('01010202020100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC02.KP01', '向心力', '', '01010202020000'),
('01010202020201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC02.KP01.OP01', '做圆周运动的物体，其运动状态在不断变化，说明物体一定受到了力的作用。', '', '01010202020100'),
('01010202020202', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC02.KP01.OP02', '忽略小球运动时受到的阻力，在桌面上做匀速圆周运动的小球所受的合力为细线的拉力，拉力即为使小球做圆周运动的力，根据拉力的特点可以知道拉力的方向指向圆心。', '', '01010202020100'),
('01010202020203', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC02.KP01.OP03', '大量实例都表明：做匀速圆周运动的物体所受的合力总指向圆心。', '', '01010202020100'),
('01010202020204', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC02.KP01.OP04', '对于做匀速圆周运动的物体，物体的速度大小不发生改变，因此，所受合力只改变速度的方向。', '', '01010202020100'),
('01010202020205', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC02.KP01.OP05', '应该强调的是，向心力并不是像重力、弹力、摩擦力那样作为具有某种性质的力来命名的。', '', '01010202020100'),
('01010202020206', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC02.KP01.OP06', '因为沙袋还受到重力的作用，手所提供的拉力不完全是向心力。', '', '01010202020100'),
('01010202020200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC02.KP02', '向心力的大小', '', '01010202020000'),
('01010202020301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC02.KP02.OP01', '在物理学中，认识物理量时可以先定性了解，然后再探究不同物理量之间的定量关系。', '', '01010202020200'),
('01010202020300', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC02.KP03', '变速圆周运动和一般曲线运动的受力特点', '', '01010202020000'),
('01010202030000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC03', '向心加速度', '', '01010202000000'),
('01010202030100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC03.KP01', '匀速圆周运动的加速度方向', '', '01010202030000'),
('01010202030201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC03.KP01.OP01', '用运动学的方法求向心加速度的方向，在本节后的“拓展学习”中会涉及。', '', '01010202030100'),
('01010202030200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC03.KP02', '匀速圆周运动的加速度大小', '', '01010202030000'),
('01010202030301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC03.KP02.OP01', '通过计算说明：要增大夹角θ，应该增大小球运动的角速度ω。', '', '01010202030200'),
('01010202040000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04', '生活中的圆周运动', '', '01010202000000'),
('01010202040100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP01', '火车转弯', '', '01010202040000'),
('01010202040201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP01.OP01', '火车转弯时实际是在做圆周运动，因而具有向心加速度。', '', '01010202040100'),
('01010202040202', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP01.OP02', '如果认为做匀速圆周运动的物体除了受到另外物体的作用，还要再受一个向心力，那就不对了。', '', '01010202040100'),
('01010202040200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP02', '汽车过拱形桥', '', '01010202040000'),
('01010202040301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP02.OP01', '汽车过拱形桥时的运动也可以看作圆周运动。', '', '01010202040200'),
('01010202040302', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP02.OP02', '公路在通过小型水库泄洪闸的下游时常常要修建凹形路面，也叫“过水路面”。', '', '01010202040200'),
('01010202040303', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP02.OP03', '同学们可以仿照上面的方法自己进行分析。', '', '01010202040200'),
('01010202040300', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP03', '航天器中的失重现象', '', '01010202040000'),
('01010202040400', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP04', '离心运动', '', '01010202040000'),
('01010202040501', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP04.OP01', '做圆周运动的物体，由于惯性，总有沿着切线方向飞出去的倾向。', '', '01010202040400'),
('01010202040502', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP04.OP02', '一旦向心力突然消失，物体就会沿切线方向飞出去。', '', '01010202040400'),
('01010202040503', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP04.OP03', '离心运动有很多应用。', '', '01010202040400'),
('01010202040504', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP04.OP04', '在炼钢厂中，把熔化的钢水浇入圆柱形模子，模子沿圆柱的中心轴线高速旋转，钢水由于离心运动趋于周壁，冷却后就形成无缝钢管。', '', '01010202040400'),
('01010202040505', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP04.OP05', '离心运动有时也会带来危害。', '', '01010202040400'),
('01010202040506', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH02.SEC04.KP04.OP06', '高速转动的砂轮、飞轮等，都不得超过允许的最大转速。', '', '01010202040400');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010203000000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03', '万有引力与宇宙航行', '', NULL),
('01010203010000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC01', '行星的运动', '', '01010203000000'),
('01010203010100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC01.KP01', '开普勒定律', '', '01010203010000'),
('01010203010201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC01.KP01.OP01', '所有行星绕太阳运动的轨道都是椭圆，太阳处在椭圆的一个焦点上。', '', '01010203010100'),
('01010203020000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02', '万有引力定律', '', '01010203000000'),
('01010203020100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP01', '行星与太阳间的引力', '', '01010203020000'),
('01010203020201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP01.OP01', '行星绕太阳的运动可以看作匀速圆周运动。', '', '01010203020100'),
('01010203020202', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP01.OP02', '历史上科学家们的探索之路充满艰辛。', '', '01010203020100'),
('01010203020203', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP01.OP03', '伽利略、开普勒及笛卡儿都提出过自己的解释。', '', '01010203020100'),
('01010203020204', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP01.OP04', '牛顿时代的科学家，如胡克和哈雷等对此作出了重要的贡献。', '', '01010203020100'),
('01010203020205', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP01.OP05', '太阳与行星间引力的方向沿着二者的连线。', '', '01010203020100'),
('01010203020200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP02', '月—地检验', '', '01010203020000'),
('01010203020301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP02.OP01', '在牛顿的时代，人们已经能够比较精确地测定自由落体加速度，当时也能比较精确地测定月球与地球的距离、月球公转的周期，从而能够算出月球运动的向心加速度。', '', '01010203020200'),
('01010203020302', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP02.OP02', '计算结果与预期符合得很好。', '', '01010203020200'),
('01010203020300', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP03', '万有引力定律', '', '01010203020000'),
('01010203020401', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP03.OP01', '很可能有，只是由于身边物体的质量比天体的质量小得多，不易觉察罢了。', '', '01010203020300'),
('01010203020402', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP03.OP02', '尽管以上推广是十分自然的，但仍要接受事实的直接或间接的检验。', '', '01010203020300'),
('01010203020400', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP04', '引力常量', '', '01010203020000'),
('01010203020501', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP04.OP01', '一百多年以后，英国物理学家卡文迪什通过实验测量了几个铅球之间的引力。', '', '01010203020400'),
('01010203020502', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP04.OP02', '引力常量是自然界中少数几个最重要的物理常量之一。', '', '01010203020400'),
('01010203020503', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC02.KP04.OP03', '引力常量的普适性成了万有引力定律正确性的有力证据。', '', '01010203020400'),
('01010203030000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03', '万有引力理论的成就', '', '01010203000000'),
('01010203030100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP01', '“称量”地球的质量', '', '01010203030000'),
('01010203030200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP02', '计算天体的质量', '', '01010203030000'),
('01010203030301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP02.OP01', '如果已知卫星绕行星运动的周期和卫星与行星之间的距离，也可以算出行星的质量。', '', '01010203030200'),
('01010203030302', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP02.OP02', '月球虽然没有天然的卫星，但人类发射的航天器会环绕月球运行，只要测得航天器绕月运行的轨道半径和周期，就可计算月球的质量。', '', '01010203030200'),
('01010203030300', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP03', '发现未知天体', '', '01010203030000'),
('01010203030401', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP03.OP01', '到了18世纪，人们已经知道太阳系有7颗行星，其中1781年发现的第七颗行星——天王星的运动轨道有些“古怪”：根据万有引力定律计算出来的轨道与实际观测的结果总有一些偏差。', '', '01010203030300'),
('01010203030402', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP03.OP02', '……英国剑桥大学的学生亚当斯和法国年轻的天文学家勒维耶相信未知行星的存在。', '', '01010203030300'),
('01010203030403', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP03.OP03', '海王星的发现过程充分显示了理论对于实践的巨大指导作用，所用的“计算、预测和观察”的方法指导人们寻找新的天体。', '', '01010203030300'),
('01010203030404', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP03.OP04', '尽管如此，黑暗寒冷的太阳系边缘依然牵动着人们的心，探索工作从来没有停止过。', '', '01010203030300'),
('01010203030400', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP04', '预言哈雷彗星回归', '', '01010203030000'),
('01010203030501', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP04.OP01', '在牛顿之前，彗星被看作是一种神秘的现象。', '', '01010203030400'),
('01010203030502', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP04.OP02', '海王星的发现和哈雷彗星的“按时回归”确立了万有引力定律的地位，也成为科学史上的美谈。', '', '01010203030400'),
('01010203030503', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP04.OP03', '诺贝尔奖获得者物理学家劳厄说：“没有任何东西像牛顿引力理论对行星轨道的计算那样，如此有力地树立起人们对年轻的物理学的尊敬。', '', '01010203030400'),
('01010203030504', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP04.OP04', '万有引力定律可以用于分析地球表面重力加速度微小差异的原因，以及指导重力探矿。', '', '01010203030400'),
('01010203030505', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC03.KP04.OP05', '除了上述成就外，万有引力定律的另一重要应用将在下节讲述。', '', '01010203030400'),
('01010203040000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04', '宇宙航行', '', '01010203000000'),
('01010203040100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP01', '宇宙速度', '', '01010203040000'),
('01010203040201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP01.OP01', '物体在地球附近绕地球运动时，太阳的作用可以忽略。', '', '01010203040100'),
('01010203040202', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP01.OP02', '在简化之后，物体只受到指向地心的引力作用，物体绕地球的运动可视作匀速圆周运动。', '', '01010203040100'),
('01010203040200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP02', '人造地球卫星', '', '01010203040000'),
('01010203040301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP02.OP01', '直到多级火箭的研制成功，才为人造地球卫星的发射创造了条件。', '', '01010203040200'),
('01010203040302', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP02.OP02', '自首颗人造地球卫星发射后，人类已经发射了数千颗人造地球卫星，其中的通信、导航、气象等卫星已极大地改变了人类的生活。', '', '01010203040200'),
('01010203040303', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP02.OP03', '其中一种的轨道平面与赤道平面成0度角，运动方向与地球自转方向相同。', '', '01010203040200'),
('01010203040304', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP02.OP04', '因其相对地面静止，也称静止卫星。', '', '01010203040200'),
('01010203040300', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP03', '载人航天与太空探索', '', '01010203040000'),
('01010203040401', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP03.OP01', '人类一直有“飞天”的梦想，万有引力定律的发现，不仅破解了天上行星的运行规律，也为人类开辟了上天的理论之路。', '', '01010203040300'),
('01010203040402', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP03.OP02', '随着技术的进步，人类迈向太空的脚步越来越大，越来越坚实。', '', '01010203040300'),
('01010203040403', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP03.OP03', '飞船绕地球飞行14圈后安全降落。', '', '01010203040300'),
('01010203040404', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP03.OP04', '2021年4月29日，具备长期自主飞行能力的天和核心舱成功发射，标志着中国空间站在轨组装建造全面展开。', '', '01010203040300'),
('01010203040405', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP03.OP05', '地球是人类的摇篮，但是人类不会永远生活在摇篮里。', '', '01010203040300'),
('01010203040406', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC04.KP03.OP06', '尽管人类已经跨入太空，登上月球，但是，相对于宇宙之宏大，地球和月球不过是茫茫宇宙中的两粒尘埃；相对于宇宙之久长，人类历史不过是宇宙年轮上一道小小的刻痕……未来的探索之路还很长。', '', '01010203040300'),
('01010203050000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05', '相对论时空观与牛顿力学的局限性', '', '01010203000000'),
('01010203050100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01', '相对论时空观', '', '01010203050000'),
('01010203050201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01.OP01', '验和观测不符的观念，如绝对时间的概念，提出能够更好地解释实验事实的假设。', '', '01010203050100'),
('01010203050202', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01.OP02', '爱因斯坦假设：在不同的惯性参考系中，物理规律的形式都是相同的；真空中的光速在不同的惯性参考系中大小都是相同的。', '', '01010203050100'),
('01010203050203', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01.OP03', '在经典物理学家的头脑中，如果两个事件在一个参考系中是同时的，在另一个参考系中一定也是同时的。', '', '01010203050100'),
('01010203050204', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01.OP04', '假设一列火车沿平直轨道飞快地匀速行驶。', '', '01010203050100'),
('01010203050205', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01.OP05', '车厢中央的光源发出了一个闪光，闪光照到了车厢的前壁和后壁。', '', '01010203050100'),
('01010203050206', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01.OP06', '在爱因斯坦两个假设的基础上，经过严格的数学推导，可以得到下述结果。', '', '01010203050100'),
('01010203050207', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01.OP07', '（1）式和（2）式表明：运动物体的长度（空间距离）和物理过程的快慢（时间进程）都跟物体的运动状态有关。', '', '01010203050100'),
('01010203050208', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01.OP08', '要验证（1）（2）两式是否正确，首先要找到高速运动的物体。', '', '01010203050100'),
('01010203050209', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01.OP09', '观察到的现象与经典理论产生了矛盾。', '', '01010203050100'),
('01010203050210', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01.OP10', '由于平均寿命增大，飞行的距离也变大，因而在地面附近实际观测到的µ子的数量就大于经典理论作出的预言。', '', '01010203050100'),
('01010203050211', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01.OP11', '相对论时空观的第一次宏观验证是在1971年进行的。', '', '01010203050100'),
('01010203050212', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP01.OP12', '两架飞机各绕地球飞行一周后回到地面，与留在地面上的铯原子钟进行比较。', '', '01010203050100'),
('01010203050200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP02', '牛顿力学的成就与局限性', '', '01010203050000'),
('01010203050301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP02.OP01', '牛顿力学的基础是牛顿运动定律，万有引力定律的建立与应用更是确立了人们对牛顿力学的尊敬。', '', '01010203050200'),
('01010203050302', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP02.OP02', '牛顿力学在如此广阔的领域里与实际相符合，显示了牛顿运动定律的正确性和牛顿力学的魅力。', '', '01010203050200'),
('01010203050303', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH03.SEC05.KP02.OP03', '相对论与量子力学都没有否定过去的科学，而只认为过去的科学是自己在一定条件下的特殊情形。', '', '01010203050200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010204000000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04', '机械能守恒定律', '', NULL),
('01010204010000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC01', '功与功率', '', '01010204000000'),
('01010204010100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC01.KP01', '功', '', '01010204010000'),
('01010204010200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC01.KP02', '正功和负功', '', '01010204010000'),
('01010204010301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC01.KP02.OP01', '实际中，物体通常会受多个力的作用。', '', '01010204010200'),
('01010204010302', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC01.KP02.OP02', '求各力对雪橇做的总功。', '', '01010204010200'),
('01010204010300', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC01.KP03', '功率', '', '01010204010000'),
('01010204010401', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC01.KP03.OP01', '在物理学中，做功的快慢用功率表示。', '', '01010204010300'),
('01010204010402', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC01.KP03.OP02', '发动机的额定功率是汽车长时间行驶时所能输出的最大功率。', '', '01010204010300'),
('01010204020000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02', '重力势能', '', '01010204000000'),
('01010204020100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP01', '重力做的功', '', '01010204020000'),
('01010204020201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP01.OP01', '当物体的高度发生变化时，重力做功，势能发生变化：物体下降时重力做正功，势能减小；物体被举高时重力做负功，势能增大。', '', '01010204020100'),
('01010204020200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP02', '重力势能', '', '01010204020000'),
('01010204020300', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP03', '重力势能的相对性', '', '01010204020000'),
('01010204020401', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP03.OP01', '选择哪个水平面为参考平面，可视研究问题的方便而定。', '', '01010204020300'),
('01010204020402', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP03.OP02', '通常选择地面为参考平面。', '', '01010204020300'),
('01010204020403', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP03.OP03', '力势能也是正值；下方物体的高度是负值，重力势能也是负值。', '', '01010204020300'),
('01010204020400', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP04', '弹性势能', '', '01010204020000'),
('01010204020501', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP04.OP01', '弹性势能跟形变的大小有关系。', '', '01010204020400'),
('01010204020502', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP04.OP02', '被拉伸或压缩的长度越长，恢复原状过程中对外做的功就越多，弹簧的弹性势能就越大。', '', '01010204020400'),
('01010204020503', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP04.OP03', '不同的弹簧发生同样大小的形变，劲度系数越大，弹簧恢复原状过程中对外做的功就越多，因而弹簧的弹性势能就越大。', '', '01010204020400'),
('01010204020504', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP04.OP04', '势能也叫位能，与相互作用的物体的相对位置有关。', '', '01010204020400'),
('01010204020505', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC02.KP04.OP05', '重力势能是由地球和地面上物体的相对位置决定的，弹性势能是由发生弹性形变的物体各部分的相对位置决定的。', '', '01010204020400'),
('01010204030000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC03', '动能和动能定理', '', '01010204000000'),
('01010204030100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC03.KP01', '动能的表达式', '', '01010204030000'),
('01010204030201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC03.KP01.OP01', '大量实例说明，物体动能的变化和力对物体做的功密切相关。', '', '01010204030100'),
('01010204030200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC03.KP02', '动能定理', '', '01010204030000'),
('01010204030301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC03.KP02.OP01', '在应用动能定理时还应该注意到，力对物体做的功可以为正值，也可以为负值。', '', '01010204030200'),
('01010204030302', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC03.KP02.OP02', '合力做正功时，物体的动能增加；合力做负功时，物体的动能减少。', '', '01010204030200'),
('01010204040000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04', '机械能守恒定律', '', '01010204000000'),
('01010204040100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP01', '追寻守恒量', '', '01010204040000'),
('01010204040201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP01.OP01', '能量对于科学研究和日常生活有着巨大的影响，但要用一句话说清楚能量究竟是什么却非易事。', '', '01010204040100'),
('01010204040202', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP01.OP02', '其实，伽利略已经走到了机械能守恒的大门口，只是当时还没有“能量”的概念，因此，伽利略没有得出机械能守恒的结论。', '', '01010204040100'),
('01010204040203', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP01.OP03', '能量概念的引入是科学前辈们追寻守恒量的一个重要事例。', '', '01010204040100'),
('01010204040200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP02', '动能与势能的相互转化', '', '01010204040000'),
('01010204040301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP02.OP01', '物体沿光滑斜面滑下时，重力对物体做正功，物体的重力势能减少。', '', '01010204040200'),
('01010204040302', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP02.OP02', '不仅重力势能可以与动能相互转化，弹性势能也可以与动能相互转化。', '', '01010204040200'),
('01010204040303', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP02.OP03', '通过重力或弹力做功，机械能可以从一种形式转化成另一种形式。', '', '01010204040200'),
('01010204040300', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP03', '机械能守恒定律', '', '01010204040000'),
('01010204040401', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP03.OP01', '同样可以证明，在只有弹力做功的系统内，动能和弹性势能互相转化时总的机械能也保持不变。', '', '01010204040300'),
('01010204040402', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP03.OP02', '此种情况下，动能与重力势能可以互相转化，总的机械能守恒。', '', '01010204040300'),
('01010204040403', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP03.OP03', '如果阻力做的功较大，不能忽略，则机械能不守恒。', '', '01010204040300'),
('01010204040404', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP03.OP04', '守恒定律不仅给处理问题带来方便，而且有更深刻的意义。', '', '01010204040300'),
('01010204040405', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP03.OP05', '机械能守恒定律就是其中一个。', '', '01010204040300'),
('01010204040406', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC04.KP03.OP06', '正因为自然界存在着“守恒量”，而且，某些守恒定律的适用范围很广，所以，在物理学中寻求“守恒量”已经成为物理学研究的一种重要思想方法。', '', '01010204040300'),
('01010204050000', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05', '实验：验证机械能守恒定律', '', '01010204000000'),
('01010204050100', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP01', '实验思路', '', '01010204050000'),
('01010204050201', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP01.OP01', '自由下落的物体只受到重力作用，满足机械能守恒的条件。', '', '01010204050100'),
('01010204050202', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP01.OP02', '用细线悬挂的小球摆动时，细线的拉力与小球的运动方向垂直，对物体不做功。', '', '01010204050100'),
('01010204050200', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP02', '物理量的测量', '', '01010204050000'),
('01010204050301', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP02.OP01', '研究对象确定后，还需要明确所需测量的物理量和实验器材。', '', '01010204050200'),
('01010204050300', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP03', '数据分析', '', '01010204050000'),
('01010204050401', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP03.OP01', '人们在长期生活和劳动中，形成了许多动作习惯。', '', '01010204050300'),
('01010204050402', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP03.OP02', '特别是作为物理课程的学习者，研究人体动作的物理原理，对增强理论联系实际的意识和发展科学思维能力都具有积极的意义。', '', '01010204050300'),
('01010204050403', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP03.OP03', '本课题研究人甩手动作的物理原理。', '', '01010204050300'),
('01010204050404', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP03.OP04', '如果知道摄像时的帧频，就知道相邻两动作之间的时间间隔，便可以对各部位的位置－时间关系进行分析，从而发现隐含在甩手动作中的物理原理。', '', '01010204050300'),
('01010204050405', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP03.OP05', '从分析运动的视角看，人的上肢可以认为由上臂、前臂和手掌组成。', '', '01010204050300'),
('01010204050400', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP04', '研究样例', '', '01010204050000'),
('01010204050501', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP04.OP01', '否则水滴便会做离心运动，飞离手指。', '', '01010204050400'),
('01010204050502', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP04.OP02', '通过对照片的测量了解照片所反映的实物尺寸，首先要知道照片尺寸与实物尺寸的缩放比例。', '', '01010204050400'),
('01010204050503', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP04.OP03', '在手机上安装相应的应用程序，用手机的加速度传感器进行检验。', '', '01010204050400'),
('01010204050504', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP04.OP04', '因为很难把手机固定在指尖，所以测量指尖部位的加速度不容易操作。', '', '01010204050400'),
('01010204050505', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP04.OP05', '（不同型号的手机，加速度传感器所在的位置可能不同。', '', '01010204050400'),
('01010204050506', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP04.OP06', '请查找资料，采用合适的握法握紧手机，完成实验。', '', '01010204050400'),
('01010204050507', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP04.OP07', '手臂运动的最后一刻，指尖做圆周运动，圆心是腕关节，半径很小，由此产生了巨大的向心加速度。', '', '01010204050400'),
('01010204050508', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP04.OP08', '掷标枪竞赛是奥运会田径竞赛项目，掷标枪有以下关键动作。', '', '01010204050400'),
('01010204050509', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP04.OP09', '2.助跑：采用直线助跑，助跑后期，持枪手臂向后引枪。', '', '01010204050400'),
('01010204050510', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP04.OP10', '以上动作，包含了诸多物理原理。', '', '01010204050400'),
('01010204050500', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP05', '参考选题', '', '01010204050000'),
('01010204050601', 'PHYSICS', 'SENIOR', 'TERM_2', 'CH04.SEC05.KP05.OP01', '的动作习惯是通过有效经验的积累形成的，其中隐藏着许多物理原理。', '', '01010204050500');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010301000000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01', '静电场及其应用', '', NULL),
('01010301010000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01', '电荷', '', '01010301000000'),
('01010301010100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01', '电荷', '', '01010301010000'),
('01010301010201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP01', '公元前600年左右，古希腊学者泰勒斯就发现摩擦过的琥珀吸引轻小物体的现象。', '', '01010301010100'),
('01010301010202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP02', '此语意为摩擦过的琥珀能吸引像草芥一类的轻小物体。', '', '01010301010100'),
('01010301010203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP03', '自然界的电荷只有两种。', '', '01010301010100'),
('01010301010204', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP04', '正电荷的电荷量为正值，负电荷的电荷量为负值。', '', '01010301010100'),
('01010301010205', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP05', '每个原子中质子的正电荷数量与电子的负电荷数量一样多，所以整个原子对外界表现为电中性。', '', '01010301010100'),
('01010301010206', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP06', '原子内部的质子和中子被紧密地束缚在一起构成原子核，原子核的结构一般是很稳定的。', '', '01010301010100'),
('01010301010207', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP07', '通常离原子核较远的电子受到的束缚较弱，容易受到外界的作用而脱离原子。', '', '01010301010100'),
('01010301010208', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP08', '当两种物质组成的物体互相摩擦时，一些受束缚较弱的电子会转移到另一个物体上。', '', '01010301010100'),
('01010301010209', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP09', '不同物质的微观结构不同，由于原子或分子间的相互作用，原子中电子的多少和运动状况也不相同。', '', '01010301010100'),
('01010301010210', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP10', '绝缘体中几乎不存在能自由移动的电荷。', '', '01010301010100'),
('01010301010200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP02', '静电感应', '', '01010301010000'),
('01010301010301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP02.OP01', '当一个带电体靠近导体时，由于电荷间相互吸引或排斥，导体中的自由电荷便会趋向或远离带电体，使导体靠近带电体的一端带异种电荷，远离带电体的一端带同种电荷。', '', '01010301010200'),
('01010301010302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP02.OP02', '利用静电感应使金属导体带电的过程叫作感应起电。', '', '01010301010200'),
('01010301010300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP03', '电荷守恒定律', '', '01010301010000'),
('01010301010401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP03.OP01', '静电感应过程中导体中的自由电荷只是从导体的一部分转移到另一部分。', '', '01010301010300'),
('01010301010402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP03.OP02', '近代物理实验发现，在一定条件下，带电粒子可以产生或湮没。', '', '01010301010300'),
('01010301010403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP03.OP03', '不带电，所以电荷的代数和仍然不变。', '', '01010301010300'),
('01010301010400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP04', '元电荷', '', '01010301010000'),
('01010301010501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP04.OP01', '迄今为止，实验发现的最小电荷量就是电子所带的电荷量。', '', '01010301010400'),
('01010301010502', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP04.OP02', '在密立根实验之后，人们又做了许多测量。', '', '01010301010400'),
('01010301010503', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP04.OP03', '比荷也是一个重要的物理量。', '', '01010301010400'),
('01010301020000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02', '库仑定律', '', '01010301000000'),
('01010301020100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP01', '电荷之间的作用力', '', '01010301020000'),
('01010301020201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP01.OP01', '通过上面的实验可以看到，电荷之间的作用力随着电荷量的增大而增大，随着距离的增大而减小。', '', '01010301020100'),
('01010301020202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP01.OP02', '事实上，电荷之间的作用力与万有引力是否相似的问题早已引起当年一些研究者的注意，英国科学家卡文迪什和普里斯特利等人都确信“平方反比”规律适用于电荷间的力。', '', '01010301020100'),
('01010301020203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP01.OP03', '任何带电体都有形状和大小。', '', '01010301020100'),
('01010301020200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP02', '库仑的实验', '', '01010301020000'),
('01010301020301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP02.OP01', '库仑做实验用的装置叫作库仑扭秤。', '', '01010301020200'),
('01010301020300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03', '静电力计算', '', '01010301020000'),
('01010301020401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03.OP01', '差不多相当于一百万吨的物体所受的重力！', '', '01010301020300'),
('01010301020402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03.OP02', '通常，一把梳子和衣袖摩擦后所带的电荷量不到百万分之一库仑，但天空中发生闪电之前，巨大的云层中积累的电荷量可达几百库仑。', '', '01010301020300'),
('01010301020403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03.OP03', '试比较氢原子核与电子之间的静电力和万有引力。', '', '01010301020300'),
('01010301020404', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03.OP04', '库仑定律描述的是两个点电荷之间的作用力。', '', '01010301020300'),
('01010301020405', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03.OP05', '库仑定律是电磁学的基本定律之一。', '', '01010301020300'),
('01010301020406', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03.OP06', '库仑定律给出的虽然是点电荷之间的静电力，但是任何一个带电体都可以看成是由许多点电荷组成的。', '', '01010301020300'),
('01010301030000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03', '电场   电场强度', '', '01010301000000'),
('01010301030100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP01', '电场', '', '01010301030000'),
('01010301030201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP01.OP01', '电场以及磁场已被证明是一种客观存在。', '', '01010301030100'),
('01010301030202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP01.OP02', '场像分子、原子等实物粒子一样具有能量，因而场也是物质存在的一种形式。', '', '01010301030100'),
('01010301030203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP01.OP03', '应该指出，只有在研究运动的电荷，特别是运动状态迅速变化的电荷时，上述场的物质性才突显出来。', '', '01010301030100'),
('01010301030200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP02', '电场强度', '', '01010301030000'),
('01010301030301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP02.OP01', '电场是在与电荷的相互作用中表现出自己的特性的。', '', '01010301030200'),
('01010301030302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP02.OP02', '物理学中规定，电场中某点的电场强度的方向与正电荷在该点所受的静电力的方向相同。', '', '01010301030200'),
('01010301030300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP03', '点电荷的电场    电场强度的叠加', '', '01010301030000'),
('01010301030400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP04', '电场线', '', '01010301030000'),
('01010301030501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP04.OP01', '除了用数学公式描述电场外，形象地了解和描述电场中各点电场强度的大小和方向也很重要。', '', '01010301030400'),
('01010301030500', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP05', '匀强电场', '', '01010301030000'),
('01010301030601', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP05.OP01', '由于方向相同，匀强电场中的电场线应该是平行的；又由于电场强度大小相等，电场线的疏密程度应该是相同的。', '', '01010301030500'),
('01010301040000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04', '静电的防止与利用', '', '01010301000000'),
('01010301040100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP01', '静电平衡', '', '01010301040000'),
('01010301040201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP01.OP01', '处于静电平衡状态的导体，其内部的电场强度处处为0。', '', '01010301040100'),
('01010301040200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02', '尖端放电', '', '01010301040000'),
('01010301040301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02.OP01', '静电平衡时，导体内部没有净剩电荷，电荷只分布在导体的外表面。', '', '01010301040200'),
('01010301040302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02.OP02', '并且在导体外表面，越尖锐的位置，电荷的密度（单位面积的电荷量）越大，周围的电场强度越大。', '', '01010301040200'),
('01010301040303', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02.OP03', '第九章静电场及其应用19在一定条件下，导体尖端周围的强电场足以使空气中残留的带电粒子发生剧烈运动，并与空气分子碰撞从而使空气分子中的正负电荷分离。', '', '01010301040200'),
('01010301040304', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02.OP04', '中性的分子电离后变成带负电的自由电子和失去电子而带正电的离子。', '', '01010301040200'),
('01010301040305', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02.OP05', '当带电的雷雨云接近建筑物时，由于静电感应，金属棒出现与云层相反的电荷。', '', '01010301040200'),
('01010301040306', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02.OP06', '尖端放电会导致高压设备上电能的损失，所以高压设备中导体的表面应该尽量光滑。', '', '01010301040200'),
('01010301040300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP03', '静电屏蔽', '', '01010301040000'),
('01010301040401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP03.OP01', '处于静电平衡状态的导体内部没有电荷，电荷只分布在导体的外表面。', '', '01010301040300'),
('01010301040402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP03.OP02', '实现静电屏蔽不一定要用密封的金属容器，金属网也能起到屏蔽作用。', '', '01010301040300'),
('01010301040403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP03.OP03', '静电的危害可能随时发生。', '', '01010301040300'),
('01010301040400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP04', '静电吸附', '', '01010301040000'),
('01010301040501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP04.OP01', '静电虽然会有危害，但也可以利用。', '', '01010301040400'),
('01010301040502', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP04.OP02', '在电场中，带电粒子受到静电力的作用，向着电极运动，最后会被吸附在电极上。', '', '01010301040400'),
('01010301040503', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP04.OP03', '静电除尘用于粉尘较多的各种场所，除去有害的微粒，或者回收物资，如回收水泥粉尘。', '', '01010301040400'),
('01010301040504', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP04.OP04', '复印机也应用了静电吸附。', '', '01010301040400');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010302000000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02', '静电场中的能量', '', NULL),
('01010302010000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01', '电势能和电势', '', '01010302000000'),
('01010302010100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP01', '静电力做功的特点', '', '01010302010000'),
('01010302010201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP01.OP01', '节前“问题”中电荷所减少的能量，必定跟静电力做的功相关。', '', '01010302010100'),
('01010302010202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP01.OP02', '静电力做功具有跟重力做功一样的特点，即静电力做功的多少与路径无关，只与电荷在电场中的始、末位置有关。', '', '01010302010100'),
('01010302010200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP02', '电势能', '', '01010302010000'),
('01010302010301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP02.OP01', '应该注意，静电力做的功只能决定电势能的变化量，而不能决定电荷在电场中某点电势能的数值。', '', '01010302010200'),
('01010302010302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP02.OP02', '通常把电荷在离场源电荷无限远处的电势能规定为0，或把电荷在大地表面的电势能规定为0。', '', '01010302010200'),
('01010302010303', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP02.OP03', '重力势能属于地球和物体组成的系统，是地球和物体相互作用产生的。', '', '01010302010200'),
('01010302010300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP03', '电势', '', '01010302010000'),
('01010302010401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP03.OP01', '在规定了零电势点之后，电场中各点的电势可以是正值，也可以是负值。', '', '01010302010300'),
('01010302010402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP03.OP02', '电势只有大小，没有方向，是个标量。', '', '01010302010300'),
('01010302020000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC02', '电势差', '', '01010302000000'),
('01010302020100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC02.KP01', '电势差', '', '01010302020000'),
('01010302020201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC02.KP01.OP01', '选择不同的位置作为零电势点，电场中某点电势的数值也会改变，但电场中某两点之间电势的差值却保持不变。', '', '01010302020100'),
('01010302020200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC02.KP02', '等势面', '', '01010302020000'),
('01010302020301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC02.KP02.OP01', '与电场线的功能相似，等势面也是用来形象地描绘电场的。', '', '01010302020200'),
('01010302020302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC02.KP02.OP02', '在同一个等势面上，任何两点的电势都相等。', '', '01010302020200'),
('01010302030000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC03', '电势差与电场强度的关系', '', '01010302000000'),
('01010302040000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04', '电容器的电容', '', '01010302000000'),
('01010302040100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP01', '电容器', '', '01010302040000'),
('01010302040201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP01.OP01', '在两个相距很近的平行金属板中间夹上一层绝缘物质——电介质（空气也是一种电介质），就组成一个最简单的电容器，叫作平行板电容器。', '', '01010302040100'),
('01010302040202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP01.OP02', '实际上，任何两个彼此绝缘又相距很近的导体，都可以看成一个电容器。', '', '01010302040100'),
('01010302040200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP02', '电容', '', '01010302040000'),
('01010302040301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP02.OP01', '以上实验表明，电容器的电荷量变为原来的一半时，其两极板间的电势差也变为原来的一半。', '', '01010302040200'),
('01010302040300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03', '常用电容器', '', '01010302040000'),
('01010302040401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP01', '常用的电容器，从构造上看，可以分为固定电容器和可变电容器两类。', '', '01010302040300'),
('01010302040402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP02', '固定电容器的电容是固定不变的。', '', '01010302040300'),
('01010302040403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP03', '常用的有聚苯乙烯电容器和电解电容器。', '', '01010302040300'),
('01010302040404', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP04', '改变铝箔的面积和薄膜的厚度，可以制成不同电容的聚苯乙烯电容器。', '', '01010302040300'),
('01010302040405', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP05', '以陶瓷为电介质的固定电容器也很多。', '', '01010302040300'),
('01010302040406', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP06', '由于氧化膜很薄，所以电容较大。', '', '01010302040300'),
('01010302040407', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP07', '固定的一组铝片叫作定片，可以转动的一组铝片叫作动片。', '', '01010302040300'),
('01010302040408', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP08', '转动动片，使两组铝片的正对面积发生变化，电容就随着改变。', '', '01010302040300'),
('01010302040409', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP09', '超级电容器的充电时间短，储存电能多，放电功率大，使用寿命长。', '', '01010302040300'),
('01010302050000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05', '带电粒子在电场中的运动', '', '01010302000000'),
('01010302050100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01', '带电粒子在电场中的加速', '', '01010302050000'),
('01010302050201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01.OP01', '在现代科学实验和技术设备中，常常利用电场来改变或控制带电粒子的运动。', '', '01010302050100'),
('01010302050202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01.OP02', '利用电场使带电粒子加速，就是其中一种简单的情况。', '', '01010302050100'),
('01010302050203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01.OP03', '分析带电粒子加速的问题，常常有两种思路：一种是利用牛顿第二定律结合匀变速直线运动公式来分析；另一种是利用静电力做功结合动能定理来分析。', '', '01010302050100'),
('01010302050204', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01.OP04', '当解决的问题属于匀强电场且涉及运动时间等描述运动过程的物理量时，适合运用前一种思路分析；当问题只涉及位移、速率等动能定理公式中的物理量或非匀强电场情景时，适合运用后一种思路分析。', '', '01010302050100'),
('01010302050205', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01.OP05', '第十章静电场中的能量45对偶数圆筒的电势差为正值，此时位于和偶数圆筒相连的金属圆板（序号为0）中央的一个电子，在圆板和圆筒1之间的电场中由静止开始加速，沿中心轴线冲进圆筒1。', '', '01010302050100'),
('01010302050206', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01.OP06', '为使电子运动到圆筒与圆筒之间各个间隙中都能恰好使静电力的方向跟运动方向相同而不断加速，圆筒长度的设计必须遵照一定的规律。', '', '01010302050100'),
('01010302050200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP02', '带电粒子在电场中的偏转', '', '01010302050000'),
('01010302050301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP02.OP01', '在匀强电场中，带电粒子的运动轨迹是一条抛物线，类似平抛运动的轨迹。', '', '01010302050200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010303000000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03', '电路及其应用', '', NULL),
('01010303010000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC01', '电源和电流', '', '01010303000000'),
('01010303010100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC01.KP01', '电源', '', '01010303010000'),
('01010303010201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC01.KP01.OP01', '手电筒中的小灯泡能持续发光，是因为电路中有电源，使得电路中的电流能够持续存在。', '', '01010303010100'),
('01010303010200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC01.KP02', '恒定电流', '', '01010303010000'),
('01010303010301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC01.KP02.OP01', '在恒定电场的作用下，导体中的自由电荷做定向运动，在运动过程中与导体内不动的粒子不断碰撞，碰撞阻碍了自由电荷的定向运动，结果是大量自由电荷定向运动的平均速率不随时间变化。', '', '01010303010200'),
('01010303010302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC01.KP02.OP02', '单位时间内通过导体横截面的电荷量越多，电流就越大。', '', '01010303010200'),
('01010303020000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02', '导体的电阻', '', '01010303000000'),
('01010303020100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP01', '电阻', '', '01010303020000'),
('01010303020201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP01.OP01', '选取一个导体，研究导体两端的电压随导体中的电流的变化情况。', '', '01010303020100'),
('01010303020200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP02', '影响导体电阻的因素', '', '01010303020000'),
('01010303020300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03', '导体的电阻率', '', '01010303020000'),
('01010303020401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP01', '从表中可以看出，纯金属的电阻率较小，合金的电阻率较大。', '', '01010303020300'),
('01010303020402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP02', '连接电路的导线一般用电阻率小的铜来制作，必要时可在导线表面镀银。', '', '01010303020300'),
('01010303020403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP03', '由于用电器的电阻通常远大于导线的电阻，一般情况下，可以认为导线电阻为0。', '', '01010303020300'),
('01010303020404', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP04', '有些合金，如锰铜合金和镍铜合金，电阻率几乎不受温度变化的影响，常用来制作标准电阻。', '', '01010303020300'),
('01010303020405', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP05', '从上述关系式可以看出，在长度、横截面积一定的条件下，ρ越大，导体的电阻越大。', '', '01010303020300'),
('01010303020406', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP06', '金属的电阻率随温度的升高而增大。', '', '01010303020300'),
('01010303020407', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP07', '精密的电阻温度计是用铂做的。', '', '01010303020300'),
('01010303020408', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP08', '已知铂丝的电阻随温度的变化情况，测出铂丝的电阻就可以知道温度。', '', '01010303020300'),
('01010303020409', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP09', '当温度降低时，导体的电阻率将会减小。', '', '01010303020300'),
('01010303020410', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP10', '若用超导材料形成回路，一旦回路中有了电流，电流就将无损耗地持续下去。', '', '01010303020300'),
('01010303030000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03', '实验：导体电阻率的测量', '', '01010303000000'),
('01010303030100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP01', '实验1   长度的测量及测量工具的选用', '', '01010303030000'),
('01010303030201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP01.OP01', '长度是物理学中基本的物理量，长度的测量是最基本的测量。', '', '01010303030100'),
('01010303030202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP01.OP02', '根据测量要求的不同，可以选用不同的测量工具。', '', '01010303030100'),
('01010303030200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP02', '游标卡尺', '', '01010303030000'),
('01010303030301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP02.OP01', '当外（内）测量爪一侧的两个刃接触时，游标尺上的零刻度线与主尺上的零刻度线正好对齐。', '', '01010303030200'),
('01010303030302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP02.OP02', '合起来，就是被测物体的长度。', '', '01010303030200'),
('01010303030303', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP02.OP03', '请练习使用游标卡尺测量以下物体的尺寸。', '', '01010303030200'),
('01010303030304', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP02.OP04', '（1）用游标卡尺的外测量爪测量小钢球的直径、小圆管的外径、教科书的厚度等。', '', '01010303030200'),
('01010303030305', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP02.OP05', '（2）用游标卡尺的内测量爪测量小圆管的内径、槽的宽度等。', '', '01010303030200'),
('01010303030300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP03', '螺旋测微器', '', '01010303030000'),
('01010303030400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP04', '实验2 金属丝电阻率的测量', '', '01010303030000'),
('01010303030500', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP05', '实验思路', '', '01010303030000'),
('01010303030600', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP06', '物理量的测量', '', '01010303030000'),
('01010303030701', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP06.OP01', '电阻丝长度的测量工具应选用刻度尺。', '', '01010303030600'),
('01010303030702', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP06.OP02', '使用螺旋测微器测量以下物体的尺寸。', '', '01010303030600'),
('01010303030703', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP06.OP03', '（2）测量头发丝的直径。', '', '01010303030600'),
('01010303030704', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP06.OP04', '因为电阻丝比较细，所以直接用刻度尺测量就会产生比较大的误差。', '', '01010303030600'),
('01010303030705', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP06.OP05', '下面提供了几种测量直径的方案供大家选用。', '', '01010303030600'),
('01010303030700', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP07', '数据分析', '', '01010303030000'),
('01010303030801', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP07.OP01', '将三个测量值代入上述公式，计算得出导体的电阻率。', '', '01010303030700'),
('01010303040000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04', '串联电路和并联电路', '', '01010303000000'),
('01010303040100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP01', '串、并联电路中的电流', '', '01010303040000'),
('01010303040200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP02', '串、并联电路中的电压', '', '01010303040000'),
('01010303040301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP02.OP01', '同样，几个电阻右边的电势也相同。', '', '01010303040200'),
('01010303040300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP03', '串、并联电路中的电阻', '', '01010303040000'),
('01010303040400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP04', '电压表和电流表的电路结构', '', '01010303040000'),
('01010303040501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP04.OP01', '常用的电压表和电流表都是由小量程的电流表（表头）改装而成的。', '', '01010303040400'),
('01010303040502', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP04.OP02', '表头的工作原理涉及磁场对通电导线的作用。', '', '01010303040400'),
('01010303040503', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP04.OP03', '从电路的角度看，表头就是一个电阻，同样遵从欧姆定律。', '', '01010303040400'),
('01010303040504', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP04.OP04', '电流表可以看作一个电流可读的“小电阻”。', '', '01010303040400'),
('01010303050000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05', '实验：练习使用多用电表', '', '01010303000000'),
('01010303050100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP01', '认识多用电表', '', '01010303050000'),
('01010303050201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP01.OP01', '表的上半部分为表盘，下半部分是选择开关，开关周围标有测量功能的区域及量程。', '', '01010303050100'),
('01010303050200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02', '使用多用电表', '', '01010303050000'),
('01010303050301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP01', '使用前应该调整指针定位螺丝，使指针指到零刻度。', '', '01010303050200'),
('01010303050302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP02', '使用时，应先将选择开关旋转到与被测物理量对应的位置上并选择合适的量程。', '', '01010303050200'),
('01010303050303', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP03', '将多用电表的选择开关旋至直流电压挡，注意选择大于小灯泡两端电压估计值的量程。', '', '01010303050200'),
('01010303050304', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP04', '根据表盘上相应量程的直流电压刻度进行读数，记录小灯泡两端的电压值。', '', '01010303050200'),
('01010303050305', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP05', '将多用电表的选择开关旋至直流电流挡，注意选择大于通过小灯泡电流估计值的量程。', '', '01010303050200'),
('01010303050306', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP06', '闭合开关，根据表盘上相应量程的直流电流刻度进行读数，记录通过小灯泡的电流值。', '', '01010303050200'),
('01010303050307', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP07', '使用多用电表的欧姆挡测电阻时，如果指针偏转过大、过小，读数误差都会比较大。', '', '01010303050200'),
('01010303050308', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP08', '如果不能估计未知电阻的大小，可以先用中等倍率的某个欧姆挡试测，然后根据读数的大小选择合适的挡位再次测量，电阻值为读数乘倍率。', '', '01010303050200'),
('01010303050309', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP09', '数字式多用电表的测量值以数字形式直接显示，使用方便。', '', '01010303050200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010304000000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04', '电能  能量守恒定律', '', NULL),
('01010304010000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01', '电路中的能量转化', '', '01010304000000'),
('01010304010100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP01', '电功和电功率', '', '01010304010000'),
('01010304010201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP01.OP01', '电流做功的实质是，导体中的恒定电场对自由电荷的静电力在做功。', '', '01010304010100'),
('01010304010200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP02', '焦耳定律', '', '01010304010000'),
('01010304010301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP02.OP01', '电流做功，究竟电能会转化为哪种形式的能量，要看电路中有哪种类型的用电器。', '', '01010304010200'),
('01010304010302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP02.OP02', '是指电流通过导体发热的功率。', '', '01010304010200'),
('01010304010300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP03', '电路中的能量转化', '', '01010304010000'),
('01010304010401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP03.OP01', '从能量转化与守恒的角度看，电动机从电源获得能量，一部分转化为机械能，还有一部分转化为内能。', '', '01010304010300'),
('01010304010402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP03.OP02', '电动机的转子与轴承均有摩擦，另外还有空气阻力。', '', '01010304010300'),
('01010304010403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP03.OP03', '同样，对于正在充电的电池，电能除了转化为化学能之外，还有一部分转化为内能。', '', '01010304010300'),
('01010304020000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02', '闭合电路的欧姆定律', '', '01010304000000'),
('01010304020100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP01', '电动势', '', '01010304020000'),
('01010304020201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP01.OP01', '在金属导体中，能够自由移动的电荷是自由电子。', '', '01010304020100'),
('01010304020202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP01.OP02', '在电源内部，存在着由正极指向负极的电场。', '', '01010304020100'),
('01010304020203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP01.OP03', '在电源内部，电源移动电荷，增加电荷的电势能。', '', '01010304020100'),
('01010304020204', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP01.OP04', '电动势由电源中非静电力的特性决定，跟外电路无关。', '', '01010304020100'),
('01010304020205', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP01.OP05', '对于常用的干电池来说，电动势跟电池的体积无关。', '', '01010304020100'),
('01010304020200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP02', '闭合电路欧姆定律及其能量分析', '', '01010304020000'),
('01010304020300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP03', '路端电压与负载的关系', '', '01010304020000'),
('01010304020401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP03.OP01', '负载变化时，电路中的电流就会变化，路端电压也随之变化。', '', '01010304020300'),
('01010304020402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP03.OP02', '电流过大，会导致温度过高，烧坏电源，甚至引起火灾。', '', '01010304020300'),
('01010304030000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03', '实验：电池电动势和内阻的测量', '', '01010304000000'),
('01010304030100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP01', '实验思路', '', '01010304030000'),
('01010304030201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP01.OP01', '电动势和内阻都是电源的重要参数。', '', '01010304030100'),
('01010304030202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP01.OP02', '根据闭合电路欧姆定律，有多种方法可以测定电池的电动势和内阻。', '', '01010304030100'),
('01010304030200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP02', '物理量的测量', '', '01010304030000'),
('01010304030301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP02.OP01', '只有多次测量，并对数据进行处理，才能减小误差。', '', '01010304030200'),
('01010304030300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP03', '数据分析', '', '01010304030000'),
('01010304030401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP03.OP01', '采用另外一种方法，也能减小误差，而且更简便、直观。', '', '01010304030300'),
('01010304040000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04', '能源与可持续发展', '', '01010304000000'),
('01010304040100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP01', '能量转移或转化的方向性', '', '01010304040000'),
('01010304040201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP01.OP01', '科学家研究发现，一切与热现象有关的宏观自然过程都是不可逆的。', '', '01010304040100'),
('01010304040202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP01.OP02', '虽然能量是守恒的，但是，在自然界中，能量的转化过程有些是可以自然发生的，有些则不能。', '', '01010304040100'),
('01010304040203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP01.OP03', '旦把热量释放出去，就不会再次自动聚集起来供人类重新利用。', '', '01010304040100'),
('01010304040204', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP01.OP04', '能量的耗散表明，在能源的利用过程中，能量在数量上虽未减少，但在可利用的品质上降低了，从便于利用的能源变成不便于利用的能源。', '', '01010304040100'),
('01010304040200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02', '能源的分类与应用', '', '01010304040000'),
('01010304040301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP01', '太阳能的利用对环境的影响很小。', '', '01010304040200'),
('01010304040302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP02', '利用太阳能最有前途的领域是，通过太阳能电池将太阳能直接转化成电能。', '', '01010304040200'),
('01010304040303', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP03', '水能是可再生能源，水电站是利用水能的重要形式。', '', '01010304040200'),
('01010304040304', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP04', '水电对环境的影响比火电小，发电成本低。', '', '01010304040200'),
('01010304040305', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP05', '为了增加风力发电的功率，通常把很多风车建在一起，成为“风车田”。', '', '01010304040200'),
('01010304040306', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP06', '一些质量较大的原子核在裂变成较轻的原子核时，会释放出惊人的能量。', '', '01010304040200'),
('01010304040307', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP07', '同样质量的化学燃料与核燃料相比，如果都进行了完全反应，核燃料释放出的能量要远远大于化学燃料释放出的能量。', '', '01010304040200'),
('01010304040308', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP08', '可控制的裂变反应所释放出的能量，可以很好地被人们所利用。', '', '01010304040200'),
('01010304040309', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP09', '核电站就是利用可控核裂变来发电的。', '', '01010304040200'),
('01010304040310', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP10', '人们预计21世纪中叶，核聚变会有重大的技术突破。', '', '01010304040200'),
('01010304040311', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP11', '由于核电站一旦发生核泄漏事故，就可能发生严重的危害，同时核废料仍然具有放射性，因此科学家制定了严格的安全措施和安全标准。', '', '01010304040200'),
('01010304040300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03', '能源与社会发展', '', '01010304040000'),
('01010304040401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP01', '自工业革命以来，煤和石油成为人类的主要能源。', '', '01010304040300'),
('01010304040402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP02', '18世纪末发明和逐步完善的蒸汽机，以对化石能源的大规模利用为特征，开始了人类文明的新纪元。', '', '01010304040300'),
('01010304040403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP03', '内燃机的发展则强有力地推动了19世纪末、20世纪初开始的机械化与电气化进程。', '', '01010304040300'),
('01010304040404', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP04', '到了20世纪50年代，世界石油和天然气的消耗量超过了煤炭。', '', '01010304040300'),
('01010304040405', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP05', '能源科技的每一次突破，都带来了生产力的巨大飞跃和社会的进步。', '', '01010304040300'),
('01010304040406', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP06', '能源是人类社会活动的物质基础。', '', '01010304040300'),
('01010304040407', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP07', '内燃机工作时的高温使空气和燃料中的多种物质发生化学反应，产生氮的氧化物和碳氢化合物。', '', '01010304040300'),
('01010304040408', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP08', '燃烧时产生的浮尘也是主要的污染物。', '', '01010304040300'),
('01010304040409', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP09', '随着人口迅速增长、经济快速发展以及工业化程度的提高，能源短缺和过度使用化石能源带来的环境恶化已经成为关系到人类社会能否持续发展的大问题。', '', '01010304040300'),
('01010304040410', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP10', '人类的生存与发展需要能源，能源的开发与使用又会对环境造成影响。', '', '01010304040300'),
('01010304040411', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP11', '可持续发展的核心是追求发展与资源、环境的平衡：既满足当代人的需求，又不损害子孙后代的需求。', '', '01010304040300'),
('01010304040412', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP12', '立新的能源安全观，并转变能源的供需模式。', '', '01010304040300');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010305000000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05', '电磁感应与电磁波初步', '', NULL),
('01010305010000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01', '磁场 磁感线', '', '01010305000000'),
('01010305010100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP01', '电和磁的联系', '', '01010305010000'),
('01010305010201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP01.OP01', '自然界中的磁体总存在着两个磁极，自然界中同样存在着两种电荷。', '', '01010305010100'),
('01010305010202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP01.OP02', '不仅如此，磁极之间的相互作用，与电荷之间的相互作用具有相似的特征：同名磁极或同种电荷相互排斥，异名磁极或异种电荷相互吸引。', '', '01010305010100'),
('01010305010203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP01.OP03', '丹麦物理学家奥斯特相信，电和磁之间应该存在某种联系，并开始了不懈的探索。', '', '01010305010100'),
('01010305010200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP02', '磁场', '', '01010305010000'),
('01010305010301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP02.OP01', '”自奥斯特实验之后，安培等人又做了很多实验研究。', '', '01010305010200'),
('01010305010302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP02.OP02', '正像在电场中可以用电场线来描述电场一样，利用磁感线可以形象地描述磁场。', '', '01010305010200'),
('01010305010300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP03', '安培定则', '', '01010305010000'),
('01010305010401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP03.OP01', '实验表明，改变电流的方向，各点的磁场方向都变成相反的方向。', '', '01010305010300'),
('01010305010400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP04', '磁感线', '', '01010305010000'),
('01010305010501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP04.OP01', '通电螺线管可以看作许多匝环形电流串联而成。', '', '01010305010400'),
('01010305010502', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP04.OP02', '与天然磁体的磁场相比，电流磁场的强弱容易控制，因而在实际中有很多重要的应用。', '', '01010305010400'),
('01010305010503', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP04.OP03', '电磁起重机、电动机、发电机，以及在自动控制中普遍应用的电磁继电器等，都离不开电流的磁场。', '', '01010305010400'),
('01010305010504', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP04.OP04', '近些年来，随着超导、新材料等技术的运用，人们可以较方便地获得大电流和强磁场。', '', '01010305010400'),
('01010305010505', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP04.OP05', '利用磁场与电流之间的相互作用，人们发明了磁浮列车、电磁弹射装置等。', '', '01010305010400'),
('01010305020000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02', '磁感应强度 磁通量', '', '01010305000000'),
('01010305020100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02.KP01', '磁感应强度', '', '01010305020000'),
('01010305020201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02.KP01.OP01', '若以通电导线作为磁场的检验物体，则既可以知道导线中电流的大小，又能测量导线的长度，从而可以进行定量的研究。', '', '01010305020100'),
('01010305020200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02.KP02', '匀强磁场', '', '01010305020000'),
('01010305020301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02.KP02.OP01', '匀强磁场的磁感线用一些间隔相等的平行直线表示。', '', '01010305020200'),
('01010305020300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02.KP03', '磁通量', '', '01010305020000'),
('01010305020401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02.KP03.OP01', '磁感线的疏密程度表示了磁场的强弱。', '', '01010305020300'),
('01010305030000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03', '电磁感应现象及应用', '', '01010305000000'),
('01010305030100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01', '划时代的发现', '', '01010305030000'),
('01010305030201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01.OP01', '有关电与磁关系的崭新研究领域洞开在人们面前，激发了科学家们的探索热情。', '', '01010305030100'),
('01010305030202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01.OP02', '人们早就认识了磁化现象，知道磁体能使附近的铁棒产生磁性，带电体能在导体上感应出电荷。', '', '01010305030100'),
('01010305030203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01.OP03', '最初，法拉第认为，很强的磁体或很强的电流可能会在邻近的闭合导线中感应出电流。', '', '01010305030100'),
('01010305030204', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01.OP04', '当给一个线圈通电或断电的瞬间，在另一个线圈上出现了电流。', '', '01010305030100'),
('01010305030205', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01.OP05', '法拉第从中领悟到，“磁生电”是一种在变化、运动的过程中才能出现的效应。', '', '01010305030100'),
('01010305030206', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01.OP06', '电磁感应的发现使人们对电与磁内在联系的认识更加深入，宣告了电磁学作为一门统一学科的诞生。', '', '01010305030100'),
('01010305030200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP02', '产生感应电流的条件', '', '01010305030000'),
('01010305030300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP03', '电磁感应现象的应用', '', '01010305030000'),
('01010305030401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP03.OP01', '1831年圣诞节前夕，一次科学报告会上，法拉第当众表演了一个实验。', '', '01010305030300'),
('01010305030402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP03.OP02', '一个铜盘的轴和铜盘的边缘分别连在“电流表”的两端。', '', '01010305030300'),
('01010305030403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP03.OP03', '法拉第摇动手柄使铜盘在磁极之间旋转，“电流表”的指针随之摆动。', '', '01010305030300'),
('01010305030404', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP03.OP04', '生产、生活中广泛使用的变压器、电磁炉等也是根据电磁感应制造的。', '', '01010305030300'),
('01010305040000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04', '电磁波的发现及应用', '', '01010305000000'),
('01010305040100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP01', '电磁场', '', '01010305040000'),
('01010305040201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP01.OP01', '在变化的磁场中放入一个闭合电路，电路里会产生感应电流。', '', '01010305040100'),
('01010305040202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP01.OP02', '麦克斯韦确信自然规律的统一性与和谐性，相信电场与磁场的对称之美。', '', '01010305040100'),
('01010305040200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02', '电磁波', '', '01010305040000'),
('01010305040301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02.OP01', '空气、水是声波和水波传播的介质。', '', '01010305040200'),
('01010305040302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02.OP02', '水波和声波的传播都离不开介质。', '', '01010305040200'),
('01010305040303', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02.OP03', '麦克斯韦推算出一个出人意料的答案：电磁波的速度等于光速！', '', '01010305040200'),
('01010305040304', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02.OP04', '遗憾的是，麦克斯韦英年早逝，没有看到科学实验对电磁场理论的证实。', '', '01010305040200'),
('01010305040305', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02.OP05', '把天才的预言变成世人公认的真理的人，是德国科学家赫兹。', '', '01010305040200'),
('01010305040306', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02.OP06', '1886年，赫兹通过实验捕捉到了电磁波。', '', '01010305040200'),
('01010305040300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP03', '电磁波谱', '', '01010305040000'),
('01010305040401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP03.OP01', '波速、波长、频率三者之间的关系是波速＝波长×频率对于电磁波，有同样的关系。', '', '01010305040300'),
('01010305040402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP03.OP02', '不同电磁波由于具有不同的波长（或频率），因此具有不同的特性。', '', '01010305040300'),
('01010305040400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP04', '电磁波的能量', '', '01010305040000'),
('01010305040501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP04.OP01', '法拉第用“力线”形象地描述了电磁场，麦克斯韦用数学语言表述了电磁场。', '', '01010305040400'),
('01010305040502', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP04.OP02', '食物中的水分子在微波的作用下热运动加剧，内能增加，温度升高。', '', '01010305040400'),
('01010305040503', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP04.OP03', '因为电台发射的电磁波在收音机的天线里感应出了电流，有电流就有能量。', '', '01010305040400'),
('01010305040500', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP05', '电磁波通信', '', '01010305040000'),
('01010305040601', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP05.OP01', '随着计算机网络的发展和智能手机的出现，负责通话的电信网、广播电视网和互联网相互渗透、相互兼容，逐步整合成为统一的信息通信网络。', '', '01010305040500'),
('01010305040602', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP05.OP02', '移动电话的主要业务也已经发展成为以互联网为基础的信息服务。', '', '01010305040500'),
('01010305040603', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP05.OP03', '人们可以通过接入互联网的手机看电影、聊天、购物、查阅资料、视频通话。', '', '01010305040500'),
('01010305040604', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP05.OP04', '构建卫星宽带通信网络，可以使人们无论身处万米高空的飞机，还是茫茫大海的轮船，甚至没有人烟的荒漠、深山、海岛，都能高速浏览网页，与家人视频通话等。', '', '01010305040500'),
('01010305050000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05', '能量量子化', '', '01010305000000'),
('01010305050100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01', '热辐射', '', '01010305050000'),
('01010305050201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01.OP01', '物体在室温时，热辐射的主要成分是波长较长的电磁波，不能引起人的视觉。', '', '01010305050100'),
('01010305050202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01.OP02', '当温度升高时，热辐射中波长较短的成分越来越强。', '', '01010305050100'),
('01010305050203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01.OP03', '大量实验结果表明，辐射强度按波长的分布情况随物体的温度而有所不同。', '', '01010305050100'),
('01010305050204', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01.OP04', '除了热辐射外，物体表面还会吸收和反射外界射来的电磁波。', '', '01010305050100'),
('01010305050205', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01.OP05', '黑体虽然不反射电磁波，但是却可以向外辐射电磁波。', '', '01010305050100'),
('01010305050206', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01.OP06', '物体中存在着不停运动的带电微粒，带电微粒的振动会产生变化的电磁场，从而产生电磁辐射。', '', '01010305050100'),
('01010305050200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP02', '能量子', '', '01010305050000'),
('01010305050301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP02.OP01', '为了得出同实验相符的黑体辐射公式，德国物理学家普朗克进行了多种尝试，进行了激烈的思想斗争。', '', '01010305050200'),
('01010305050302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP02.OP02', '而普朗克的假设则认为微观粒子的能量是量子化的，或者说微观粒子的能量是不连续（分立）的。', '', '01010305050200'),
('01010305050300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP03', '能级', '', '01010305050000'),
('01010305050401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP03.OP01', '微观世界中能量取分立值的观念也适用于原子系统，原子的能量是量子化的。', '', '01010305050300'),
('01010305050402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP03.OP02', '气体放电管中的原子受到高速运动的电子的撞击，有可能跃迁到较高的能量状态。', '', '01010305050300'),
('01010305050403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP03.OP03', '原子从高能态向低能态跃迁时放出的光子的能量，等于前后两个能级之差。', '', '01010305050300'),
('01010305050404', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP03.OP04', '核能的利用，计算机和智能手机的制造，激光技术等的应用都离不开量子力学。', '', '01010305050300'),
('01010305050400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04', '研究样例', '', '01010305050000'),
('01010305050501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP01', '随着智能手机耗电的增加，充电宝成了为手机及时充电的一种重要选择。', '', '01010305050400'),
('01010305050502', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP02', '在人们心目中，充电宝就是跟蓄电池和干电池一样的可移动直流电源。', '', '01010305050400'),
('01010305050503', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP03', '如果是一条直线，表明其电路特性跟普通电池一样，具有一定电动势和内阻。', '', '01010305050400'),
('01010305050504', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP04', '剥开充电宝连接线的外绝缘层，里面有四根导线，红导线为充电宝的正极，黑导线为负极，其余两根导线空置不用。', '', '01010305050400'),
('01010305050505', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP05', '将滑动变阻器电阻调至最大。', '', '01010305050400'),
('01010305050506', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP06', '不管是充电宝还是锂电池，其内阻跟储电量没有明显的相关。', '', '01010305050400'),
('01010305050507', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP07', '4.本实验结论具有一定的现实意义。', '', '01010305050400'),
('01010305050508', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP08', '手机锂电池随着储电量的减少，电动势逐步降低，其电源性能的变化主要是电动势的变化。', '', '01010305050400'),
('01010305050509', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP09', '充电宝的内阻很小，包括电路中被测量的导线在内，其阻值总计也只有0.2Ω左右，因此，充电宝的内电路对输出电压的影响是非常小的。', '', '01010305050400'),
('01010305050510', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP10', '研究表明，如果使用所测试的充电宝供电，不必考虑充电宝的电量百分比以及电流大小对输出电压的影响。', '', '01010305050400'),
('01010305050500', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP05', '参考选题', '', '01010305050000'),
('01010305050601', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP05.OP01', '研究内容：智能手机功能强大，带来方便的同时，耗电太快已经成为大众普遍关注的问题。', '', '01010305050500'),
('01010305050602', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP05.OP02', '屏幕亮度、音量等功能的设置，上网浏览、网络聊天、主叫电话、视频播放、录音重放、网络游戏等功能的应用，都是影响手机耗电的因素。', '', '01010305050500');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010401000000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01', '动量守恒定律', '', NULL),
('01010401010000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC01', '动量', '', '01010401000000'),
('01010401010100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC01.KP01', '寻求碰撞中的不变量', '', '01010401010000'),
('01010401010201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC01.KP01.OP01', '小车的速度用滑轨上的数字计时器测量。', '', '01010401010100'),
('01010401010202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC01.KP01.OP02', '从实验的数据可以看出，此实验中两辆小车碰撞前后，动能之和并不相等，但是质量与速度的乘积之和却基本不变。', '', '01010401010100'),
('01010401010200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC01.KP02', '动量', '', '01010401010000'),
('01010401010301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC01.KP02.OP01', '动量是矢量，动量的方向与速度的方向相同。', '', '01010401010200'),
('01010401010302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC01.KP02.OP02', '取水平向右为坐标轴的方向。', '', '01010401010200'),
('01010401020000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC02', '动量定理', '', '01010401000000'),
('01010401020100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC02.KP01', '动量定理', '', '01010401020000'),
('01010401020201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC02.KP01.OP01', '实际上，物体在碰撞过程中受到的作用力往往不是恒力，物体不做匀变速运动。', '', '01010401020100'),
('01010401020202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC02.KP01.OP02', '把应用于每个短暂过程的关系式相加，就得到整个过程的动量定理。', '', '01010401020100'),
('01010401020200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC02.KP02', '动量定理的应用', '', '01010401020000'),
('01010401020301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC02.KP02.OP01', '从同样的高度落到地面或地毯上时，在与地面或地毯的相互作用中，两种情况下动量的变化量相等，地面或地毯对杯子的力的冲量也相等。', '', '01010401020200'),
('01010401020302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC02.KP02.OP02', '如果在船舷和码头悬挂一些具有弹性的物体（如旧轮胎），就可以延长作用时间，以减小船和码头间的作用力。', '', '01010401020200'),
('01010401030000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC03', '动量守恒定律', '', '01010401000000'),
('01010401030100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC03.KP01', '相互作用的两个物体的动量改变', '', '01010401030000'),
('01010401030201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC03.KP01.OP01', '两个物体各自既受到对方的作用力，同时又受到重力和桌面的支持力，重力和支持力是一对平衡力。', '', '01010401030100'),
('01010401030200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC03.KP02', '动量守恒定律', '', '01010401030000'),
('01010401030301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC03.KP02.OP01', '一般而言，碰撞、爆炸等现象的研究对象是两个（或多个）物体。', '', '01010401030200'),
('01010401030302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC03.KP02.OP02', '求货车碰撞后运动的速度。', '', '01010401030200'),
('01010401030300', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC03.KP03', '动量守恒定律的普适性', '', '01010401030000'),
('01010401030401', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC03.KP03.OP01', '在实际过程中，往往涉及多个力，力随时间变化的规律也可能很复杂，使得问题难以求解。', '', '01010401030300'),
('01010401030402', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC03.KP03.OP02', '事实上，动量守恒定律的适用范围非常广泛。', '', '01010401030300'),
('01010401040000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC04', '实验：验证动量守恒定律', '', '01010401000000'),
('01010401040100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC04.KP01', '实验思路', '', '01010401040000'),
('01010401040201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC04.KP01.OP01', '动量守恒定律的适用条件是系统不受外力，或者所受外力的矢量和为0。', '', '01010401040100'),
('01010401040202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC04.KP01.OP02', '两个物体在发生碰撞时，作用时间很短。', '', '01010401040100'),
('01010401040203', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC04.KP01.OP03', '在设计实验时应着重考虑如下问题。', '', '01010401040100'),
('01010401040200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC04.KP02', '物理量的测量', '', '01010401040000'),
('01010401040301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC04.KP02.OP01', '研究对象确定后，还需要明确所需测量的物理量和实验器材。', '', '01010401040200'),
('01010401040302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC04.KP02.OP02', '根据动量的定义，很自然地想到，需要测量物体的质量，以及两个物体发生碰撞前后各自的速度。', '', '01010401040200'),
('01010401040300', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC04.KP03', '数据分析', '', '01010401040000'),
('01010401050000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC05', '弹性碰撞和非弹性碰撞', '', '01010401000000'),
('01010401050100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC05.KP01', '弹性碰撞和非弹性碰撞', '', '01010401050000'),
('01010401050200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC05.KP02', '弹性碰撞的实例分析', '', '01010401050000'),
('01010401050301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC05.KP02.OP01', '保龄球保持静止，而乒乓球以大致相同的速率被弹回。', '', '01010401050200'),
('01010401060000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC06', '反冲现象  火箭', '', '01010401000000'),
('01010401060100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC06.KP01', '反冲现象', '', '01010401060000'),
('01010401060201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC06.KP01.OP01', '发射炮弹时，炮弹从炮筒中飞出，炮身则向后退。', '', '01010401060100'),
('01010401060202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC06.KP01.OP02', '炮弹射出后以很大的速度向前运动，根据动量守恒定律，炮身必将向后运动。', '', '01010401060100'),
('01010401060203', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC06.KP01.OP03', '只是由于炮身的质量远大于炮弹的质量，所以炮身向后的速度很小。', '', '01010401060100'),
('01010401060204', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC06.KP01.OP04', '在实际中常常需要考虑反冲现象的影响。', '', '01010401060100'),
('01010401060205', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC06.KP01.OP05', '用枪射击时，子弹向前飞去，枪身发生反冲向后运动。', '', '01010401060100'),
('01010401060206', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC06.KP01.OP06', '枪身的反冲会影响射击的准确性，所以用步枪射击时要把枪身抵在肩部，以减少反冲的影响。', '', '01010401060100'),
('01010401060200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC06.KP02', '火箭', '', '01010401060000'),
('01010401060301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH01.SEC06.KP02.OP01', '箭杆上捆了一个前端封闭的火药筒，点燃后生成的燃气以很大速度向后喷出，箭杆由于反冲而向前运动。', '', '01010401060200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010402000000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02', '机械振动', '', NULL),
('01010402010000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC01', '简谐运动', '', '01010402000000'),
('01010402010100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC01.KP01', '弹簧振子', '', '01010402010000'),
('01010402010201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC01.KP01.OP01', '弹簧的质量与小球相比可以忽略。', '', '01010402010100'),
('01010402010202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC01.KP01.OP02', '小球运动时空气阻力很小，也可以忽略。', '', '01010402010100'),
('01010402010200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC01.KP02', '弹簧振子的位移—时间图像', '', '01010402010000'),
('01010402010301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC01.KP02.OP01', '要想得到位移与时间的关系，关键在于记录不同时刻小球的位置，为了便于分析，最好是相等时间间隔的位置。', '', '01010402010200'),
('01010402010302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC01.KP02.OP02', '利用频闪照相、照相机连拍，或用摄像机摄像后逐帧观察的方式，都可以得到相等时间间隔的不同时刻小球的位置。', '', '01010402010200'),
('01010402010303', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC01.KP02.OP03', '拍摄时底片从下向上匀速运动，因此在底片上留下了小球和弹簧的一系列的像。', '', '01010402010200'),
('01010402010300', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC01.KP03', '简谐运动', '', '01010402010000'),
('01010402010401', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC01.KP03.OP01', '还需要进行深入的研究。', '', '01010402010300'),
('01010402010402', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC01.KP03.OP02', '要想确定弹簧振子中小球的位移与时间的关系是否遵从正弦函数的规律，可采用下面的方法。', '', '01010402010300'),
('01010402010403', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC01.KP03.OP03', '简谐运动是最基本的振动。', '', '01010402010300'),
('01010402020000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC02', '简谐运动的描述', '', '01010402000000'),
('01010402020100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC02.KP01', '振幅', '', '01010402020000'),
('01010402020201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC02.KP01.OP01', '振动物体运动的范围是振幅的两倍。', '', '01010402020100'),
('01010402020200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC02.KP02', '周期和频率', '', '01010402020000'),
('01010402020301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC02.KP02.OP01', '周期和频率都是表示物体振动快慢的物理量，周期越小，频率越大，表示振动越快。', '', '01010402020200'),
('01010402020302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC02.KP02.OP02', '不仅弹簧振子的简谐运动，所有简谐运动的周期均与其振幅无关。', '', '01010402020200'),
('01010402020300', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC02.KP03', '相位', '', '01010402020000'),
('01010402020401', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC02.KP03.OP01', '【例题】而当第二个小球到达最高点时，第一个已经返回平衡位置了。', '', '01010402020300'),
('01010402030000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC03', '简谐运动的回复力和能量', '', '01010402000000'),
('01010402030100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC03.KP01', '简谐运动的回复力', '', '01010402030000'),
('01010402030200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC03.KP02', '简谐运动的能量', '', '01010402030000'),
('01010402030301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC03.KP02.OP01', '沿水平方向振动的弹簧振子的势能为弹性势能，其大小取决于弹簧的形变量。', '', '01010402030200'),
('01010402030302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC03.KP02.OP02', '小球运动远离平衡位置时，势能会增大。', '', '01010402030200'),
('01010402030303', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC03.KP02.OP03', '与此同时，小球的速度减小，动能减小。', '', '01010402030200'),
('01010402030304', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC03.KP02.OP04', '小球到达最大位移时，动能为０，势能最大。', '', '01010402030200'),
('01010402030305', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC03.KP02.OP05', '理论上可以证明，在弹簧振子运动的任意位置，系统的动能与势能之和都是一定的，遵守机械能守恒定律。', '', '01010402030200'),
('01010402030306', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC03.KP02.OP06', '实际的运动都有一定的能量损耗，所以简谐运动是一种理想化的模型。', '', '01010402030200'),
('01010402030307', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC03.KP02.OP07', '当小球运动到最大位移时，动能为０，弹性势能最大，系统的机械能等于最大弹性势能。', '', '01010402030200'),
('01010402030308', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC03.KP02.OP08', '对于弹簧劲度系数和小球质量都一定的系统，振幅越大，机械能越大。', '', '01010402030200'),
('01010402040000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC04', '单摆', '', '01010402000000'),
('01010402040100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC04.KP01', '单摆的回复力', '', '01010402040000'),
('01010402040200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC04.KP02', '单摆的周期', '', '01010402040000'),
('01010402040301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC04.KP02.OP01', '实验表明：单摆做简谐运动的周期与摆长有关，摆长越长，周期越大；单摆的周期与摆球质量和振幅无关。', '', '01010402040200'),
('01010402040302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC04.KP02.OP02', '单摆周期公式的发现，为人类利用简谐运动定量计时提供了可能，并以此为基础发明了真正可持续运转的时钟。', '', '01010402040200'),
('01010402050000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC05', '实验：用单摆测量重力加速度', '', '01010402000000'),
('01010402050100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC05.KP01', '实验思路', '', '01010402050000'),
('01010402050201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC05.KP01.OP01', '惠更斯在推导出单摆的周期公式后，用一个单摆测出了巴黎的重力加速度。', '', '01010402050100'),
('01010402050200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC05.KP02', '实验装置', '', '01010402050000'),
('01010402050301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC05.KP02.OP01', '将铁夹固定在铁架台的上端，铁架台放在实验桌边，使铁夹伸到桌面以外，方便使用不同长度的摆线。', '', '01010402050200'),
('01010402050300', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC05.KP03', '物理量的测量', '', '01010402050000'),
('01010402050401', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC05.KP03.OP01', '摆长是摆线长度和小球半径之和。', '', '01010402050300'),
('01010402050400', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC05.KP04', '数据分析', '', '01010402050000'),
('01010402050501', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC05.KP04.OP01', '查询所在地区的重力加速度值，并与测得的结果进行比较。', '', '01010402050400'),
('01010402050502', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC05.KP04.OP02', '如果有差异，想想可能是什么原因造成的。', '', '01010402050400'),
('01010402060000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06', '受迫振动  共振', '', '01010402000000'),
('01010402060100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP01', '振动中的能量损失', '', '01010402060000'),
('01010402060201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP01.OP01', '生活中，摇曳的树叶会停下来，摆动的秋千也会停止运动。', '', '01010402060100'),
('01010402060202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP01.OP02', '由于实际的振动系统都会受到摩擦力、黏滞力等阻碍作用，振幅必然逐渐减小。', '', '01010402060100'),
('01010402060203', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP01.OP03', '振动系统能量衰减的方式通常有两种。', '', '01010402060100'),
('01010402060204', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP01.OP04', '一种是由于振动系统受到摩擦阻力的作用，使振动系统的机械能逐渐转化为内能。', '', '01010402060100'),
('01010402060205', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP01.OP05', '另一种是由于振动系统引起邻近介质中各质点的振动，使能量向四周辐射出去，从而自身机械能减少。', '', '01010402060100'),
('01010402060200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP02', '受迫振动', '', '01010402060000'),
('01010402060301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP02.OP01', '最简单的办法是使周期性的外力作用于振动系统，外力对系统做功，补偿系统的能量损耗，使系统的振动维持下去。', '', '01010402060200'),
('01010402060302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP02.OP02', '机器运转时底座发生的振动、扬声器纸盆的振动，都是受迫振动。', '', '01010402060200'),
('01010402060303', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP02.OP03', '大量的实验都证实：物体做受迫振动达到稳定后，物体振动的频率等于驱动力的频率，与物体的固有频率无关。', '', '01010402060200'),
('01010402060300', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP03', '共振现象及其应用', '', '01010402060000'),
('01010402060401', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP03.OP01', '把一些不同长度的钢片安装在同一个支架上，可以制作转速计。', '', '01010402060300'),
('01010402060402', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH02.SEC06.KP03.OP02', '有的情况下需要避免共振。', '', '01010402060300');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010403000000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03', '机械波', '', NULL),
('01010403010000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01', '波的形成', '', '01010403000000'),
('01010403010100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP01', '波的形成', '', '01010403010000'),
('01010403010201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP01.OP01', '绳子是有弹性的物体。', '', '01010403010100'),
('01010403010202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP01.OP02', '实验中观察到，弹簧圈密集的部分和稀疏的部分交替向右传播，在弹簧上形成一种与横波不一样的波。', '', '01010403010100'),
('01010403010203', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP01.OP03', '手执弹簧一端左右振动起来以后，近端的质点依次带动远端的质点左右振动，但后一个质点总比前一个质点迟一些开始振动。', '', '01010403010100'),
('01010403010204', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP01.OP04', '从整体上看，就形成了疏密相间的波。', '', '01010403010100'),
('01010403010200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP02', '横波和纵波', '', '01010403010000'),
('01010403010301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP02.OP01', '纵波中，质点分布最密的位置叫作密部，质点分布最疏的位置叫作疏部。', '', '01010403010200'),
('01010403010302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP02.OP02', '发声体振动时在空气中产生的声波是纵波。', '', '01010403010200'),
('01010403010303', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP02.OP03', '声波传入人耳，使鼓膜振动，就引起声音的感觉。', '', '01010403010200'),
('01010403010304', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP02.OP04', '声波不仅能在空气中传播，也能在液体、固体中传播。', '', '01010403010200'),
('01010403010300', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP03', '机械波', '', '01010403010000'),
('01010403010401', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP03.OP01', '绳上和弹簧上的波是在绳和弹簧上传播的，水波是在水面传播的，声波通常是在空气中传播的。', '', '01010403010300'),
('01010403010402', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP03.OP02', '组成介质的质点之间有相互作用，一个质点的振动会引起相邻质点的振动。', '', '01010403010300'),
('01010403010403', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP03.OP03', '介质中有机械波传播时，介质本身并不随波一起传播。', '', '01010403010300'),
('01010403010404', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP03.OP04', '波是传递能量的一种方式。', '', '01010403010300'),
('01010403010405', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC01.KP03.OP05', '波不但传递能量，而且可以传递信息。', '', '01010403010300'),
('01010403020000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC02', '波的描述', '', '01010403000000'),
('01010403020100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC02.KP01', '波的图像', '', '01010403020000'),
('01010403020201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC02.KP01.OP01', '弦波传播时，介质的质点在做简谐运动。', '', '01010403020100'),
('01010403020200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC02.KP02', '波长、频率和波速', '', '01010403020000'),
('01010403020301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC02.KP02.OP01', '在横波中，两个相邻波峰或两个相邻波谷之间的距离等于波长。', '', '01010403020200'),
('01010403020302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC02.KP02.OP02', '在纵波中，两个相邻密部或两个相邻疏部之间的距离等于波长。', '', '01010403020200'),
('01010403020303', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC02.KP02.OP03', '声速还与温度有关，表中列出了0℃时声波在几种介质中的传播速度。', '', '01010403020200'),
('01010403030000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03', '波的反射、折射和衍射', '', '01010403000000'),
('01010403030100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP01', '波的反射', '', '01010403030000'),
('01010403030201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP01.OP01', '反射线、法线与入射线在同一平面内，反射线与入射线分居法线两侧，反射角等于入射角。', '', '01010403030100'),
('01010403030200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP02', '波的折射', '', '01010403030000'),
('01010403030301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP02.OP01', '理论和实验证明，一切波都会发生折射现象。', '', '01010403030200'),
('01010403030302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP02.OP02', '如果用一条射线代表水波的入射方向，用另一条射线代表水波的折射方向，可以更清晰地看出水波的折射现象。', '', '01010403030200'),
('01010403030300', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP03', '波的衍射', '', '01010403030000'),
('01010403030401', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP03.OP01', '在波的前进方向上放一个有孔的屏，可以看到波通过小孔而在屏的后面向各个方向传播。', '', '01010403030300'),
('01010403030402', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP03.OP02', '保持狭缝的宽度不变，改变水波的波长，观察波的传播情况有什么变化。', '', '01010403030300'),
('01010403030403', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP03.OP03', '可以推测，当波长与狭缝宽度相比非常小时，水波将沿直线传播，观察不到衍射现象。', '', '01010403030300'),
('01010403030404', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP03.OP04', '实验表明，只有缝、孔的宽度或障碍物的尺寸跟波长相差不多，或者比波长更小时，才能观察到明显的衍射现象。', '', '01010403030300'),
('01010403030405', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP03.OP05', '不只是水波，声波也能发生衍射。', '', '01010403030300'),
('01010403030406', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP03.OP06', '一切波都能发生衍射。', '', '01010403030300'),
('01010403030407', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC03.KP03.OP07', '衍射是波特有的现象。', '', '01010403030300'),
('01010403040000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04', '波的干涉', '', '01010403000000'),
('01010403040100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP01', '波的叠加', '', '01010403040000'),
('01010403040201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP01.OP01', '在介质中常常有几列波同时传播。', '', '01010403040100'),
('01010403040202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP01.OP02', '生活中常见的水波也是如此，两列水波相遇后彼此穿过，仍然保持各自的运动特征，继续传播，就像没有跟另一列水波相遇一样。', '', '01010403040100'),
('01010403040203', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP01.OP03', '蓝线圆与黑线圆间的距离等于半个波长，蓝线与蓝线、黑线与黑线之间的距离等于一个波长。', '', '01010403040100'),
('01010403040200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP02', '波的干涉', '', '01010403040000'),
('01010403040301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP02.OP01', '波峰与波峰相遇时，质点向上的位移最大，等于两列波的振幅之和；波谷与波谷相遇时，质点的位移尽管是向下的，但也是最大，也等于两列波的振幅之和。', '', '01010403040200'),
('01010403040302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP02.OP02', '如果两列波的振幅相同，质点的位移之和就总等于0。', '', '01010403040200'),
('01010403040303', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP02.OP03', '在上面的实验中，两个波源的频率是相同的。', '', '01010403040200'),
('01010403040304', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP02.OP04', '如果两列波的频率不同，在相互叠加时，波中相遇区域的各个质点的振幅会随时间变化，不会出现振动总是加强与总是减弱的区域。', '', '01010403040200'),
('01010403040305', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP02.OP05', '同时，在上面的实验中，两个波源的相位差是保持不变的。', '', '01010403040200'),
('01010403040306', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP02.OP06', '如果两个波源的相位差随时间变化，波中相遇区域的各个质点的相位差会随时间变化，同样不会出现振动总是加强与总是减弱的区域。', '', '01010403040200'),
('01010403040307', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP02.OP07', '否则也不会出现振动总是加强与总是减弱的区域。', '', '01010403040200'),
('01010403040308', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP02.OP08', '不仅水波会发生干涉现象，声波、电磁波等一切波，只要满足上述条件都能发生干涉。', '', '01010403040200'),
('01010403040309', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC04.KP02.OP09', '跟衍射一样，干涉也是波特有的现象。', '', '01010403040200'),
('01010403050000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC05', '多普勒效应', '', '01010403000000'),
('01010403050100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC05.KP01', '多普勒效应', '', '01010403050000'),
('01010403050201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC05.KP01.OP01', '要了解多普勒效应，可以做如下的模拟实验。', '', '01010403050100'),
('01010403050200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC05.KP02', '多普勒效应的应用', '', '01010403050000'),
('01010403050301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC05.KP02.OP01', '行进中的车辆发射频率已知的超声波，同时测量反射波的频率，根据反射波频率变化的多少就能知道车辆的速度。', '', '01010403050200'),
('01010403050302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC05.KP02.OP02', '装有多普勒测速仪的监视器可以装在公路上方，在测速的同时把车辆牌号拍摄下来，并把测得的速度自动打印在照片上。', '', '01010403050200'),
('01010403050303', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC05.KP02.OP03', '医生向人体内发射频率已知的超声波，超声波被血管中的血流反射后又被仪器接收。', '', '01010403050200'),
('01010403050304', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC05.KP02.OP04', '测出反射波的频率变化，就能知道血流的速度。', '', '01010403050200'),
('01010403050305', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC05.KP02.OP05', '理论和实验都证明，光波或电磁波都有多普勒效应，多普勒效应在科学技术中也有着广泛的应用。', '', '01010403050200'),
('01010403050306', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH03.SEC05.KP02.OP06', '宇宙中的星球都在不停地运动。', '', '01010403050200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010404000000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04', '光', '', NULL),
('01010404010000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC01', '光的折射', '', '01010404000000'),
('01010404010100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC01.KP01', '折射定律', '', '01010404010000'),
('01010404010200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC01.KP02', '折射率', '', '01010404010000'),
('01010404010301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC01.KP02.OP01', '真空的折射率为1，空气的折射率近似为1。', '', '01010404010200'),
('01010404010302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC01.KP02.OP02', '折射率是衡量材料光学性能的重要指标。', '', '01010404010200'),
('01010404010303', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC01.KP02.OP03', '根据折射定律，可以测量材料的折射率。', '', '01010404010200'),
('01010404010304', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC01.KP02.OP04', '求油的折射率和光在油中传播的速度。', '', '01010404010200'),
('01010404020000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02', '全反射', '', '01010404000000'),
('01010404020100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP01', '全反射', '', '01010404020000'),
('01010404020201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP01.OP01', '科研和技术中常常通过测量临界角来测定材料的折射率。', '', '01010404020100'),
('01010404020202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP01.OP02', '全反射是自然界中常见的现象。', '', '01010404020100'),
('01010404020203', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP01.OP03', '当光从光密介质射入光疏介质时，同时发生折射和反射。', '', '01010404020100'),
('01010404020204', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP01.OP04', '如果入射角逐渐增大，折射光离法线会越来越远，而且越来越弱，反射光却越来越强。', '', '01010404020100'),
('01010404020205', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP01.OP05', '当光从光密介质射入光疏介质时，如果入射角等于或大于临界角，就会发生全反射现象。', '', '01010404020100'),
('01010404020200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP02', '全反射棱镜', '', '01010404020000'),
('01010404020301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP02.OP01', '第四章光93当光在有机玻璃棒内传播时，如果从有机玻璃射向空气的入射角大于临界角，光会发生全反射，于是光在有机玻璃棒内沿着锯齿形路线传播。', '', '01010404020200'),
('01010404020302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP02.OP02', '①实用光导纤维的直径只有几微米到一百微米。', '', '01010404020200'),
('01010404020303', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP02.OP03', '因为很细，一定程度上可以弯折。', '', '01010404020200'),
('01010404020304', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP02.OP04', '光也可以像无线电波那样，作为载体来传递信息。', '', '01010404020200'),
('01010404020305', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP02.OP05', '光纤通信有传输容量大的特点。', '', '01010404020200'),
('01010404020300', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP03', '光导纤维', '', '01010404020000'),
('01010404020401', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP03.OP01', '尽管光纤通信的发展历史较短，但是发展速度惊人。', '', '01010404020300'),
('01010404020402', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP03.OP02', '随着技术的发展，光纤宽带也越来越普及，已有许多企业、家庭实现了光纤入户。', '', '01010404020300'),
('01010404020403', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC02.KP03.OP03', '光缆线路已经与通信卫星、微波中继站、普通电缆相结合，构成了现代国家的“神经系统”。', '', '01010404020300'),
('01010404030000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC03', '光的干涉', '', '01010404000000'),
('01010404030100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC03.KP01', '光的双缝干涉', '', '01010404030000'),
('01010404030201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC03.KP01.OP01', '距离屏的中心越远，路程差越大。', '', '01010404030100'),
('01010404030202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC03.KP01.OP02', '托马斯·杨的时代没有激光。', '', '01010404030100'),
('01010404030200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC03.KP02', '干涉条纹和光的波长之间的关系', '', '01010404030000'),
('01010404030301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC03.KP02.OP01', '实验中确实发现，各种颜色的单色光都会发生干涉，但条纹之间的距离不一样。', '', '01010404030200'),
('01010404030300', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC03.KP03', '薄膜干涉', '', '01010404030000'),
('01010404030401', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC03.KP03.OP01', '灯焰的像和节前“问题”中提到的现象，都是液膜前后两个面反射的光共同形成的。', '', '01010404030300'),
('01010404030402', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC03.KP03.OP02', '来自两个面的反射光相互叠加，发生干涉，也称薄膜干涉。', '', '01010404030300'),
('01010404030403', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC03.KP03.OP03', '薄膜上不同颜色的光的条纹的明暗位置不同，相互交错，所以，看上去会有彩色条纹。', '', '01010404030300'),
('01010404030404', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC03.KP03.OP04', '薄膜干涉在技术上有很多应用。', '', '01010404030300'),
('01010404040000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04', '实验：用双缝干涉测量光的波长', '', '01010404000000'),
('01010404040100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP01', '实验思路', '', '01010404040000'),
('01010404040201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP01.OP01', '光源发出的光经滤光片（装在单缝前）成为单色光，把单缝照亮。', '', '01010404040100'),
('01010404040202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP01.OP02', '来自双缝的光在双缝右边的空间发生干涉。', '', '01010404040100'),
('01010404040203', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP01.OP03', '透镜的作用是使射向单缝的光更集中。', '', '01010404040100'),
('01010404040200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP02', '物理量的测量', '', '01010404040000'),
('01010404040301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP02.OP01', '转动手轮，分划板会左右移动。', '', '01010404040200'),
('01010404040302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP02.OP02', '然后转动手轮，使分划板中心刻线与另一相邻亮条纹的中心对齐，再次记下手轮上的读数。', '', '01010404040200'),
('01010404040303', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP02.OP03', '为了减小测量误差，可测多条亮条纹间的距离，再求出相邻两条亮条纹间的距离。', '', '01010404040200'),
('01010404040300', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP03', '进行实验', '', '01010404040000'),
('01010404040401', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP03.OP01', '然后放好单缝和双缝。', '', '01010404040300'),
('01010404040402', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP03.OP02', '注意使单缝与双缝相互平行，尽量使缝的中点位于遮光筒的轴线上。', '', '01010404040300'),
('01010404040403', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP03.OP03', '分别改变滤光片的颜色和双缝的距离，观察干涉条纹的变化，并求出相应的波长。', '', '01010404040300'),
('01010404040400', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC04.KP04', '数据分析', '', '01010404040000'),
('01010404050000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC05', '光的衍射', '', '01010404000000'),
('01010404050100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC05.KP01', '光的衍射', '', '01010404050000'),
('01010404050201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC05.KP01.OP01', '在挡板上安装一个宽度可调的狭缝，缝后放一个光屏。', '', '01010404050100'),
('01010404050202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC05.KP01.OP02', '在单缝衍射和圆孔衍射的照片中，都有一些亮条纹和暗条纹。', '', '01010404050100'),
('01010404050203', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC05.KP01.OP03', '光在没有障碍物的均匀介质中是沿直线传播的。', '', '01010404050100'),
('01010404050204', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC05.KP01.OP04', '对衍射现象的研究表明，在障碍物或狭缝的尺寸很大时，衍射现象不明显，也可以近似认为光是沿直线传播的。', '', '01010404050100'),
('01010404050200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC05.KP02', '衍射光栅', '', '01010404050000'),
('01010404050301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC05.KP02.OP01', '单缝衍射的条纹比较宽，而且距离中央亮条纹较远的条纹，亮度也很低。', '', '01010404050200'),
('01010404050302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC05.KP02.OP02', '实验表明，如果增加狭缝的个数，衍射条纹的宽度将变窄，亮度将增加。', '', '01010404050200'),
('01010404050303', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC05.KP02.OP03', '光学仪器中用的衍射光栅就是据此制成的。', '', '01010404050200'),
('01010404050304', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC05.KP02.OP04', '如果在高反射率的金属上刻痕，就可以做成反射光栅。', '', '01010404050200'),
('01010404060000', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06', '光的偏振  激光', '', '01010404000000'),
('01010404060100', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP01', '偏振', '', '01010404060000'),
('01010404060201', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP01.OP01', '在纵波中，各点的振动方向总与波的传播方向在同一直线上。', '', '01010404060100'),
('01010404060202', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP01.OP02', '在横波中，各点的振动方向总与波的传播方向垂直。', '', '01010404060100'),
('01010404060203', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP01.OP03', '不同的横波，即使传播方向相同，振动方向也可能是不同的。', '', '01010404060100'),
('01010404060204', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP01.OP04', '横波的振动方向称为“偏振方向”。', '', '01010404060100'),
('01010404060200', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP02', '光的偏振', '', '01010404060000'),
('01010404060301', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP02.OP01', '研究表明，光是一种横波。', '', '01010404060200'),
('01010404060302', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP02.OP02', '实际上，太阳以及日光灯、发光二极管等普通光源发出的光，包含着在垂直于传播方向上沿一切方向振动的光，而且沿着各个方向振动的光波的强度都相同。', '', '01010404060200'),
('01010404060300', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP03', '偏振现象的应用', '', '01010404060000'),
('01010404060401', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP03.OP01', '光的偏振现象有很多应用。', '', '01010404060300'),
('01010404060400', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP04', '激光的特点及其应用', '', '01010404060000'),
('01010404060501', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP04.OP01', '光是从物质的原子中发射出来的。', '', '01010404060400'),
('01010404060502', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP04.OP02', '迄今人类已经制造了各种类型的激光器，比如气体激光器、半导体激光器、染料激光器、宝石激光器，等等。', '', '01010404060400'),
('01010404060503', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP04.OP03', '激光的诞生是一件大事。', '', '01010404060400'),
('01010404060504', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP04.OP04', '激光纯净的特性能在实际应用中带来很多方便。', '', '01010404060400'),
('01010404060505', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP04.OP05', '激光能像无线电波那样被调制，用来传递信息。', '', '01010404060400'),
('01010404060506', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP04.OP06', '光纤通信就是激光和光导纤维相结合的产物。', '', '01010404060400'),
('01010404060507', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP04.OP07', '今天，每时每刻都有无数激光信号承载着海量的信息在海底光缆中传输。', '', '01010404060400'),
('01010404060508', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP04.OP08', '对准目标发出一个极短的激光脉冲，测量发射脉冲与收到反射回波的时间间隔，就可以求出目标的距离。', '', '01010404060400'),
('01010404060509', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP04.OP09', '现在，廉价的激光测距装置已经被应用到房屋装修等日常生活中。', '', '01010404060400'),
('01010404060510', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP04.OP10', '医学上可以用激光做“光刀”来切开皮肤、切除肿瘤，还可以用激光“焊接”剥落的视网膜。', '', '01010404060400'),
('01010404060511', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP04.OP11', '激光的用途还有很多。', '', '01010404060400'),
('01010404060500', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP05', '研究样例', '', '01010404060000'),
('01010404060601', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP05.OP01', '在铁架台的立柱跟铅垂线平行的情况下把杆线摆装在立柱上，调节摆线的长度，使摆杆与立柱垂直，则此时摆杆是水平的。', '', '01010404060500'),
('01010404060602', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP05.OP02', '测出静止时摆杆与铅垂线的夹角为β，则该倾斜平面与水平面的夹角θ＝90°－β。', '', '01010404060500'),
('01010404060603', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP05.OP03', '同样的操作进行三次，取平均值作为该周期的测量值。', '', '01010404060500'),
('01010404060600', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP06', '参考选题', '', '01010404060000'),
('01010404060701', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP06.OP01', '由于弹簧振子由轻质弹簧和振动小球组成，因此猜想：弹簧振子的运动周期跟弹簧的劲度系数、振动小球的质量有关。', '', '01010404060600'),
('01010404060702', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP06.OP02', '在保持弹簧劲度系数不变的情况下，改变小球质量，探究弹簧振子的运动周期跟小球质量的定量关系；在保持小球质量不变的情况下，改变弹簧的劲度系数，探究弹簧振子运动的周期跟弹簧劲度系数的定量关系。', '', '01010404060600'),
('01010404060703', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP06.OP03', '弹簧劲度系数的改变，可以通过改变弹簧的长度来实现。', '', '01010404060600'),
('01010404060704', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP06.OP04', '理论上可以证明，对同一根均匀弹簧，取其中不同长度进行实验，其劲度系数跟所取的弹簧长度具有确定的定量关系。', '', '01010404060600'),
('01010404060705', 'PHYSICS', 'SENIOR', 'TERM_4', 'CH04.SEC06.KP06.OP05', '也可以通过实验来测量不同长度弹簧的劲度系数。', '', '01010404060600');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010501000000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01', '安培力与洛伦兹力', '', NULL),
('01010501010000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01', '磁场对通电导线的作用力', '', '01010501000000'),
('01010501010100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP01', '安培力的方向', '', '01010501010000'),
('01010501010201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP01.OP01', '1.1-1所示，把一段导体棒悬挂在蹄形磁体的两极间，通以电流。', '', '01010501010100'),
('01010501010202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP01.OP02', '研究安培力的方向与哪些因素有关。', '', '01010501010100'),
('01010501010203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP01.OP03', '实验现象表明，通电导体棒受力方向与磁场方向、电流方向有关。', '', '01010501010100'),
('01010501010204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP01.OP04', '研究库仑力时，受力的物体是点电荷，点电荷受力的方向与电场的方向相同或相反；但在研究安培力时，受力的物体是通电导线，通电导线受力的方向与磁场的方向、电流的方向不但不在一条直线上，而且不在一个平面里。', '', '01010501010100'),
('01010501010200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP02', '安培力的大小', '', '01010501010000'),
('01010501010300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03', '磁电式电流表', '', '01010501010000'),
('01010501010401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP01', '磁电式电流表最基本的组成部分是磁体和放在磁体两极之间的线圈。', '', '01010501010300'),
('01010501010402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP02', '当电流通过线圈时，导线受到安培力的作用。', '', '01010501010300'),
('01010501010403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP03', '由左手定则可以判定，线圈左右两边所受的安培力的方向相反，于是安装在轴上的线圈就要转动。', '', '01010501010300'),
('01010501010404', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP04', '电流越大，安培力就越大，螺旋弹簧的形变也就越大，线圈偏转的角度也越大，达到新的平衡。', '', '01010501010300'),
('01010501010405', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP05', '从前面的分析可知，安培力总与磁感应强度的方向垂直。', '', '01010501010300'),
('01010501010406', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP06', '电流表的两磁极装有极靴，极靴中间还有一个用软铁制成的圆柱。', '', '01010501010300'),
('01010501010407', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP07', '线圈中的电流方向改变时，安培力的方向随着改变，指针的偏转方向也随着改变。', '', '01010501010300'),
('01010501010408', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP08', '磁电式电流表的优点是灵敏度高，可以测出很弱的电流；缺点是线圈的导线很细，允许通过的电流很弱（几十微安到几毫安）。', '', '01010501010300'),
('01010501020000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02', '磁场对运动电荷的作用力', '', '01010501000000'),
('01010501020100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02.KP01', '洛伦兹力的方向', '', '01010501020000'),
('01010501020201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02.KP01.OP01', '负电荷受力的方向与正电荷受力的方向相反。', '', '01010501020100'),
('01010501020200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02.KP02', '洛伦兹力的大小', '', '01010501020000'),
('01010501020300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02.KP03', '电子束的磁偏转', '', '01010501020000'),
('01010501020401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02.KP03.OP01', '撞击荧光屏，就能发光。', '', '01010501020300'),
('01010501030000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03', '带电粒子在匀强磁场中的运动', '', '01010501000000'),
('01010501030100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP01', '带电粒子在匀强磁场中的运动', '', '01010501030000'),
('01010501030201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP01.OP01', '要分析上述问题中带电粒子的运动，就需要分析粒子的受力情况。', '', '01010501030100'),
('01010501030202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP01.OP02', '洛伦兹力总是与粒子的运动方向垂直，只改变粒子速度的方向，不改变粒子速度的大小。', '', '01010501030100'),
('01010501030203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP01.OP03', '由于粒子速度的大小不变，粒子在匀强磁场中所受洛伦兹力的大小也不改变，洛伦兹力对粒子起到了向心力的作用。', '', '01010501030100'),
('01010501030200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP02', '带电粒子在磁场中做圆周运动的半径和周期', '', '01010501030000'),
('01010501030301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP02.OP01', '然后就可以分别判断粒子的速度和磁场的强弱对圆半径的影响。', '', '01010501030200'),
('01010501030302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP02.OP02', '（1）求粒子所受的重力和洛伦兹力的大小之比。', '', '01010501030200'),
('01010501030303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP02.OP03', '（2）求粒子在磁场中运动的轨道半径。', '', '01010501030200'),
('01010501030304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP02.OP04', '（3）求粒子做匀速圆周运动的周期。', '', '01010501030200'),
('01010501040000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04', '质谱仪与回旋加速器', '', '01010501000000'),
('01010501040100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP01', '质谱仪', '', '01010501040000'),
('01010501040201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP01.OP01', '可以利用电场和磁场来控制带电粒子的运动。', '', '01010501040100'),
('01010501040202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP01.OP02', '利用电场让带电粒子获得一定的速度，利用磁场让粒子做圆周运动。', '', '01010501040100'),
('01010501040200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02', '回旋加速器', '', '01010501040000'),
('01010501040301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP01', '要认识原子核内部的情况，必须把核“打开”进行“观察”。', '', '01010501040200'),
('01010501040302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP02', '由于库仑力可以对带电粒子做功，从而增加粒子的动能，因此，人们首先想到加速器中一定要用到电场。', '', '01010501040200'),
('01010501040303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP03', '加速电压越高，粒子获得的动能就越高。', '', '01010501040200'),
('01010501040304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP04', '由于粒子在加速过程中的径迹为直线，要得到较高动能的粒子，其加速装置要很长。', '', '01010501040200'),
('01010501040305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP05', '而磁场正好能使带电粒子“转圈圈”！', '', '01010501040200'),
('01010501040306', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP06', '两个半圆盒处于与盒面垂直的匀强磁场中，所以粒子在磁场中做匀速圆周运动。', '', '01010501040200'),
('01010501040307', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP07', '原因是，按照狭义相对论，粒子的质量随着速度增加而增大，而质量的变化会导致其回转周期的变化，从而破坏了与电场变化周期的同步。', '', '01010501040200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010502000000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02', '电磁感应', '', NULL),
('01010502010000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01', '楞次定律', '', '01010502000000'),
('01010502010100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP01', '影响感应电流方向的因素', '', '01010502010000'),
('01010502010200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02', '楞次定律', '', '01010502010000'),
('01010502010301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02.OP01', '感应电流沿着楞次定律所述的方向，是能量守恒定律的必然结果。', '', '01010502010200'),
('01010502010302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02.OP02', '在上述实验中，把磁极插入线圈或从线圈内抽出时，推力或拉力都必须做机械功，做功过程中消耗的机械能转化成感应电流的电能。', '', '01010502010200'),
('01010502010303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02.OP03', '用楞次定律可以判定感应电流的方向。', '', '01010502010200'),
('01010502010304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02.OP04', '已知距离载流直导线较近的位置磁场较强。', '', '01010502010200'),
('01010502010305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02.OP05', '根据楞次定律，感应电流的磁场应该是阻碍穿过线圈的磁通量变化的。', '', '01010502010200'),
('01010502010306', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02.OP06', '现在已经判明感应电流的磁场从纸面内向外指向读者，是跟原来磁场的方向相反的。', '', '01010502010200'),
('01010502010300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP03', '右手定则', '', '01010502010000'),
('01010502020000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02', '法拉第电磁感应定律', '', '01010502000000'),
('01010502020100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP01', '电磁感应定律', '', '01010502020000'),
('01010502020201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP01.OP01', '电路中有感应电流，就一定有电动势。', '', '01010502020100'),
('01010502020202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP01.OP02', '产生感应电动势的那部分导体就相当于电源。', '', '01010502020100'),
('01010502020200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP02', '导线切割磁感线时的感应电动势', '', '01010502020000'),
('01010502020301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP02.OP01', '后者不切割磁感线，不产生感应电动势。', '', '01010502020200'),
('01010502020302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP02.OP02', '可以判断，安培力的方向与推动导体棒运动的力的方向是相反的。', '', '01010502020200'),
('01010502020303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP02.OP03', '如果没有推力的作用，导体棒将克服安培力做功而消耗本身的机械能。', '', '01010502020200'),
('01010502030000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03', '涡流、电磁阻尼和电磁驱动', '', '01010502000000'),
('01010502030100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP01', '电磁感应现象中的感生电场', '', '01010502030000'),
('01010502030201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP01.OP01', '麦克斯韦认为，磁场变化时会在空间激发一种电场。', '', '01010502030100'),
('01010502030202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP01.OP02', '如果此刻空间存在闭合导体，导体中的自由电荷就会在感生电场的作用下做定向运动，产生感应电流，也就是说导体中产生了感应电动势。', '', '01010502030100'),
('01010502030203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP01.OP03', '现代科学研究中常要用到高速电子，电子感应加速器就是利用感生电场使电子加速的设备。', '', '01010502030100'),
('01010502030204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP01.OP04', '电磁铁线圈中电流的大小、方向可以变化，产生的感生电场使电子加速。', '', '01010502030100'),
('01010502030205', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP01.OP05', '根据楞次定律，为使真空室中产生顺时针方向的感生电场，磁场应该由弱变强。', '', '01010502030100'),
('01010502030200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02', '涡流', '', '01010502030000'),
('01010502030301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP01', '当某线圈中的电流随时间变化时，由于电磁感应，附近的另一个线圈中可能会产生感应电流。', '', '01010502030200'),
('01010502030302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP02', '金属块中的涡流会产生热量。', '', '01010502030200'),
('01010502030303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP03', '涡流产生的热量使金属熔化。', '', '01010502030200'),
('01010502030304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP04', '利用涡流冶炼金属的优点是，整个过程可以在真空中进行，能防止空气中的杂质进入金属，可以冶炼高质量的合金。', '', '01010502030200'),
('01010502030305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP05', '电动机、变压器的线圈都绕在铁芯上。', '', '01010502030200'),
('01010502030306', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP06', '线圈中流过变化的电流，在铁芯中产生的涡流使铁芯发热，浪费了能量，还可能损坏电器。', '', '01010502030200'),
('01010502030307', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP07', '一种探测地雷的探雷器是利用涡流工作的。', '', '01010502030200'),
('01010502030300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP03', '电磁阻尼', '', '01010502030000'),
('01010502030401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP03.OP01', '如果地下埋着金属物品，金属中会感应出涡流，涡流的磁场反过来影响线圈中的电流，使仪器报警。', '', '01010502030300'),
('01010502030402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP03.OP02', '机场、车站和重要活动场所的安检门可以探测人身携带的金属物品，道理是一样的。', '', '01010502030300'),
('01010502030403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP03.OP03', '交流感应电动机就是利用电磁驱动的原理工作的。', '', '01010502030300'),
('01010502030400', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP04', '电磁驱动', '', '01010502030000'),
('01010502040000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04', '互感和自感', '', '01010502000000'),
('01010502040100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP01', '互感现象', '', '01010502040000'),
('01010502040201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP01.OP01', '利用互感现象可以把能量由一个线圈传递到另一个线圈，因此互感在电工技术和电子技术中有广泛的应用。', '', '01010502040100'),
('01010502040202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP01.OP02', '变压器就是利用互感现象制成的。', '', '01010502040100'),
('01010502040200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP02', '自感现象', '', '01010502040000'),
('01010502040301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP02.OP01', '变压器、电动机等设备中有匝数很多的线圈，当电路中的开关断开时会产生很大的自感电动势，使得开关中的金属片之间产生电火花，烧蚀接触点，甚至会引起人身伤害。', '', '01010502040200'),
('01010502040300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP03', '自感系数', '', '01010502040000'),
('01010502040400', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP04', '磁场的能量', '', '01010502040000'),
('01010502040501', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP04.OP01', '开关断开以后，线圈中的电流并未立即消失，线圈中有电流，有电流就有磁场，能量储存在磁场中。', '', '01010502040400'),
('01010502040502', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP04.OP02', '当线圈刚刚接通电源的时候，自感电动势阻碍线圈中电流的增加；当电源断开的时候，自感电动势又阻碍线圈中电流的减小。', '', '01010502040400');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010503000000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03', '交变电流', '', NULL),
('01010503010000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01', '交变电流', '', '01010503000000'),
('01010503010100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP01', '交变电流', '', '01010503010000'),
('01010503010201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP01.OP01', '在恒定电流的电路中，电源的电动势不随时间变化，电路中的电流、电压也不随时间变化。', '', '01010503010100'),
('01010503010202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP01.OP02', '电池供给的电流方向不随时间变化，所以属于直流。', '', '01010503010100'),
('01010503010203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP01.OP03', '日常使用的各种充电器能把交变电流变成低压直流。', '', '01010503010100'),
('01010503010200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP02', '交变电流的产生', '', '01010503010000'),
('01010503010301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP02.OP01', '教学用发电机产生的电流，大小和方向都在不断地变化，是一种交变电流。', '', '01010503010200'),
('01010503010300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP03', '交变电流的变化规律', '', '01010503010000'),
('01010503010401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP03.OP01', '正弦式电流是最简单、最基本的交变电流。', '', '01010503010300'),
('01010503010400', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP04', '交流发电机', '', '01010503010000'),
('01010503010501', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP04.OP01', '电枢转动，磁极不动的发电机，叫作旋转电枢式发电机。', '', '01010503010400'),
('01010503010502', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP04.OP02', '不论哪种发电机，转动的部分都叫转子，不动的部分都叫定子。', '', '01010503010400'),
('01010503010503', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP04.OP03', '同时，转动的电枢无法做得很大，线圈匝数也不可能很多，所以产生的感应电动势也不能很高。', '', '01010503010400'),
('01010503010504', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP04.OP04', '旋转磁极式发电机克服了上述缺点，能够产生几千伏到几万伏的电压，输出功率可达几百兆瓦。', '', '01010503010400'),
('01010503010505', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP04.OP05', '蒸汽轮机、水轮机等将机械能传递给发电机，发电机将机械能转化为电能，输送给外电路。', '', '01010503010400'),
('01010503020000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02', '交变电流的描述', '', '01010503000000'),
('01010503020100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP01', '周期和频率', '', '01010503020000'),
('01010503020201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP01.OP01', '交变电流与振动和波一样具有周期性。', '', '01010503020100'),
('01010503020202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP01.OP02', '与任何周期性过程一样，交变电流也可以用周期或频率表示其变化的快慢。', '', '01010503020100'),
('01010503020203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP01.OP03', '使用交流的电气设备上，标出的额定电压和额定电流都是有效值，一般交流电压表测量的数值也是有效值。', '', '01010503020100'),
('01010503020200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP02', '峰值和有效值', '', '01010503020000'),
('01010503020301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP02.OP01', '电容器所能承受的电压要高于交流电压的峰值，否则电容器就可能被击穿。', '', '01010503020200'),
('01010503020300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP03', '正弦式交变电流的公式和图像', '', '01010503020000'),
('01010503030000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03', '变压器', '', '01010503000000'),
('01010503030100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01', '变压器的原理', '', '01010503030000'),
('01010503030201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01.OP01', '互感现象是变压器工作的基础。', '', '01010503030100'),
('01010503030202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01.OP02', '电流通过原线圈时在铁芯中激发磁场，由于电流的大小、方向在不断变化，铁芯中的磁场也在不断变化。', '', '01010503030100'),
('01010503030203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01.OP03', '变化的磁场在副线圈中产生感应电动势，所以尽管两个线圈之间没有导线相连，副线圈也能够输出电流。', '', '01010503030100'),
('01010503030204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01.OP04', '在输入的交流电压一定时，原线圈、副线圈取不同的匝数，副线圈输出的电压也不一样，变压器由此得名。', '', '01010503030100'),
('01010503030205', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01.OP05', '其实，变压器线圈通过电流时会发热；铁芯在交变磁场的作用下也会发热；此外，交变电流产生的磁场也不可能完全局限在铁芯内。', '', '01010503030100'),
('01010503030206', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01.OP06', '理想变压器也是一个理想化模型。', '', '01010503030100'),
('01010503030200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP02', '电压与匝数的关系', '', '01010503030000'),
('01010503030301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP02.OP01', '实际上变压器的效率都是比较高的，特别是电力设备中的巨大变压器，在满负荷工作时效率可以超过95%。', '', '01010503030200'),
('01010503030302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP02.OP02', '实际应用中需要改变交流电压的情况是很多的。', '', '01010503030200'),
('01010503030303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP02.OP03', '大型发电机发出的交流电压为几万伏，而远距离输电却需要几十万伏以上的电压。', '', '01010503030200'),
('01010503030304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP02.OP04', '各种用电设备所需的电压也不相同。', '', '01010503030200'),
('01010503030305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP02.OP05', '变压器能输送电能是利用了电磁感应。', '', '01010503030200'),
('01010503040000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04', '电能的输送', '', '01010503000000'),
('01010503040100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01', '降低输电损耗的两个途径', '', '01010503040000'),
('01010503040201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP01', '一个途径是减小输电线的电阻。', '', '01010503040100'),
('01010503040202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP02', '过粗的导线会耗费太多的金属材料，而且输电线太重、太粗也给铺设工程带来困难。', '', '01010503040100'),
('01010503040203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP03', '远距离输电时，为了降低输电线路中的损耗，就要减小输电电流；为了减小输电电流，同时又要保证向用户提供一定的电功率，就要提高输电电压。', '', '01010503040100'),
('01010503040204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP04', '现代远距离输电的电压都很高。', '', '01010503040100'),
('01010503040205', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP05', '输电电压也不是越高越好。', '', '01010503040100'),
('01010503040206', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP06', '电压越高，对输电线路绝缘性能的要求就越高，线路修建费用就会增多。', '', '01010503040100'),
('01010503040207', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP07', '输电电压越高，变压器上的电压也越高，对变压器的要求也相应提高。', '', '01010503040100'),
('01010503040208', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP08', '实际输送电能时，要综合考虑各种因素，如输送功率的大小、距离的远近、技术和经济要求等，依照不同情况选择合适的输电电压。', '', '01010503040100'),
('01010503040200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP02', '电网供电', '', '01010503040000'),
('01010503040301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP02.OP01', '采用电网送电，是输电技术的重要发展。', '', '01010503040200'),
('01010503040302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP02.OP02', '电网可以减小断电的风险，调剂不同地区电力供需的平衡。', '', '01010503040200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010504000000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04', '电磁振荡与电磁波', '', NULL),
('01010504010000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01', '电磁振荡', '', '01010504000000'),
('01010504010100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP01', '电磁振荡的产生', '', '01010504010000'),
('01010504010201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP01.OP01', '在前面的实验中，电路的电压发生周期性的变化，电路中的电流也发生周期性的变化。', '', '01010504010100'),
('01010504010202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP01.OP02', '会立即减小为0，而要保持原来的方向继续流动，并逐渐减小。', '', '01010504010100'),
('01010504010203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP01.OP03', '由于电流继续流动，电容器充电，电容器两极板带上与原来相反的电荷，并且电荷逐渐增多。', '', '01010504010100'),
('01010504010200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP02', '电磁振荡中的能量变化', '', '01010504010000'),
('01010504010301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP02.OP01', '从能量的观点来看，电容器刚要放电时，电容器里的电场最强，电路里的能量全部储存在电容器的电场中；电容器开始放电后，电容器里的电场逐渐减弱，线圈的磁场逐渐增强，电场能逐渐转化为磁场能；在放电完毕的瞬间，电场能全部转化为磁场能；之后，线圈的磁场逐渐减弱，电容器里的电场逐渐增强，磁场能逐渐转化为电场能；到反方向充电完毕的瞬间，磁场能全部转化为电场能。', '', '01010504010200'),
('01010504010302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP02.OP02', '如果没有能量损耗，振荡可以永远持续下去，振荡电流的振幅保持不变。', '', '01010504010200'),
('01010504010303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP02.OP03', '也可以用可调电容器或可调电感的线圈组成电路，改变电容器的电容或线圈的电感，振荡电路的周期和频率就会随着改变。', '', '01010504010200'),
('01010504010304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP02.OP04', '会有一部分能量会转化为内能。', '', '01010504010200'),
('01010504010300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP03', '电磁振荡的周期和频率', '', '01010504010000'),
('01010504010401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP03.OP01', '电磁振荡完成一次周期性变化需要的时间叫作周期。', '', '01010504010300'),
('01010504010402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP03.OP02', '求振荡电路的频率范围。', '', '01010504010300'),
('01010504020000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02', '电磁场与电磁波', '', '01010504000000'),
('01010504020100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP01', '电磁场', '', '01010504020000'),
('01010504020201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP01.OP01', '在变化的磁场中放一个闭合电路，电路里就会产生感应电流。', '', '01010504020100'),
('01010504020202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP01.OP02', '当电荷从静止到运动时，电场就发生变化，即空间各点的电场强度将随时间变化。', '', '01010504020100'),
('01010504020203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP01.OP03', '运动的电荷在空间要产生磁场。', '', '01010504020100'),
('01010504020204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP01.OP04', '如果在空间某区域有周期性变化的电场，就会在周围引起变化的磁场，变化的电场和磁场又会在较远的空间引起新的变化的电场和磁场。', '', '01010504020100'),
('01010504020200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02', '电磁波', '', '01010504020000'),
('01010504020301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP01', '因为电磁波的传播，靠的是电和磁的相互“感应”，而不是靠介质的机械传递。', '', '01010504020200'),
('01010504020302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP02', '1886年，赫兹在做放电实验时发现附近未闭合的线圈也出现了电火花。', '', '01010504020200'),
('01010504020303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP03', '仪器中有一对抛光的金属小球，两球之间有很小的空气间隙。', '', '01010504020200'),
('01010504020304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP04', '将两个球连接到产生高电压的感应圈的两端时，两球之间就出现了火花放电。', '', '01010504020200'),
('01010504020305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP05', '仪器的另一部分是弯成环状的导线，导线两端也安装两个金属小球，小球之间也有空隙。', '', '01010504020200'),
('01010504020306', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP06', '电磁波从发射器到达了接收器。', '', '01010504020200'),
('01010504020307', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP07', '当与感应圈相连的两个金属球间产生电火花时，周围空间出现了迅速变化的电磁场。', '', '01010504020200'),
('01010504020308', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP08', '在以后的一系列实验中，赫兹观察到了电磁波的反射、折射、干涉、偏振和衍射等现象。', '', '01010504020200'),
('01010504020309', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP09', '根据电磁场理论，电磁场的转换就是电场能量和磁场能量的转换，因而电磁波的发射过程就是辐射能量的过程，传播过程就是能量传播的过程。', '', '01010504020200'),
('01010504020310', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP10', '能量是电磁场的物质性最有说服力的证据之一。', '', '01010504020200'),
('01010504020311', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP11', '由此，人们认识到物质存在两种形式，一种是由原子和分子构成的实物，另一种则是以电磁场为代表的场。', '', '01010504020200'),
('01010504030000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03', '无线电波的发射和接收', '', '01010504000000'),
('01010504030100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP01', '无线电波的发射', '', '01010504030000'),
('01010504030201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP01.OP01', '研究表明，要有效地发射电磁波，振荡电路必须具有如下两个特点。', '', '01010504030100'),
('01010504030202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP01.OP02', '振荡电路向外界辐射能量的本领，与振荡频率密切相关。', '', '01010504030100'),
('01010504030203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP01.OP03', '频率越高，发射电磁波的本领就越大。', '', '01010504030100'),
('01010504030204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP01.OP04', '板间的距离，减小极板间的正对面积，从而使电场和磁场扩展到电容器的外部。', '', '01010504030100'),
('01010504030200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP02', '无线电波的接收', '', '01010504030000'),
('01010504030301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP02.OP01', '电磁波在传播时如果遇到导体，会使导体中产生感应电流。', '', '01010504030200'),
('01010504030302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP02.OP02', '调幅波的解调也叫检波。', '', '01010504030200'),
('01010504030300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP03', '电视广播的发射和接收', '', '01010504030000'),
('01010504030401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP03.OP01', '电视广播信号也是一种无线电信号。', '', '01010504030300'),
('01010504030402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP03.OP02', '在现代电视发射系统中，首先通过摄像机的感光器件将景物的光信号转变为电信号。', '', '01010504030300'),
('01010504030403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP03.OP03', '目前，高频电视信号的传播方式主要有三种，即地面无线电传输、有线网络传输以及卫星传输。', '', '01010504030300'),
('01010504030404', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP03.OP04', '不同的传播方式使用不同频率范围的电磁波，采取不同的调制方式。', '', '01010504030300'),
('01010504030405', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP03.OP05', '伴音信号经解调后送到扬声器。', '', '01010504030300'),
('01010504040000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04', '电磁波谱', '', '01010504000000'),
('01010504040100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP01', '无线电波', '', '01010504040000'),
('01010504040201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP01.OP01', '波长短的电磁波，由于衍射现象不明显，传播的方向性好，有利于用电磁波定位，因此雷达用的是微波。', '', '01010504040100'),
('01010504040202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP01.OP02', '移动电话也应用了无线电波。', '', '01010504040100'),
('01010504040203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP01.OP03', '许多自然过程也辐射无线电波。', '', '01010504040100'),
('01010504040200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP02', '红外线', '', '01010504040000'),
('01010504040301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP02.OP01', '所有物体都发射红外线。', '', '01010504040200'),
('01010504040302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP02.OP02', '热物体的红外辐射比冷物体的红外辐射强。', '', '01010504040200'),
('01010504040300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP03', '可见光', '', '01010504040000'),
('01010504040401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP03.OP01', '能使人的眼睛产生视觉效应的电磁波，称为可见光。', '', '01010504040300'),
('01010504040402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP03.OP02', '用灵敏的红外探测器接收远处物体发出的红外线，然后用电子电路对信号进行处理，可以得知被测对象的形状及温度、湿度等参数。', '', '01010504040300'),
('01010504040403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP03.OP03', '利用红外遥感技术可以在飞机或人造地球卫星上勘测地热、寻找水源、监视森林火情、预报风暴和寒潮。', '', '01010504040300'),
('01010504040404', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP03.OP04', '红外遥感技术在军事上的应用也十分重要。', '', '01010504040300'),
('01010504040405', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP03.OP05', '人体也在发射红外线，体温越高，发射的红外线越强。', '', '01010504040300'),
('01010504040400', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP04', '紫外线', '', '01010504040000'),
('01010504040501', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP04.OP01', '人眼看不到比紫光波长更短的电磁波。', '', '01010504040400'),
('01010504040502', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP04.OP02', '紫外线具有较高的能量，足以破坏细胞核中的物质。', '', '01010504040400'),
('01010504040503', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP04.OP03', '太阳光中有许多紫外线，人体接受适量的紫外线照射，能促进钙的吸收，改善身体健康状况。', '', '01010504040400'),
('01010504040500', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP05', 'X射线和', '', '01010504040000'),
('01010504040600', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP06', '射线', '', '01010504040000'),
('01010504040701', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP06.OP01', 'γ射线能破坏生命物质。', '', '01010504040600'),
('01010504040702', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP06.OP02', 'γ射线的穿透能力很强，也可用于探测金属构件内部的缺陷。', '', '01010504040600');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010505000000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05', '传感器', '', NULL),
('01010505010000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01', '认识传感器', '', '01010505000000'),
('01010505010100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP01', '神奇的传感器', '', '01010505010000'),
('01010505010201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP01.OP01', '才会接通，灯才会亮。', '', '01010505010100'),
('01010505010202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP01.OP02', '实际上，有些传感器还具有“超人”的本领。', '', '01010505010100'),
('01010505010203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP01.OP03', '通常转换成的可用信号是电压、电流等电学量，或转换为电路的通断。', '', '01010505010100'),
('01010505010204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP01.OP04', '把非电学量转换为电学量，可以很方便地进行测量、传输、处理和控制。', '', '01010505010100'),
('01010505010200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP02', '传感器的种类', '', '01010505010000'),
('01010505010301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP02.OP01', '在林林总总的传感器世界中，大多数传感器是以物理原理为基础制作的。', '', '01010505010200'),
('01010505010302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP02.OP02', '根据被测量的不同，可分为声、光、压力、位移、加速度、温度等传感器；根据具体工作原理的不同，可分为电阻式、电容式、电感式、光电式、热电式、压电式、磁电式等传感器。', '', '01010505010200'),
('01010505010300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP03', '传感器的组成与应用模式', '', '01010505010000'),
('01010505010401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP03.OP01', '传感器种类繁多，工作原理各异，外观和结构也千差万别，但是，传感器的基本部分一般由敏感元件、转换元件组成。', '', '01010505010300'),
('01010505010402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP03.OP02', '敏感元件是指能直接感受或响应外界被测非电学量的部分；转换元件是指能将敏感元件输出的信号直接转换成电信号的部分。', '', '01010505010300'),
('01010505010403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP03.OP03', '应该指出的是，并不是所有传感器都能够明显地区分敏感元件和转换元件。', '', '01010505010300'),
('01010505010404', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP03.OP04', '声波使振动膜片发生振动，振动膜片作为敏感元件，感受声音信号的变化，与固定电极组成的电容器相当于转换元件，将声音信号的强弱转换成电容器的电容变化。', '', '01010505010300'),
('01010505020000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02', '常见传感器的工作原理及应用', '', '01010505000000'),
('01010505020100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP01', '光敏电阻', '', '01010505020000'),
('01010505020201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP01.OP01', '硫化镉表面受到的光照强度不同时，两个电极间的电阻也不一样。', '', '01010505020100'),
('01010505020202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP01.OP02', '光敏电阻是光电传感器中常见的光敏元件。', '', '01010505020100'),
('01010505020203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP01.OP03', '光敏电阻在被光照射时电阻发生变化，原因是：硫化镉是一种半导体材料，无光照时，载流子极少，导电性能差；随着光照的增强，载流子增多，导电性变好。', '', '01010505020100'),
('01010505020204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP01.OP04', '光敏电阻有许多应用。', '', '01010505020100'),
('01010505020200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02', '金属热电阻和热敏电阻', '', '01010505020000'),
('01010505020301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP01', '除了光照以外，温度也能明显地影响金属导体和半导体材料的导电性能。', '', '01010505020200'),
('01010505020302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP02', '金属热电阻和热敏电阻就是传感器中常见的感知温度的敏感元件。', '', '01010505020200'),
('01010505020303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP03', '用金属丝可以制作温度传感器，称为热电阻。', '', '01010505020200'),
('01010505020304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP04', '常用的一种热电阻是用铂制作的，可用来做电阻温度计。', '', '01010505020200'),
('01010505020305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP05', '与热敏电阻相比，金属热电阻的化学稳定性好，测温范围大，但灵敏度较差。', '', '01010505020200'),
('01010505020306', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP06', '若给热敏电阻通以一定的电流，热敏电阻会发热。', '', '01010505020200'),
('01010505020307', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP07', '通过判断热敏电阻的阻值变化，就可以知道液面是否低于设定值。', '', '01010505020200'),
('01010505020300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP03', '电阻应变片', '', '01010505020000'),
('01010505020401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP03.OP01', '当金属丝受到拉力时，长度变长、横截面积变小，导致电阻变大；当金属丝受到压力时，长度变短、横截面积变大，导致电阻变小。', '', '01010505020300'),
('01010505020402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP03.OP02', '通过前面的学习可以知道，光敏电阻、热敏电阻、电阻应变片等电阻式传感器的工作共性是通过测量电阻的变化来确定外界非电学量的变化。', '', '01010505020300'),
('01010505020403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP03.OP03', '电容式传感器有非常广泛的应用。', '', '01010505020300'),
('01010505030000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03', '利用传感器制作简单的自动控制装置', '', '01010505000000'),
('01010505030100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01', '实验1 门窗防盗报警装置', '', '01010505030000'),
('01010505030201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP01', '门窗防盗报警装置具有自动提示报警的功能。', '', '01010505030100'),
('01010505030202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP02', '在睡觉前连接好电路，启动防盗报警装置。', '', '01010505030100'),
('01010505030203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP03', '当门窗紧闭时，蜂鸣器不响，指示灯亮；当门窗被打开时，蜂鸣器发出声音警报，指示灯灭。', '', '01010505030100'),
('01010505030204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP04', '利用干簧管在磁场中的特性是不错的选择。', '', '01010505030100'),
('01010505030205', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP05', '继电器（虚线方框部分）作为执行装置。', '', '01010505030100'),
('01010505030206', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP06', '当门窗开启时，磁体离开干簧管，干簧管失磁断开，继电器被断电。', '', '01010505030100'),
('01010505030207', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP07', '干簧管在电路中起传感器和控制开关的作用，继电器则相当于一个自动的双向开关。', '', '01010505030100'),
('01010505030208', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP08', '实验操作（1）连接电路前，要先判断一下干簧管是否可以正常工作。', '', '01010505030100'),
('01010505030209', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP09', '用磁体直接靠近干簧管，观察簧片能否正常动作。', '', '01010505030100'),
('01010505030210', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP10', '（3）接通电源后，将磁体靠近和离开干簧管，分别观察实验现象。', '', '01010505030100'),
('01010505030200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02', '实验2 光控开关', '', '01010505030000'),
('01010505030301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP01', '光控路灯可以根据光照的变化自动开启或关闭。', '', '01010505030200'),
('01010505030302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP02', '种模拟的光控开关电路，用发光二极管或小灯泡代表路灯。', '', '01010505030200'),
('01010505030303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP03', '在控制电路中，由于光敏电阻的工作电流很微弱，不能直接驱动执行机构工作，因此一般都需要配置一定的放大电路实现控制。', '', '01010505030200'),
('01010505030304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP04', '晶体三极管是半导体基本元件之一，具有电流放大作用，在控制电路中常用作电子开关。', '', '01010505030200'),
('01010505030305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP05', '本实验采用三极管配合光敏电阻完成光控开关的任务。', '', '01010505030200'),
('01010505030306', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP06', '三极管的一个重要特性是，从基极输入一个较小的电流，就会在集电极获得较大的电流。', '', '01010505030200'),
('01010505030307', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP07', '如果实验现象不明显，可用手电筒加强光照，或遮盖光敏电阻，再进行观察。', '', '01010505030200'),
('01010505030300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03', '研究样例', '', '01010505030000'),
('01010505030401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP01', '压阻效应是指当半导体材料沿某一方向受到外力作用时，其电阻率发生变化的现象。', '', '01010505030300'),
('01010505030402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP02', '为使应变片受力均匀，在圆柱体与应变片之间垫一个面积相当于有效区域的聚氨酯橡胶垫片。', '', '01010505030300'),
('01010505030403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP03', '传感器的两根引线跟数字多用电表两表笔相连。', '', '01010505030300'),
('01010505030404', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP04', '数字多用电表选用电阻挡，可以直接测量传感器的电阻。', '', '01010505030300'),
('01010505030405', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP05', '改变钩码的数量，就改变了传感器所受的压力。', '', '01010505030300'),
('01010505030406', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP06', '该装置可以测量不同压力下传感器的电阻值。', '', '01010505030300'),
('01010505030407', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP07', '要使传感器比较灵敏，就应使之工作在灵敏度较大的区间。', '', '01010505030300'),
('01010505030408', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP08', '该装置把大小不同的苹果，按一定质量标准自动分拣为大苹果和小苹果。', '', '01010505030300'),
('01010505030409', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP09', '该放大电路中包含保持电路，能够确保大苹果在衔铁上运动时电磁铁始终保持吸动状态。', '', '01010505030300'),
('01010505030400', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP04', '参考选题', '', '01010505030000'),
('01010505030501', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP04.OP01', '热敏电阻价格低廉，为研究热敏电阻的特性提供了良好的器材条件。', '', '01010505030400'),
('01010505030502', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP04.OP02', '通过加兑热水改变水温，测量不同温度下热敏电阻的阻值，绘制电阻－温度曲线，描述热敏电阻的阻值随温度变化的规律。', '', '01010505030400');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010601000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01', '分子动理论', '', NULL),
('01010601010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01', '分子动理论的基本内容', '', '01010601000000'),
('01010601010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01', '物体是由大量分子组成的', '', '01010601010000'),
('01010601010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP01', '人们用肉眼无法直接看到分子，就是用高倍的光学显微镜也看不到。', '', '01010601010100'),
('01010601010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP02', '直至1982年，人们研制了能放大几亿倍的扫描隧道显微镜③，才观察到物质表面原子的排列。', '', '01010601010100'),
('01010601010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02', '分子热运动', '', '01010601010000'),
('01010601010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP01', '扩散现象是物质分子永不停息地做无规则运动的证据之一。', '', '01010601010200'),
('01010601010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP02', '扩散现象在科学技术中有很多应用。', '', '01010601010200'),
('01010601010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP03', '19世纪初，一些人观察到，悬浮在液体中的小颗粒总在不停地运动。', '', '01010601010200'),
('01010601010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP04', '从实验结果可以看出，小炭粒的运动是无规则的，温度越高，小炭粒的运动越明显。', '', '01010601010200'),
('01010601010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP05', '在扩散现象中，温度越高，扩散得越快。', '', '01010601010200'),
('01010601010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP06', '观察布朗运动，温度越高，悬浮微粒的运动就越明显。', '', '01010601010200'),
('01010601010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP07', '温度是分子热运动剧烈程度的标志。', '', '01010601010200'),
('01010601010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03', '分子间的作用力', '', '01010601010000'),
('01010601010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP01', '水和酒精混合后的总体积变小了。', '', '01010601010300'),
('01010601010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP02', '当用力拉伸物体时，物体内各部分之间要产生反抗拉伸的作用力，此时分子间的作用力表现为引力。', '', '01010601010300'),
('01010601010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP03', '当用力压缩物体时，物体内各部分之间会产生反抗压缩的作用力，此时分子间的作用力表现为斥力。', '', '01010601010300'),
('01010601010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP04', '原子内部有带正电的原子核和带负电的电子。', '', '01010601010300'),
('01010601010400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP04', '分子动理论', '', '01010601010000'),
('01010601020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02', '实验：用油膜法估测油酸分子的大小', '', '01010601000000'),
('01010601020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP01', '实验思路', '', '01010601020000'),
('01010601020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP02', '物理量的测量', '', '01010601020000'),
('01010601020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP03', '数据分析', '', '01010601020000'),
('01010601030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03', '分子运动速率分布规律', '', '01010601000000'),
('01010601030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01', '气体分子运动的特点', '', '01010601030000'),
('01010601030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP01', '热现象与大量分子热运动的统计规律有关。', '', '01010601030100'),
('01010601030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP02', '要研究气体的热现象，就要了解气体分子运动的特点。', '', '01010601030100'),
('01010601030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP03', '液体变为气体后，体积要增大上千倍，可见，气体分子间距离大约是分子直径的10倍。', '', '01010601030100'),
('01010601030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP04', '分子的大小相对分子间的空隙来说很小，所以，可以把气体分子视为质点。', '', '01010601030100'),
('01010601030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP05', '分子的运动杂乱无章，在某一时刻，向着任何一个方向运动的分子都有，而且向各个方向运动的分子数目几乎相等。', '', '01010601030100'),
('01010601030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02', '分子运动速率分布图像', '', '01010601030000'),
('01010601030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP01', '尽管分子做无规则运动，速率有大有小，但大量分子的速率却按一定的规律分布。', '', '01010601030200'),
('01010601030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP02', '100℃的氧气，速率大的分子比例较多，其分子的平均速率比0℃的大。', '', '01010601030200'),
('01010601030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03', '气体压强的微观解释', '', '01010601030000'),
('01010601030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP01', '器壁单位面积上受到的压力，就是气体的压强。', '', '01010601030300'),
('01010601030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP02', '从微观角度来看，气体分子的数量是巨大的。', '', '01010601030300'),
('01010601040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04', '分子动能和分子势能', '', '01010601000000'),
('01010601040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01', '分子动能', '', '01010601040000'),
('01010601040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP01', '物体中分子热运动的速率大小不一，所以各个分子的动能也有大有小，而且在不断改变。', '', '01010601040100'),
('01010601040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP02', '温度升高时，分子的热运动加剧，温度越高，分子热运动的平均动能越大。', '', '01010601040100'),
('01010601040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP03', '温度越低，分子热运动的平均动能越小。', '', '01010601040100'),
('01010601040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02', '分子势能', '', '01010601040000'),
('01010601040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP01', '分子间存在着相互作用力，可以证明分子间的作用力所做的功与路径无关，分子组成的系统具有分子势能。', '', '01010601040200'),
('01010601040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP02', '物体的体积变化时，分子间距离将发生变化，因而分子势能随之改变。', '', '01010601040200'),
('01010601040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03', '物体的内能', '', '01010601040000'),
('01010601040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP01', '任何物体都具有内能。', '', '01010601040300'),
('01010601040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP02', '分子热运动的平均动能与温度有关，分子势能与物体的体积有关。', '', '01010601040300');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010602000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02', '气体、固体和液体', '', NULL),
('01010602010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01', '温度和温标', '', '01010602000000'),
('01010602010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01', '状态参量与平衡态', '', '01010602010000'),
('01010602010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01.OP01', '要定量地描述系统的状态往往很难，因为有时系统的参量会随时间变化。', '', '01010602010100'),
('01010602010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP02', '热平衡与温度', '', '01010602010000'),
('01010602010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03', '温度计与温标', '', '01010602010000'),
('01010602010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03.OP01', '现代科学中用得更多的是热力学温标。', '', '01010602010300'),
('01010602020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC02', '气体的等温变化', '', '01010602000000'),
('01010602030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03', '气体的等压变化和等容变化', '', '01010602000000'),
('01010602030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP01', '气体的等压变化', '', '01010602030000'),
('01010602030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02', '气体的等容变化', '', '01010602030000'),
('01010602030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02.OP01', '法国科学家查理在分析了实验事实后发现，当一定质量的气体体积一定时，各种气体的压强与温度之间都有线性关系。', '', '01010602030200'),
('01010602030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03', '理想气体', '', '01010602030000'),
('01010602030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP01', '当压强很大、温度很低时，由上述规律计算的结果与实际测量结果有很大的差别。', '', '01010602030300'),
('01010602030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP02', '实际气体的分子之间有相互作用力，但是作用力很小；分子也有大小，但气体分子之间的间距比分子直径大得多；气体分子与器壁碰撞几乎是弹性的，动能损失也很小。', '', '01010602030300'),
('01010602030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP03', '在温度不低于零下几十摄氏度、压强不超过大气压的几倍时，把实际气体当成理想气体来处理，误差很小。', '', '01010602030300'),
('01010602030400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04', '气体实验定律的微观解释', '', '01010602030000'),
('01010602030501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP01', '用分子动理论可以定性解释气体的实验定律。', '', '01010602030400'),
('01010602030502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP02', '一定质量的某种理想气体，温度保持不变时，分子的平均动能是一定的。', '', '01010602030400'),
('01010602030503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP03', '一定质量的某种理想气体，温度升高时，分子的平均动能增大；只有气体的体积同时增大，使分子的数密度减小，才能保持压强不变。', '', '01010602030400'),
('01010602030504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP04', '一定质量的某种理想气体，体积保持不变时，分子的数密度保持不变。', '', '01010602030400'),
('01010602040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04', '固体', '', '01010602000000'),
('01010602040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01', '晶体和非晶体', '', '01010602040000'),
('01010602040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP01', '固体可以分为晶体和非晶体两类。', '', '01010602040100'),
('01010602040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP02', '石英、云母①、明矾、食盐、硫酸铜、味精等是晶体。', '', '01010602040100'),
('01010602040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP03', '玻璃、蜂蜡、松香、沥青、橡胶等是非晶体。', '', '01010602040100'),
('01010602040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP04', '非晶体没有规则的外形。', '', '01010602040100'),
('01010602040205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP05', '除了形状是否规则外，晶体和非晶体在物理性质上也有所不同。', '', '01010602040100'),
('01010602040206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP06', '从实验结果可以看出，玻璃片上石蜡熔化区域的形状近似于圆形，表明玻璃沿各个方向的导热性能相同；云母片上石蜡熔化区域的形状呈椭圆形，表明云母沿不同方向的导热性能不同。', '', '01010602040100'),
('01010602040207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP07', '蔗糖受潮后粘在一起形成的糖块，看起来没有确定的几何形状。', '', '01010602040100'),
('01010602040208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP08', '粘在一起的糖块也是多晶体，单个的蔗糖晶体颗粒是单晶体。', '', '01010602040100'),
('01010602040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02', '晶体的微观结构', '', '01010602040000'),
('01010602040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP01', '在20世纪70年代，人们又用电子显微镜观察到了铀、钍原子的像。', '', '01010602040200'),
('01010602040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP02', '1982年，人们用扫描隧道显微镜观察到物质表面原子的排列。', '', '01010602040200'),
('01010602040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP03', '在各种晶体中，原子（或分子、离子）都是按照一定的规则排列的，具有空间上的周期性。', '', '01010602040200'),
('01010602040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP04', '石墨是层状结构，层与层之间距离较大，原子间的作用力比较弱，所以石墨质地松软，可以用来制作粉状润滑剂；金刚石中碳原子间的作用力很强，所以金刚石有很大的硬度，可以用来切割玻璃。', '', '01010602040200'),
('01010602040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP05', '同种物质也可能以晶体和非晶体两种不同的形态出现，也就是说，物质是晶体还是非晶体，并不是绝对的。', '', '01010602040200'),
('01010602040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP06', '有些非晶体在一定条件下也可以转化为晶体。', '', '01010602040200'),
('01010602050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05', '液体', '', '01010602000000'),
('01010602050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP01', '液体的表面张力', '', '01010602050000'),
('01010602050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02', '浸润和不浸润', '', '01010602050000'),
('01010602050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP01', '管的内径越小，上述现象就越明显。', '', '01010602050200'),
('01010602050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP02', '有些物体内部有许多细小的孔道，孔道起到了毛细管的作用。', '', '01010602050200'),
('01010602050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP03', '当液体和与之接触的固体的相互作用比液体分子之间的相互作用强时，液体能够浸润固体。', '', '01010602050200'),
('01010602050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP04', '如果把水银滴到干净的铅板上，水银就会附着在铅板上，很难擦去。', '', '01010602050200'),
('01010602050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03', '液晶', '', '01010602050000'),
('01010602050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP01', '液晶在现代生活中扮演着重要角色，广泛应用于手机屏幕、平板电视等显示设备中。', '', '01010602050300'),
('01010602050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP02', '液晶态既具有液体的流动性，又在一定程度上具有晶体分子的规则排列的性质。', '', '01010602050300'),
('01010602050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP03', '有些物质在特定的温度范围之内具有液晶态；另一些物质，在适当的溶剂中溶解时，在一定的浓度范围具有液晶态。', '', '01010602050300'),
('01010602050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP04', '分子取向排列的液晶具有光学各向异性，具体地说，沿分子长轴方向上的折射率不同于沿短轴的。', '', '01010602050300'),
('01010602050405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP05', '入射光的偏振方向与液晶分子长轴的方向成不同夹角时，液晶对光的折射率不同。', '', '01010602050300'),
('01010602050406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP06', '液晶的研究在生命科学中也有应用。', '', '01010602050300');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010603000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03', '热力学定律', '', NULL),
('01010603010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01', '功、热和内能的改变', '', '01010603000000'),
('01010603010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01', '焦耳的实验', '', '01010603010000'),
('01010603010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP01', '在焦耳研究功与热等效性的许多实验中，有两个最具代表性。', '', '01010603010100'),
('01010603010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP02', '一个是让重物下落带动叶片搅拌容器中的水，引起水温上升；另一个是通过电流的热效应给水加热。', '', '01010603010100'),
('01010603010203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP03', '焦耳的多次实验测量表明，尽管各次悬挂重物的质量不同，下落的高度也不一样，但只要重力所做的功相同，容器内水温上升的数值都是相同的，即系统状态的变化是相同的。', '', '01010603010100'),
('01010603010204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP04', '多次实验结果表明，对于同一个系统，如果过程是绝热的，那么不管通过电阻丝的电流或大或小、通电时间或长或短，只要所做的电功相等，则系统温度上升的数值是相同的，即系统的状态就发生了同样的变化。', '', '01010603010100'),
('01010603010205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP05', '两个位置间移动时重力所做的功。', '', '01010603010100'),
('01010603010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02', '功与内能的改变', '', '01010603010000'),
('01010603010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP01', '当温度达到易燃物的燃点时，易燃物发生自燃。', '', '01010603010200'),
('01010603010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP02', '当橡胶塞跳出时，瓶内出现白雾。', '', '01010603010200'),
('01010603010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP03', '以瓶内气体为研究对象，橡胶塞跳出后，瓶内的气体迅速膨胀，系统对外做功，因此，气体的内能迅速减少，瓶内气体温度迅速下降，瓶内水蒸气液化，就会出现大量的雾状小水滴。', '', '01010603010200'),
('01010603010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03', '热与内能的改变', '', '01010603010000'),
('01010603010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03.OP01', '外界对系统做功可以改变系统的热力学状态，而单纯地对系统传热也能改变系统的热力学状态。', '', '01010603010300'),
('01010603020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02', '热力学第一定律', '', '01010603000000'),
('01010603020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01', '热力学第一定律', '', '01010603020000'),
('01010603020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01.OP01', '（1）求上述压缩过程中气体内能的变化量。', '', '01010603020100'),
('01010603020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP02', '热力学第一定律的应用', '', '01010603020000'),
('01010603030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03', '能量守恒定律', '', '01010603000000'),
('01010603030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01', '探索能量守恒的足迹', '', '01010603030000'),
('01010603030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP01', '能量的概念是人类在对物质运动规律进行长期探索中建立起来的。', '', '01010603030100'),
('01010603030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP02', '所有自然现象都涉及能量，人类的任何活动都离不开能量。', '', '01010603030100'),
('01010603030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP03', '能量具有不同的形式，有描述热运动的内能、描述机械运动的机械能、描述光辐射的光能，等等。', '', '01010603030100'),
('01010603030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP04', '不同形式的运动都可以用能量来描述。', '', '01010603030100'),
('01010603030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP05', '在认识自然的进程中，科学家慢慢知道了要用联系的观点去观察自然。', '', '01010603030100'),
('01010603030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP06', '由于能量既抽象又以各种不同的形式广泛存在，所以能量守恒思想的萌芽及产生并不是一蹴而就的。', '', '01010603030100'),
('01010603030207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP07', '从各种实验的验证，再到实验结果的被质疑，科学家们付出了大量的努力。', '', '01010603030100'),
('01010603030208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP08', '从18世纪末到19世纪中叶，不同领域的科学家从不同角度都提出过能量守恒的思想。', '', '01010603030100'),
('01010603030209', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP09', '人类对能量守恒的认识经历了一个由浅入深、由含糊到清晰的过程。', '', '01010603030100'),
('01010603030210', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP10', '俄国化学家盖斯的研究发现，任何一个化学反应，不论是一步完成，还是分几步完成，放出的总热量相同。', '', '01010603030100'),
('01010603030211', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP11', '焦耳的实验精确地测量了做功与传热之间的等价关系，从而为能量守恒定律奠定了牢固的实验基础，也为能量守恒的定量描述迈出了重要的一步。', '', '01010603030100'),
('01010603030212', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP12', '德国医生迈尔通过比对不同地区人血颜色的差异，认识到食物中化学能与内能的等效性，即生物体内能量的输入和输出是平衡的。', '', '01010603030100'),
('01010603030213', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP13', '半个多世纪人类对能量以及能量守恒研究的历史，预示着人们把各分立的领域连成一体的时刻已经到来，也就是到了建立能量转化与守恒定律的时候了。', '', '01010603030100'),
('01010603030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02', '能量守恒定律', '', '01010603030000'),
('01010603030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP01', '在热力学领域内，做功和传热可以改变系统的内能，即系统内能与系统外的能量会发生转化或转移，但能的总量不会改变。', '', '01010603030200'),
('01010603030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP02', '能量守恒是自然界的普遍规律。', '', '01010603030200'),
('01010603030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP03', '根据能量守恒定律，物理学发现和解释了很多科学现象。', '', '01010603030200'),
('01010603030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP04', '在20世纪30年代初，奥地利物理学家泡利根据能量守恒定律预言了中微子。', '', '01010603030200'),
('01010603030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP05', '能量守恒定律经受住了新的检验。', '', '01010603030200'),
('01010603030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP06', '能量守恒定律的发现是科学史上的重大事件。', '', '01010603030200'),
('01010603030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03', '永动机不可能制成', '', '01010603030000'),
('01010603030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP01', '“问题”栏目中的饮水小鸭不是永动机。', '', '01010603030300'),
('01010603030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP02', '小鸭的头和身由两个玻璃球构成，并通过细玻璃管相连。', '', '01010603030300'),
('01010603030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP03', '下球盛有一些乙醚，上球连同尖嘴被吸水毛毡包起来。', '', '01010603030300'),
('01010603030404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP04', '永动机的思想违背了能量守恒定律，所以是不可能制成的。', '', '01010603030300'),
('01010603030405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP05', '应该指出的是，制造永动机的千万次失败使人们的头脑冷静下来，开始在更深层次寻找失败的原因。', '', '01010603030300'),
('01010603030406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP06', '国科学院宣布“不再审查有关永动机的一切设计”。', '', '01010603030300'),
('01010603040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04', '热力学第二定律', '', '01010603000000'),
('01010603040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01', '热力学第二定律', '', '01010603040000'),
('01010603040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP01', '在电冰箱的实例中，热量的确从低温物体——冰箱内的食品，传到了高温物体——冰箱外的空气。', '', '01010603040100'),
('01010603040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP02', '从能量的角度看，热机的工作分为两个阶段：第一个阶段是燃烧燃料，把燃料中的化学能变成工作物质的内能；第二个阶段是工作物质对外做功，把自己的内能变成机械能。', '', '01010603040100'),
('01010603040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP03', '热力学第二定律的开尔文表述阐述了机械能与内能转化的方向性：机械能可以全部转化为内能，而内能无法全部用来做功以转化成机械能。', '', '01010603040100'),
('01010603040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP04', '可以证明，热力学第二定律的克劳修斯表述和开尔文表述是等价的。', '', '01010603040100'),
('01010603040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02', '能源是有限的', '', '01010603040000'),
('01010603040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP01', '自然界的一切变化，人类社会的所有活动，都伴随着能量的转移和转化，能量是一切物质运动的源泉，是一切生命活动的基础。', '', '01010603040200'),
('01010603040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP02', '能量在数量上虽然守恒，但其转移和转化却具有方向性。', '', '01010603040200'),
('01010603040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP03', '核能、生物能……最终都转化成内能，流散到周围的环境中。', '', '01010603040200'),
('01010603040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP04', '周围环境中的内能很难再利用，而机械能、光能、化学能、电能相对于周围环境中的内能来说，可利用的品质要高。', '', '01010603040200'),
('01010603040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP05', '能源的使用过程中虽然能的总量保持守恒，但能量的品质下降了。', '', '01010603040200'),
('01010603040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP06', '虽然能量总量不会减少，但能源会逐步减少，因此能源是有限的资源。', '', '01010603040200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010604000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04', '原子结构和波粒二象性', '', NULL),
('01010604010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01', '普朗克黑体辐射理论', '', '01010604000000'),
('01010604010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01', '黑体与黑体辐射', '', '01010604010000'),
('01010604010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP01', '19世纪，由于冶金、星体测温等需求，人们对热辐射进行了大量的研究。', '', '01010604010100'),
('01010604010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP02', '研究表明，对于一般材料的物体，辐射电磁波的情况除了与温度有关，还与材料的种类及表面状况有关，而黑体辐射电磁波的强度按波长的分布只与黑体的温度有关。', '', '01010604010100'),
('01010604010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02', '黑体辐射的实验规律', '', '01010604010000'),
('01010604010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP01', '利用分光技术和热电偶等设备，可以测出黑体辐射电磁波的强度按波长分布的情况。', '', '01010604010200'),
('01010604010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP02', '从中可以看出，随着温度的升高，一方面，各种波长的辐射强度都有增加，另一方面，辐射强度的极大值向波长较短的方向移动。', '', '01010604010200'),
('01010604010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP03', '德国物理学家维恩在1896年、英国物理学家瑞利在1900年，分别提出了辐射强度按波长分布的理论公式。', '', '01010604010200'),
('01010604010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP04', '维恩公式在短波区与实验非常接近，在长波区则与实验偏离较大；瑞利公式在长波区与实验基本一致，但在短波区与实验严重不符。', '', '01010604010200'),
('01010604010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP05', '为了得出同全部实验相符的黑体辐射公式，德国物理学家普朗克做了多种尝试。', '', '01010604010200'),
('01010604010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03', '能量子', '', '01010604010000'),
('01010604010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP01', '弹簧振子的能量不一定是某个最小值的整数倍。', '', '01010604010300'),
('01010604010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP02', '而普朗克的假设则认为微观粒子的能量是量子化的，或者说微观粒子的能量是分立的。', '', '01010604010300'),
('01010604010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP03', '普朗克本人因此获得了1918年的诺贝尔物理学奖。', '', '01010604010300'),
('01010604020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02', '光电效应', '', '01010604000000'),
('01010604020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01', '光电效应的实验规律', '', '01010604020000'),
('01010604020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP01', '1887年，赫兹在研究电磁波的实验中偶尔发现，接收电路的间隙如果受到光照，就更容易产生电火花。', '', '01010604020100'),
('01010604020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP02', '科学家通过实验发现了以下规律。', '', '01010604020100'),
('01010604020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP03', '实验表明，不同金属的截止频率不同。', '', '01010604020100'),
('01010604020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP04', '在光照条件不变的情况下，随着所加电压的增大，光电流趋于一个饱和值。', '', '01010604020100'),
('01010604020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP05', '实验表明，在光的频率不变的情况下，入射光越强，饱和电流越大。', '', '01010604020100'),
('01010604020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02', '光电效应经典解释中的疑难', '', '01010604020000'),
('01010604020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP01', '当光照射金属表面时，电子会吸收光的能量。', '', '01010604020200'),
('01010604020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP02', '光越强，逸出的电子数越多，光电流也就越大。', '', '01010604020200'),
('01010604020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP03', '●不管光的频率如何，只要光足够强，电子都可以获得足够能量从而逸出表面，不应存在截止频率。', '', '01010604020200'),
('01010604020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03', '爱因斯坦的光电效应理论', '', '01010604020000'),
('01010604020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP01', '对于光电效应的解释，爱因斯坦是在普朗克量子假说的基础上作出的。', '', '01010604020300'),
('01010604020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP02', '从下面的讨论可以看出，爱因斯坦光电效应方程可以很好地解释光电效应实验中的各种现象。', '', '01010604020300'),
('01010604020403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP03', '●电子一次性吸收光子的全部能量，不需要积累能量的时间，光电流自然几乎是瞬时产生的。', '', '01010604020300'),
('01010604020404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP04', '爱因斯坦由于提出了光电效应理论而获得1921年的诺贝尔物理学奖。', '', '01010604020300'),
('01010604020405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP05', '金属表面的光子数较多，照射金属时产生的光电子较多，因而饱和电流较大。', '', '01010604020300'),
('01010604020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04', '康普顿效应和光子的动量', '', '01010604020000'),
('01010604020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP01', '光可以与介质中的物质微粒发生散射，改变传播方向。', '', '01010604020400'),
('01010604020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP02', '康普顿效应让人们对光子有了更深入的认识。', '', '01010604020400'),
('01010604020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP03', '康普顿因此获得了1927年的诺贝尔物理学奖。', '', '01010604020400'),
('01010604020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05', '光的波粒二象性', '', '01010604020000'),
('01010604020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP01', '众所周知，在麦克斯韦的电磁理论建立之后，人们认识到光是一种电磁波，从而光的波动说被普遍接受，人们不再认为光是由粒子组成的。', '', '01010604020500'),
('01010604020602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP02', '而爱因斯坦的光电效应理论和康普顿效应理论表明，光在某些方面确实会表现得像是由一些粒子（即一个个有确定能量和动量的“光子”）组成的。', '', '01010604020500'),
('01010604020603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP03', '人们意识到，光既具有波动性，又具有粒子性。', '', '01010604020500'),
('01010604020604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP04', '从牛顿的微粒说、惠更斯和托马斯·杨的波动说，到麦克斯韦的电磁理论，再到爱因斯坦的光子理论乃至量子电动力学，人类对光的认识构成了一部科学史诗。', '', '01010604020500'),
('01010604030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03', '原子的核式结构模型', '', '01010604000000'),
('01010604030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01', '电子的发现', '', '01010604030000'),
('01010604030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP01', '为了找到有利于自己的证据，双方都做了许多实验。', '', '01010604030100'),
('01010604030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP02', '由实验测得的阴极射线粒子的比荷是氢离子①（也就是质子）比荷的近两千倍。', '', '01010604030100'),
('01010604030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP03', '金属受热还是放射性物质的自发辐射，都能发射同样的带电粒子——电子。', '', '01010604030100'),
('01010604030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02', '原子的核式结构模型', '', '01010604030000'),
('01010604030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP01', 'α粒子是从放射性物质（如铀和镭）中发射出来的快速运动的粒子，质量为氢原子质量的4倍、电子质量的7300倍。', '', '01010604030200'),
('01010604030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP02', '被散射的α粒子打在荧光屏上会有微弱的闪光产生。', '', '01010604030200'),
('01010604030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP03', '通过显微镜观察闪光就可以记录在某一时间内向某一方向散射的α粒子数。', '', '01010604030200'),
('01010604030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP04', 'α粒子有库仑力的作用，一些α粒子的运动方向改变，也就是发生了α粒子的散射。', '', '01010604030200'),
('01010604030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP05', 'α粒子所占的比例，可以推知原子中电荷的分布情况。', '', '01010604030200'),
('01010604030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP06', '卢瑟福分析了实验数据后认为，事实应该是：占原子质量绝大部分的带正电的物质集中在很小的空间范围。', '', '01010604030200'),
('01010604030307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP07', '1911年，卢瑟福提出了自己的原子结构模型。', '', '01010604030200'),
('01010604030308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP08', '因为正电体很小，当α粒子进入原子区域后，大部分离正电体很远，受到的库仑斥力很小，运动方向几乎不改变。', '', '01010604030200'),
('01010604030309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP09', '只有极少数α粒子在穿过时距离正电体很近，因此受到很强的库仑斥力，发生大角度散射。', '', '01010604030200'),
('01010604030310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP10', '按照卢瑟福的理论，正电体的尺度是很小的，称为原子核。', '', '01010604030200'),
('01010604030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03', '原子核的电荷与尺度', '', '01010604030000'),
('01010604030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP01', '又由于原子是电中性的，可以推算出原子内含有的电子数。', '', '01010604030300'),
('01010604030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP02', '通常用核半径描述核的大小。', '', '01010604030300'),
('01010604040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04', '氢原子光谱和玻尔的原子模型', '', '01010604000000'),
('01010604040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01', '光谱', '', '01010604040000'),
('01010604040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP01', '有的光谱看起来不是一条条分立的谱线，而是连在一起的光带，叫作连续谱。', '', '01010604040100'),
('01010604040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP02', '气体中中性原子的发光光谱都是线状谱，说明原子只发出几种特定频率的光。', '', '01010604040100'),
('01010604040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02', '氢原子光谱的实验规律', '', '01010604040000'),
('01010604040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP01', '光谱的结果显示氢原子只能发出一系列特定波长的光。', '', '01010604040200'),
('01010604040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP02', '巴耳末公式以简洁的形式反映了氢原子的线状光谱的特征。', '', '01010604040200'),
('01010604040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03', '经典理论的困难', '', '01010604040000'),
('01010604040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP01', '卢瑟福的核式结构模型正确地指出了原子核的存在，很好地解释了α粒子散射实验，但跟经典电磁理论发生了矛盾。', '', '01010604040300'),
('01010604040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP02', '核外电子受到原子核的库仑引力的作用，却没有被吸引到原子核上，而是在以一定的速度绕核运动。', '', '01010604040300'),
('01010604040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP03', '随着绕核运动轨道半径的不断变化，电子运动的频率也要不断变化，因此原子辐射电磁波的频率也要不断变化。', '', '01010604040300'),
('01010604040400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04', '玻尔原子理论的基本假设', '', '01010604040000'),
('01010604040501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP01', '丹麦物理学家玻尔意识到了经典理论在解释原子结构方面的困难。', '', '01010604040400'),
('01010604040502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP02', '玻尔的原子结构假说包括以下两方面的内容。', '', '01010604040400'),
('01010604040503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP03', '玻尔认为，原子中的电子在库仑引力的作用下，绕原子核做圆周运动，服从经典力学的规律。', '', '01010604040400'),
('01010604040504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP04', '在玻尔理论中，电子的轨道半径只可能是某些分立的数值。', '', '01010604040400'),
('01010604040505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP05', '当电子在不同的轨道上运动时，原子处于不同的状态，具有不同的能量。', '', '01010604040400'),
('01010604040506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP06', '根据玻尔理论，电子只能在特定轨道上运动，因此，原子的能量也只能取一系列特定的值。', '', '01010604040400'),
('01010604040507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP07', '按照玻尔的观点，电子在一系列定态轨道上运动，不会发生电磁辐射。', '', '01010604040400'),
('01010604040500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05', '玻尔理论对氢光谱的解释', '', '01010604040000'),
('01010604040601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP01', '从玻尔的基本假设出发，运用经典电磁学和经典力学的理论，可以计算氢原子中电子的可能轨道半径及相应的能量。', '', '01010604040500'),
('01010604040602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP02', '处于基态的原子非常稳定。', '', '01010604040500'),
('01010604040603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP03', '处于激发态的原子是不稳定的，会自发地向能量较低的能级跃迁，放出光子，最终回到基态。', '', '01010604040500'),
('01010604040604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP04', '由于原子的能级是分立的，所以放出的光子的能量也是分立的。', '', '01010604040500'),
('01010604040605', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP05', '由于不同的原子具有不同的结构，能级各不相同，因此辐射（或吸收）的光子频率也不相同。', '', '01010604040500'),
('01010604040606', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP06', '各种气体原子的能级不同，跃迁时发射光子的能量各异。', '', '01010604040500'),
('01010604040607', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP07', '街道上的霓虹灯、试电笔中的氖管，都是由灯管内的气体原子从高能级向低能级跃迁而发光的。', '', '01010604040500'),
('01010604040608', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP08', '食盐被灼烧时发的光，也主要是由食盐蒸气中钠原子的能级跃迁而造成的。', '', '01010604040500'),
('01010604040600', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06', '玻尔理论的局限性', '', '01010604040000'),
('01010604040701', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP01', '玻尔的原子理论第一次将量子观念引入原子领域，提出了定态和跃迁的概念。', '', '01010604040600'),
('01010604040702', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP02', '玻尔理论成功地解释了氢原子光谱的实验规律。', '', '01010604040600'),
('01010604040703', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP03', '①玻尔理论的不足之处在于保留了经典粒子的观念，仍然把电子的运动看作经典力学描述下的轨道运动。', '', '01010604040600'),
('01010604040704', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP04', '实际上，根据量子力学，原子中电子的坐标没有确定的值。', '', '01010604040600'),
('01010604040705', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP05', '当原子处于不同的状态时，电子在各处出现的概率是不一样的。', '', '01010604040600'),
('01010604050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05', '粒子的波动性和量子力学的建立', '', '01010604000000'),
('01010604050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01', '粒子的波动性', '', '01010604050000'),
('01010604050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01.OP01', '1924年，法国物理学家德布罗意在对光的波粒二象性、玻尔氢原子理论以及相对论的深入研究的基础上，把波粒二象性推广到实物粒子，如电子、质子等。', '', '01010604050100'),
('01010604050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02', '物质波的实验验证', '', '01010604050000'),
('01010604050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP01', '德布罗意关于实物粒子具有波动性的假说在当时来看是难以理解的。', '', '01010604050200'),
('01010604050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP02', '1929年，德布罗意因提出物质波的假说获得了诺贝尔物理学奖。', '', '01010604050200'),
('01010604050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP03', '除了电子以外，后来还陆续证实了中子、质子以及原子、分子的波动性。', '', '01010604050200'),
('01010604050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP04', '德布罗意提出物质波的观念被实验证实，表明电子、质子、原子等粒子不但具有粒子的性质，而且具有波动的性质。', '', '01010604050200'),
('01010604050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03', '量子力学的建立', '', '01010604050000'),
('01010604050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP01', '19、20世纪之交，人们在黑体辐射、光电效应、氢原子光谱等许多类问题中，都发现了经典物理学无法解释的现象。', '', '01010604050300'),
('01010604050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP02', '普朗克黑体辐射理论、爱因斯坦光电效应理论、康普顿散射理论、玻尔氢原子理论以及德布罗意物质波假说等一系列理论在解释实验方面都取得了成功。', '', '01010604050300'),
('01010604050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP03', '1925年，德国物理学家海森堡和玻恩等人对玻尔的氢原子理论进行了推广和改造，使之可以适用于更普遍的情况。', '', '01010604050300'),
('01010604050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04', '量子力学的应用', '', '01010604050000'),
('01010604050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP01', '量子力学被应用到众多具体物理系统中，得到了与实验符合得很好的结果，获得了极大的成功。', '', '01010604050400'),
('01010604050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP02', '借助量子力学，人们深入认识了微观世界的组成、结构和属性。', '', '01010604050400'),
('01010604050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP03', '量子力学推动了核物理和粒子物理的发展。', '', '01010604050400'),
('01010604050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP04', '人们认识了原子、原子核、基本粒子等各个微观层次的物质结构。', '', '01010604050400'),
('01010604050505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP05', '而粒子物理学的发展又促进了天文学和宇宙学的研究。', '', '01010604050400'),
('01010604050506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP06', '从整个宇宙到微小的粒子，人类探索自然的视野前所未有的宽广。', '', '01010604050400'),
('01010604050507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP07', '人们惊讶地发现，世界具有奇妙的结构，最微观层次和最宏观层次的规律，竟有着紧密的联系。', '', '01010604050400'),
('01010604050508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP08', '核物理的发展，还让人们成功地认识并利用了原子核反应堆所释放的能量——核能。', '', '01010604050400'),
('01010604050509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP09', '”量子力学推动了原子、分子物理和光学的发展。', '', '01010604050400'),
('01010604050510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP10', '人们认识了原子的结构，以及原子、分子和电磁场相互作用的方式。', '', '01010604050400'),
('01010604050511', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP11', '在此基础上，发展了各式各样的对原子和电磁场进行精确操控和测量的技术，如激光、核磁共振、原子钟，等等。', '', '01010604050400'),
('01010604050512', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP12', '以利用振荡的磁场测量材料中原子的性质，因此，被广泛地用于化学、生物研究和医学诊断。', '', '01010604050400'),
('01010604050513', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP13', '原子钟利用原子为电磁波校准频率，从而实现了对时间的高精度测量。', '', '01010604050400'),
('01010604050514', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP14', '在日常生活和国家安全中发挥巨大作用的卫星定位技术，其核心部件就是原子钟。', '', '01010604050400'),
('01010604050515', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP15', '量子力学推动了固体物理的发展。', '', '01010604050400'),
('01010604050516', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP16', '人们了解了固体中电子运行的规律，并弄清了为什么固体有导体、绝缘体和半导体之分。', '', '01010604050400'),
('01010604050517', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP17', '量子力学的应用还有很多。', '', '01010604050400'),
('01010604050518', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP18', '毫不夸张地说，在过去的近一百年中，量子力学极大地推动了人类的进步。', '', '01010604050400');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010605000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05', '原子核', '', NULL),
('01010605010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01', '原子核的组成', '', '01010605000000'),
('01010605010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01', '天然放射现象', '', '01010605010000'),
('01010605010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP01', '受到贝克勒尔的发现的鼓舞，波兰裔法国物理学家玛丽·居里和她的丈夫皮埃尔·居里对铀和含铀的各种矿石进行了深入研究。', '', '01010605010100'),
('01010605010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP02', '放射性元素自发地发出射线的现象，叫作天然放射现象。', '', '01010605010100'),
('01010605010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02', '射线的本质', '', '01010605010000'),
('01010605010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP01', '把放射源铀、钋或镭放入用铅做成的容器中，射线只能从容器的小孔射出，成为细细的一束。', '', '01010605010200'),
('01010605010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP02', '物理学家经过多方面的研究后确认α射线、β射线和γ射线具有以下特征。', '', '01010605010200'),
('01010605010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP03', 'α粒子带正电，电荷量是电子的2倍，质量是氢原子的4倍，其组成与氦原子核相同。', '', '01010605010200'),
('01010605010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP04', '由于α粒子带电，质量又比较大，通过气体时很容易把气体分子中的电子剥离，使气体电离。', '', '01010605010200'),
('01010605010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP05', '是电子流，速度可以接近光速。', '', '01010605010200'),
('01010605010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP06', 'β射线的电离作用较弱，穿透能力较强，很容易穿透黑纸，也能穿透几毫米厚的铝板。', '', '01010605010200'),
('01010605010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP07', 'γ射线的电离作用更弱，穿透能力更强，甚至能穿透几厘米厚的铅板和几十厘米厚的混凝土。', '', '01010605010200'),
('01010605010308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP08', '放射性的强度也不受温度、外界压强的影响。', '', '01010605010200'),
('01010605010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03', '原子核的组成', '', '01010605010000'),
('01010605010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP01', '绝大多数原子核的质量与电荷量之比都大于质子的相应比值。', '', '01010605010300'),
('01010605010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP02', '质子带正电荷，电荷量与一个电子的电荷量相等。', '', '01010605010300'),
('01010605010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP03', '由于中子不带电，原子核所带的电荷等于核内质子电荷的总和。', '', '01010605010300'),
('01010605010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP04', '元素的性质与原子核外的电子有密切的关系。', '', '01010605010300'),
('01010605020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02', '放射性元素的衰变', '', '01010605000000'),
('01010605020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01', '原子核的衰变', '', '01010605020000'),
('01010605020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP01', '铀238放出一个α粒子后，质量数减少4，电荷数减少2，成为新核。', '', '01010605020100'),
('01010605020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP02', '研究还表明，在一定条件下，原子核中的两个中子和两个质子会结合起来形成α粒子，并被释放出来。', '', '01010605020100'),
('01010605020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP03', '原子核的能量也跟原子的能量一样，其变化是不连续的，也只能取一系列不连续的数值，因此，也存在着能级，同样是能级越低越稳定。', '', '01010605020100'),
('01010605020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP04', '当放射性物质连续衰变时，原子核中有的发生α衰变，有的发生β衰变，同时伴随着γ射线辐射。', '', '01010605020100'),
('01010605020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02', '半衰期', '', '01010605020000'),
('01010605020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP01', '放射性同位素衰变的快慢有一定的规律。', '', '01010605020200'),
('01010605020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP02', '不同的放射性元素，半衰期不同，甚至差别非常大。', '', '01010605020200'),
('01010605020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP03', '放射性元素衰变的快慢是由核内部自身的因素决定的，跟原子所处的化学状态和外部条件没有关系。', '', '01010605020200'),
('01010605020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03', '核反应', '', '01010605020000'),
('01010605020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP01', '衰变是原子核的自发变化，科学家更希望人工控制原子核的变化。', '', '01010605020300'),
('01010605020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP02', '与衰变过程一样，在核反应中，质量数守恒、电荷数守恒。', '', '01010605020300'),
('01010605020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04', '放射性同位素及其应用', '', '01010605020000'),
('01010605020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP01', '天然放射性同位素不过40多种，而今天通过核反应生成的人工放射性同位素已达3000多种，每种元素都有了自己的放射性同位素。', '', '01010605020400'),
('01010605020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP02', '丰富的放射性同位素资源在国民经济和科学研究的各个领域中得到了广泛的应用。', '', '01010605020400'),
('01010605020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP03', '工业部门可以使用放射性同位素发出的射线来测厚度。', '', '01010605020400'),
('01010605020504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP04', '像癌细胞那样，不断迅速繁殖的、无法控制的细胞组织，在射线照射下破坏得比健康细胞快。', '', '01010605020400'),
('01010605020505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP05', '利用γ射线照射种子，会使种子的遗传基因发生变异，经过筛选，可以培育出新品种。', '', '01010605020400'),
('01010605020506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP06', '棉花在开花、结桃的时候需要较多的磷肥，把磷肥喷在棉花叶子上，磷肥也能被吸收。', '', '01010605020400'),
('01010605020507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP07', '如果用磷的放射性同位素制成肥料喷在棉花的叶面上，然后每隔一定时间用探测器测量棉株各部位的放射性强度，上面的问题就解决了。', '', '01010605020400'),
('01010605020508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP08', '人体甲状腺的工作需要碘，碘被吸收后聚集在甲状腺内。', '', '01010605020400'),
('01010605020509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP09', '定时用探测器测量甲状腺及邻近组织的放射强度，有助于诊断甲状腺的疾病。', '', '01010605020400'),
('01010605020510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP10', '近年来，有关生物大分子的结构及其功能的研究，几乎都要借助于示踪原子。', '', '01010605020400'),
('01010605020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05', '辐射与安全', '', '01010605020000'),
('01010605020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05.OP01', '人类一直生活在放射性的环境中。', '', '01010605020500'),
('01010605030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03', '核力与结合能', '', '01010605000000'),
('01010605030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01', '核力与四种基本相互作用', '', '01010605030000'),
('01010605030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP01', '现代物理学认为，自然界存在着四种基本相互作用，即引力相互作用、电磁相互作用、强相互作用和弱相互作用。', '', '01010605030100'),
('01010605030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP02', '17世纪下半叶，人们发现了万有引力。', '', '01010605030100'),
('01010605030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP03', '引力是自然界的一种基本相互作用，地面物体所受的重力只是引力在地球表面附近的一种表现。', '', '01010605030100'),
('01010605030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP04', '20世纪，物理学家发现原子核是由若干带正电荷的质子和不带电的中子组成的。', '', '01010605030100'),
('01010605030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP05', '19世纪末20世纪初，物理学家发现了放射现象。', '', '01010605030100'),
('01010605030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP06', '①弱相互作用是引起原子核β衰变的原因，即引起中子—质子转变的原因。', '', '01010605030100'),
('01010605030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02', '结合能', '', '01010605030000'),
('01010605030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP01', '相距很远的两个物体，由于万有引力而相互接近，运动速度越来越大，引力势能转化为动能。', '', '01010605030200'),
('01010605030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP02', '最后撞在一起，一部分动能变成热并散失掉了。', '', '01010605030200'),
('01010605030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP03', '比结合能越大，原子核中核子结合得越牢固，原子核越稳定。', '', '01010605030200'),
('01010605030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03', '质量亏损', '', '01010605030000'),
('01010605030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP01', '原子核的结合能很难直接测量。', '', '01010605030300'),
('01010605030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP02', '质量亏损表明，的确存在着原子核的结合能。', '', '01010605030300'),
('01010605040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04', '核裂变与核聚变', '', '01010605000000'),
('01010605040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01', '核裂变的发现', '', '01010605040000'),
('01010605040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP01', '以铀235为例，核裂变时产生两或三个中子。', '', '01010605040100'),
('01010605040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP02', '原子核的体积非常小，原子内部的空隙很大，如果铀块不够大，中子在铀块中通过时，很有可能碰不到铀核而跑到铀块外面去，链式反应不能继续。', '', '01010605040100'),
('01010605040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP03', '只有当铀块足够大时，核裂变产生的中子才有足够大的概率打中某个铀核，使链式反应进行下去。', '', '01010605040100'),
('01010605040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP04', '铀核裂变时如果生成物不同，释放的能量也有差异。', '', '01010605040100'),
('01010605040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02', '反应堆与核电站', '', '01010605040000'),
('01010605040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP01', '原子核的链式反应也可以在人工控制下进行。', '', '01010605040200'),
('01010605040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP02', '其实，在第一个原子弹制成以前，科学家们已经实现了核能的可控释放。', '', '01010605040200'),
('01010605040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP03', '1942年，美籍意大利物理学家费米就主持建立了世界上第一个称为“核反应堆”的装置，首次通过可控制的链式反应实现了核能的释放。', '', '01010605040200'),
('01010605040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP04', '常用的慢化剂有石墨、重水和普通水（也叫轻水）。', '', '01010605040200'),
('01010605040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP05', '为了调节中子数目以控制反应速度，还需要在铀棒之间插进一些镉棒。', '', '01010605040200'),
('01010605040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP06', '核燃料发生核裂变释放的能量使反应区温度升高。', '', '01010605040200'),
('01010605040307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP07', '水或液态的金属钠等流体在反应堆内外循环流动，把反应堆内的热量传输出去，用于发电，同时也使反应堆冷却。', '', '01010605040200'),
('01010605040308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP08', '在热交换器内，第二回路中的水被加热生成高温高压蒸汽，驱动汽轮机运转。', '', '01010605040200'),
('01010605040309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP09', '建造核电站时需要特别注意防止放射线和放射性物质的泄漏，以避免射线对人体的伤害和放射性物质对水源、空气和工作场所造成放射性污染。', '', '01010605040200'),
('01010605040310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP10', '核反应堆中的核废料具有很强的放射性，需要装入特制的容器，深埋地下。', '', '01010605040200'),
('01010605040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03', '核聚变', '', '01010605040000'),
('01010605040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP01', '当物质的温度达到几百万开尔文时，剧烈的热运动使得一部分原子核具有足够的动能，可以克服库仑斥力，碰撞时十分接近，发生核聚变。', '', '01010605040300'),
('01010605040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP02', '太阳的主要成分是氢。', '', '01010605040300'),
('01010605040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP03', '现在地球上消耗的能量，追根溯源，绝大部分还是来自太阳，即太阳内部核聚变时释放的核能。', '', '01010605040300'),
('01010605040404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP04', '太阳在“核燃烧”的过程中“体重”不断减轻。', '', '01010605040300'),
('01010605040405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP05', '太阳的寿命已经有50亿年了。', '', '01010605040300'),
('01010605040406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP06', '目前，人工产生的热核反应主要用在核武器上，那就是氢弹。', '', '01010605040300'),
('01010605040407', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP07', '与核裂变相比，核聚变有很多优点。', '', '01010605040300'),
('01010605040408', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP08', '利用核聚变物质的惯性进行约束。', '', '01010605040300'),
('01010605040409', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP09', '总的来说，实现受控核聚变还有一段很长的路要走。', '', '01010605040300'),
('01010605050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05', '“基本”粒子', '', '01010605000000'),
('01010605050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01', '发现新粒子', '', '01010605050000'),
('01010605050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP01', '从20世纪30年代以来，人们在对宇宙线的研究中陆续发现了一些新的粒子。', '', '01010605050100'),
('01010605050202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP02', '1932年发明的粒子加速器，能使带电粒子加速到很高的能量。', '', '01010605050100'),
('01010605050203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP03', '利用加速器和对撞机，人们发现了更多的粒子。', '', '01010605050100'),
('01010605050204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP04', '粒子物理的研究不再单纯依赖宇宙线。', '', '01010605050100'),
('01010605050205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP05', '高能量的对撞机往往极为庞大和复杂。', '', '01010605050100'),
('01010605050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02', '粒子的分类', '', '01010605050000'),
('01010605050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP01', '现在已经发现的粒子达400多种。', '', '01010605050200'),
('01010605050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP02', '强子是参与强相互作用的粒子。', '', '01010605050200'),
('01010605050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP03', '质子和中子都是强子。', '', '01010605050200'),
('01010605050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP04', '轻子不参与强相互作用。', '', '01010605050200'),
('01010605050305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP05', '最早发现的轻子是电子，后来发现的轻子有电子中微子、μ子、μ子中微子以及τ子和τ子中微子。', '', '01010605050200'),
('01010605050306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP06', '每种轻子都有对应的反粒子。', '', '01010605050200'),
('01010605050307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP07', '现代实验还没有发现轻子的内部结构。', '', '01010605050200'),
('01010605050308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP08', '光子传递电磁相互作用，中间玻色子传递弱相互作用，胶子传递强相互作用。', '', '01010605050200'),
('01010605050309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP09', '希格斯玻色子是希格斯场的量子激发。', '', '01010605050200'),
('01010605050310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP10', '基本粒子因与希格斯场耦合而获得质量。', '', '01010605050200'),
('01010605050311', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP11', '2012年，欧洲核子研究中心利用大型强子对撞机发现了希格斯玻色子。', '', '01010605050200'),
('01010605050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03', '研究样例', '', '01010605050000'),
('01010605050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP01', '①燃气灶烧水时的能量转化和转移的情况如下。', '', '01010605050300'),
('01010605050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP02', '记录数据时室温为26℃，基本上保证了实验环境温度的恒定。', '', '01010605050300'),
('01010605050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP03', '为减小水在高温时的放热损失，应缩短水在高温时的加热时间。', '', '01010605050300'),
('01010605050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP04', '用实验检验研究结论的正确性。', '', '01010605050300'),
('01010605050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04', '参考选题', '', '01010605050000'),
('01010605050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP01', '使用热水器洗澡时，常有两种调温习惯：一种是把加热后的水温直接调节至洗澡的合适温度，如38℃左右；另一种是把热水器的加热温度调到更高，如50℃左右，再通过淋浴水龙头加兑冷水至合适水温，后者可以获得更大的适温水量。', '', '01010605050400'),
('01010605050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP02', '热水器工作前，先观察燃气表示数，测量加热前水的温度。', '', '01010605050400'),
('01010605050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP03', '观察燃气表示数，记录所用去的燃气体积。', '', '01010605050400'),
('01010605050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP04', '如果已知被加热水的体积，查找燃气的热值，还可以算出两种情况下热效率的大小。', '', '01010605050400');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010601000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01', '分子动理论', '', NULL),
('01010601010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01', '分子动理论的基本内容', '', '01010601000000'),
('01010601010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01', '物体是由大量分子组成的', '', '01010601010000'),
('01010601010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP01', '人们用肉眼无法直接看到分子，就是用高倍的光学显微镜也看不到。', '', '01010601010100'),
('01010601010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP02', '直至1982年，人们研制了能放大几亿倍的扫描隧道显微镜③，才观察到物质表面原子的排列。', '', '01010601010100'),
('01010601010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02', '分子热运动', '', '01010601010000'),
('01010601010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP01', '扩散现象是物质分子永不停息地做无规则运动的证据之一。', '', '01010601010200'),
('01010601010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP02', '扩散现象在科学技术中有很多应用。', '', '01010601010200'),
('01010601010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP03', '19世纪初，一些人观察到，悬浮在液体中的小颗粒总在不停地运动。', '', '01010601010200'),
('01010601010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP04', '从实验结果可以看出，小炭粒的运动是无规则的，温度越高，小炭粒的运动越明显。', '', '01010601010200'),
('01010601010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP05', '在扩散现象中，温度越高，扩散得越快。', '', '01010601010200'),
('01010601010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP06', '观察布朗运动，温度越高，悬浮微粒的运动就越明显。', '', '01010601010200'),
('01010601010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP07', '温度是分子热运动剧烈程度的标志。', '', '01010601010200'),
('01010601010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03', '分子间的作用力', '', '01010601010000'),
('01010601010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP01', '水和酒精混合后的总体积变小了。', '', '01010601010300'),
('01010601010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP02', '当用力拉伸物体时，物体内各部分之间要产生反抗拉伸的作用力，此时分子间的作用力表现为引力。', '', '01010601010300'),
('01010601010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP03', '当用力压缩物体时，物体内各部分之间会产生反抗压缩的作用力，此时分子间的作用力表现为斥力。', '', '01010601010300'),
('01010601010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP04', '原子内部有带正电的原子核和带负电的电子。', '', '01010601010300'),
('01010601010400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP04', '分子动理论', '', '01010601010000'),
('01010601020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02', '实验：用油膜法估测油酸分子的大小', '', '01010601000000'),
('01010601020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP01', '实验思路', '', '01010601020000'),
('01010601020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP02', '物理量的测量', '', '01010601020000'),
('01010601020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP03', '数据分析', '', '01010601020000'),
('01010601030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03', '分子运动速率分布规律', '', '01010601000000'),
('01010601030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01', '气体分子运动的特点', '', '01010601030000'),
('01010601030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP01', '热现象与大量分子热运动的统计规律有关。', '', '01010601030100'),
('01010601030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP02', '要研究气体的热现象，就要了解气体分子运动的特点。', '', '01010601030100'),
('01010601030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP03', '液体变为气体后，体积要增大上千倍，可见，气体分子间距离大约是分子直径的10倍。', '', '01010601030100'),
('01010601030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP04', '分子的大小相对分子间的空隙来说很小，所以，可以把气体分子视为质点。', '', '01010601030100'),
('01010601030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP05', '分子的运动杂乱无章，在某一时刻，向着任何一个方向运动的分子都有，而且向各个方向运动的分子数目几乎相等。', '', '01010601030100'),
('01010601030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02', '分子运动速率分布图像', '', '01010601030000'),
('01010601030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP01', '尽管分子做无规则运动，速率有大有小，但大量分子的速率却按一定的规律分布。', '', '01010601030200'),
('01010601030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP02', '100℃的氧气，速率大的分子比例较多，其分子的平均速率比0℃的大。', '', '01010601030200'),
('01010601030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03', '气体压强的微观解释', '', '01010601030000'),
('01010601030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP01', '器壁单位面积上受到的压力，就是气体的压强。', '', '01010601030300'),
('01010601030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP02', '从微观角度来看，气体分子的数量是巨大的。', '', '01010601030300'),
('01010601040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04', '分子动能和分子势能', '', '01010601000000'),
('01010601040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01', '分子动能', '', '01010601040000'),
('01010601040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP01', '物体中分子热运动的速率大小不一，所以各个分子的动能也有大有小，而且在不断改变。', '', '01010601040100'),
('01010601040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP02', '温度升高时，分子的热运动加剧，温度越高，分子热运动的平均动能越大。', '', '01010601040100'),
('01010601040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP03', '温度越低，分子热运动的平均动能越小。', '', '01010601040100'),
('01010601040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02', '分子势能', '', '01010601040000'),
('01010601040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP01', '分子间存在着相互作用力，可以证明分子间的作用力所做的功与路径无关，分子组成的系统具有分子势能。', '', '01010601040200'),
('01010601040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP02', '物体的体积变化时，分子间距离将发生变化，因而分子势能随之改变。', '', '01010601040200'),
('01010601040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03', '物体的内能', '', '01010601040000'),
('01010601040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP01', '任何物体都具有内能。', '', '01010601040300'),
('01010601040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP02', '分子热运动的平均动能与温度有关，分子势能与物体的体积有关。', '', '01010601040300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010602000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02', '气体、固体和液体', '', NULL),
('01010602010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01', '温度和温标', '', '01010602000000'),
('01010602010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01', '状态参量与平衡态', '', '01010602010000'),
('01010602010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01.OP01', '要定量地描述系统的状态往往很难，因为有时系统的参量会随时间变化。', '', '01010602010100'),
('01010602010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP02', '热平衡与温度', '', '01010602010000'),
('01010602010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03', '温度计与温标', '', '01010602010000'),
('01010602010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03.OP01', '现代科学中用得更多的是热力学温标。', '', '01010602010300'),
('01010602020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC02', '气体的等温变化', '', '01010602000000'),
('01010602030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03', '气体的等压变化和等容变化', '', '01010602000000'),
('01010602030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP01', '气体的等压变化', '', '01010602030000'),
('01010602030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02', '气体的等容变化', '', '01010602030000'),
('01010602030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02.OP01', '法国科学家查理在分析了实验事实后发现，当一定质量的气体体积一定时，各种气体的压强与温度之间都有线性关系。', '', '01010602030200'),
('01010602030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03', '理想气体', '', '01010602030000'),
('01010602030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP01', '当压强很大、温度很低时，由上述规律计算的结果与实际测量结果有很大的差别。', '', '01010602030300'),
('01010602030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP02', '实际气体的分子之间有相互作用力，但是作用力很小；分子也有大小，但气体分子之间的间距比分子直径大得多；气体分子与器壁碰撞几乎是弹性的，动能损失也很小。', '', '01010602030300'),
('01010602030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP03', '在温度不低于零下几十摄氏度、压强不超过大气压的几倍时，把实际气体当成理想气体来处理，误差很小。', '', '01010602030300'),
('01010602030400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04', '气体实验定律的微观解释', '', '01010602030000'),
('01010602030501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP01', '用分子动理论可以定性解释气体的实验定律。', '', '01010602030400'),
('01010602030502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP02', '一定质量的某种理想气体，温度保持不变时，分子的平均动能是一定的。', '', '01010602030400'),
('01010602030503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP03', '一定质量的某种理想气体，温度升高时，分子的平均动能增大；只有气体的体积同时增大，使分子的数密度减小，才能保持压强不变。', '', '01010602030400'),
('01010602030504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP04', '一定质量的某种理想气体，体积保持不变时，分子的数密度保持不变。', '', '01010602030400'),
('01010602040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04', '固体', '', '01010602000000'),
('01010602040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01', '晶体和非晶体', '', '01010602040000'),
('01010602040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP01', '固体可以分为晶体和非晶体两类。', '', '01010602040100'),
('01010602040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP02', '石英、云母①、明矾、食盐、硫酸铜、味精等是晶体。', '', '01010602040100'),
('01010602040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP03', '玻璃、蜂蜡、松香、沥青、橡胶等是非晶体。', '', '01010602040100'),
('01010602040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP04', '非晶体没有规则的外形。', '', '01010602040100'),
('01010602040205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP05', '除了形状是否规则外，晶体和非晶体在物理性质上也有所不同。', '', '01010602040100'),
('01010602040206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP06', '从实验结果可以看出，玻璃片上石蜡熔化区域的形状近似于圆形，表明玻璃沿各个方向的导热性能相同；云母片上石蜡熔化区域的形状呈椭圆形，表明云母沿不同方向的导热性能不同。', '', '01010602040100'),
('01010602040207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP07', '蔗糖受潮后粘在一起形成的糖块，看起来没有确定的几何形状。', '', '01010602040100'),
('01010602040208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP08', '粘在一起的糖块也是多晶体，单个的蔗糖晶体颗粒是单晶体。', '', '01010602040100'),
('01010602040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02', '晶体的微观结构', '', '01010602040000'),
('01010602040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP01', '在20世纪70年代，人们又用电子显微镜观察到了铀、钍原子的像。', '', '01010602040200'),
('01010602040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP02', '1982年，人们用扫描隧道显微镜观察到物质表面原子的排列。', '', '01010602040200'),
('01010602040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP03', '在各种晶体中，原子（或分子、离子）都是按照一定的规则排列的，具有空间上的周期性。', '', '01010602040200'),
('01010602040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP04', '石墨是层状结构，层与层之间距离较大，原子间的作用力比较弱，所以石墨质地松软，可以用来制作粉状润滑剂；金刚石中碳原子间的作用力很强，所以金刚石有很大的硬度，可以用来切割玻璃。', '', '01010602040200'),
('01010602040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP05', '同种物质也可能以晶体和非晶体两种不同的形态出现，也就是说，物质是晶体还是非晶体，并不是绝对的。', '', '01010602040200'),
('01010602040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP06', '有些非晶体在一定条件下也可以转化为晶体。', '', '01010602040200'),
('01010602050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05', '液体', '', '01010602000000'),
('01010602050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP01', '液体的表面张力', '', '01010602050000'),
('01010602050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02', '浸润和不浸润', '', '01010602050000'),
('01010602050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP01', '管的内径越小，上述现象就越明显。', '', '01010602050200'),
('01010602050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP02', '有些物体内部有许多细小的孔道，孔道起到了毛细管的作用。', '', '01010602050200'),
('01010602050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP03', '当液体和与之接触的固体的相互作用比液体分子之间的相互作用强时，液体能够浸润固体。', '', '01010602050200'),
('01010602050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP04', '如果把水银滴到干净的铅板上，水银就会附着在铅板上，很难擦去。', '', '01010602050200'),
('01010602050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03', '液晶', '', '01010602050000'),
('01010602050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP01', '液晶在现代生活中扮演着重要角色，广泛应用于手机屏幕、平板电视等显示设备中。', '', '01010602050300'),
('01010602050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP02', '液晶态既具有液体的流动性，又在一定程度上具有晶体分子的规则排列的性质。', '', '01010602050300'),
('01010602050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP03', '有些物质在特定的温度范围之内具有液晶态；另一些物质，在适当的溶剂中溶解时，在一定的浓度范围具有液晶态。', '', '01010602050300'),
('01010602050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP04', '分子取向排列的液晶具有光学各向异性，具体地说，沿分子长轴方向上的折射率不同于沿短轴的。', '', '01010602050300'),
('01010602050405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP05', '入射光的偏振方向与液晶分子长轴的方向成不同夹角时，液晶对光的折射率不同。', '', '01010602050300'),
('01010602050406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP06', '液晶的研究在生命科学中也有应用。', '', '01010602050300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010603000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03', '热力学定律', '', NULL),
('01010603010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01', '功、热和内能的改变', '', '01010603000000'),
('01010603010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01', '焦耳的实验', '', '01010603010000'),
('01010603010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP01', '在焦耳研究功与热等效性的许多实验中，有两个最具代表性。', '', '01010603010100'),
('01010603010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP02', '一个是让重物下落带动叶片搅拌容器中的水，引起水温上升；另一个是通过电流的热效应给水加热。', '', '01010603010100'),
('01010603010203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP03', '焦耳的多次实验测量表明，尽管各次悬挂重物的质量不同，下落的高度也不一样，但只要重力所做的功相同，容器内水温上升的数值都是相同的，即系统状态的变化是相同的。', '', '01010603010100'),
('01010603010204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP04', '多次实验结果表明，对于同一个系统，如果过程是绝热的，那么不管通过电阻丝的电流或大或小、通电时间或长或短，只要所做的电功相等，则系统温度上升的数值是相同的，即系统的状态就发生了同样的变化。', '', '01010603010100'),
('01010603010205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP05', '两个位置间移动时重力所做的功。', '', '01010603010100'),
('01010603010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02', '功与内能的改变', '', '01010603010000'),
('01010603010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP01', '当温度达到易燃物的燃点时，易燃物发生自燃。', '', '01010603010200'),
('01010603010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP02', '当橡胶塞跳出时，瓶内出现白雾。', '', '01010603010200'),
('01010603010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP03', '以瓶内气体为研究对象，橡胶塞跳出后，瓶内的气体迅速膨胀，系统对外做功，因此，气体的内能迅速减少，瓶内气体温度迅速下降，瓶内水蒸气液化，就会出现大量的雾状小水滴。', '', '01010603010200'),
('01010603010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03', '热与内能的改变', '', '01010603010000'),
('01010603010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03.OP01', '外界对系统做功可以改变系统的热力学状态，而单纯地对系统传热也能改变系统的热力学状态。', '', '01010603010300'),
('01010603020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02', '热力学第一定律', '', '01010603000000'),
('01010603020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01', '热力学第一定律', '', '01010603020000'),
('01010603020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01.OP01', '（1）求上述压缩过程中气体内能的变化量。', '', '01010603020100'),
('01010603020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP02', '热力学第一定律的应用', '', '01010603020000'),
('01010603030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03', '能量守恒定律', '', '01010603000000'),
('01010603030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01', '探索能量守恒的足迹', '', '01010603030000'),
('01010603030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP01', '能量的概念是人类在对物质运动规律进行长期探索中建立起来的。', '', '01010603030100'),
('01010603030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP02', '所有自然现象都涉及能量，人类的任何活动都离不开能量。', '', '01010603030100'),
('01010603030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP03', '能量具有不同的形式，有描述热运动的内能、描述机械运动的机械能、描述光辐射的光能，等等。', '', '01010603030100'),
('01010603030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP04', '不同形式的运动都可以用能量来描述。', '', '01010603030100'),
('01010603030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP05', '在认识自然的进程中，科学家慢慢知道了要用联系的观点去观察自然。', '', '01010603030100'),
('01010603030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP06', '由于能量既抽象又以各种不同的形式广泛存在，所以能量守恒思想的萌芽及产生并不是一蹴而就的。', '', '01010603030100'),
('01010603030207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP07', '从各种实验的验证，再到实验结果的被质疑，科学家们付出了大量的努力。', '', '01010603030100'),
('01010603030208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP08', '从18世纪末到19世纪中叶，不同领域的科学家从不同角度都提出过能量守恒的思想。', '', '01010603030100'),
('01010603030209', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP09', '人类对能量守恒的认识经历了一个由浅入深、由含糊到清晰的过程。', '', '01010603030100'),
('01010603030210', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP10', '俄国化学家盖斯的研究发现，任何一个化学反应，不论是一步完成，还是分几步完成，放出的总热量相同。', '', '01010603030100'),
('01010603030211', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP11', '焦耳的实验精确地测量了做功与传热之间的等价关系，从而为能量守恒定律奠定了牢固的实验基础，也为能量守恒的定量描述迈出了重要的一步。', '', '01010603030100'),
('01010603030212', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP12', '德国医生迈尔通过比对不同地区人血颜色的差异，认识到食物中化学能与内能的等效性，即生物体内能量的输入和输出是平衡的。', '', '01010603030100'),
('01010603030213', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP13', '半个多世纪人类对能量以及能量守恒研究的历史，预示着人们把各分立的领域连成一体的时刻已经到来，也就是到了建立能量转化与守恒定律的时候了。', '', '01010603030100'),
('01010603030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02', '能量守恒定律', '', '01010603030000'),
('01010603030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP01', '在热力学领域内，做功和传热可以改变系统的内能，即系统内能与系统外的能量会发生转化或转移，但能的总量不会改变。', '', '01010603030200'),
('01010603030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP02', '能量守恒是自然界的普遍规律。', '', '01010603030200'),
('01010603030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP03', '根据能量守恒定律，物理学发现和解释了很多科学现象。', '', '01010603030200'),
('01010603030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP04', '在20世纪30年代初，奥地利物理学家泡利根据能量守恒定律预言了中微子。', '', '01010603030200'),
('01010603030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP05', '能量守恒定律经受住了新的检验。', '', '01010603030200'),
('01010603030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP06', '能量守恒定律的发现是科学史上的重大事件。', '', '01010603030200'),
('01010603030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03', '永动机不可能制成', '', '01010603030000'),
('01010603030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP01', '“问题”栏目中的饮水小鸭不是永动机。', '', '01010603030300'),
('01010603030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP02', '小鸭的头和身由两个玻璃球构成，并通过细玻璃管相连。', '', '01010603030300'),
('01010603030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP03', '下球盛有一些乙醚，上球连同尖嘴被吸水毛毡包起来。', '', '01010603030300'),
('01010603030404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP04', '永动机的思想违背了能量守恒定律，所以是不可能制成的。', '', '01010603030300'),
('01010603030405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP05', '应该指出的是，制造永动机的千万次失败使人们的头脑冷静下来，开始在更深层次寻找失败的原因。', '', '01010603030300'),
('01010603030406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP06', '国科学院宣布“不再审查有关永动机的一切设计”。', '', '01010603030300'),
('01010603040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04', '热力学第二定律', '', '01010603000000'),
('01010603040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01', '热力学第二定律', '', '01010603040000'),
('01010603040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP01', '在电冰箱的实例中，热量的确从低温物体——冰箱内的食品，传到了高温物体——冰箱外的空气。', '', '01010603040100'),
('01010603040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP02', '从能量的角度看，热机的工作分为两个阶段：第一个阶段是燃烧燃料，把燃料中的化学能变成工作物质的内能；第二个阶段是工作物质对外做功，把自己的内能变成机械能。', '', '01010603040100'),
('01010603040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP03', '热力学第二定律的开尔文表述阐述了机械能与内能转化的方向性：机械能可以全部转化为内能，而内能无法全部用来做功以转化成机械能。', '', '01010603040100'),
('01010603040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP04', '可以证明，热力学第二定律的克劳修斯表述和开尔文表述是等价的。', '', '01010603040100'),
('01010603040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02', '能源是有限的', '', '01010603040000'),
('01010603040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP01', '自然界的一切变化，人类社会的所有活动，都伴随着能量的转移和转化，能量是一切物质运动的源泉，是一切生命活动的基础。', '', '01010603040200'),
('01010603040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP02', '能量在数量上虽然守恒，但其转移和转化却具有方向性。', '', '01010603040200'),
('01010603040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP03', '核能、生物能……最终都转化成内能，流散到周围的环境中。', '', '01010603040200'),
('01010603040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP04', '周围环境中的内能很难再利用，而机械能、光能、化学能、电能相对于周围环境中的内能来说，可利用的品质要高。', '', '01010603040200'),
('01010603040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP05', '能源的使用过程中虽然能的总量保持守恒，但能量的品质下降了。', '', '01010603040200'),
('01010603040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP06', '虽然能量总量不会减少，但能源会逐步减少，因此能源是有限的资源。', '', '01010603040200'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010604000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04', '原子结构和波粒二象性', '', NULL),
('01010604010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01', '普朗克黑体辐射理论', '', '01010604000000'),
('01010604010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01', '黑体与黑体辐射', '', '01010604010000'),
('01010604010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP01', '19世纪，由于冶金、星体测温等需求，人们对热辐射进行了大量的研究。', '', '01010604010100'),
('01010604010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP02', '研究表明，对于一般材料的物体，辐射电磁波的情况除了与温度有关，还与材料的种类及表面状况有关，而黑体辐射电磁波的强度按波长的分布只与黑体的温度有关。', '', '01010604010100'),
('01010604010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02', '黑体辐射的实验规律', '', '01010604010000'),
('01010604010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP01', '利用分光技术和热电偶等设备，可以测出黑体辐射电磁波的强度按波长分布的情况。', '', '01010604010200'),
('01010604010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP02', '从中可以看出，随着温度的升高，一方面，各种波长的辐射强度都有增加，另一方面，辐射强度的极大值向波长较短的方向移动。', '', '01010604010200'),
('01010604010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP03', '德国物理学家维恩在1896年、英国物理学家瑞利在1900年，分别提出了辐射强度按波长分布的理论公式。', '', '01010604010200'),
('01010604010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP04', '维恩公式在短波区与实验非常接近，在长波区则与实验偏离较大；瑞利公式在长波区与实验基本一致，但在短波区与实验严重不符。', '', '01010604010200'),
('01010604010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP05', '为了得出同全部实验相符的黑体辐射公式，德国物理学家普朗克做了多种尝试。', '', '01010604010200'),
('01010604010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03', '能量子', '', '01010604010000'),
('01010604010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP01', '弹簧振子的能量不一定是某个最小值的整数倍。', '', '01010604010300'),
('01010604010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP02', '而普朗克的假设则认为微观粒子的能量是量子化的，或者说微观粒子的能量是分立的。', '', '01010604010300'),
('01010604010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP03', '普朗克本人因此获得了1918年的诺贝尔物理学奖。', '', '01010604010300'),
('01010604020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02', '光电效应', '', '01010604000000'),
('01010604020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01', '光电效应的实验规律', '', '01010604020000'),
('01010604020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP01', '1887年，赫兹在研究电磁波的实验中偶尔发现，接收电路的间隙如果受到光照，就更容易产生电火花。', '', '01010604020100'),
('01010604020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP02', '科学家通过实验发现了以下规律。', '', '01010604020100'),
('01010604020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP03', '实验表明，不同金属的截止频率不同。', '', '01010604020100'),
('01010604020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP04', '在光照条件不变的情况下，随着所加电压的增大，光电流趋于一个饱和值。', '', '01010604020100'),
('01010604020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP05', '实验表明，在光的频率不变的情况下，入射光越强，饱和电流越大。', '', '01010604020100'),
('01010604020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02', '光电效应经典解释中的疑难', '', '01010604020000'),
('01010604020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP01', '当光照射金属表面时，电子会吸收光的能量。', '', '01010604020200'),
('01010604020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP02', '光越强，逸出的电子数越多，光电流也就越大。', '', '01010604020200'),
('01010604020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP03', '●不管光的频率如何，只要光足够强，电子都可以获得足够能量从而逸出表面，不应存在截止频率。', '', '01010604020200'),
('01010604020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03', '爱因斯坦的光电效应理论', '', '01010604020000'),
('01010604020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP01', '对于光电效应的解释，爱因斯坦是在普朗克量子假说的基础上作出的。', '', '01010604020300'),
('01010604020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP02', '从下面的讨论可以看出，爱因斯坦光电效应方程可以很好地解释光电效应实验中的各种现象。', '', '01010604020300'),
('01010604020403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP03', '●电子一次性吸收光子的全部能量，不需要积累能量的时间，光电流自然几乎是瞬时产生的。', '', '01010604020300'),
('01010604020404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP04', '爱因斯坦由于提出了光电效应理论而获得1921年的诺贝尔物理学奖。', '', '01010604020300'),
('01010604020405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP05', '金属表面的光子数较多，照射金属时产生的光电子较多，因而饱和电流较大。', '', '01010604020300'),
('01010604020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04', '康普顿效应和光子的动量', '', '01010604020000'),
('01010604020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP01', '光可以与介质中的物质微粒发生散射，改变传播方向。', '', '01010604020400'),
('01010604020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP02', '康普顿效应让人们对光子有了更深入的认识。', '', '01010604020400'),
('01010604020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP03', '康普顿因此获得了1927年的诺贝尔物理学奖。', '', '01010604020400'),
('01010604020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05', '光的波粒二象性', '', '01010604020000'),
('01010604020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP01', '众所周知，在麦克斯韦的电磁理论建立之后，人们认识到光是一种电磁波，从而光的波动说被普遍接受，人们不再认为光是由粒子组成的。', '', '01010604020500'),
('01010604020602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP02', '而爱因斯坦的光电效应理论和康普顿效应理论表明，光在某些方面确实会表现得像是由一些粒子（即一个个有确定能量和动量的“光子”）组成的。', '', '01010604020500'),
('01010604020603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP03', '人们意识到，光既具有波动性，又具有粒子性。', '', '01010604020500'),
('01010604020604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP04', '从牛顿的微粒说、惠更斯和托马斯·杨的波动说，到麦克斯韦的电磁理论，再到爱因斯坦的光子理论乃至量子电动力学，人类对光的认识构成了一部科学史诗。', '', '01010604020500'),
('01010604030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03', '原子的核式结构模型', '', '01010604000000'),
('01010604030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01', '电子的发现', '', '01010604030000'),
('01010604030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP01', '为了找到有利于自己的证据，双方都做了许多实验。', '', '01010604030100'),
('01010604030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP02', '由实验测得的阴极射线粒子的比荷是氢离子①（也就是质子）比荷的近两千倍。', '', '01010604030100'),
('01010604030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP03', '金属受热还是放射性物质的自发辐射，都能发射同样的带电粒子——电子。', '', '01010604030100'),
('01010604030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02', '原子的核式结构模型', '', '01010604030000'),
('01010604030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP01', 'α粒子是从放射性物质（如铀和镭）中发射出来的快速运动的粒子，质量为氢原子质量的4倍、电子质量的7300倍。', '', '01010604030200'),
('01010604030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP02', '被散射的α粒子打在荧光屏上会有微弱的闪光产生。', '', '01010604030200'),
('01010604030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP03', '通过显微镜观察闪光就可以记录在某一时间内向某一方向散射的α粒子数。', '', '01010604030200'),
('01010604030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP04', 'α粒子有库仑力的作用，一些α粒子的运动方向改变，也就是发生了α粒子的散射。', '', '01010604030200'),
('01010604030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP05', 'α粒子所占的比例，可以推知原子中电荷的分布情况。', '', '01010604030200'),
('01010604030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP06', '卢瑟福分析了实验数据后认为，事实应该是：占原子质量绝大部分的带正电的物质集中在很小的空间范围。', '', '01010604030200'),
('01010604030307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP07', '1911年，卢瑟福提出了自己的原子结构模型。', '', '01010604030200'),
('01010604030308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP08', '因为正电体很小，当α粒子进入原子区域后，大部分离正电体很远，受到的库仑斥力很小，运动方向几乎不改变。', '', '01010604030200'),
('01010604030309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP09', '只有极少数α粒子在穿过时距离正电体很近，因此受到很强的库仑斥力，发生大角度散射。', '', '01010604030200'),
('01010604030310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP10', '按照卢瑟福的理论，正电体的尺度是很小的，称为原子核。', '', '01010604030200'),
('01010604030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03', '原子核的电荷与尺度', '', '01010604030000'),
('01010604030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP01', '又由于原子是电中性的，可以推算出原子内含有的电子数。', '', '01010604030300'),
('01010604030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP02', '通常用核半径描述核的大小。', '', '01010604030300'),
('01010604040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04', '氢原子光谱和玻尔的原子模型', '', '01010604000000'),
('01010604040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01', '光谱', '', '01010604040000'),
('01010604040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP01', '有的光谱看起来不是一条条分立的谱线，而是连在一起的光带，叫作连续谱。', '', '01010604040100'),
('01010604040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP02', '气体中中性原子的发光光谱都是线状谱，说明原子只发出几种特定频率的光。', '', '01010604040100'),
('01010604040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02', '氢原子光谱的实验规律', '', '01010604040000'),
('01010604040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP01', '光谱的结果显示氢原子只能发出一系列特定波长的光。', '', '01010604040200'),
('01010604040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP02', '巴耳末公式以简洁的形式反映了氢原子的线状光谱的特征。', '', '01010604040200'),
('01010604040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03', '经典理论的困难', '', '01010604040000'),
('01010604040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP01', '卢瑟福的核式结构模型正确地指出了原子核的存在，很好地解释了α粒子散射实验，但跟经典电磁理论发生了矛盾。', '', '01010604040300'),
('01010604040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP02', '核外电子受到原子核的库仑引力的作用，却没有被吸引到原子核上，而是在以一定的速度绕核运动。', '', '01010604040300'),
('01010604040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP03', '随着绕核运动轨道半径的不断变化，电子运动的频率也要不断变化，因此原子辐射电磁波的频率也要不断变化。', '', '01010604040300'),
('01010604040400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04', '玻尔原子理论的基本假设', '', '01010604040000'),
('01010604040501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP01', '丹麦物理学家玻尔意识到了经典理论在解释原子结构方面的困难。', '', '01010604040400'),
('01010604040502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP02', '玻尔的原子结构假说包括以下两方面的内容。', '', '01010604040400'),
('01010604040503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP03', '玻尔认为，原子中的电子在库仑引力的作用下，绕原子核做圆周运动，服从经典力学的规律。', '', '01010604040400'),
('01010604040504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP04', '在玻尔理论中，电子的轨道半径只可能是某些分立的数值。', '', '01010604040400'),
('01010604040505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP05', '当电子在不同的轨道上运动时，原子处于不同的状态，具有不同的能量。', '', '01010604040400'),
('01010604040506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP06', '根据玻尔理论，电子只能在特定轨道上运动，因此，原子的能量也只能取一系列特定的值。', '', '01010604040400'),
('01010604040507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP07', '按照玻尔的观点，电子在一系列定态轨道上运动，不会发生电磁辐射。', '', '01010604040400'),
('01010604040500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05', '玻尔理论对氢光谱的解释', '', '01010604040000'),
('01010604040601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP01', '从玻尔的基本假设出发，运用经典电磁学和经典力学的理论，可以计算氢原子中电子的可能轨道半径及相应的能量。', '', '01010604040500'),
('01010604040602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP02', '处于基态的原子非常稳定。', '', '01010604040500'),
('01010604040603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP03', '处于激发态的原子是不稳定的，会自发地向能量较低的能级跃迁，放出光子，最终回到基态。', '', '01010604040500'),
('01010604040604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP04', '由于原子的能级是分立的，所以放出的光子的能量也是分立的。', '', '01010604040500'),
('01010604040605', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP05', '由于不同的原子具有不同的结构，能级各不相同，因此辐射（或吸收）的光子频率也不相同。', '', '01010604040500'),
('01010604040606', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP06', '各种气体原子的能级不同，跃迁时发射光子的能量各异。', '', '01010604040500'),
('01010604040607', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP07', '街道上的霓虹灯、试电笔中的氖管，都是由灯管内的气体原子从高能级向低能级跃迁而发光的。', '', '01010604040500'),
('01010604040608', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP08', '食盐被灼烧时发的光，也主要是由食盐蒸气中钠原子的能级跃迁而造成的。', '', '01010604040500'),
('01010604040600', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06', '玻尔理论的局限性', '', '01010604040000'),
('01010604040701', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP01', '玻尔的原子理论第一次将量子观念引入原子领域，提出了定态和跃迁的概念。', '', '01010604040600'),
('01010604040702', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP02', '玻尔理论成功地解释了氢原子光谱的实验规律。', '', '01010604040600'),
('01010604040703', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP03', '①玻尔理论的不足之处在于保留了经典粒子的观念，仍然把电子的运动看作经典力学描述下的轨道运动。', '', '01010604040600'),
('01010604040704', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP04', '实际上，根据量子力学，原子中电子的坐标没有确定的值。', '', '01010604040600'),
('01010604040705', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP05', '当原子处于不同的状态时，电子在各处出现的概率是不一样的。', '', '01010604040600'),
('01010604050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05', '粒子的波动性和量子力学的建立', '', '01010604000000'),
('01010604050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01', '粒子的波动性', '', '01010604050000'),
('01010604050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01.OP01', '1924年，法国物理学家德布罗意在对光的波粒二象性、玻尔氢原子理论以及相对论的深入研究的基础上，把波粒二象性推广到实物粒子，如电子、质子等。', '', '01010604050100'),
('01010604050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02', '物质波的实验验证', '', '01010604050000'),
('01010604050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP01', '德布罗意关于实物粒子具有波动性的假说在当时来看是难以理解的。', '', '01010604050200'),
('01010604050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP02', '1929年，德布罗意因提出物质波的假说获得了诺贝尔物理学奖。', '', '01010604050200'),
('01010604050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP03', '除了电子以外，后来还陆续证实了中子、质子以及原子、分子的波动性。', '', '01010604050200'),
('01010604050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP04', '德布罗意提出物质波的观念被实验证实，表明电子、质子、原子等粒子不但具有粒子的性质，而且具有波动的性质。', '', '01010604050200'),
('01010604050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03', '量子力学的建立', '', '01010604050000'),
('01010604050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP01', '19、20世纪之交，人们在黑体辐射、光电效应、氢原子光谱等许多类问题中，都发现了经典物理学无法解释的现象。', '', '01010604050300'),
('01010604050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP02', '普朗克黑体辐射理论、爱因斯坦光电效应理论、康普顿散射理论、玻尔氢原子理论以及德布罗意物质波假说等一系列理论在解释实验方面都取得了成功。', '', '01010604050300'),
('01010604050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP03', '1925年，德国物理学家海森堡和玻恩等人对玻尔的氢原子理论进行了推广和改造，使之可以适用于更普遍的情况。', '', '01010604050300'),
('01010604050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04', '量子力学的应用', '', '01010604050000'),
('01010604050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP01', '量子力学被应用到众多具体物理系统中，得到了与实验符合得很好的结果，获得了极大的成功。', '', '01010604050400'),
('01010604050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP02', '借助量子力学，人们深入认识了微观世界的组成、结构和属性。', '', '01010604050400'),
('01010604050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP03', '量子力学推动了核物理和粒子物理的发展。', '', '01010604050400'),
('01010604050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP04', '人们认识了原子、原子核、基本粒子等各个微观层次的物质结构。', '', '01010604050400'),
('01010604050505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP05', '而粒子物理学的发展又促进了天文学和宇宙学的研究。', '', '01010604050400'),
('01010604050506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP06', '从整个宇宙到微小的粒子，人类探索自然的视野前所未有的宽广。', '', '01010604050400'),
('01010604050507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP07', '人们惊讶地发现，世界具有奇妙的结构，最微观层次和最宏观层次的规律，竟有着紧密的联系。', '', '01010604050400'),
('01010604050508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP08', '核物理的发展，还让人们成功地认识并利用了原子核反应堆所释放的能量——核能。', '', '01010604050400'),
('01010604050509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP09', '”量子力学推动了原子、分子物理和光学的发展。', '', '01010604050400'),
('01010604050510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP10', '人们认识了原子的结构，以及原子、分子和电磁场相互作用的方式。', '', '01010604050400'),
('01010604050511', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP11', '在此基础上，发展了各式各样的对原子和电磁场进行精确操控和测量的技术，如激光、核磁共振、原子钟，等等。', '', '01010604050400'),
('01010604050512', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP12', '以利用振荡的磁场测量材料中原子的性质，因此，被广泛地用于化学、生物研究和医学诊断。', '', '01010604050400'),
('01010604050513', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP13', '原子钟利用原子为电磁波校准频率，从而实现了对时间的高精度测量。', '', '01010604050400'),
('01010604050514', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP14', '在日常生活和国家安全中发挥巨大作用的卫星定位技术，其核心部件就是原子钟。', '', '01010604050400'),
('01010604050515', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP15', '量子力学推动了固体物理的发展。', '', '01010604050400'),
('01010604050516', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP16', '人们了解了固体中电子运行的规律，并弄清了为什么固体有导体、绝缘体和半导体之分。', '', '01010604050400'),
('01010604050517', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP17', '量子力学的应用还有很多。', '', '01010604050400'),
('01010604050518', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP18', '毫不夸张地说，在过去的近一百年中，量子力学极大地推动了人类的进步。', '', '01010604050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010605000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05', '原子核', '', NULL),
('01010605010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01', '原子核的组成', '', '01010605000000'),
('01010605010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01', '天然放射现象', '', '01010605010000'),
('01010605010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP01', '受到贝克勒尔的发现的鼓舞，波兰裔法国物理学家玛丽·居里和她的丈夫皮埃尔·居里对铀和含铀的各种矿石进行了深入研究。', '', '01010605010100'),
('01010605010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP02', '放射性元素自发地发出射线的现象，叫作天然放射现象。', '', '01010605010100'),
('01010605010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02', '射线的本质', '', '01010605010000'),
('01010605010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP01', '把放射源铀、钋或镭放入用铅做成的容器中，射线只能从容器的小孔射出，成为细细的一束。', '', '01010605010200'),
('01010605010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP02', '物理学家经过多方面的研究后确认α射线、β射线和γ射线具有以下特征。', '', '01010605010200'),
('01010605010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP03', 'α粒子带正电，电荷量是电子的2倍，质量是氢原子的4倍，其组成与氦原子核相同。', '', '01010605010200'),
('01010605010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP04', '由于α粒子带电，质量又比较大，通过气体时很容易把气体分子中的电子剥离，使气体电离。', '', '01010605010200'),
('01010605010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP05', '是电子流，速度可以接近光速。', '', '01010605010200'),
('01010605010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP06', 'β射线的电离作用较弱，穿透能力较强，很容易穿透黑纸，也能穿透几毫米厚的铝板。', '', '01010605010200'),
('01010605010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP07', 'γ射线的电离作用更弱，穿透能力更强，甚至能穿透几厘米厚的铅板和几十厘米厚的混凝土。', '', '01010605010200'),
('01010605010308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP08', '放射性的强度也不受温度、外界压强的影响。', '', '01010605010200'),
('01010605010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03', '原子核的组成', '', '01010605010000'),
('01010605010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP01', '绝大多数原子核的质量与电荷量之比都大于质子的相应比值。', '', '01010605010300'),
('01010605010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP02', '质子带正电荷，电荷量与一个电子的电荷量相等。', '', '01010605010300'),
('01010605010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP03', '由于中子不带电，原子核所带的电荷等于核内质子电荷的总和。', '', '01010605010300'),
('01010605010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP04', '元素的性质与原子核外的电子有密切的关系。', '', '01010605010300'),
('01010605020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02', '放射性元素的衰变', '', '01010605000000'),
('01010605020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01', '原子核的衰变', '', '01010605020000'),
('01010605020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP01', '铀238放出一个α粒子后，质量数减少4，电荷数减少2，成为新核。', '', '01010605020100'),
('01010605020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP02', '研究还表明，在一定条件下，原子核中的两个中子和两个质子会结合起来形成α粒子，并被释放出来。', '', '01010605020100'),
('01010605020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP03', '原子核的能量也跟原子的能量一样，其变化是不连续的，也只能取一系列不连续的数值，因此，也存在着能级，同样是能级越低越稳定。', '', '01010605020100'),
('01010605020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP04', '当放射性物质连续衰变时，原子核中有的发生α衰变，有的发生β衰变，同时伴随着γ射线辐射。', '', '01010605020100'),
('01010605020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02', '半衰期', '', '01010605020000'),
('01010605020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP01', '放射性同位素衰变的快慢有一定的规律。', '', '01010605020200'),
('01010605020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP02', '不同的放射性元素，半衰期不同，甚至差别非常大。', '', '01010605020200'),
('01010605020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP03', '放射性元素衰变的快慢是由核内部自身的因素决定的，跟原子所处的化学状态和外部条件没有关系。', '', '01010605020200'),
('01010605020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03', '核反应', '', '01010605020000'),
('01010605020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP01', '衰变是原子核的自发变化，科学家更希望人工控制原子核的变化。', '', '01010605020300'),
('01010605020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP02', '与衰变过程一样，在核反应中，质量数守恒、电荷数守恒。', '', '01010605020300'),
('01010605020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04', '放射性同位素及其应用', '', '01010605020000'),
('01010605020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP01', '天然放射性同位素不过40多种，而今天通过核反应生成的人工放射性同位素已达3000多种，每种元素都有了自己的放射性同位素。', '', '01010605020400'),
('01010605020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP02', '丰富的放射性同位素资源在国民经济和科学研究的各个领域中得到了广泛的应用。', '', '01010605020400'),
('01010605020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP03', '工业部门可以使用放射性同位素发出的射线来测厚度。', '', '01010605020400'),
('01010605020504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP04', '像癌细胞那样，不断迅速繁殖的、无法控制的细胞组织，在射线照射下破坏得比健康细胞快。', '', '01010605020400'),
('01010605020505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP05', '利用γ射线照射种子，会使种子的遗传基因发生变异，经过筛选，可以培育出新品种。', '', '01010605020400'),
('01010605020506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP06', '棉花在开花、结桃的时候需要较多的磷肥，把磷肥喷在棉花叶子上，磷肥也能被吸收。', '', '01010605020400'),
('01010605020507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP07', '如果用磷的放射性同位素制成肥料喷在棉花的叶面上，然后每隔一定时间用探测器测量棉株各部位的放射性强度，上面的问题就解决了。', '', '01010605020400'),
('01010605020508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP08', '人体甲状腺的工作需要碘，碘被吸收后聚集在甲状腺内。', '', '01010605020400'),
('01010605020509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP09', '定时用探测器测量甲状腺及邻近组织的放射强度，有助于诊断甲状腺的疾病。', '', '01010605020400'),
('01010605020510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP10', '近年来，有关生物大分子的结构及其功能的研究，几乎都要借助于示踪原子。', '', '01010605020400'),
('01010605020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05', '辐射与安全', '', '01010605020000'),
('01010605020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05.OP01', '人类一直生活在放射性的环境中。', '', '01010605020500'),
('01010605030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03', '核力与结合能', '', '01010605000000'),
('01010605030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01', '核力与四种基本相互作用', '', '01010605030000'),
('01010605030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP01', '现代物理学认为，自然界存在着四种基本相互作用，即引力相互作用、电磁相互作用、强相互作用和弱相互作用。', '', '01010605030100'),
('01010605030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP02', '17世纪下半叶，人们发现了万有引力。', '', '01010605030100'),
('01010605030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP03', '引力是自然界的一种基本相互作用，地面物体所受的重力只是引力在地球表面附近的一种表现。', '', '01010605030100'),
('01010605030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP04', '20世纪，物理学家发现原子核是由若干带正电荷的质子和不带电的中子组成的。', '', '01010605030100'),
('01010605030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP05', '19世纪末20世纪初，物理学家发现了放射现象。', '', '01010605030100'),
('01010605030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP06', '①弱相互作用是引起原子核β衰变的原因，即引起中子—质子转变的原因。', '', '01010605030100'),
('01010605030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02', '结合能', '', '01010605030000'),
('01010605030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP01', '相距很远的两个物体，由于万有引力而相互接近，运动速度越来越大，引力势能转化为动能。', '', '01010605030200'),
('01010605030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP02', '最后撞在一起，一部分动能变成热并散失掉了。', '', '01010605030200'),
('01010605030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP03', '比结合能越大，原子核中核子结合得越牢固，原子核越稳定。', '', '01010605030200'),
('01010605030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03', '质量亏损', '', '01010605030000'),
('01010605030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP01', '原子核的结合能很难直接测量。', '', '01010605030300'),
('01010605030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP02', '质量亏损表明，的确存在着原子核的结合能。', '', '01010605030300'),
('01010605040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04', '核裂变与核聚变', '', '01010605000000'),
('01010605040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01', '核裂变的发现', '', '01010605040000'),
('01010605040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP01', '以铀235为例，核裂变时产生两或三个中子。', '', '01010605040100'),
('01010605040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP02', '原子核的体积非常小，原子内部的空隙很大，如果铀块不够大，中子在铀块中通过时，很有可能碰不到铀核而跑到铀块外面去，链式反应不能继续。', '', '01010605040100'),
('01010605040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP03', '只有当铀块足够大时，核裂变产生的中子才有足够大的概率打中某个铀核，使链式反应进行下去。', '', '01010605040100'),
('01010605040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP04', '铀核裂变时如果生成物不同，释放的能量也有差异。', '', '01010605040100'),
('01010605040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02', '反应堆与核电站', '', '01010605040000'),
('01010605040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP01', '原子核的链式反应也可以在人工控制下进行。', '', '01010605040200'),
('01010605040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP02', '其实，在第一个原子弹制成以前，科学家们已经实现了核能的可控释放。', '', '01010605040200'),
('01010605040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP03', '1942年，美籍意大利物理学家费米就主持建立了世界上第一个称为“核反应堆”的装置，首次通过可控制的链式反应实现了核能的释放。', '', '01010605040200'),
('01010605040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP04', '常用的慢化剂有石墨、重水和普通水（也叫轻水）。', '', '01010605040200'),
('01010605040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP05', '为了调节中子数目以控制反应速度，还需要在铀棒之间插进一些镉棒。', '', '01010605040200'),
('01010605040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP06', '核燃料发生核裂变释放的能量使反应区温度升高。', '', '01010605040200'),
('01010605040307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP07', '水或液态的金属钠等流体在反应堆内外循环流动，把反应堆内的热量传输出去，用于发电，同时也使反应堆冷却。', '', '01010605040200'),
('01010605040308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP08', '在热交换器内，第二回路中的水被加热生成高温高压蒸汽，驱动汽轮机运转。', '', '01010605040200'),
('01010605040309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP09', '建造核电站时需要特别注意防止放射线和放射性物质的泄漏，以避免射线对人体的伤害和放射性物质对水源、空气和工作场所造成放射性污染。', '', '01010605040200'),
('01010605040310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP10', '核反应堆中的核废料具有很强的放射性，需要装入特制的容器，深埋地下。', '', '01010605040200'),
('01010605040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03', '核聚变', '', '01010605040000'),
('01010605040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP01', '当物质的温度达到几百万开尔文时，剧烈的热运动使得一部分原子核具有足够的动能，可以克服库仑斥力，碰撞时十分接近，发生核聚变。', '', '01010605040300'),
('01010605040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP02', '太阳的主要成分是氢。', '', '01010605040300'),
('01010605040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP03', '现在地球上消耗的能量，追根溯源，绝大部分还是来自太阳，即太阳内部核聚变时释放的核能。', '', '01010605040300'),
('01010605040404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP04', '太阳在“核燃烧”的过程中“体重”不断减轻。', '', '01010605040300'),
('01010605040405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP05', '太阳的寿命已经有50亿年了。', '', '01010605040300'),
('01010605040406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP06', '目前，人工产生的热核反应主要用在核武器上，那就是氢弹。', '', '01010605040300'),
('01010605040407', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP07', '与核裂变相比，核聚变有很多优点。', '', '01010605040300'),
('01010605040408', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP08', '利用核聚变物质的惯性进行约束。', '', '01010605040300'),
('01010605040409', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP09', '总的来说，实现受控核聚变还有一段很长的路要走。', '', '01010605040300'),
('01010605050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05', '“基本”粒子', '', '01010605000000'),
('01010605050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01', '发现新粒子', '', '01010605050000'),
('01010605050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP01', '从20世纪30年代以来，人们在对宇宙线的研究中陆续发现了一些新的粒子。', '', '01010605050100'),
('01010605050202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP02', '1932年发明的粒子加速器，能使带电粒子加速到很高的能量。', '', '01010605050100'),
('01010605050203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP03', '利用加速器和对撞机，人们发现了更多的粒子。', '', '01010605050100'),
('01010605050204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP04', '粒子物理的研究不再单纯依赖宇宙线。', '', '01010605050100'),
('01010605050205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP05', '高能量的对撞机往往极为庞大和复杂。', '', '01010605050100'),
('01010605050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02', '粒子的分类', '', '01010605050000'),
('01010605050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP01', '现在已经发现的粒子达400多种。', '', '01010605050200'),
('01010605050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP02', '强子是参与强相互作用的粒子。', '', '01010605050200'),
('01010605050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP03', '质子和中子都是强子。', '', '01010605050200'),
('01010605050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP04', '轻子不参与强相互作用。', '', '01010605050200'),
('01010605050305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP05', '最早发现的轻子是电子，后来发现的轻子有电子中微子、μ子、μ子中微子以及τ子和τ子中微子。', '', '01010605050200'),
('01010605050306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP06', '每种轻子都有对应的反粒子。', '', '01010605050200'),
('01010605050307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP07', '现代实验还没有发现轻子的内部结构。', '', '01010605050200'),
('01010605050308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP08', '光子传递电磁相互作用，中间玻色子传递弱相互作用，胶子传递强相互作用。', '', '01010605050200'),
('01010605050309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP09', '希格斯玻色子是希格斯场的量子激发。', '', '01010605050200'),
('01010605050310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP10', '基本粒子因与希格斯场耦合而获得质量。', '', '01010605050200'),
('01010605050311', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP11', '2012年，欧洲核子研究中心利用大型强子对撞机发现了希格斯玻色子。', '', '01010605050200'),
('01010605050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03', '研究样例', '', '01010605050000'),
('01010605050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP01', '①燃气灶烧水时的能量转化和转移的情况如下。', '', '01010605050300'),
('01010605050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP02', '记录数据时室温为26℃，基本上保证了实验环境温度的恒定。', '', '01010605050300'),
('01010605050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP03', '为减小水在高温时的放热损失，应缩短水在高温时的加热时间。', '', '01010605050300'),
('01010605050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP04', '用实验检验研究结论的正确性。', '', '01010605050300'),
('01010605050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04', '参考选题', '', '01010605050000'),
('01010605050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP01', '使用热水器洗澡时，常有两种调温习惯：一种是把加热后的水温直接调节至洗澡的合适温度，如38℃左右；另一种是把热水器的加热温度调到更高，如50℃左右，再通过淋浴水龙头加兑冷水至合适水温，后者可以获得更大的适温水量。', '', '01010605050400'),
('01010605050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP02', '热水器工作前，先观察燃气表示数，测量加热前水的温度。', '', '01010605050400'),
('01010605050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP03', '观察燃气表示数，记录所用去的燃气体积。', '', '01010605050400'),
('01010605050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP04', '如果已知被加热水的体积，查找燃气的热值，还可以算出两种情况下热效率的大小。', '', '01010605050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010601000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01', '分子动理论', '', NULL),
('01010601010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01', '分子动理论的基本内容', '', '01010601000000'),
('01010601010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01', '物体是由大量分子组成的', '', '01010601010000'),
('01010601010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP01', '人们用肉眼无法直接看到分子，就是用高倍的光学显微镜也看不到。', '', '01010601010100'),
('01010601010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP02', '直至1982年，人们研制了能放大几亿倍的扫描隧道显微镜③，才观察到物质表面原子的排列。', '', '01010601010100'),
('01010601010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02', '分子热运动', '', '01010601010000'),
('01010601010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP01', '扩散现象是物质分子永不停息地做无规则运动的证据之一。', '', '01010601010200'),
('01010601010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP02', '扩散现象在科学技术中有很多应用。', '', '01010601010200'),
('01010601010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP03', '19世纪初，一些人观察到，悬浮在液体中的小颗粒总在不停地运动。', '', '01010601010200'),
('01010601010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP04', '从实验结果可以看出，小炭粒的运动是无规则的，温度越高，小炭粒的运动越明显。', '', '01010601010200'),
('01010601010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP05', '在扩散现象中，温度越高，扩散得越快。', '', '01010601010200'),
('01010601010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP06', '观察布朗运动，温度越高，悬浮微粒的运动就越明显。', '', '01010601010200'),
('01010601010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP07', '温度是分子热运动剧烈程度的标志。', '', '01010601010200'),
('01010601010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03', '分子间的作用力', '', '01010601010000'),
('01010601010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP01', '水和酒精混合后的总体积变小了。', '', '01010601010300'),
('01010601010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP02', '当用力拉伸物体时，物体内各部分之间要产生反抗拉伸的作用力，此时分子间的作用力表现为引力。', '', '01010601010300'),
('01010601010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP03', '当用力压缩物体时，物体内各部分之间会产生反抗压缩的作用力，此时分子间的作用力表现为斥力。', '', '01010601010300'),
('01010601010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP04', '原子内部有带正电的原子核和带负电的电子。', '', '01010601010300'),
('01010601010400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP04', '分子动理论', '', '01010601010000'),
('01010601020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02', '实验：用油膜法估测油酸分子的大小', '', '01010601000000'),
('01010601020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP01', '实验思路', '', '01010601020000'),
('01010601020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP02', '物理量的测量', '', '01010601020000'),
('01010601020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP03', '数据分析', '', '01010601020000'),
('01010601030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03', '分子运动速率分布规律', '', '01010601000000'),
('01010601030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01', '气体分子运动的特点', '', '01010601030000'),
('01010601030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP01', '热现象与大量分子热运动的统计规律有关。', '', '01010601030100'),
('01010601030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP02', '要研究气体的热现象，就要了解气体分子运动的特点。', '', '01010601030100'),
('01010601030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP03', '液体变为气体后，体积要增大上千倍，可见，气体分子间距离大约是分子直径的10倍。', '', '01010601030100'),
('01010601030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP04', '分子的大小相对分子间的空隙来说很小，所以，可以把气体分子视为质点。', '', '01010601030100'),
('01010601030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP05', '分子的运动杂乱无章，在某一时刻，向着任何一个方向运动的分子都有，而且向各个方向运动的分子数目几乎相等。', '', '01010601030100'),
('01010601030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02', '分子运动速率分布图像', '', '01010601030000'),
('01010601030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP01', '尽管分子做无规则运动，速率有大有小，但大量分子的速率却按一定的规律分布。', '', '01010601030200'),
('01010601030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP02', '100℃的氧气，速率大的分子比例较多，其分子的平均速率比0℃的大。', '', '01010601030200'),
('01010601030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03', '气体压强的微观解释', '', '01010601030000'),
('01010601030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP01', '器壁单位面积上受到的压力，就是气体的压强。', '', '01010601030300'),
('01010601030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP02', '从微观角度来看，气体分子的数量是巨大的。', '', '01010601030300'),
('01010601040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04', '分子动能和分子势能', '', '01010601000000'),
('01010601040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01', '分子动能', '', '01010601040000'),
('01010601040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP01', '物体中分子热运动的速率大小不一，所以各个分子的动能也有大有小，而且在不断改变。', '', '01010601040100'),
('01010601040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP02', '温度升高时，分子的热运动加剧，温度越高，分子热运动的平均动能越大。', '', '01010601040100'),
('01010601040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP03', '温度越低，分子热运动的平均动能越小。', '', '01010601040100'),
('01010601040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02', '分子势能', '', '01010601040000'),
('01010601040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP01', '分子间存在着相互作用力，可以证明分子间的作用力所做的功与路径无关，分子组成的系统具有分子势能。', '', '01010601040200'),
('01010601040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP02', '物体的体积变化时，分子间距离将发生变化，因而分子势能随之改变。', '', '01010601040200'),
('01010601040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03', '物体的内能', '', '01010601040000'),
('01010601040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP01', '任何物体都具有内能。', '', '01010601040300'),
('01010601040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP02', '分子热运动的平均动能与温度有关，分子势能与物体的体积有关。', '', '01010601040300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010602000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02', '气体、固体和液体', '', NULL),
('01010602010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01', '温度和温标', '', '01010602000000'),
('01010602010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01', '状态参量与平衡态', '', '01010602010000'),
('01010602010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01.OP01', '要定量地描述系统的状态往往很难，因为有时系统的参量会随时间变化。', '', '01010602010100'),
('01010602010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP02', '热平衡与温度', '', '01010602010000'),
('01010602010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03', '温度计与温标', '', '01010602010000'),
('01010602010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03.OP01', '现代科学中用得更多的是热力学温标。', '', '01010602010300'),
('01010602020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC02', '气体的等温变化', '', '01010602000000'),
('01010602030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03', '气体的等压变化和等容变化', '', '01010602000000'),
('01010602030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP01', '气体的等压变化', '', '01010602030000'),
('01010602030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02', '气体的等容变化', '', '01010602030000'),
('01010602030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02.OP01', '法国科学家查理在分析了实验事实后发现，当一定质量的气体体积一定时，各种气体的压强与温度之间都有线性关系。', '', '01010602030200'),
('01010602030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03', '理想气体', '', '01010602030000'),
('01010602030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP01', '当压强很大、温度很低时，由上述规律计算的结果与实际测量结果有很大的差别。', '', '01010602030300'),
('01010602030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP02', '实际气体的分子之间有相互作用力，但是作用力很小；分子也有大小，但气体分子之间的间距比分子直径大得多；气体分子与器壁碰撞几乎是弹性的，动能损失也很小。', '', '01010602030300'),
('01010602030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP03', '在温度不低于零下几十摄氏度、压强不超过大气压的几倍时，把实际气体当成理想气体来处理，误差很小。', '', '01010602030300'),
('01010602030400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04', '气体实验定律的微观解释', '', '01010602030000'),
('01010602030501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP01', '用分子动理论可以定性解释气体的实验定律。', '', '01010602030400'),
('01010602030502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP02', '一定质量的某种理想气体，温度保持不变时，分子的平均动能是一定的。', '', '01010602030400'),
('01010602030503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP03', '一定质量的某种理想气体，温度升高时，分子的平均动能增大；只有气体的体积同时增大，使分子的数密度减小，才能保持压强不变。', '', '01010602030400'),
('01010602030504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP04', '一定质量的某种理想气体，体积保持不变时，分子的数密度保持不变。', '', '01010602030400'),
('01010602040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04', '固体', '', '01010602000000'),
('01010602040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01', '晶体和非晶体', '', '01010602040000'),
('01010602040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP01', '固体可以分为晶体和非晶体两类。', '', '01010602040100'),
('01010602040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP02', '石英、云母①、明矾、食盐、硫酸铜、味精等是晶体。', '', '01010602040100'),
('01010602040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP03', '玻璃、蜂蜡、松香、沥青、橡胶等是非晶体。', '', '01010602040100'),
('01010602040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP04', '非晶体没有规则的外形。', '', '01010602040100'),
('01010602040205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP05', '除了形状是否规则外，晶体和非晶体在物理性质上也有所不同。', '', '01010602040100'),
('01010602040206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP06', '从实验结果可以看出，玻璃片上石蜡熔化区域的形状近似于圆形，表明玻璃沿各个方向的导热性能相同；云母片上石蜡熔化区域的形状呈椭圆形，表明云母沿不同方向的导热性能不同。', '', '01010602040100'),
('01010602040207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP07', '蔗糖受潮后粘在一起形成的糖块，看起来没有确定的几何形状。', '', '01010602040100'),
('01010602040208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP08', '粘在一起的糖块也是多晶体，单个的蔗糖晶体颗粒是单晶体。', '', '01010602040100'),
('01010602040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02', '晶体的微观结构', '', '01010602040000'),
('01010602040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP01', '在20世纪70年代，人们又用电子显微镜观察到了铀、钍原子的像。', '', '01010602040200'),
('01010602040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP02', '1982年，人们用扫描隧道显微镜观察到物质表面原子的排列。', '', '01010602040200'),
('01010602040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP03', '在各种晶体中，原子（或分子、离子）都是按照一定的规则排列的，具有空间上的周期性。', '', '01010602040200'),
('01010602040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP04', '石墨是层状结构，层与层之间距离较大，原子间的作用力比较弱，所以石墨质地松软，可以用来制作粉状润滑剂；金刚石中碳原子间的作用力很强，所以金刚石有很大的硬度，可以用来切割玻璃。', '', '01010602040200'),
('01010602040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP05', '同种物质也可能以晶体和非晶体两种不同的形态出现，也就是说，物质是晶体还是非晶体，并不是绝对的。', '', '01010602040200'),
('01010602040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP06', '有些非晶体在一定条件下也可以转化为晶体。', '', '01010602040200'),
('01010602050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05', '液体', '', '01010602000000'),
('01010602050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP01', '液体的表面张力', '', '01010602050000'),
('01010602050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02', '浸润和不浸润', '', '01010602050000'),
('01010602050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP01', '管的内径越小，上述现象就越明显。', '', '01010602050200'),
('01010602050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP02', '有些物体内部有许多细小的孔道，孔道起到了毛细管的作用。', '', '01010602050200'),
('01010602050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP03', '当液体和与之接触的固体的相互作用比液体分子之间的相互作用强时，液体能够浸润固体。', '', '01010602050200'),
('01010602050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP04', '如果把水银滴到干净的铅板上，水银就会附着在铅板上，很难擦去。', '', '01010602050200'),
('01010602050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03', '液晶', '', '01010602050000'),
('01010602050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP01', '液晶在现代生活中扮演着重要角色，广泛应用于手机屏幕、平板电视等显示设备中。', '', '01010602050300'),
('01010602050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP02', '液晶态既具有液体的流动性，又在一定程度上具有晶体分子的规则排列的性质。', '', '01010602050300'),
('01010602050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP03', '有些物质在特定的温度范围之内具有液晶态；另一些物质，在适当的溶剂中溶解时，在一定的浓度范围具有液晶态。', '', '01010602050300'),
('01010602050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP04', '分子取向排列的液晶具有光学各向异性，具体地说，沿分子长轴方向上的折射率不同于沿短轴的。', '', '01010602050300'),
('01010602050405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP05', '入射光的偏振方向与液晶分子长轴的方向成不同夹角时，液晶对光的折射率不同。', '', '01010602050300'),
('01010602050406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP06', '液晶的研究在生命科学中也有应用。', '', '01010602050300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010603000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03', '热力学定律', '', NULL),
('01010603010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01', '功、热和内能的改变', '', '01010603000000'),
('01010603010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01', '焦耳的实验', '', '01010603010000'),
('01010603010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP01', '在焦耳研究功与热等效性的许多实验中，有两个最具代表性。', '', '01010603010100'),
('01010603010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP02', '一个是让重物下落带动叶片搅拌容器中的水，引起水温上升；另一个是通过电流的热效应给水加热。', '', '01010603010100'),
('01010603010203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP03', '焦耳的多次实验测量表明，尽管各次悬挂重物的质量不同，下落的高度也不一样，但只要重力所做的功相同，容器内水温上升的数值都是相同的，即系统状态的变化是相同的。', '', '01010603010100'),
('01010603010204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP04', '多次实验结果表明，对于同一个系统，如果过程是绝热的，那么不管通过电阻丝的电流或大或小、通电时间或长或短，只要所做的电功相等，则系统温度上升的数值是相同的，即系统的状态就发生了同样的变化。', '', '01010603010100'),
('01010603010205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP05', '两个位置间移动时重力所做的功。', '', '01010603010100'),
('01010603010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02', '功与内能的改变', '', '01010603010000'),
('01010603010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP01', '当温度达到易燃物的燃点时，易燃物发生自燃。', '', '01010603010200'),
('01010603010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP02', '当橡胶塞跳出时，瓶内出现白雾。', '', '01010603010200'),
('01010603010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP03', '以瓶内气体为研究对象，橡胶塞跳出后，瓶内的气体迅速膨胀，系统对外做功，因此，气体的内能迅速减少，瓶内气体温度迅速下降，瓶内水蒸气液化，就会出现大量的雾状小水滴。', '', '01010603010200'),
('01010603010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03', '热与内能的改变', '', '01010603010000'),
('01010603010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03.OP01', '外界对系统做功可以改变系统的热力学状态，而单纯地对系统传热也能改变系统的热力学状态。', '', '01010603010300'),
('01010603020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02', '热力学第一定律', '', '01010603000000'),
('01010603020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01', '热力学第一定律', '', '01010603020000'),
('01010603020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01.OP01', '（1）求上述压缩过程中气体内能的变化量。', '', '01010603020100'),
('01010603020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP02', '热力学第一定律的应用', '', '01010603020000'),
('01010603030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03', '能量守恒定律', '', '01010603000000'),
('01010603030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01', '探索能量守恒的足迹', '', '01010603030000'),
('01010603030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP01', '能量的概念是人类在对物质运动规律进行长期探索中建立起来的。', '', '01010603030100'),
('01010603030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP02', '所有自然现象都涉及能量，人类的任何活动都离不开能量。', '', '01010603030100'),
('01010603030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP03', '能量具有不同的形式，有描述热运动的内能、描述机械运动的机械能、描述光辐射的光能，等等。', '', '01010603030100'),
('01010603030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP04', '不同形式的运动都可以用能量来描述。', '', '01010603030100'),
('01010603030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP05', '在认识自然的进程中，科学家慢慢知道了要用联系的观点去观察自然。', '', '01010603030100'),
('01010603030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP06', '由于能量既抽象又以各种不同的形式广泛存在，所以能量守恒思想的萌芽及产生并不是一蹴而就的。', '', '01010603030100'),
('01010603030207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP07', '从各种实验的验证，再到实验结果的被质疑，科学家们付出了大量的努力。', '', '01010603030100'),
('01010603030208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP08', '从18世纪末到19世纪中叶，不同领域的科学家从不同角度都提出过能量守恒的思想。', '', '01010603030100'),
('01010603030209', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP09', '人类对能量守恒的认识经历了一个由浅入深、由含糊到清晰的过程。', '', '01010603030100'),
('01010603030210', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP10', '俄国化学家盖斯的研究发现，任何一个化学反应，不论是一步完成，还是分几步完成，放出的总热量相同。', '', '01010603030100'),
('01010603030211', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP11', '焦耳的实验精确地测量了做功与传热之间的等价关系，从而为能量守恒定律奠定了牢固的实验基础，也为能量守恒的定量描述迈出了重要的一步。', '', '01010603030100'),
('01010603030212', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP12', '德国医生迈尔通过比对不同地区人血颜色的差异，认识到食物中化学能与内能的等效性，即生物体内能量的输入和输出是平衡的。', '', '01010603030100'),
('01010603030213', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP13', '半个多世纪人类对能量以及能量守恒研究的历史，预示着人们把各分立的领域连成一体的时刻已经到来，也就是到了建立能量转化与守恒定律的时候了。', '', '01010603030100'),
('01010603030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02', '能量守恒定律', '', '01010603030000'),
('01010603030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP01', '在热力学领域内，做功和传热可以改变系统的内能，即系统内能与系统外的能量会发生转化或转移，但能的总量不会改变。', '', '01010603030200'),
('01010603030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP02', '能量守恒是自然界的普遍规律。', '', '01010603030200'),
('01010603030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP03', '根据能量守恒定律，物理学发现和解释了很多科学现象。', '', '01010603030200'),
('01010603030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP04', '在20世纪30年代初，奥地利物理学家泡利根据能量守恒定律预言了中微子。', '', '01010603030200'),
('01010603030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP05', '能量守恒定律经受住了新的检验。', '', '01010603030200'),
('01010603030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP06', '能量守恒定律的发现是科学史上的重大事件。', '', '01010603030200'),
('01010603030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03', '永动机不可能制成', '', '01010603030000'),
('01010603030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP01', '“问题”栏目中的饮水小鸭不是永动机。', '', '01010603030300'),
('01010603030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP02', '小鸭的头和身由两个玻璃球构成，并通过细玻璃管相连。', '', '01010603030300'),
('01010603030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP03', '下球盛有一些乙醚，上球连同尖嘴被吸水毛毡包起来。', '', '01010603030300'),
('01010603030404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP04', '永动机的思想违背了能量守恒定律，所以是不可能制成的。', '', '01010603030300'),
('01010603030405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP05', '应该指出的是，制造永动机的千万次失败使人们的头脑冷静下来，开始在更深层次寻找失败的原因。', '', '01010603030300'),
('01010603030406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP06', '国科学院宣布“不再审查有关永动机的一切设计”。', '', '01010603030300'),
('01010603040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04', '热力学第二定律', '', '01010603000000'),
('01010603040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01', '热力学第二定律', '', '01010603040000'),
('01010603040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP01', '在电冰箱的实例中，热量的确从低温物体——冰箱内的食品，传到了高温物体——冰箱外的空气。', '', '01010603040100'),
('01010603040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP02', '从能量的角度看，热机的工作分为两个阶段：第一个阶段是燃烧燃料，把燃料中的化学能变成工作物质的内能；第二个阶段是工作物质对外做功，把自己的内能变成机械能。', '', '01010603040100'),
('01010603040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP03', '热力学第二定律的开尔文表述阐述了机械能与内能转化的方向性：机械能可以全部转化为内能，而内能无法全部用来做功以转化成机械能。', '', '01010603040100'),
('01010603040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP04', '可以证明，热力学第二定律的克劳修斯表述和开尔文表述是等价的。', '', '01010603040100'),
('01010603040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02', '能源是有限的', '', '01010603040000'),
('01010603040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP01', '自然界的一切变化，人类社会的所有活动，都伴随着能量的转移和转化，能量是一切物质运动的源泉，是一切生命活动的基础。', '', '01010603040200'),
('01010603040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP02', '能量在数量上虽然守恒，但其转移和转化却具有方向性。', '', '01010603040200'),
('01010603040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP03', '核能、生物能……最终都转化成内能，流散到周围的环境中。', '', '01010603040200'),
('01010603040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP04', '周围环境中的内能很难再利用，而机械能、光能、化学能、电能相对于周围环境中的内能来说，可利用的品质要高。', '', '01010603040200'),
('01010603040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP05', '能源的使用过程中虽然能的总量保持守恒，但能量的品质下降了。', '', '01010603040200'),
('01010603040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP06', '虽然能量总量不会减少，但能源会逐步减少，因此能源是有限的资源。', '', '01010603040200'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010604000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04', '原子结构和波粒二象性', '', NULL),
('01010604010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01', '普朗克黑体辐射理论', '', '01010604000000'),
('01010604010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01', '黑体与黑体辐射', '', '01010604010000'),
('01010604010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP01', '19世纪，由于冶金、星体测温等需求，人们对热辐射进行了大量的研究。', '', '01010604010100'),
('01010604010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP02', '研究表明，对于一般材料的物体，辐射电磁波的情况除了与温度有关，还与材料的种类及表面状况有关，而黑体辐射电磁波的强度按波长的分布只与黑体的温度有关。', '', '01010604010100'),
('01010604010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02', '黑体辐射的实验规律', '', '01010604010000'),
('01010604010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP01', '利用分光技术和热电偶等设备，可以测出黑体辐射电磁波的强度按波长分布的情况。', '', '01010604010200'),
('01010604010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP02', '从中可以看出，随着温度的升高，一方面，各种波长的辐射强度都有增加，另一方面，辐射强度的极大值向波长较短的方向移动。', '', '01010604010200'),
('01010604010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP03', '德国物理学家维恩在1896年、英国物理学家瑞利在1900年，分别提出了辐射强度按波长分布的理论公式。', '', '01010604010200'),
('01010604010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP04', '维恩公式在短波区与实验非常接近，在长波区则与实验偏离较大；瑞利公式在长波区与实验基本一致，但在短波区与实验严重不符。', '', '01010604010200'),
('01010604010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP05', '为了得出同全部实验相符的黑体辐射公式，德国物理学家普朗克做了多种尝试。', '', '01010604010200'),
('01010604010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03', '能量子', '', '01010604010000'),
('01010604010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP01', '弹簧振子的能量不一定是某个最小值的整数倍。', '', '01010604010300'),
('01010604010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP02', '而普朗克的假设则认为微观粒子的能量是量子化的，或者说微观粒子的能量是分立的。', '', '01010604010300'),
('01010604010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP03', '普朗克本人因此获得了1918年的诺贝尔物理学奖。', '', '01010604010300'),
('01010604020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02', '光电效应', '', '01010604000000'),
('01010604020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01', '光电效应的实验规律', '', '01010604020000'),
('01010604020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP01', '1887年，赫兹在研究电磁波的实验中偶尔发现，接收电路的间隙如果受到光照，就更容易产生电火花。', '', '01010604020100'),
('01010604020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP02', '科学家通过实验发现了以下规律。', '', '01010604020100'),
('01010604020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP03', '实验表明，不同金属的截止频率不同。', '', '01010604020100'),
('01010604020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP04', '在光照条件不变的情况下，随着所加电压的增大，光电流趋于一个饱和值。', '', '01010604020100'),
('01010604020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP05', '实验表明，在光的频率不变的情况下，入射光越强，饱和电流越大。', '', '01010604020100'),
('01010604020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02', '光电效应经典解释中的疑难', '', '01010604020000'),
('01010604020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP01', '当光照射金属表面时，电子会吸收光的能量。', '', '01010604020200'),
('01010604020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP02', '光越强，逸出的电子数越多，光电流也就越大。', '', '01010604020200'),
('01010604020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP03', '●不管光的频率如何，只要光足够强，电子都可以获得足够能量从而逸出表面，不应存在截止频率。', '', '01010604020200'),
('01010604020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03', '爱因斯坦的光电效应理论', '', '01010604020000'),
('01010604020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP01', '对于光电效应的解释，爱因斯坦是在普朗克量子假说的基础上作出的。', '', '01010604020300'),
('01010604020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP02', '从下面的讨论可以看出，爱因斯坦光电效应方程可以很好地解释光电效应实验中的各种现象。', '', '01010604020300'),
('01010604020403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP03', '●电子一次性吸收光子的全部能量，不需要积累能量的时间，光电流自然几乎是瞬时产生的。', '', '01010604020300'),
('01010604020404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP04', '爱因斯坦由于提出了光电效应理论而获得1921年的诺贝尔物理学奖。', '', '01010604020300'),
('01010604020405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP05', '金属表面的光子数较多，照射金属时产生的光电子较多，因而饱和电流较大。', '', '01010604020300'),
('01010604020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04', '康普顿效应和光子的动量', '', '01010604020000'),
('01010604020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP01', '光可以与介质中的物质微粒发生散射，改变传播方向。', '', '01010604020400'),
('01010604020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP02', '康普顿效应让人们对光子有了更深入的认识。', '', '01010604020400'),
('01010604020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP03', '康普顿因此获得了1927年的诺贝尔物理学奖。', '', '01010604020400'),
('01010604020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05', '光的波粒二象性', '', '01010604020000'),
('01010604020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP01', '众所周知，在麦克斯韦的电磁理论建立之后，人们认识到光是一种电磁波，从而光的波动说被普遍接受，人们不再认为光是由粒子组成的。', '', '01010604020500'),
('01010604020602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP02', '而爱因斯坦的光电效应理论和康普顿效应理论表明，光在某些方面确实会表现得像是由一些粒子（即一个个有确定能量和动量的“光子”）组成的。', '', '01010604020500'),
('01010604020603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP03', '人们意识到，光既具有波动性，又具有粒子性。', '', '01010604020500'),
('01010604020604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP04', '从牛顿的微粒说、惠更斯和托马斯·杨的波动说，到麦克斯韦的电磁理论，再到爱因斯坦的光子理论乃至量子电动力学，人类对光的认识构成了一部科学史诗。', '', '01010604020500'),
('01010604030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03', '原子的核式结构模型', '', '01010604000000'),
('01010604030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01', '电子的发现', '', '01010604030000'),
('01010604030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP01', '为了找到有利于自己的证据，双方都做了许多实验。', '', '01010604030100'),
('01010604030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP02', '由实验测得的阴极射线粒子的比荷是氢离子①（也就是质子）比荷的近两千倍。', '', '01010604030100'),
('01010604030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP03', '金属受热还是放射性物质的自发辐射，都能发射同样的带电粒子——电子。', '', '01010604030100'),
('01010604030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02', '原子的核式结构模型', '', '01010604030000'),
('01010604030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP01', 'α粒子是从放射性物质（如铀和镭）中发射出来的快速运动的粒子，质量为氢原子质量的4倍、电子质量的7300倍。', '', '01010604030200'),
('01010604030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP02', '被散射的α粒子打在荧光屏上会有微弱的闪光产生。', '', '01010604030200'),
('01010604030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP03', '通过显微镜观察闪光就可以记录在某一时间内向某一方向散射的α粒子数。', '', '01010604030200'),
('01010604030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP04', 'α粒子有库仑力的作用，一些α粒子的运动方向改变，也就是发生了α粒子的散射。', '', '01010604030200'),
('01010604030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP05', 'α粒子所占的比例，可以推知原子中电荷的分布情况。', '', '01010604030200'),
('01010604030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP06', '卢瑟福分析了实验数据后认为，事实应该是：占原子质量绝大部分的带正电的物质集中在很小的空间范围。', '', '01010604030200'),
('01010604030307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP07', '1911年，卢瑟福提出了自己的原子结构模型。', '', '01010604030200'),
('01010604030308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP08', '因为正电体很小，当α粒子进入原子区域后，大部分离正电体很远，受到的库仑斥力很小，运动方向几乎不改变。', '', '01010604030200'),
('01010604030309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP09', '只有极少数α粒子在穿过时距离正电体很近，因此受到很强的库仑斥力，发生大角度散射。', '', '01010604030200'),
('01010604030310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP10', '按照卢瑟福的理论，正电体的尺度是很小的，称为原子核。', '', '01010604030200'),
('01010604030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03', '原子核的电荷与尺度', '', '01010604030000'),
('01010604030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP01', '又由于原子是电中性的，可以推算出原子内含有的电子数。', '', '01010604030300'),
('01010604030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP02', '通常用核半径描述核的大小。', '', '01010604030300'),
('01010604040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04', '氢原子光谱和玻尔的原子模型', '', '01010604000000'),
('01010604040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01', '光谱', '', '01010604040000'),
('01010604040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP01', '有的光谱看起来不是一条条分立的谱线，而是连在一起的光带，叫作连续谱。', '', '01010604040100'),
('01010604040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP02', '气体中中性原子的发光光谱都是线状谱，说明原子只发出几种特定频率的光。', '', '01010604040100'),
('01010604040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02', '氢原子光谱的实验规律', '', '01010604040000'),
('01010604040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP01', '光谱的结果显示氢原子只能发出一系列特定波长的光。', '', '01010604040200'),
('01010604040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP02', '巴耳末公式以简洁的形式反映了氢原子的线状光谱的特征。', '', '01010604040200'),
('01010604040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03', '经典理论的困难', '', '01010604040000'),
('01010604040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP01', '卢瑟福的核式结构模型正确地指出了原子核的存在，很好地解释了α粒子散射实验，但跟经典电磁理论发生了矛盾。', '', '01010604040300'),
('01010604040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP02', '核外电子受到原子核的库仑引力的作用，却没有被吸引到原子核上，而是在以一定的速度绕核运动。', '', '01010604040300'),
('01010604040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP03', '随着绕核运动轨道半径的不断变化，电子运动的频率也要不断变化，因此原子辐射电磁波的频率也要不断变化。', '', '01010604040300'),
('01010604040400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04', '玻尔原子理论的基本假设', '', '01010604040000'),
('01010604040501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP01', '丹麦物理学家玻尔意识到了经典理论在解释原子结构方面的困难。', '', '01010604040400'),
('01010604040502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP02', '玻尔的原子结构假说包括以下两方面的内容。', '', '01010604040400'),
('01010604040503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP03', '玻尔认为，原子中的电子在库仑引力的作用下，绕原子核做圆周运动，服从经典力学的规律。', '', '01010604040400'),
('01010604040504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP04', '在玻尔理论中，电子的轨道半径只可能是某些分立的数值。', '', '01010604040400'),
('01010604040505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP05', '当电子在不同的轨道上运动时，原子处于不同的状态，具有不同的能量。', '', '01010604040400'),
('01010604040506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP06', '根据玻尔理论，电子只能在特定轨道上运动，因此，原子的能量也只能取一系列特定的值。', '', '01010604040400'),
('01010604040507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP07', '按照玻尔的观点，电子在一系列定态轨道上运动，不会发生电磁辐射。', '', '01010604040400'),
('01010604040500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05', '玻尔理论对氢光谱的解释', '', '01010604040000'),
('01010604040601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP01', '从玻尔的基本假设出发，运用经典电磁学和经典力学的理论，可以计算氢原子中电子的可能轨道半径及相应的能量。', '', '01010604040500'),
('01010604040602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP02', '处于基态的原子非常稳定。', '', '01010604040500'),
('01010604040603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP03', '处于激发态的原子是不稳定的，会自发地向能量较低的能级跃迁，放出光子，最终回到基态。', '', '01010604040500'),
('01010604040604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP04', '由于原子的能级是分立的，所以放出的光子的能量也是分立的。', '', '01010604040500'),
('01010604040605', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP05', '由于不同的原子具有不同的结构，能级各不相同，因此辐射（或吸收）的光子频率也不相同。', '', '01010604040500'),
('01010604040606', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP06', '各种气体原子的能级不同，跃迁时发射光子的能量各异。', '', '01010604040500'),
('01010604040607', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP07', '街道上的霓虹灯、试电笔中的氖管，都是由灯管内的气体原子从高能级向低能级跃迁而发光的。', '', '01010604040500'),
('01010604040608', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP08', '食盐被灼烧时发的光，也主要是由食盐蒸气中钠原子的能级跃迁而造成的。', '', '01010604040500'),
('01010604040600', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06', '玻尔理论的局限性', '', '01010604040000'),
('01010604040701', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP01', '玻尔的原子理论第一次将量子观念引入原子领域，提出了定态和跃迁的概念。', '', '01010604040600'),
('01010604040702', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP02', '玻尔理论成功地解释了氢原子光谱的实验规律。', '', '01010604040600'),
('01010604040703', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP03', '①玻尔理论的不足之处在于保留了经典粒子的观念，仍然把电子的运动看作经典力学描述下的轨道运动。', '', '01010604040600'),
('01010604040704', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP04', '实际上，根据量子力学，原子中电子的坐标没有确定的值。', '', '01010604040600'),
('01010604040705', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP05', '当原子处于不同的状态时，电子在各处出现的概率是不一样的。', '', '01010604040600'),
('01010604050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05', '粒子的波动性和量子力学的建立', '', '01010604000000'),
('01010604050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01', '粒子的波动性', '', '01010604050000'),
('01010604050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01.OP01', '1924年，法国物理学家德布罗意在对光的波粒二象性、玻尔氢原子理论以及相对论的深入研究的基础上，把波粒二象性推广到实物粒子，如电子、质子等。', '', '01010604050100'),
('01010604050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02', '物质波的实验验证', '', '01010604050000'),
('01010604050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP01', '德布罗意关于实物粒子具有波动性的假说在当时来看是难以理解的。', '', '01010604050200'),
('01010604050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP02', '1929年，德布罗意因提出物质波的假说获得了诺贝尔物理学奖。', '', '01010604050200'),
('01010604050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP03', '除了电子以外，后来还陆续证实了中子、质子以及原子、分子的波动性。', '', '01010604050200'),
('01010604050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP04', '德布罗意提出物质波的观念被实验证实，表明电子、质子、原子等粒子不但具有粒子的性质，而且具有波动的性质。', '', '01010604050200'),
('01010604050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03', '量子力学的建立', '', '01010604050000'),
('01010604050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP01', '19、20世纪之交，人们在黑体辐射、光电效应、氢原子光谱等许多类问题中，都发现了经典物理学无法解释的现象。', '', '01010604050300'),
('01010604050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP02', '普朗克黑体辐射理论、爱因斯坦光电效应理论、康普顿散射理论、玻尔氢原子理论以及德布罗意物质波假说等一系列理论在解释实验方面都取得了成功。', '', '01010604050300'),
('01010604050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP03', '1925年，德国物理学家海森堡和玻恩等人对玻尔的氢原子理论进行了推广和改造，使之可以适用于更普遍的情况。', '', '01010604050300'),
('01010604050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04', '量子力学的应用', '', '01010604050000'),
('01010604050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP01', '量子力学被应用到众多具体物理系统中，得到了与实验符合得很好的结果，获得了极大的成功。', '', '01010604050400'),
('01010604050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP02', '借助量子力学，人们深入认识了微观世界的组成、结构和属性。', '', '01010604050400'),
('01010604050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP03', '量子力学推动了核物理和粒子物理的发展。', '', '01010604050400'),
('01010604050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP04', '人们认识了原子、原子核、基本粒子等各个微观层次的物质结构。', '', '01010604050400'),
('01010604050505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP05', '而粒子物理学的发展又促进了天文学和宇宙学的研究。', '', '01010604050400'),
('01010604050506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP06', '从整个宇宙到微小的粒子，人类探索自然的视野前所未有的宽广。', '', '01010604050400'),
('01010604050507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP07', '人们惊讶地发现，世界具有奇妙的结构，最微观层次和最宏观层次的规律，竟有着紧密的联系。', '', '01010604050400'),
('01010604050508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP08', '核物理的发展，还让人们成功地认识并利用了原子核反应堆所释放的能量——核能。', '', '01010604050400'),
('01010604050509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP09', '”量子力学推动了原子、分子物理和光学的发展。', '', '01010604050400'),
('01010604050510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP10', '人们认识了原子的结构，以及原子、分子和电磁场相互作用的方式。', '', '01010604050400'),
('01010604050511', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP11', '在此基础上，发展了各式各样的对原子和电磁场进行精确操控和测量的技术，如激光、核磁共振、原子钟，等等。', '', '01010604050400'),
('01010604050512', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP12', '以利用振荡的磁场测量材料中原子的性质，因此，被广泛地用于化学、生物研究和医学诊断。', '', '01010604050400'),
('01010604050513', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP13', '原子钟利用原子为电磁波校准频率，从而实现了对时间的高精度测量。', '', '01010604050400'),
('01010604050514', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP14', '在日常生活和国家安全中发挥巨大作用的卫星定位技术，其核心部件就是原子钟。', '', '01010604050400'),
('01010604050515', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP15', '量子力学推动了固体物理的发展。', '', '01010604050400'),
('01010604050516', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP16', '人们了解了固体中电子运行的规律，并弄清了为什么固体有导体、绝缘体和半导体之分。', '', '01010604050400'),
('01010604050517', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP17', '量子力学的应用还有很多。', '', '01010604050400'),
('01010604050518', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP18', '毫不夸张地说，在过去的近一百年中，量子力学极大地推动了人类的进步。', '', '01010604050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010605000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05', '原子核', '', NULL),
('01010605010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01', '原子核的组成', '', '01010605000000'),
('01010605010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01', '天然放射现象', '', '01010605010000'),
('01010605010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP01', '受到贝克勒尔的发现的鼓舞，波兰裔法国物理学家玛丽·居里和她的丈夫皮埃尔·居里对铀和含铀的各种矿石进行了深入研究。', '', '01010605010100'),
('01010605010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP02', '放射性元素自发地发出射线的现象，叫作天然放射现象。', '', '01010605010100'),
('01010605010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02', '射线的本质', '', '01010605010000'),
('01010605010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP01', '把放射源铀、钋或镭放入用铅做成的容器中，射线只能从容器的小孔射出，成为细细的一束。', '', '01010605010200'),
('01010605010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP02', '物理学家经过多方面的研究后确认α射线、β射线和γ射线具有以下特征。', '', '01010605010200'),
('01010605010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP03', 'α粒子带正电，电荷量是电子的2倍，质量是氢原子的4倍，其组成与氦原子核相同。', '', '01010605010200'),
('01010605010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP04', '由于α粒子带电，质量又比较大，通过气体时很容易把气体分子中的电子剥离，使气体电离。', '', '01010605010200'),
('01010605010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP05', '是电子流，速度可以接近光速。', '', '01010605010200'),
('01010605010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP06', 'β射线的电离作用较弱，穿透能力较强，很容易穿透黑纸，也能穿透几毫米厚的铝板。', '', '01010605010200'),
('01010605010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP07', 'γ射线的电离作用更弱，穿透能力更强，甚至能穿透几厘米厚的铅板和几十厘米厚的混凝土。', '', '01010605010200'),
('01010605010308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP08', '放射性的强度也不受温度、外界压强的影响。', '', '01010605010200'),
('01010605010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03', '原子核的组成', '', '01010605010000'),
('01010605010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP01', '绝大多数原子核的质量与电荷量之比都大于质子的相应比值。', '', '01010605010300'),
('01010605010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP02', '质子带正电荷，电荷量与一个电子的电荷量相等。', '', '01010605010300'),
('01010605010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP03', '由于中子不带电，原子核所带的电荷等于核内质子电荷的总和。', '', '01010605010300'),
('01010605010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP04', '元素的性质与原子核外的电子有密切的关系。', '', '01010605010300'),
('01010605020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02', '放射性元素的衰变', '', '01010605000000'),
('01010605020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01', '原子核的衰变', '', '01010605020000'),
('01010605020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP01', '铀238放出一个α粒子后，质量数减少4，电荷数减少2，成为新核。', '', '01010605020100'),
('01010605020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP02', '研究还表明，在一定条件下，原子核中的两个中子和两个质子会结合起来形成α粒子，并被释放出来。', '', '01010605020100'),
('01010605020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP03', '原子核的能量也跟原子的能量一样，其变化是不连续的，也只能取一系列不连续的数值，因此，也存在着能级，同样是能级越低越稳定。', '', '01010605020100'),
('01010605020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP04', '当放射性物质连续衰变时，原子核中有的发生α衰变，有的发生β衰变，同时伴随着γ射线辐射。', '', '01010605020100'),
('01010605020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02', '半衰期', '', '01010605020000'),
('01010605020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP01', '放射性同位素衰变的快慢有一定的规律。', '', '01010605020200'),
('01010605020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP02', '不同的放射性元素，半衰期不同，甚至差别非常大。', '', '01010605020200'),
('01010605020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP03', '放射性元素衰变的快慢是由核内部自身的因素决定的，跟原子所处的化学状态和外部条件没有关系。', '', '01010605020200'),
('01010605020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03', '核反应', '', '01010605020000'),
('01010605020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP01', '衰变是原子核的自发变化，科学家更希望人工控制原子核的变化。', '', '01010605020300'),
('01010605020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP02', '与衰变过程一样，在核反应中，质量数守恒、电荷数守恒。', '', '01010605020300'),
('01010605020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04', '放射性同位素及其应用', '', '01010605020000'),
('01010605020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP01', '天然放射性同位素不过40多种，而今天通过核反应生成的人工放射性同位素已达3000多种，每种元素都有了自己的放射性同位素。', '', '01010605020400'),
('01010605020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP02', '丰富的放射性同位素资源在国民经济和科学研究的各个领域中得到了广泛的应用。', '', '01010605020400'),
('01010605020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP03', '工业部门可以使用放射性同位素发出的射线来测厚度。', '', '01010605020400'),
('01010605020504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP04', '像癌细胞那样，不断迅速繁殖的、无法控制的细胞组织，在射线照射下破坏得比健康细胞快。', '', '01010605020400'),
('01010605020505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP05', '利用γ射线照射种子，会使种子的遗传基因发生变异，经过筛选，可以培育出新品种。', '', '01010605020400'),
('01010605020506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP06', '棉花在开花、结桃的时候需要较多的磷肥，把磷肥喷在棉花叶子上，磷肥也能被吸收。', '', '01010605020400'),
('01010605020507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP07', '如果用磷的放射性同位素制成肥料喷在棉花的叶面上，然后每隔一定时间用探测器测量棉株各部位的放射性强度，上面的问题就解决了。', '', '01010605020400'),
('01010605020508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP08', '人体甲状腺的工作需要碘，碘被吸收后聚集在甲状腺内。', '', '01010605020400'),
('01010605020509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP09', '定时用探测器测量甲状腺及邻近组织的放射强度，有助于诊断甲状腺的疾病。', '', '01010605020400'),
('01010605020510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP10', '近年来，有关生物大分子的结构及其功能的研究，几乎都要借助于示踪原子。', '', '01010605020400'),
('01010605020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05', '辐射与安全', '', '01010605020000'),
('01010605020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05.OP01', '人类一直生活在放射性的环境中。', '', '01010605020500'),
('01010605030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03', '核力与结合能', '', '01010605000000'),
('01010605030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01', '核力与四种基本相互作用', '', '01010605030000'),
('01010605030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP01', '现代物理学认为，自然界存在着四种基本相互作用，即引力相互作用、电磁相互作用、强相互作用和弱相互作用。', '', '01010605030100'),
('01010605030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP02', '17世纪下半叶，人们发现了万有引力。', '', '01010605030100'),
('01010605030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP03', '引力是自然界的一种基本相互作用，地面物体所受的重力只是引力在地球表面附近的一种表现。', '', '01010605030100'),
('01010605030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP04', '20世纪，物理学家发现原子核是由若干带正电荷的质子和不带电的中子组成的。', '', '01010605030100'),
('01010605030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP05', '19世纪末20世纪初，物理学家发现了放射现象。', '', '01010605030100'),
('01010605030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP06', '①弱相互作用是引起原子核β衰变的原因，即引起中子—质子转变的原因。', '', '01010605030100'),
('01010605030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02', '结合能', '', '01010605030000'),
('01010605030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP01', '相距很远的两个物体，由于万有引力而相互接近，运动速度越来越大，引力势能转化为动能。', '', '01010605030200'),
('01010605030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP02', '最后撞在一起，一部分动能变成热并散失掉了。', '', '01010605030200'),
('01010605030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP03', '比结合能越大，原子核中核子结合得越牢固，原子核越稳定。', '', '01010605030200'),
('01010605030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03', '质量亏损', '', '01010605030000'),
('01010605030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP01', '原子核的结合能很难直接测量。', '', '01010605030300'),
('01010605030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP02', '质量亏损表明，的确存在着原子核的结合能。', '', '01010605030300'),
('01010605040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04', '核裂变与核聚变', '', '01010605000000'),
('01010605040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01', '核裂变的发现', '', '01010605040000'),
('01010605040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP01', '以铀235为例，核裂变时产生两或三个中子。', '', '01010605040100'),
('01010605040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP02', '原子核的体积非常小，原子内部的空隙很大，如果铀块不够大，中子在铀块中通过时，很有可能碰不到铀核而跑到铀块外面去，链式反应不能继续。', '', '01010605040100'),
('01010605040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP03', '只有当铀块足够大时，核裂变产生的中子才有足够大的概率打中某个铀核，使链式反应进行下去。', '', '01010605040100'),
('01010605040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP04', '铀核裂变时如果生成物不同，释放的能量也有差异。', '', '01010605040100'),
('01010605040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02', '反应堆与核电站', '', '01010605040000'),
('01010605040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP01', '原子核的链式反应也可以在人工控制下进行。', '', '01010605040200'),
('01010605040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP02', '其实，在第一个原子弹制成以前，科学家们已经实现了核能的可控释放。', '', '01010605040200'),
('01010605040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP03', '1942年，美籍意大利物理学家费米就主持建立了世界上第一个称为“核反应堆”的装置，首次通过可控制的链式反应实现了核能的释放。', '', '01010605040200'),
('01010605040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP04', '常用的慢化剂有石墨、重水和普通水（也叫轻水）。', '', '01010605040200'),
('01010605040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP05', '为了调节中子数目以控制反应速度，还需要在铀棒之间插进一些镉棒。', '', '01010605040200'),
('01010605040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP06', '核燃料发生核裂变释放的能量使反应区温度升高。', '', '01010605040200'),
('01010605040307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP07', '水或液态的金属钠等流体在反应堆内外循环流动，把反应堆内的热量传输出去，用于发电，同时也使反应堆冷却。', '', '01010605040200'),
('01010605040308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP08', '在热交换器内，第二回路中的水被加热生成高温高压蒸汽，驱动汽轮机运转。', '', '01010605040200'),
('01010605040309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP09', '建造核电站时需要特别注意防止放射线和放射性物质的泄漏，以避免射线对人体的伤害和放射性物质对水源、空气和工作场所造成放射性污染。', '', '01010605040200'),
('01010605040310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP10', '核反应堆中的核废料具有很强的放射性，需要装入特制的容器，深埋地下。', '', '01010605040200'),
('01010605040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03', '核聚变', '', '01010605040000'),
('01010605040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP01', '当物质的温度达到几百万开尔文时，剧烈的热运动使得一部分原子核具有足够的动能，可以克服库仑斥力，碰撞时十分接近，发生核聚变。', '', '01010605040300'),
('01010605040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP02', '太阳的主要成分是氢。', '', '01010605040300'),
('01010605040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP03', '现在地球上消耗的能量，追根溯源，绝大部分还是来自太阳，即太阳内部核聚变时释放的核能。', '', '01010605040300'),
('01010605040404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP04', '太阳在“核燃烧”的过程中“体重”不断减轻。', '', '01010605040300'),
('01010605040405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP05', '太阳的寿命已经有50亿年了。', '', '01010605040300'),
('01010605040406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP06', '目前，人工产生的热核反应主要用在核武器上，那就是氢弹。', '', '01010605040300'),
('01010605040407', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP07', '与核裂变相比，核聚变有很多优点。', '', '01010605040300'),
('01010605040408', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP08', '利用核聚变物质的惯性进行约束。', '', '01010605040300'),
('01010605040409', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP09', '总的来说，实现受控核聚变还有一段很长的路要走。', '', '01010605040300'),
('01010605050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05', '“基本”粒子', '', '01010605000000'),
('01010605050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01', '发现新粒子', '', '01010605050000'),
('01010605050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP01', '从20世纪30年代以来，人们在对宇宙线的研究中陆续发现了一些新的粒子。', '', '01010605050100'),
('01010605050202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP02', '1932年发明的粒子加速器，能使带电粒子加速到很高的能量。', '', '01010605050100'),
('01010605050203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP03', '利用加速器和对撞机，人们发现了更多的粒子。', '', '01010605050100'),
('01010605050204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP04', '粒子物理的研究不再单纯依赖宇宙线。', '', '01010605050100'),
('01010605050205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP05', '高能量的对撞机往往极为庞大和复杂。', '', '01010605050100'),
('01010605050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02', '粒子的分类', '', '01010605050000'),
('01010605050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP01', '现在已经发现的粒子达400多种。', '', '01010605050200'),
('01010605050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP02', '强子是参与强相互作用的粒子。', '', '01010605050200'),
('01010605050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP03', '质子和中子都是强子。', '', '01010605050200'),
('01010605050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP04', '轻子不参与强相互作用。', '', '01010605050200'),
('01010605050305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP05', '最早发现的轻子是电子，后来发现的轻子有电子中微子、μ子、μ子中微子以及τ子和τ子中微子。', '', '01010605050200'),
('01010605050306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP06', '每种轻子都有对应的反粒子。', '', '01010605050200'),
('01010605050307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP07', '现代实验还没有发现轻子的内部结构。', '', '01010605050200'),
('01010605050308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP08', '光子传递电磁相互作用，中间玻色子传递弱相互作用，胶子传递强相互作用。', '', '01010605050200'),
('01010605050309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP09', '希格斯玻色子是希格斯场的量子激发。', '', '01010605050200'),
('01010605050310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP10', '基本粒子因与希格斯场耦合而获得质量。', '', '01010605050200'),
('01010605050311', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP11', '2012年，欧洲核子研究中心利用大型强子对撞机发现了希格斯玻色子。', '', '01010605050200'),
('01010605050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03', '研究样例', '', '01010605050000'),
('01010605050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP01', '①燃气灶烧水时的能量转化和转移的情况如下。', '', '01010605050300'),
('01010605050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP02', '记录数据时室温为26℃，基本上保证了实验环境温度的恒定。', '', '01010605050300'),
('01010605050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP03', '为减小水在高温时的放热损失，应缩短水在高温时的加热时间。', '', '01010605050300'),
('01010605050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP04', '用实验检验研究结论的正确性。', '', '01010605050300'),
('01010605050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04', '参考选题', '', '01010605050000'),
('01010605050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP01', '使用热水器洗澡时，常有两种调温习惯：一种是把加热后的水温直接调节至洗澡的合适温度，如38℃左右；另一种是把热水器的加热温度调到更高，如50℃左右，再通过淋浴水龙头加兑冷水至合适水温，后者可以获得更大的适温水量。', '', '01010605050400'),
('01010605050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP02', '热水器工作前，先观察燃气表示数，测量加热前水的温度。', '', '01010605050400'),
('01010605050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP03', '观察燃气表示数，记录所用去的燃气体积。', '', '01010605050400'),
('01010605050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP04', '如果已知被加热水的体积，查找燃气的热值，还可以算出两种情况下热效率的大小。', '', '01010605050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010601000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01', '分子动理论', '', NULL),
('01010601010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01', '分子动理论的基本内容', '', '01010601000000'),
('01010601010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01', '物体是由大量分子组成的', '', '01010601010000'),
('01010601010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP01', '人们用肉眼无法直接看到分子，就是用高倍的光学显微镜也看不到。', '', '01010601010100'),
('01010601010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP02', '直至1982年，人们研制了能放大几亿倍的扫描隧道显微镜③，才观察到物质表面原子的排列。', '', '01010601010100'),
('01010601010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02', '分子热运动', '', '01010601010000'),
('01010601010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP01', '扩散现象是物质分子永不停息地做无规则运动的证据之一。', '', '01010601010200'),
('01010601010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP02', '扩散现象在科学技术中有很多应用。', '', '01010601010200'),
('01010601010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP03', '19世纪初，一些人观察到，悬浮在液体中的小颗粒总在不停地运动。', '', '01010601010200'),
('01010601010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP04', '从实验结果可以看出，小炭粒的运动是无规则的，温度越高，小炭粒的运动越明显。', '', '01010601010200'),
('01010601010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP05', '在扩散现象中，温度越高，扩散得越快。', '', '01010601010200'),
('01010601010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP06', '观察布朗运动，温度越高，悬浮微粒的运动就越明显。', '', '01010601010200'),
('01010601010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP07', '温度是分子热运动剧烈程度的标志。', '', '01010601010200'),
('01010601010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03', '分子间的作用力', '', '01010601010000'),
('01010601010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP01', '水和酒精混合后的总体积变小了。', '', '01010601010300'),
('01010601010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP02', '当用力拉伸物体时，物体内各部分之间要产生反抗拉伸的作用力，此时分子间的作用力表现为引力。', '', '01010601010300'),
('01010601010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP03', '当用力压缩物体时，物体内各部分之间会产生反抗压缩的作用力，此时分子间的作用力表现为斥力。', '', '01010601010300'),
('01010601010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP04', '原子内部有带正电的原子核和带负电的电子。', '', '01010601010300'),
('01010601010400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP04', '分子动理论', '', '01010601010000'),
('01010601020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02', '实验：用油膜法估测油酸分子的大小', '', '01010601000000'),
('01010601020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP01', '实验思路', '', '01010601020000'),
('01010601020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP02', '物理量的测量', '', '01010601020000'),
('01010601020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP03', '数据分析', '', '01010601020000'),
('01010601030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03', '分子运动速率分布规律', '', '01010601000000'),
('01010601030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01', '气体分子运动的特点', '', '01010601030000'),
('01010601030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP01', '热现象与大量分子热运动的统计规律有关。', '', '01010601030100'),
('01010601030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP02', '要研究气体的热现象，就要了解气体分子运动的特点。', '', '01010601030100'),
('01010601030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP03', '液体变为气体后，体积要增大上千倍，可见，气体分子间距离大约是分子直径的10倍。', '', '01010601030100'),
('01010601030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP04', '分子的大小相对分子间的空隙来说很小，所以，可以把气体分子视为质点。', '', '01010601030100'),
('01010601030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP05', '分子的运动杂乱无章，在某一时刻，向着任何一个方向运动的分子都有，而且向各个方向运动的分子数目几乎相等。', '', '01010601030100'),
('01010601030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02', '分子运动速率分布图像', '', '01010601030000'),
('01010601030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP01', '尽管分子做无规则运动，速率有大有小，但大量分子的速率却按一定的规律分布。', '', '01010601030200'),
('01010601030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP02', '100℃的氧气，速率大的分子比例较多，其分子的平均速率比0℃的大。', '', '01010601030200'),
('01010601030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03', '气体压强的微观解释', '', '01010601030000'),
('01010601030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP01', '器壁单位面积上受到的压力，就是气体的压强。', '', '01010601030300'),
('01010601030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP02', '从微观角度来看，气体分子的数量是巨大的。', '', '01010601030300'),
('01010601040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04', '分子动能和分子势能', '', '01010601000000'),
('01010601040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01', '分子动能', '', '01010601040000'),
('01010601040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP01', '物体中分子热运动的速率大小不一，所以各个分子的动能也有大有小，而且在不断改变。', '', '01010601040100'),
('01010601040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP02', '温度升高时，分子的热运动加剧，温度越高，分子热运动的平均动能越大。', '', '01010601040100'),
('01010601040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP03', '温度越低，分子热运动的平均动能越小。', '', '01010601040100'),
('01010601040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02', '分子势能', '', '01010601040000'),
('01010601040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP01', '分子间存在着相互作用力，可以证明分子间的作用力所做的功与路径无关，分子组成的系统具有分子势能。', '', '01010601040200'),
('01010601040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP02', '物体的体积变化时，分子间距离将发生变化，因而分子势能随之改变。', '', '01010601040200'),
('01010601040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03', '物体的内能', '', '01010601040000'),
('01010601040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP01', '任何物体都具有内能。', '', '01010601040300'),
('01010601040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP02', '分子热运动的平均动能与温度有关，分子势能与物体的体积有关。', '', '01010601040300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010602000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02', '气体、固体和液体', '', NULL),
('01010602010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01', '温度和温标', '', '01010602000000'),
('01010602010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01', '状态参量与平衡态', '', '01010602010000'),
('01010602010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01.OP01', '要定量地描述系统的状态往往很难，因为有时系统的参量会随时间变化。', '', '01010602010100'),
('01010602010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP02', '热平衡与温度', '', '01010602010000'),
('01010602010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03', '温度计与温标', '', '01010602010000'),
('01010602010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03.OP01', '现代科学中用得更多的是热力学温标。', '', '01010602010300'),
('01010602020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC02', '气体的等温变化', '', '01010602000000'),
('01010602030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03', '气体的等压变化和等容变化', '', '01010602000000'),
('01010602030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP01', '气体的等压变化', '', '01010602030000'),
('01010602030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02', '气体的等容变化', '', '01010602030000'),
('01010602030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02.OP01', '法国科学家查理在分析了实验事实后发现，当一定质量的气体体积一定时，各种气体的压强与温度之间都有线性关系。', '', '01010602030200'),
('01010602030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03', '理想气体', '', '01010602030000'),
('01010602030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP01', '当压强很大、温度很低时，由上述规律计算的结果与实际测量结果有很大的差别。', '', '01010602030300'),
('01010602030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP02', '实际气体的分子之间有相互作用力，但是作用力很小；分子也有大小，但气体分子之间的间距比分子直径大得多；气体分子与器壁碰撞几乎是弹性的，动能损失也很小。', '', '01010602030300'),
('01010602030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP03', '在温度不低于零下几十摄氏度、压强不超过大气压的几倍时，把实际气体当成理想气体来处理，误差很小。', '', '01010602030300'),
('01010602030400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04', '气体实验定律的微观解释', '', '01010602030000'),
('01010602030501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP01', '用分子动理论可以定性解释气体的实验定律。', '', '01010602030400'),
('01010602030502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP02', '一定质量的某种理想气体，温度保持不变时，分子的平均动能是一定的。', '', '01010602030400'),
('01010602030503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP03', '一定质量的某种理想气体，温度升高时，分子的平均动能增大；只有气体的体积同时增大，使分子的数密度减小，才能保持压强不变。', '', '01010602030400'),
('01010602030504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP04', '一定质量的某种理想气体，体积保持不变时，分子的数密度保持不变。', '', '01010602030400'),
('01010602040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04', '固体', '', '01010602000000'),
('01010602040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01', '晶体和非晶体', '', '01010602040000'),
('01010602040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP01', '固体可以分为晶体和非晶体两类。', '', '01010602040100'),
('01010602040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP02', '石英、云母①、明矾、食盐、硫酸铜、味精等是晶体。', '', '01010602040100'),
('01010602040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP03', '玻璃、蜂蜡、松香、沥青、橡胶等是非晶体。', '', '01010602040100'),
('01010602040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP04', '非晶体没有规则的外形。', '', '01010602040100'),
('01010602040205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP05', '除了形状是否规则外，晶体和非晶体在物理性质上也有所不同。', '', '01010602040100'),
('01010602040206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP06', '从实验结果可以看出，玻璃片上石蜡熔化区域的形状近似于圆形，表明玻璃沿各个方向的导热性能相同；云母片上石蜡熔化区域的形状呈椭圆形，表明云母沿不同方向的导热性能不同。', '', '01010602040100'),
('01010602040207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP07', '蔗糖受潮后粘在一起形成的糖块，看起来没有确定的几何形状。', '', '01010602040100'),
('01010602040208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP08', '粘在一起的糖块也是多晶体，单个的蔗糖晶体颗粒是单晶体。', '', '01010602040100'),
('01010602040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02', '晶体的微观结构', '', '01010602040000'),
('01010602040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP01', '在20世纪70年代，人们又用电子显微镜观察到了铀、钍原子的像。', '', '01010602040200'),
('01010602040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP02', '1982年，人们用扫描隧道显微镜观察到物质表面原子的排列。', '', '01010602040200'),
('01010602040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP03', '在各种晶体中，原子（或分子、离子）都是按照一定的规则排列的，具有空间上的周期性。', '', '01010602040200'),
('01010602040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP04', '石墨是层状结构，层与层之间距离较大，原子间的作用力比较弱，所以石墨质地松软，可以用来制作粉状润滑剂；金刚石中碳原子间的作用力很强，所以金刚石有很大的硬度，可以用来切割玻璃。', '', '01010602040200'),
('01010602040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP05', '同种物质也可能以晶体和非晶体两种不同的形态出现，也就是说，物质是晶体还是非晶体，并不是绝对的。', '', '01010602040200'),
('01010602040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP06', '有些非晶体在一定条件下也可以转化为晶体。', '', '01010602040200'),
('01010602050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05', '液体', '', '01010602000000'),
('01010602050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP01', '液体的表面张力', '', '01010602050000'),
('01010602050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02', '浸润和不浸润', '', '01010602050000'),
('01010602050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP01', '管的内径越小，上述现象就越明显。', '', '01010602050200'),
('01010602050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP02', '有些物体内部有许多细小的孔道，孔道起到了毛细管的作用。', '', '01010602050200'),
('01010602050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP03', '当液体和与之接触的固体的相互作用比液体分子之间的相互作用强时，液体能够浸润固体。', '', '01010602050200'),
('01010602050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP04', '如果把水银滴到干净的铅板上，水银就会附着在铅板上，很难擦去。', '', '01010602050200'),
('01010602050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03', '液晶', '', '01010602050000'),
('01010602050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP01', '液晶在现代生活中扮演着重要角色，广泛应用于手机屏幕、平板电视等显示设备中。', '', '01010602050300'),
('01010602050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP02', '液晶态既具有液体的流动性，又在一定程度上具有晶体分子的规则排列的性质。', '', '01010602050300'),
('01010602050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP03', '有些物质在特定的温度范围之内具有液晶态；另一些物质，在适当的溶剂中溶解时，在一定的浓度范围具有液晶态。', '', '01010602050300'),
('01010602050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP04', '分子取向排列的液晶具有光学各向异性，具体地说，沿分子长轴方向上的折射率不同于沿短轴的。', '', '01010602050300'),
('01010602050405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP05', '入射光的偏振方向与液晶分子长轴的方向成不同夹角时，液晶对光的折射率不同。', '', '01010602050300'),
('01010602050406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP06', '液晶的研究在生命科学中也有应用。', '', '01010602050300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010603000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03', '热力学定律', '', NULL),
('01010603010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01', '功、热和内能的改变', '', '01010603000000'),
('01010603010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01', '焦耳的实验', '', '01010603010000'),
('01010603010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP01', '在焦耳研究功与热等效性的许多实验中，有两个最具代表性。', '', '01010603010100'),
('01010603010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP02', '一个是让重物下落带动叶片搅拌容器中的水，引起水温上升；另一个是通过电流的热效应给水加热。', '', '01010603010100'),
('01010603010203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP03', '焦耳的多次实验测量表明，尽管各次悬挂重物的质量不同，下落的高度也不一样，但只要重力所做的功相同，容器内水温上升的数值都是相同的，即系统状态的变化是相同的。', '', '01010603010100'),
('01010603010204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP04', '多次实验结果表明，对于同一个系统，如果过程是绝热的，那么不管通过电阻丝的电流或大或小、通电时间或长或短，只要所做的电功相等，则系统温度上升的数值是相同的，即系统的状态就发生了同样的变化。', '', '01010603010100'),
('01010603010205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP05', '两个位置间移动时重力所做的功。', '', '01010603010100'),
('01010603010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02', '功与内能的改变', '', '01010603010000'),
('01010603010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP01', '当温度达到易燃物的燃点时，易燃物发生自燃。', '', '01010603010200'),
('01010603010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP02', '当橡胶塞跳出时，瓶内出现白雾。', '', '01010603010200'),
('01010603010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP03', '以瓶内气体为研究对象，橡胶塞跳出后，瓶内的气体迅速膨胀，系统对外做功，因此，气体的内能迅速减少，瓶内气体温度迅速下降，瓶内水蒸气液化，就会出现大量的雾状小水滴。', '', '01010603010200'),
('01010603010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03', '热与内能的改变', '', '01010603010000'),
('01010603010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03.OP01', '外界对系统做功可以改变系统的热力学状态，而单纯地对系统传热也能改变系统的热力学状态。', '', '01010603010300'),
('01010603020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02', '热力学第一定律', '', '01010603000000'),
('01010603020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01', '热力学第一定律', '', '01010603020000'),
('01010603020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01.OP01', '（1）求上述压缩过程中气体内能的变化量。', '', '01010603020100'),
('01010603020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP02', '热力学第一定律的应用', '', '01010603020000'),
('01010603030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03', '能量守恒定律', '', '01010603000000'),
('01010603030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01', '探索能量守恒的足迹', '', '01010603030000'),
('01010603030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP01', '能量的概念是人类在对物质运动规律进行长期探索中建立起来的。', '', '01010603030100'),
('01010603030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP02', '所有自然现象都涉及能量，人类的任何活动都离不开能量。', '', '01010603030100'),
('01010603030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP03', '能量具有不同的形式，有描述热运动的内能、描述机械运动的机械能、描述光辐射的光能，等等。', '', '01010603030100'),
('01010603030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP04', '不同形式的运动都可以用能量来描述。', '', '01010603030100'),
('01010603030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP05', '在认识自然的进程中，科学家慢慢知道了要用联系的观点去观察自然。', '', '01010603030100'),
('01010603030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP06', '由于能量既抽象又以各种不同的形式广泛存在，所以能量守恒思想的萌芽及产生并不是一蹴而就的。', '', '01010603030100'),
('01010603030207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP07', '从各种实验的验证，再到实验结果的被质疑，科学家们付出了大量的努力。', '', '01010603030100'),
('01010603030208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP08', '从18世纪末到19世纪中叶，不同领域的科学家从不同角度都提出过能量守恒的思想。', '', '01010603030100'),
('01010603030209', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP09', '人类对能量守恒的认识经历了一个由浅入深、由含糊到清晰的过程。', '', '01010603030100'),
('01010603030210', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP10', '俄国化学家盖斯的研究发现，任何一个化学反应，不论是一步完成，还是分几步完成，放出的总热量相同。', '', '01010603030100'),
('01010603030211', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP11', '焦耳的实验精确地测量了做功与传热之间的等价关系，从而为能量守恒定律奠定了牢固的实验基础，也为能量守恒的定量描述迈出了重要的一步。', '', '01010603030100'),
('01010603030212', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP12', '德国医生迈尔通过比对不同地区人血颜色的差异，认识到食物中化学能与内能的等效性，即生物体内能量的输入和输出是平衡的。', '', '01010603030100'),
('01010603030213', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP13', '半个多世纪人类对能量以及能量守恒研究的历史，预示着人们把各分立的领域连成一体的时刻已经到来，也就是到了建立能量转化与守恒定律的时候了。', '', '01010603030100'),
('01010603030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02', '能量守恒定律', '', '01010603030000'),
('01010603030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP01', '在热力学领域内，做功和传热可以改变系统的内能，即系统内能与系统外的能量会发生转化或转移，但能的总量不会改变。', '', '01010603030200'),
('01010603030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP02', '能量守恒是自然界的普遍规律。', '', '01010603030200'),
('01010603030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP03', '根据能量守恒定律，物理学发现和解释了很多科学现象。', '', '01010603030200'),
('01010603030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP04', '在20世纪30年代初，奥地利物理学家泡利根据能量守恒定律预言了中微子。', '', '01010603030200'),
('01010603030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP05', '能量守恒定律经受住了新的检验。', '', '01010603030200'),
('01010603030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP06', '能量守恒定律的发现是科学史上的重大事件。', '', '01010603030200'),
('01010603030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03', '永动机不可能制成', '', '01010603030000'),
('01010603030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP01', '“问题”栏目中的饮水小鸭不是永动机。', '', '01010603030300'),
('01010603030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP02', '小鸭的头和身由两个玻璃球构成，并通过细玻璃管相连。', '', '01010603030300'),
('01010603030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP03', '下球盛有一些乙醚，上球连同尖嘴被吸水毛毡包起来。', '', '01010603030300'),
('01010603030404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP04', '永动机的思想违背了能量守恒定律，所以是不可能制成的。', '', '01010603030300'),
('01010603030405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP05', '应该指出的是，制造永动机的千万次失败使人们的头脑冷静下来，开始在更深层次寻找失败的原因。', '', '01010603030300'),
('01010603030406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP06', '国科学院宣布“不再审查有关永动机的一切设计”。', '', '01010603030300'),
('01010603040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04', '热力学第二定律', '', '01010603000000'),
('01010603040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01', '热力学第二定律', '', '01010603040000'),
('01010603040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP01', '在电冰箱的实例中，热量的确从低温物体——冰箱内的食品，传到了高温物体——冰箱外的空气。', '', '01010603040100'),
('01010603040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP02', '从能量的角度看，热机的工作分为两个阶段：第一个阶段是燃烧燃料，把燃料中的化学能变成工作物质的内能；第二个阶段是工作物质对外做功，把自己的内能变成机械能。', '', '01010603040100'),
('01010603040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP03', '热力学第二定律的开尔文表述阐述了机械能与内能转化的方向性：机械能可以全部转化为内能，而内能无法全部用来做功以转化成机械能。', '', '01010603040100'),
('01010603040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP04', '可以证明，热力学第二定律的克劳修斯表述和开尔文表述是等价的。', '', '01010603040100'),
('01010603040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02', '能源是有限的', '', '01010603040000'),
('01010603040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP01', '自然界的一切变化，人类社会的所有活动，都伴随着能量的转移和转化，能量是一切物质运动的源泉，是一切生命活动的基础。', '', '01010603040200'),
('01010603040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP02', '能量在数量上虽然守恒，但其转移和转化却具有方向性。', '', '01010603040200'),
('01010603040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP03', '核能、生物能……最终都转化成内能，流散到周围的环境中。', '', '01010603040200'),
('01010603040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP04', '周围环境中的内能很难再利用，而机械能、光能、化学能、电能相对于周围环境中的内能来说，可利用的品质要高。', '', '01010603040200'),
('01010603040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP05', '能源的使用过程中虽然能的总量保持守恒，但能量的品质下降了。', '', '01010603040200'),
('01010603040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP06', '虽然能量总量不会减少，但能源会逐步减少，因此能源是有限的资源。', '', '01010603040200'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010604000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04', '原子结构和波粒二象性', '', NULL),
('01010604010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01', '普朗克黑体辐射理论', '', '01010604000000'),
('01010604010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01', '黑体与黑体辐射', '', '01010604010000'),
('01010604010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP01', '19世纪，由于冶金、星体测温等需求，人们对热辐射进行了大量的研究。', '', '01010604010100'),
('01010604010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP02', '研究表明，对于一般材料的物体，辐射电磁波的情况除了与温度有关，还与材料的种类及表面状况有关，而黑体辐射电磁波的强度按波长的分布只与黑体的温度有关。', '', '01010604010100'),
('01010604010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02', '黑体辐射的实验规律', '', '01010604010000'),
('01010604010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP01', '利用分光技术和热电偶等设备，可以测出黑体辐射电磁波的强度按波长分布的情况。', '', '01010604010200'),
('01010604010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP02', '从中可以看出，随着温度的升高，一方面，各种波长的辐射强度都有增加，另一方面，辐射强度的极大值向波长较短的方向移动。', '', '01010604010200'),
('01010604010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP03', '德国物理学家维恩在1896年、英国物理学家瑞利在1900年，分别提出了辐射强度按波长分布的理论公式。', '', '01010604010200'),
('01010604010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP04', '维恩公式在短波区与实验非常接近，在长波区则与实验偏离较大；瑞利公式在长波区与实验基本一致，但在短波区与实验严重不符。', '', '01010604010200'),
('01010604010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP05', '为了得出同全部实验相符的黑体辐射公式，德国物理学家普朗克做了多种尝试。', '', '01010604010200'),
('01010604010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03', '能量子', '', '01010604010000'),
('01010604010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP01', '弹簧振子的能量不一定是某个最小值的整数倍。', '', '01010604010300'),
('01010604010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP02', '而普朗克的假设则认为微观粒子的能量是量子化的，或者说微观粒子的能量是分立的。', '', '01010604010300'),
('01010604010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP03', '普朗克本人因此获得了1918年的诺贝尔物理学奖。', '', '01010604010300'),
('01010604020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02', '光电效应', '', '01010604000000'),
('01010604020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01', '光电效应的实验规律', '', '01010604020000'),
('01010604020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP01', '1887年，赫兹在研究电磁波的实验中偶尔发现，接收电路的间隙如果受到光照，就更容易产生电火花。', '', '01010604020100'),
('01010604020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP02', '科学家通过实验发现了以下规律。', '', '01010604020100'),
('01010604020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP03', '实验表明，不同金属的截止频率不同。', '', '01010604020100'),
('01010604020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP04', '在光照条件不变的情况下，随着所加电压的增大，光电流趋于一个饱和值。', '', '01010604020100'),
('01010604020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP05', '实验表明，在光的频率不变的情况下，入射光越强，饱和电流越大。', '', '01010604020100'),
('01010604020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02', '光电效应经典解释中的疑难', '', '01010604020000'),
('01010604020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP01', '当光照射金属表面时，电子会吸收光的能量。', '', '01010604020200'),
('01010604020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP02', '光越强，逸出的电子数越多，光电流也就越大。', '', '01010604020200'),
('01010604020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP03', '●不管光的频率如何，只要光足够强，电子都可以获得足够能量从而逸出表面，不应存在截止频率。', '', '01010604020200'),
('01010604020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03', '爱因斯坦的光电效应理论', '', '01010604020000'),
('01010604020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP01', '对于光电效应的解释，爱因斯坦是在普朗克量子假说的基础上作出的。', '', '01010604020300'),
('01010604020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP02', '从下面的讨论可以看出，爱因斯坦光电效应方程可以很好地解释光电效应实验中的各种现象。', '', '01010604020300'),
('01010604020403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP03', '●电子一次性吸收光子的全部能量，不需要积累能量的时间，光电流自然几乎是瞬时产生的。', '', '01010604020300'),
('01010604020404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP04', '爱因斯坦由于提出了光电效应理论而获得1921年的诺贝尔物理学奖。', '', '01010604020300'),
('01010604020405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP05', '金属表面的光子数较多，照射金属时产生的光电子较多，因而饱和电流较大。', '', '01010604020300'),
('01010604020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04', '康普顿效应和光子的动量', '', '01010604020000'),
('01010604020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP01', '光可以与介质中的物质微粒发生散射，改变传播方向。', '', '01010604020400'),
('01010604020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP02', '康普顿效应让人们对光子有了更深入的认识。', '', '01010604020400'),
('01010604020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP03', '康普顿因此获得了1927年的诺贝尔物理学奖。', '', '01010604020400'),
('01010604020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05', '光的波粒二象性', '', '01010604020000'),
('01010604020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP01', '众所周知，在麦克斯韦的电磁理论建立之后，人们认识到光是一种电磁波，从而光的波动说被普遍接受，人们不再认为光是由粒子组成的。', '', '01010604020500'),
('01010604020602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP02', '而爱因斯坦的光电效应理论和康普顿效应理论表明，光在某些方面确实会表现得像是由一些粒子（即一个个有确定能量和动量的“光子”）组成的。', '', '01010604020500'),
('01010604020603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP03', '人们意识到，光既具有波动性，又具有粒子性。', '', '01010604020500'),
('01010604020604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP04', '从牛顿的微粒说、惠更斯和托马斯·杨的波动说，到麦克斯韦的电磁理论，再到爱因斯坦的光子理论乃至量子电动力学，人类对光的认识构成了一部科学史诗。', '', '01010604020500'),
('01010604030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03', '原子的核式结构模型', '', '01010604000000'),
('01010604030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01', '电子的发现', '', '01010604030000'),
('01010604030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP01', '为了找到有利于自己的证据，双方都做了许多实验。', '', '01010604030100'),
('01010604030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP02', '由实验测得的阴极射线粒子的比荷是氢离子①（也就是质子）比荷的近两千倍。', '', '01010604030100'),
('01010604030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP03', '金属受热还是放射性物质的自发辐射，都能发射同样的带电粒子——电子。', '', '01010604030100'),
('01010604030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02', '原子的核式结构模型', '', '01010604030000'),
('01010604030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP01', 'α粒子是从放射性物质（如铀和镭）中发射出来的快速运动的粒子，质量为氢原子质量的4倍、电子质量的7300倍。', '', '01010604030200'),
('01010604030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP02', '被散射的α粒子打在荧光屏上会有微弱的闪光产生。', '', '01010604030200'),
('01010604030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP03', '通过显微镜观察闪光就可以记录在某一时间内向某一方向散射的α粒子数。', '', '01010604030200'),
('01010604030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP04', 'α粒子有库仑力的作用，一些α粒子的运动方向改变，也就是发生了α粒子的散射。', '', '01010604030200'),
('01010604030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP05', 'α粒子所占的比例，可以推知原子中电荷的分布情况。', '', '01010604030200'),
('01010604030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP06', '卢瑟福分析了实验数据后认为，事实应该是：占原子质量绝大部分的带正电的物质集中在很小的空间范围。', '', '01010604030200'),
('01010604030307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP07', '1911年，卢瑟福提出了自己的原子结构模型。', '', '01010604030200'),
('01010604030308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP08', '因为正电体很小，当α粒子进入原子区域后，大部分离正电体很远，受到的库仑斥力很小，运动方向几乎不改变。', '', '01010604030200'),
('01010604030309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP09', '只有极少数α粒子在穿过时距离正电体很近，因此受到很强的库仑斥力，发生大角度散射。', '', '01010604030200'),
('01010604030310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP10', '按照卢瑟福的理论，正电体的尺度是很小的，称为原子核。', '', '01010604030200'),
('01010604030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03', '原子核的电荷与尺度', '', '01010604030000'),
('01010604030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP01', '又由于原子是电中性的，可以推算出原子内含有的电子数。', '', '01010604030300'),
('01010604030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP02', '通常用核半径描述核的大小。', '', '01010604030300'),
('01010604040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04', '氢原子光谱和玻尔的原子模型', '', '01010604000000'),
('01010604040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01', '光谱', '', '01010604040000'),
('01010604040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP01', '有的光谱看起来不是一条条分立的谱线，而是连在一起的光带，叫作连续谱。', '', '01010604040100'),
('01010604040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP02', '气体中中性原子的发光光谱都是线状谱，说明原子只发出几种特定频率的光。', '', '01010604040100'),
('01010604040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02', '氢原子光谱的实验规律', '', '01010604040000'),
('01010604040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP01', '光谱的结果显示氢原子只能发出一系列特定波长的光。', '', '01010604040200'),
('01010604040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP02', '巴耳末公式以简洁的形式反映了氢原子的线状光谱的特征。', '', '01010604040200'),
('01010604040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03', '经典理论的困难', '', '01010604040000'),
('01010604040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP01', '卢瑟福的核式结构模型正确地指出了原子核的存在，很好地解释了α粒子散射实验，但跟经典电磁理论发生了矛盾。', '', '01010604040300'),
('01010604040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP02', '核外电子受到原子核的库仑引力的作用，却没有被吸引到原子核上，而是在以一定的速度绕核运动。', '', '01010604040300'),
('01010604040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP03', '随着绕核运动轨道半径的不断变化，电子运动的频率也要不断变化，因此原子辐射电磁波的频率也要不断变化。', '', '01010604040300'),
('01010604040400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04', '玻尔原子理论的基本假设', '', '01010604040000'),
('01010604040501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP01', '丹麦物理学家玻尔意识到了经典理论在解释原子结构方面的困难。', '', '01010604040400'),
('01010604040502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP02', '玻尔的原子结构假说包括以下两方面的内容。', '', '01010604040400'),
('01010604040503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP03', '玻尔认为，原子中的电子在库仑引力的作用下，绕原子核做圆周运动，服从经典力学的规律。', '', '01010604040400'),
('01010604040504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP04', '在玻尔理论中，电子的轨道半径只可能是某些分立的数值。', '', '01010604040400'),
('01010604040505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP05', '当电子在不同的轨道上运动时，原子处于不同的状态，具有不同的能量。', '', '01010604040400'),
('01010604040506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP06', '根据玻尔理论，电子只能在特定轨道上运动，因此，原子的能量也只能取一系列特定的值。', '', '01010604040400'),
('01010604040507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP07', '按照玻尔的观点，电子在一系列定态轨道上运动，不会发生电磁辐射。', '', '01010604040400'),
('01010604040500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05', '玻尔理论对氢光谱的解释', '', '01010604040000'),
('01010604040601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP01', '从玻尔的基本假设出发，运用经典电磁学和经典力学的理论，可以计算氢原子中电子的可能轨道半径及相应的能量。', '', '01010604040500'),
('01010604040602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP02', '处于基态的原子非常稳定。', '', '01010604040500'),
('01010604040603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP03', '处于激发态的原子是不稳定的，会自发地向能量较低的能级跃迁，放出光子，最终回到基态。', '', '01010604040500'),
('01010604040604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP04', '由于原子的能级是分立的，所以放出的光子的能量也是分立的。', '', '01010604040500'),
('01010604040605', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP05', '由于不同的原子具有不同的结构，能级各不相同，因此辐射（或吸收）的光子频率也不相同。', '', '01010604040500'),
('01010604040606', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP06', '各种气体原子的能级不同，跃迁时发射光子的能量各异。', '', '01010604040500'),
('01010604040607', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP07', '街道上的霓虹灯、试电笔中的氖管，都是由灯管内的气体原子从高能级向低能级跃迁而发光的。', '', '01010604040500'),
('01010604040608', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP08', '食盐被灼烧时发的光，也主要是由食盐蒸气中钠原子的能级跃迁而造成的。', '', '01010604040500'),
('01010604040600', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06', '玻尔理论的局限性', '', '01010604040000'),
('01010604040701', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP01', '玻尔的原子理论第一次将量子观念引入原子领域，提出了定态和跃迁的概念。', '', '01010604040600'),
('01010604040702', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP02', '玻尔理论成功地解释了氢原子光谱的实验规律。', '', '01010604040600'),
('01010604040703', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP03', '①玻尔理论的不足之处在于保留了经典粒子的观念，仍然把电子的运动看作经典力学描述下的轨道运动。', '', '01010604040600'),
('01010604040704', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP04', '实际上，根据量子力学，原子中电子的坐标没有确定的值。', '', '01010604040600'),
('01010604040705', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP05', '当原子处于不同的状态时，电子在各处出现的概率是不一样的。', '', '01010604040600'),
('01010604050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05', '粒子的波动性和量子力学的建立', '', '01010604000000'),
('01010604050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01', '粒子的波动性', '', '01010604050000'),
('01010604050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01.OP01', '1924年，法国物理学家德布罗意在对光的波粒二象性、玻尔氢原子理论以及相对论的深入研究的基础上，把波粒二象性推广到实物粒子，如电子、质子等。', '', '01010604050100'),
('01010604050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02', '物质波的实验验证', '', '01010604050000'),
('01010604050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP01', '德布罗意关于实物粒子具有波动性的假说在当时来看是难以理解的。', '', '01010604050200'),
('01010604050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP02', '1929年，德布罗意因提出物质波的假说获得了诺贝尔物理学奖。', '', '01010604050200'),
('01010604050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP03', '除了电子以外，后来还陆续证实了中子、质子以及原子、分子的波动性。', '', '01010604050200'),
('01010604050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP04', '德布罗意提出物质波的观念被实验证实，表明电子、质子、原子等粒子不但具有粒子的性质，而且具有波动的性质。', '', '01010604050200'),
('01010604050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03', '量子力学的建立', '', '01010604050000'),
('01010604050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP01', '19、20世纪之交，人们在黑体辐射、光电效应、氢原子光谱等许多类问题中，都发现了经典物理学无法解释的现象。', '', '01010604050300'),
('01010604050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP02', '普朗克黑体辐射理论、爱因斯坦光电效应理论、康普顿散射理论、玻尔氢原子理论以及德布罗意物质波假说等一系列理论在解释实验方面都取得了成功。', '', '01010604050300'),
('01010604050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP03', '1925年，德国物理学家海森堡和玻恩等人对玻尔的氢原子理论进行了推广和改造，使之可以适用于更普遍的情况。', '', '01010604050300'),
('01010604050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04', '量子力学的应用', '', '01010604050000'),
('01010604050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP01', '量子力学被应用到众多具体物理系统中，得到了与实验符合得很好的结果，获得了极大的成功。', '', '01010604050400'),
('01010604050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP02', '借助量子力学，人们深入认识了微观世界的组成、结构和属性。', '', '01010604050400'),
('01010604050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP03', '量子力学推动了核物理和粒子物理的发展。', '', '01010604050400'),
('01010604050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP04', '人们认识了原子、原子核、基本粒子等各个微观层次的物质结构。', '', '01010604050400'),
('01010604050505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP05', '而粒子物理学的发展又促进了天文学和宇宙学的研究。', '', '01010604050400'),
('01010604050506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP06', '从整个宇宙到微小的粒子，人类探索自然的视野前所未有的宽广。', '', '01010604050400'),
('01010604050507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP07', '人们惊讶地发现，世界具有奇妙的结构，最微观层次和最宏观层次的规律，竟有着紧密的联系。', '', '01010604050400'),
('01010604050508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP08', '核物理的发展，还让人们成功地认识并利用了原子核反应堆所释放的能量——核能。', '', '01010604050400'),
('01010604050509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP09', '”量子力学推动了原子、分子物理和光学的发展。', '', '01010604050400'),
('01010604050510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP10', '人们认识了原子的结构，以及原子、分子和电磁场相互作用的方式。', '', '01010604050400'),
('01010604050511', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP11', '在此基础上，发展了各式各样的对原子和电磁场进行精确操控和测量的技术，如激光、核磁共振、原子钟，等等。', '', '01010604050400'),
('01010604050512', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP12', '以利用振荡的磁场测量材料中原子的性质，因此，被广泛地用于化学、生物研究和医学诊断。', '', '01010604050400'),
('01010604050513', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP13', '原子钟利用原子为电磁波校准频率，从而实现了对时间的高精度测量。', '', '01010604050400'),
('01010604050514', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP14', '在日常生活和国家安全中发挥巨大作用的卫星定位技术，其核心部件就是原子钟。', '', '01010604050400'),
('01010604050515', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP15', '量子力学推动了固体物理的发展。', '', '01010604050400'),
('01010604050516', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP16', '人们了解了固体中电子运行的规律，并弄清了为什么固体有导体、绝缘体和半导体之分。', '', '01010604050400'),
('01010604050517', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP17', '量子力学的应用还有很多。', '', '01010604050400'),
('01010604050518', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP18', '毫不夸张地说，在过去的近一百年中，量子力学极大地推动了人类的进步。', '', '01010604050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010605000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05', '原子核', '', NULL),
('01010605010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01', '原子核的组成', '', '01010605000000'),
('01010605010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01', '天然放射现象', '', '01010605010000'),
('01010605010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP01', '受到贝克勒尔的发现的鼓舞，波兰裔法国物理学家玛丽·居里和她的丈夫皮埃尔·居里对铀和含铀的各种矿石进行了深入研究。', '', '01010605010100'),
('01010605010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP02', '放射性元素自发地发出射线的现象，叫作天然放射现象。', '', '01010605010100'),
('01010605010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02', '射线的本质', '', '01010605010000'),
('01010605010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP01', '把放射源铀、钋或镭放入用铅做成的容器中，射线只能从容器的小孔射出，成为细细的一束。', '', '01010605010200'),
('01010605010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP02', '物理学家经过多方面的研究后确认α射线、β射线和γ射线具有以下特征。', '', '01010605010200'),
('01010605010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP03', 'α粒子带正电，电荷量是电子的2倍，质量是氢原子的4倍，其组成与氦原子核相同。', '', '01010605010200'),
('01010605010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP04', '由于α粒子带电，质量又比较大，通过气体时很容易把气体分子中的电子剥离，使气体电离。', '', '01010605010200'),
('01010605010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP05', '是电子流，速度可以接近光速。', '', '01010605010200'),
('01010605010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP06', 'β射线的电离作用较弱，穿透能力较强，很容易穿透黑纸，也能穿透几毫米厚的铝板。', '', '01010605010200'),
('01010605010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP07', 'γ射线的电离作用更弱，穿透能力更强，甚至能穿透几厘米厚的铅板和几十厘米厚的混凝土。', '', '01010605010200'),
('01010605010308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP08', '放射性的强度也不受温度、外界压强的影响。', '', '01010605010200'),
('01010605010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03', '原子核的组成', '', '01010605010000'),
('01010605010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP01', '绝大多数原子核的质量与电荷量之比都大于质子的相应比值。', '', '01010605010300'),
('01010605010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP02', '质子带正电荷，电荷量与一个电子的电荷量相等。', '', '01010605010300'),
('01010605010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP03', '由于中子不带电，原子核所带的电荷等于核内质子电荷的总和。', '', '01010605010300'),
('01010605010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP04', '元素的性质与原子核外的电子有密切的关系。', '', '01010605010300'),
('01010605020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02', '放射性元素的衰变', '', '01010605000000'),
('01010605020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01', '原子核的衰变', '', '01010605020000'),
('01010605020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP01', '铀238放出一个α粒子后，质量数减少4，电荷数减少2，成为新核。', '', '01010605020100'),
('01010605020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP02', '研究还表明，在一定条件下，原子核中的两个中子和两个质子会结合起来形成α粒子，并被释放出来。', '', '01010605020100'),
('01010605020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP03', '原子核的能量也跟原子的能量一样，其变化是不连续的，也只能取一系列不连续的数值，因此，也存在着能级，同样是能级越低越稳定。', '', '01010605020100'),
('01010605020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP04', '当放射性物质连续衰变时，原子核中有的发生α衰变，有的发生β衰变，同时伴随着γ射线辐射。', '', '01010605020100'),
('01010605020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02', '半衰期', '', '01010605020000'),
('01010605020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP01', '放射性同位素衰变的快慢有一定的规律。', '', '01010605020200'),
('01010605020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP02', '不同的放射性元素，半衰期不同，甚至差别非常大。', '', '01010605020200'),
('01010605020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP03', '放射性元素衰变的快慢是由核内部自身的因素决定的，跟原子所处的化学状态和外部条件没有关系。', '', '01010605020200'),
('01010605020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03', '核反应', '', '01010605020000'),
('01010605020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP01', '衰变是原子核的自发变化，科学家更希望人工控制原子核的变化。', '', '01010605020300'),
('01010605020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP02', '与衰变过程一样，在核反应中，质量数守恒、电荷数守恒。', '', '01010605020300'),
('01010605020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04', '放射性同位素及其应用', '', '01010605020000'),
('01010605020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP01', '天然放射性同位素不过40多种，而今天通过核反应生成的人工放射性同位素已达3000多种，每种元素都有了自己的放射性同位素。', '', '01010605020400'),
('01010605020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP02', '丰富的放射性同位素资源在国民经济和科学研究的各个领域中得到了广泛的应用。', '', '01010605020400'),
('01010605020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP03', '工业部门可以使用放射性同位素发出的射线来测厚度。', '', '01010605020400'),
('01010605020504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP04', '像癌细胞那样，不断迅速繁殖的、无法控制的细胞组织，在射线照射下破坏得比健康细胞快。', '', '01010605020400'),
('01010605020505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP05', '利用γ射线照射种子，会使种子的遗传基因发生变异，经过筛选，可以培育出新品种。', '', '01010605020400'),
('01010605020506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP06', '棉花在开花、结桃的时候需要较多的磷肥，把磷肥喷在棉花叶子上，磷肥也能被吸收。', '', '01010605020400'),
('01010605020507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP07', '如果用磷的放射性同位素制成肥料喷在棉花的叶面上，然后每隔一定时间用探测器测量棉株各部位的放射性强度，上面的问题就解决了。', '', '01010605020400'),
('01010605020508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP08', '人体甲状腺的工作需要碘，碘被吸收后聚集在甲状腺内。', '', '01010605020400'),
('01010605020509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP09', '定时用探测器测量甲状腺及邻近组织的放射强度，有助于诊断甲状腺的疾病。', '', '01010605020400'),
('01010605020510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP10', '近年来，有关生物大分子的结构及其功能的研究，几乎都要借助于示踪原子。', '', '01010605020400'),
('01010605020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05', '辐射与安全', '', '01010605020000'),
('01010605020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05.OP01', '人类一直生活在放射性的环境中。', '', '01010605020500'),
('01010605030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03', '核力与结合能', '', '01010605000000'),
('01010605030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01', '核力与四种基本相互作用', '', '01010605030000'),
('01010605030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP01', '现代物理学认为，自然界存在着四种基本相互作用，即引力相互作用、电磁相互作用、强相互作用和弱相互作用。', '', '01010605030100'),
('01010605030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP02', '17世纪下半叶，人们发现了万有引力。', '', '01010605030100'),
('01010605030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP03', '引力是自然界的一种基本相互作用，地面物体所受的重力只是引力在地球表面附近的一种表现。', '', '01010605030100'),
('01010605030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP04', '20世纪，物理学家发现原子核是由若干带正电荷的质子和不带电的中子组成的。', '', '01010605030100'),
('01010605030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP05', '19世纪末20世纪初，物理学家发现了放射现象。', '', '01010605030100'),
('01010605030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP06', '①弱相互作用是引起原子核β衰变的原因，即引起中子—质子转变的原因。', '', '01010605030100'),
('01010605030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02', '结合能', '', '01010605030000'),
('01010605030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP01', '相距很远的两个物体，由于万有引力而相互接近，运动速度越来越大，引力势能转化为动能。', '', '01010605030200'),
('01010605030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP02', '最后撞在一起，一部分动能变成热并散失掉了。', '', '01010605030200'),
('01010605030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP03', '比结合能越大，原子核中核子结合得越牢固，原子核越稳定。', '', '01010605030200'),
('01010605030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03', '质量亏损', '', '01010605030000'),
('01010605030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP01', '原子核的结合能很难直接测量。', '', '01010605030300'),
('01010605030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP02', '质量亏损表明，的确存在着原子核的结合能。', '', '01010605030300'),
('01010605040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04', '核裂变与核聚变', '', '01010605000000'),
('01010605040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01', '核裂变的发现', '', '01010605040000'),
('01010605040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP01', '以铀235为例，核裂变时产生两或三个中子。', '', '01010605040100'),
('01010605040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP02', '原子核的体积非常小，原子内部的空隙很大，如果铀块不够大，中子在铀块中通过时，很有可能碰不到铀核而跑到铀块外面去，链式反应不能继续。', '', '01010605040100'),
('01010605040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP03', '只有当铀块足够大时，核裂变产生的中子才有足够大的概率打中某个铀核，使链式反应进行下去。', '', '01010605040100'),
('01010605040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP04', '铀核裂变时如果生成物不同，释放的能量也有差异。', '', '01010605040100'),
('01010605040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02', '反应堆与核电站', '', '01010605040000'),
('01010605040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP01', '原子核的链式反应也可以在人工控制下进行。', '', '01010605040200'),
('01010605040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP02', '其实，在第一个原子弹制成以前，科学家们已经实现了核能的可控释放。', '', '01010605040200'),
('01010605040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP03', '1942年，美籍意大利物理学家费米就主持建立了世界上第一个称为“核反应堆”的装置，首次通过可控制的链式反应实现了核能的释放。', '', '01010605040200'),
('01010605040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP04', '常用的慢化剂有石墨、重水和普通水（也叫轻水）。', '', '01010605040200'),
('01010605040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP05', '为了调节中子数目以控制反应速度，还需要在铀棒之间插进一些镉棒。', '', '01010605040200'),
('01010605040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP06', '核燃料发生核裂变释放的能量使反应区温度升高。', '', '01010605040200'),
('01010605040307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP07', '水或液态的金属钠等流体在反应堆内外循环流动，把反应堆内的热量传输出去，用于发电，同时也使反应堆冷却。', '', '01010605040200'),
('01010605040308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP08', '在热交换器内，第二回路中的水被加热生成高温高压蒸汽，驱动汽轮机运转。', '', '01010605040200'),
('01010605040309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP09', '建造核电站时需要特别注意防止放射线和放射性物质的泄漏，以避免射线对人体的伤害和放射性物质对水源、空气和工作场所造成放射性污染。', '', '01010605040200'),
('01010605040310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP10', '核反应堆中的核废料具有很强的放射性，需要装入特制的容器，深埋地下。', '', '01010605040200'),
('01010605040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03', '核聚变', '', '01010605040000'),
('01010605040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP01', '当物质的温度达到几百万开尔文时，剧烈的热运动使得一部分原子核具有足够的动能，可以克服库仑斥力，碰撞时十分接近，发生核聚变。', '', '01010605040300'),
('01010605040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP02', '太阳的主要成分是氢。', '', '01010605040300'),
('01010605040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP03', '现在地球上消耗的能量，追根溯源，绝大部分还是来自太阳，即太阳内部核聚变时释放的核能。', '', '01010605040300'),
('01010605040404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP04', '太阳在“核燃烧”的过程中“体重”不断减轻。', '', '01010605040300'),
('01010605040405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP05', '太阳的寿命已经有50亿年了。', '', '01010605040300'),
('01010605040406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP06', '目前，人工产生的热核反应主要用在核武器上，那就是氢弹。', '', '01010605040300'),
('01010605040407', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP07', '与核裂变相比，核聚变有很多优点。', '', '01010605040300'),
('01010605040408', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP08', '利用核聚变物质的惯性进行约束。', '', '01010605040300'),
('01010605040409', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP09', '总的来说，实现受控核聚变还有一段很长的路要走。', '', '01010605040300'),
('01010605050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05', '“基本”粒子', '', '01010605000000'),
('01010605050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01', '发现新粒子', '', '01010605050000'),
('01010605050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP01', '从20世纪30年代以来，人们在对宇宙线的研究中陆续发现了一些新的粒子。', '', '01010605050100'),
('01010605050202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP02', '1932年发明的粒子加速器，能使带电粒子加速到很高的能量。', '', '01010605050100'),
('01010605050203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP03', '利用加速器和对撞机，人们发现了更多的粒子。', '', '01010605050100'),
('01010605050204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP04', '粒子物理的研究不再单纯依赖宇宙线。', '', '01010605050100'),
('01010605050205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP05', '高能量的对撞机往往极为庞大和复杂。', '', '01010605050100'),
('01010605050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02', '粒子的分类', '', '01010605050000'),
('01010605050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP01', '现在已经发现的粒子达400多种。', '', '01010605050200'),
('01010605050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP02', '强子是参与强相互作用的粒子。', '', '01010605050200'),
('01010605050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP03', '质子和中子都是强子。', '', '01010605050200'),
('01010605050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP04', '轻子不参与强相互作用。', '', '01010605050200'),
('01010605050305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP05', '最早发现的轻子是电子，后来发现的轻子有电子中微子、μ子、μ子中微子以及τ子和τ子中微子。', '', '01010605050200'),
('01010605050306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP06', '每种轻子都有对应的反粒子。', '', '01010605050200'),
('01010605050307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP07', '现代实验还没有发现轻子的内部结构。', '', '01010605050200'),
('01010605050308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP08', '光子传递电磁相互作用，中间玻色子传递弱相互作用，胶子传递强相互作用。', '', '01010605050200'),
('01010605050309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP09', '希格斯玻色子是希格斯场的量子激发。', '', '01010605050200'),
('01010605050310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP10', '基本粒子因与希格斯场耦合而获得质量。', '', '01010605050200'),
('01010605050311', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP11', '2012年，欧洲核子研究中心利用大型强子对撞机发现了希格斯玻色子。', '', '01010605050200'),
('01010605050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03', '研究样例', '', '01010605050000'),
('01010605050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP01', '①燃气灶烧水时的能量转化和转移的情况如下。', '', '01010605050300'),
('01010605050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP02', '记录数据时室温为26℃，基本上保证了实验环境温度的恒定。', '', '01010605050300'),
('01010605050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP03', '为减小水在高温时的放热损失，应缩短水在高温时的加热时间。', '', '01010605050300'),
('01010605050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP04', '用实验检验研究结论的正确性。', '', '01010605050300'),
('01010605050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04', '参考选题', '', '01010605050000'),
('01010605050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP01', '使用热水器洗澡时，常有两种调温习惯：一种是把加热后的水温直接调节至洗澡的合适温度，如38℃左右；另一种是把热水器的加热温度调到更高，如50℃左右，再通过淋浴水龙头加兑冷水至合适水温，后者可以获得更大的适温水量。', '', '01010605050400'),
('01010605050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP02', '热水器工作前，先观察燃气表示数，测量加热前水的温度。', '', '01010605050400'),
('01010605050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP03', '观察燃气表示数，记录所用去的燃气体积。', '', '01010605050400'),
('01010605050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP04', '如果已知被加热水的体积，查找燃气的热值，还可以算出两种情况下热效率的大小。', '', '01010605050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010601000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01', '分子动理论', '', NULL),
('01010601010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01', '分子动理论的基本内容', '', '01010601000000'),
('01010601010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01', '物体是由大量分子组成的', '', '01010601010000'),
('01010601010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP01', '人们用肉眼无法直接看到分子，就是用高倍的光学显微镜也看不到。', '', '01010601010100'),
('01010601010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP02', '直至1982年，人们研制了能放大几亿倍的扫描隧道显微镜③，才观察到物质表面原子的排列。', '', '01010601010100'),
('01010601010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02', '分子热运动', '', '01010601010000'),
('01010601010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP01', '扩散现象是物质分子永不停息地做无规则运动的证据之一。', '', '01010601010200'),
('01010601010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP02', '扩散现象在科学技术中有很多应用。', '', '01010601010200'),
('01010601010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP03', '19世纪初，一些人观察到，悬浮在液体中的小颗粒总在不停地运动。', '', '01010601010200'),
('01010601010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP04', '从实验结果可以看出，小炭粒的运动是无规则的，温度越高，小炭粒的运动越明显。', '', '01010601010200'),
('01010601010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP05', '在扩散现象中，温度越高，扩散得越快。', '', '01010601010200'),
('01010601010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP06', '观察布朗运动，温度越高，悬浮微粒的运动就越明显。', '', '01010601010200'),
('01010601010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP07', '温度是分子热运动剧烈程度的标志。', '', '01010601010200'),
('01010601010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03', '分子间的作用力', '', '01010601010000'),
('01010601010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP01', '水和酒精混合后的总体积变小了。', '', '01010601010300'),
('01010601010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP02', '当用力拉伸物体时，物体内各部分之间要产生反抗拉伸的作用力，此时分子间的作用力表现为引力。', '', '01010601010300'),
('01010601010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP03', '当用力压缩物体时，物体内各部分之间会产生反抗压缩的作用力，此时分子间的作用力表现为斥力。', '', '01010601010300'),
('01010601010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP04', '原子内部有带正电的原子核和带负电的电子。', '', '01010601010300'),
('01010601010400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP04', '分子动理论', '', '01010601010000'),
('01010601020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02', '实验：用油膜法估测油酸分子的大小', '', '01010601000000'),
('01010601020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP01', '实验思路', '', '01010601020000'),
('01010601020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP02', '物理量的测量', '', '01010601020000'),
('01010601020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP03', '数据分析', '', '01010601020000'),
('01010601030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03', '分子运动速率分布规律', '', '01010601000000'),
('01010601030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01', '气体分子运动的特点', '', '01010601030000'),
('01010601030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP01', '热现象与大量分子热运动的统计规律有关。', '', '01010601030100'),
('01010601030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP02', '要研究气体的热现象，就要了解气体分子运动的特点。', '', '01010601030100'),
('01010601030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP03', '液体变为气体后，体积要增大上千倍，可见，气体分子间距离大约是分子直径的10倍。', '', '01010601030100'),
('01010601030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP04', '分子的大小相对分子间的空隙来说很小，所以，可以把气体分子视为质点。', '', '01010601030100'),
('01010601030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP05', '分子的运动杂乱无章，在某一时刻，向着任何一个方向运动的分子都有，而且向各个方向运动的分子数目几乎相等。', '', '01010601030100'),
('01010601030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02', '分子运动速率分布图像', '', '01010601030000'),
('01010601030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP01', '尽管分子做无规则运动，速率有大有小，但大量分子的速率却按一定的规律分布。', '', '01010601030200'),
('01010601030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP02', '100℃的氧气，速率大的分子比例较多，其分子的平均速率比0℃的大。', '', '01010601030200'),
('01010601030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03', '气体压强的微观解释', '', '01010601030000'),
('01010601030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP01', '器壁单位面积上受到的压力，就是气体的压强。', '', '01010601030300'),
('01010601030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP02', '从微观角度来看，气体分子的数量是巨大的。', '', '01010601030300'),
('01010601040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04', '分子动能和分子势能', '', '01010601000000'),
('01010601040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01', '分子动能', '', '01010601040000'),
('01010601040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP01', '物体中分子热运动的速率大小不一，所以各个分子的动能也有大有小，而且在不断改变。', '', '01010601040100'),
('01010601040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP02', '温度升高时，分子的热运动加剧，温度越高，分子热运动的平均动能越大。', '', '01010601040100'),
('01010601040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP03', '温度越低，分子热运动的平均动能越小。', '', '01010601040100'),
('01010601040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02', '分子势能', '', '01010601040000'),
('01010601040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP01', '分子间存在着相互作用力，可以证明分子间的作用力所做的功与路径无关，分子组成的系统具有分子势能。', '', '01010601040200'),
('01010601040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP02', '物体的体积变化时，分子间距离将发生变化，因而分子势能随之改变。', '', '01010601040200'),
('01010601040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03', '物体的内能', '', '01010601040000'),
('01010601040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP01', '任何物体都具有内能。', '', '01010601040300'),
('01010601040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP02', '分子热运动的平均动能与温度有关，分子势能与物体的体积有关。', '', '01010601040300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010602000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02', '气体、固体和液体', '', NULL),
('01010602010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01', '温度和温标', '', '01010602000000'),
('01010602010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01', '状态参量与平衡态', '', '01010602010000'),
('01010602010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01.OP01', '要定量地描述系统的状态往往很难，因为有时系统的参量会随时间变化。', '', '01010602010100'),
('01010602010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP02', '热平衡与温度', '', '01010602010000'),
('01010602010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03', '温度计与温标', '', '01010602010000'),
('01010602010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03.OP01', '现代科学中用得更多的是热力学温标。', '', '01010602010300'),
('01010602020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC02', '气体的等温变化', '', '01010602000000'),
('01010602030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03', '气体的等压变化和等容变化', '', '01010602000000'),
('01010602030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP01', '气体的等压变化', '', '01010602030000'),
('01010602030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02', '气体的等容变化', '', '01010602030000'),
('01010602030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02.OP01', '法国科学家查理在分析了实验事实后发现，当一定质量的气体体积一定时，各种气体的压强与温度之间都有线性关系。', '', '01010602030200'),
('01010602030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03', '理想气体', '', '01010602030000'),
('01010602030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP01', '当压强很大、温度很低时，由上述规律计算的结果与实际测量结果有很大的差别。', '', '01010602030300'),
('01010602030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP02', '实际气体的分子之间有相互作用力，但是作用力很小；分子也有大小，但气体分子之间的间距比分子直径大得多；气体分子与器壁碰撞几乎是弹性的，动能损失也很小。', '', '01010602030300'),
('01010602030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP03', '在温度不低于零下几十摄氏度、压强不超过大气压的几倍时，把实际气体当成理想气体来处理，误差很小。', '', '01010602030300'),
('01010602030400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04', '气体实验定律的微观解释', '', '01010602030000'),
('01010602030501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP01', '用分子动理论可以定性解释气体的实验定律。', '', '01010602030400'),
('01010602030502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP02', '一定质量的某种理想气体，温度保持不变时，分子的平均动能是一定的。', '', '01010602030400'),
('01010602030503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP03', '一定质量的某种理想气体，温度升高时，分子的平均动能增大；只有气体的体积同时增大，使分子的数密度减小，才能保持压强不变。', '', '01010602030400'),
('01010602030504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP04', '一定质量的某种理想气体，体积保持不变时，分子的数密度保持不变。', '', '01010602030400'),
('01010602040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04', '固体', '', '01010602000000'),
('01010602040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01', '晶体和非晶体', '', '01010602040000'),
('01010602040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP01', '固体可以分为晶体和非晶体两类。', '', '01010602040100'),
('01010602040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP02', '石英、云母①、明矾、食盐、硫酸铜、味精等是晶体。', '', '01010602040100'),
('01010602040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP03', '玻璃、蜂蜡、松香、沥青、橡胶等是非晶体。', '', '01010602040100'),
('01010602040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP04', '非晶体没有规则的外形。', '', '01010602040100'),
('01010602040205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP05', '除了形状是否规则外，晶体和非晶体在物理性质上也有所不同。', '', '01010602040100'),
('01010602040206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP06', '从实验结果可以看出，玻璃片上石蜡熔化区域的形状近似于圆形，表明玻璃沿各个方向的导热性能相同；云母片上石蜡熔化区域的形状呈椭圆形，表明云母沿不同方向的导热性能不同。', '', '01010602040100'),
('01010602040207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP07', '蔗糖受潮后粘在一起形成的糖块，看起来没有确定的几何形状。', '', '01010602040100'),
('01010602040208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP08', '粘在一起的糖块也是多晶体，单个的蔗糖晶体颗粒是单晶体。', '', '01010602040100'),
('01010602040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02', '晶体的微观结构', '', '01010602040000'),
('01010602040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP01', '在20世纪70年代，人们又用电子显微镜观察到了铀、钍原子的像。', '', '01010602040200'),
('01010602040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP02', '1982年，人们用扫描隧道显微镜观察到物质表面原子的排列。', '', '01010602040200'),
('01010602040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP03', '在各种晶体中，原子（或分子、离子）都是按照一定的规则排列的，具有空间上的周期性。', '', '01010602040200'),
('01010602040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP04', '石墨是层状结构，层与层之间距离较大，原子间的作用力比较弱，所以石墨质地松软，可以用来制作粉状润滑剂；金刚石中碳原子间的作用力很强，所以金刚石有很大的硬度，可以用来切割玻璃。', '', '01010602040200'),
('01010602040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP05', '同种物质也可能以晶体和非晶体两种不同的形态出现，也就是说，物质是晶体还是非晶体，并不是绝对的。', '', '01010602040200'),
('01010602040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP06', '有些非晶体在一定条件下也可以转化为晶体。', '', '01010602040200'),
('01010602050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05', '液体', '', '01010602000000'),
('01010602050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP01', '液体的表面张力', '', '01010602050000'),
('01010602050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02', '浸润和不浸润', '', '01010602050000'),
('01010602050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP01', '管的内径越小，上述现象就越明显。', '', '01010602050200'),
('01010602050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP02', '有些物体内部有许多细小的孔道，孔道起到了毛细管的作用。', '', '01010602050200'),
('01010602050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP03', '当液体和与之接触的固体的相互作用比液体分子之间的相互作用强时，液体能够浸润固体。', '', '01010602050200'),
('01010602050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP04', '如果把水银滴到干净的铅板上，水银就会附着在铅板上，很难擦去。', '', '01010602050200'),
('01010602050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03', '液晶', '', '01010602050000'),
('01010602050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP01', '液晶在现代生活中扮演着重要角色，广泛应用于手机屏幕、平板电视等显示设备中。', '', '01010602050300'),
('01010602050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP02', '液晶态既具有液体的流动性，又在一定程度上具有晶体分子的规则排列的性质。', '', '01010602050300'),
('01010602050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP03', '有些物质在特定的温度范围之内具有液晶态；另一些物质，在适当的溶剂中溶解时，在一定的浓度范围具有液晶态。', '', '01010602050300'),
('01010602050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP04', '分子取向排列的液晶具有光学各向异性，具体地说，沿分子长轴方向上的折射率不同于沿短轴的。', '', '01010602050300'),
('01010602050405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP05', '入射光的偏振方向与液晶分子长轴的方向成不同夹角时，液晶对光的折射率不同。', '', '01010602050300'),
('01010602050406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP06', '液晶的研究在生命科学中也有应用。', '', '01010602050300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010603000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03', '热力学定律', '', NULL),
('01010603010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01', '功、热和内能的改变', '', '01010603000000'),
('01010603010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01', '焦耳的实验', '', '01010603010000'),
('01010603010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP01', '在焦耳研究功与热等效性的许多实验中，有两个最具代表性。', '', '01010603010100'),
('01010603010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP02', '一个是让重物下落带动叶片搅拌容器中的水，引起水温上升；另一个是通过电流的热效应给水加热。', '', '01010603010100'),
('01010603010203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP03', '焦耳的多次实验测量表明，尽管各次悬挂重物的质量不同，下落的高度也不一样，但只要重力所做的功相同，容器内水温上升的数值都是相同的，即系统状态的变化是相同的。', '', '01010603010100'),
('01010603010204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP04', '多次实验结果表明，对于同一个系统，如果过程是绝热的，那么不管通过电阻丝的电流或大或小、通电时间或长或短，只要所做的电功相等，则系统温度上升的数值是相同的，即系统的状态就发生了同样的变化。', '', '01010603010100'),
('01010603010205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP05', '两个位置间移动时重力所做的功。', '', '01010603010100'),
('01010603010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02', '功与内能的改变', '', '01010603010000'),
('01010603010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP01', '当温度达到易燃物的燃点时，易燃物发生自燃。', '', '01010603010200'),
('01010603010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP02', '当橡胶塞跳出时，瓶内出现白雾。', '', '01010603010200'),
('01010603010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP03', '以瓶内气体为研究对象，橡胶塞跳出后，瓶内的气体迅速膨胀，系统对外做功，因此，气体的内能迅速减少，瓶内气体温度迅速下降，瓶内水蒸气液化，就会出现大量的雾状小水滴。', '', '01010603010200'),
('01010603010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03', '热与内能的改变', '', '01010603010000'),
('01010603010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03.OP01', '外界对系统做功可以改变系统的热力学状态，而单纯地对系统传热也能改变系统的热力学状态。', '', '01010603010300'),
('01010603020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02', '热力学第一定律', '', '01010603000000'),
('01010603020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01', '热力学第一定律', '', '01010603020000'),
('01010603020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01.OP01', '（1）求上述压缩过程中气体内能的变化量。', '', '01010603020100'),
('01010603020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP02', '热力学第一定律的应用', '', '01010603020000'),
('01010603030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03', '能量守恒定律', '', '01010603000000'),
('01010603030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01', '探索能量守恒的足迹', '', '01010603030000'),
('01010603030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP01', '能量的概念是人类在对物质运动规律进行长期探索中建立起来的。', '', '01010603030100'),
('01010603030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP02', '所有自然现象都涉及能量，人类的任何活动都离不开能量。', '', '01010603030100'),
('01010603030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP03', '能量具有不同的形式，有描述热运动的内能、描述机械运动的机械能、描述光辐射的光能，等等。', '', '01010603030100'),
('01010603030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP04', '不同形式的运动都可以用能量来描述。', '', '01010603030100'),
('01010603030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP05', '在认识自然的进程中，科学家慢慢知道了要用联系的观点去观察自然。', '', '01010603030100'),
('01010603030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP06', '由于能量既抽象又以各种不同的形式广泛存在，所以能量守恒思想的萌芽及产生并不是一蹴而就的。', '', '01010603030100'),
('01010603030207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP07', '从各种实验的验证，再到实验结果的被质疑，科学家们付出了大量的努力。', '', '01010603030100'),
('01010603030208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP08', '从18世纪末到19世纪中叶，不同领域的科学家从不同角度都提出过能量守恒的思想。', '', '01010603030100'),
('01010603030209', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP09', '人类对能量守恒的认识经历了一个由浅入深、由含糊到清晰的过程。', '', '01010603030100'),
('01010603030210', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP10', '俄国化学家盖斯的研究发现，任何一个化学反应，不论是一步完成，还是分几步完成，放出的总热量相同。', '', '01010603030100'),
('01010603030211', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP11', '焦耳的实验精确地测量了做功与传热之间的等价关系，从而为能量守恒定律奠定了牢固的实验基础，也为能量守恒的定量描述迈出了重要的一步。', '', '01010603030100'),
('01010603030212', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP12', '德国医生迈尔通过比对不同地区人血颜色的差异，认识到食物中化学能与内能的等效性，即生物体内能量的输入和输出是平衡的。', '', '01010603030100'),
('01010603030213', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP13', '半个多世纪人类对能量以及能量守恒研究的历史，预示着人们把各分立的领域连成一体的时刻已经到来，也就是到了建立能量转化与守恒定律的时候了。', '', '01010603030100'),
('01010603030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02', '能量守恒定律', '', '01010603030000'),
('01010603030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP01', '在热力学领域内，做功和传热可以改变系统的内能，即系统内能与系统外的能量会发生转化或转移，但能的总量不会改变。', '', '01010603030200'),
('01010603030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP02', '能量守恒是自然界的普遍规律。', '', '01010603030200'),
('01010603030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP03', '根据能量守恒定律，物理学发现和解释了很多科学现象。', '', '01010603030200'),
('01010603030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP04', '在20世纪30年代初，奥地利物理学家泡利根据能量守恒定律预言了中微子。', '', '01010603030200'),
('01010603030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP05', '能量守恒定律经受住了新的检验。', '', '01010603030200'),
('01010603030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP06', '能量守恒定律的发现是科学史上的重大事件。', '', '01010603030200'),
('01010603030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03', '永动机不可能制成', '', '01010603030000'),
('01010603030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP01', '“问题”栏目中的饮水小鸭不是永动机。', '', '01010603030300'),
('01010603030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP02', '小鸭的头和身由两个玻璃球构成，并通过细玻璃管相连。', '', '01010603030300'),
('01010603030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP03', '下球盛有一些乙醚，上球连同尖嘴被吸水毛毡包起来。', '', '01010603030300'),
('01010603030404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP04', '永动机的思想违背了能量守恒定律，所以是不可能制成的。', '', '01010603030300'),
('01010603030405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP05', '应该指出的是，制造永动机的千万次失败使人们的头脑冷静下来，开始在更深层次寻找失败的原因。', '', '01010603030300'),
('01010603030406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP06', '国科学院宣布“不再审查有关永动机的一切设计”。', '', '01010603030300'),
('01010603040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04', '热力学第二定律', '', '01010603000000'),
('01010603040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01', '热力学第二定律', '', '01010603040000'),
('01010603040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP01', '在电冰箱的实例中，热量的确从低温物体——冰箱内的食品，传到了高温物体——冰箱外的空气。', '', '01010603040100'),
('01010603040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP02', '从能量的角度看，热机的工作分为两个阶段：第一个阶段是燃烧燃料，把燃料中的化学能变成工作物质的内能；第二个阶段是工作物质对外做功，把自己的内能变成机械能。', '', '01010603040100'),
('01010603040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP03', '热力学第二定律的开尔文表述阐述了机械能与内能转化的方向性：机械能可以全部转化为内能，而内能无法全部用来做功以转化成机械能。', '', '01010603040100'),
('01010603040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP04', '可以证明，热力学第二定律的克劳修斯表述和开尔文表述是等价的。', '', '01010603040100'),
('01010603040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02', '能源是有限的', '', '01010603040000'),
('01010603040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP01', '自然界的一切变化，人类社会的所有活动，都伴随着能量的转移和转化，能量是一切物质运动的源泉，是一切生命活动的基础。', '', '01010603040200'),
('01010603040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP02', '能量在数量上虽然守恒，但其转移和转化却具有方向性。', '', '01010603040200'),
('01010603040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP03', '核能、生物能……最终都转化成内能，流散到周围的环境中。', '', '01010603040200'),
('01010603040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP04', '周围环境中的内能很难再利用，而机械能、光能、化学能、电能相对于周围环境中的内能来说，可利用的品质要高。', '', '01010603040200'),
('01010603040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP05', '能源的使用过程中虽然能的总量保持守恒，但能量的品质下降了。', '', '01010603040200'),
('01010603040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP06', '虽然能量总量不会减少，但能源会逐步减少，因此能源是有限的资源。', '', '01010603040200'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010604000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04', '原子结构和波粒二象性', '', NULL),
('01010604010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01', '普朗克黑体辐射理论', '', '01010604000000'),
('01010604010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01', '黑体与黑体辐射', '', '01010604010000'),
('01010604010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP01', '19世纪，由于冶金、星体测温等需求，人们对热辐射进行了大量的研究。', '', '01010604010100'),
('01010604010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP02', '研究表明，对于一般材料的物体，辐射电磁波的情况除了与温度有关，还与材料的种类及表面状况有关，而黑体辐射电磁波的强度按波长的分布只与黑体的温度有关。', '', '01010604010100'),
('01010604010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02', '黑体辐射的实验规律', '', '01010604010000'),
('01010604010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP01', '利用分光技术和热电偶等设备，可以测出黑体辐射电磁波的强度按波长分布的情况。', '', '01010604010200'),
('01010604010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP02', '从中可以看出，随着温度的升高，一方面，各种波长的辐射强度都有增加，另一方面，辐射强度的极大值向波长较短的方向移动。', '', '01010604010200'),
('01010604010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP03', '德国物理学家维恩在1896年、英国物理学家瑞利在1900年，分别提出了辐射强度按波长分布的理论公式。', '', '01010604010200'),
('01010604010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP04', '维恩公式在短波区与实验非常接近，在长波区则与实验偏离较大；瑞利公式在长波区与实验基本一致，但在短波区与实验严重不符。', '', '01010604010200'),
('01010604010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP05', '为了得出同全部实验相符的黑体辐射公式，德国物理学家普朗克做了多种尝试。', '', '01010604010200'),
('01010604010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03', '能量子', '', '01010604010000'),
('01010604010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP01', '弹簧振子的能量不一定是某个最小值的整数倍。', '', '01010604010300'),
('01010604010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP02', '而普朗克的假设则认为微观粒子的能量是量子化的，或者说微观粒子的能量是分立的。', '', '01010604010300'),
('01010604010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP03', '普朗克本人因此获得了1918年的诺贝尔物理学奖。', '', '01010604010300'),
('01010604020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02', '光电效应', '', '01010604000000'),
('01010604020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01', '光电效应的实验规律', '', '01010604020000'),
('01010604020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP01', '1887年，赫兹在研究电磁波的实验中偶尔发现，接收电路的间隙如果受到光照，就更容易产生电火花。', '', '01010604020100'),
('01010604020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP02', '科学家通过实验发现了以下规律。', '', '01010604020100'),
('01010604020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP03', '实验表明，不同金属的截止频率不同。', '', '01010604020100'),
('01010604020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP04', '在光照条件不变的情况下，随着所加电压的增大，光电流趋于一个饱和值。', '', '01010604020100'),
('01010604020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP05', '实验表明，在光的频率不变的情况下，入射光越强，饱和电流越大。', '', '01010604020100'),
('01010604020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02', '光电效应经典解释中的疑难', '', '01010604020000'),
('01010604020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP01', '当光照射金属表面时，电子会吸收光的能量。', '', '01010604020200'),
('01010604020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP02', '光越强，逸出的电子数越多，光电流也就越大。', '', '01010604020200'),
('01010604020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP03', '●不管光的频率如何，只要光足够强，电子都可以获得足够能量从而逸出表面，不应存在截止频率。', '', '01010604020200'),
('01010604020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03', '爱因斯坦的光电效应理论', '', '01010604020000'),
('01010604020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP01', '对于光电效应的解释，爱因斯坦是在普朗克量子假说的基础上作出的。', '', '01010604020300'),
('01010604020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP02', '从下面的讨论可以看出，爱因斯坦光电效应方程可以很好地解释光电效应实验中的各种现象。', '', '01010604020300'),
('01010604020403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP03', '●电子一次性吸收光子的全部能量，不需要积累能量的时间，光电流自然几乎是瞬时产生的。', '', '01010604020300'),
('01010604020404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP04', '爱因斯坦由于提出了光电效应理论而获得1921年的诺贝尔物理学奖。', '', '01010604020300'),
('01010604020405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP05', '金属表面的光子数较多，照射金属时产生的光电子较多，因而饱和电流较大。', '', '01010604020300'),
('01010604020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04', '康普顿效应和光子的动量', '', '01010604020000'),
('01010604020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP01', '光可以与介质中的物质微粒发生散射，改变传播方向。', '', '01010604020400'),
('01010604020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP02', '康普顿效应让人们对光子有了更深入的认识。', '', '01010604020400'),
('01010604020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP03', '康普顿因此获得了1927年的诺贝尔物理学奖。', '', '01010604020400'),
('01010604020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05', '光的波粒二象性', '', '01010604020000'),
('01010604020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP01', '众所周知，在麦克斯韦的电磁理论建立之后，人们认识到光是一种电磁波，从而光的波动说被普遍接受，人们不再认为光是由粒子组成的。', '', '01010604020500'),
('01010604020602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP02', '而爱因斯坦的光电效应理论和康普顿效应理论表明，光在某些方面确实会表现得像是由一些粒子（即一个个有确定能量和动量的“光子”）组成的。', '', '01010604020500'),
('01010604020603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP03', '人们意识到，光既具有波动性，又具有粒子性。', '', '01010604020500'),
('01010604020604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP04', '从牛顿的微粒说、惠更斯和托马斯·杨的波动说，到麦克斯韦的电磁理论，再到爱因斯坦的光子理论乃至量子电动力学，人类对光的认识构成了一部科学史诗。', '', '01010604020500'),
('01010604030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03', '原子的核式结构模型', '', '01010604000000'),
('01010604030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01', '电子的发现', '', '01010604030000'),
('01010604030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP01', '为了找到有利于自己的证据，双方都做了许多实验。', '', '01010604030100'),
('01010604030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP02', '由实验测得的阴极射线粒子的比荷是氢离子①（也就是质子）比荷的近两千倍。', '', '01010604030100'),
('01010604030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP03', '金属受热还是放射性物质的自发辐射，都能发射同样的带电粒子——电子。', '', '01010604030100'),
('01010604030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02', '原子的核式结构模型', '', '01010604030000'),
('01010604030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP01', 'α粒子是从放射性物质（如铀和镭）中发射出来的快速运动的粒子，质量为氢原子质量的4倍、电子质量的7300倍。', '', '01010604030200'),
('01010604030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP02', '被散射的α粒子打在荧光屏上会有微弱的闪光产生。', '', '01010604030200'),
('01010604030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP03', '通过显微镜观察闪光就可以记录在某一时间内向某一方向散射的α粒子数。', '', '01010604030200'),
('01010604030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP04', 'α粒子有库仑力的作用，一些α粒子的运动方向改变，也就是发生了α粒子的散射。', '', '01010604030200'),
('01010604030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP05', 'α粒子所占的比例，可以推知原子中电荷的分布情况。', '', '01010604030200'),
('01010604030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP06', '卢瑟福分析了实验数据后认为，事实应该是：占原子质量绝大部分的带正电的物质集中在很小的空间范围。', '', '01010604030200'),
('01010604030307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP07', '1911年，卢瑟福提出了自己的原子结构模型。', '', '01010604030200'),
('01010604030308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP08', '因为正电体很小，当α粒子进入原子区域后，大部分离正电体很远，受到的库仑斥力很小，运动方向几乎不改变。', '', '01010604030200'),
('01010604030309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP09', '只有极少数α粒子在穿过时距离正电体很近，因此受到很强的库仑斥力，发生大角度散射。', '', '01010604030200'),
('01010604030310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP10', '按照卢瑟福的理论，正电体的尺度是很小的，称为原子核。', '', '01010604030200'),
('01010604030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03', '原子核的电荷与尺度', '', '01010604030000'),
('01010604030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP01', '又由于原子是电中性的，可以推算出原子内含有的电子数。', '', '01010604030300'),
('01010604030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP02', '通常用核半径描述核的大小。', '', '01010604030300'),
('01010604040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04', '氢原子光谱和玻尔的原子模型', '', '01010604000000'),
('01010604040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01', '光谱', '', '01010604040000'),
('01010604040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP01', '有的光谱看起来不是一条条分立的谱线，而是连在一起的光带，叫作连续谱。', '', '01010604040100'),
('01010604040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP02', '气体中中性原子的发光光谱都是线状谱，说明原子只发出几种特定频率的光。', '', '01010604040100'),
('01010604040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02', '氢原子光谱的实验规律', '', '01010604040000'),
('01010604040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP01', '光谱的结果显示氢原子只能发出一系列特定波长的光。', '', '01010604040200'),
('01010604040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP02', '巴耳末公式以简洁的形式反映了氢原子的线状光谱的特征。', '', '01010604040200'),
('01010604040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03', '经典理论的困难', '', '01010604040000'),
('01010604040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP01', '卢瑟福的核式结构模型正确地指出了原子核的存在，很好地解释了α粒子散射实验，但跟经典电磁理论发生了矛盾。', '', '01010604040300'),
('01010604040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP02', '核外电子受到原子核的库仑引力的作用，却没有被吸引到原子核上，而是在以一定的速度绕核运动。', '', '01010604040300'),
('01010604040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP03', '随着绕核运动轨道半径的不断变化，电子运动的频率也要不断变化，因此原子辐射电磁波的频率也要不断变化。', '', '01010604040300'),
('01010604040400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04', '玻尔原子理论的基本假设', '', '01010604040000'),
('01010604040501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP01', '丹麦物理学家玻尔意识到了经典理论在解释原子结构方面的困难。', '', '01010604040400'),
('01010604040502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP02', '玻尔的原子结构假说包括以下两方面的内容。', '', '01010604040400'),
('01010604040503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP03', '玻尔认为，原子中的电子在库仑引力的作用下，绕原子核做圆周运动，服从经典力学的规律。', '', '01010604040400'),
('01010604040504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP04', '在玻尔理论中，电子的轨道半径只可能是某些分立的数值。', '', '01010604040400'),
('01010604040505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP05', '当电子在不同的轨道上运动时，原子处于不同的状态，具有不同的能量。', '', '01010604040400'),
('01010604040506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP06', '根据玻尔理论，电子只能在特定轨道上运动，因此，原子的能量也只能取一系列特定的值。', '', '01010604040400'),
('01010604040507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP07', '按照玻尔的观点，电子在一系列定态轨道上运动，不会发生电磁辐射。', '', '01010604040400'),
('01010604040500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05', '玻尔理论对氢光谱的解释', '', '01010604040000'),
('01010604040601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP01', '从玻尔的基本假设出发，运用经典电磁学和经典力学的理论，可以计算氢原子中电子的可能轨道半径及相应的能量。', '', '01010604040500'),
('01010604040602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP02', '处于基态的原子非常稳定。', '', '01010604040500'),
('01010604040603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP03', '处于激发态的原子是不稳定的，会自发地向能量较低的能级跃迁，放出光子，最终回到基态。', '', '01010604040500'),
('01010604040604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP04', '由于原子的能级是分立的，所以放出的光子的能量也是分立的。', '', '01010604040500'),
('01010604040605', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP05', '由于不同的原子具有不同的结构，能级各不相同，因此辐射（或吸收）的光子频率也不相同。', '', '01010604040500'),
('01010604040606', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP06', '各种气体原子的能级不同，跃迁时发射光子的能量各异。', '', '01010604040500'),
('01010604040607', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP07', '街道上的霓虹灯、试电笔中的氖管，都是由灯管内的气体原子从高能级向低能级跃迁而发光的。', '', '01010604040500'),
('01010604040608', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP08', '食盐被灼烧时发的光，也主要是由食盐蒸气中钠原子的能级跃迁而造成的。', '', '01010604040500'),
('01010604040600', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06', '玻尔理论的局限性', '', '01010604040000'),
('01010604040701', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP01', '玻尔的原子理论第一次将量子观念引入原子领域，提出了定态和跃迁的概念。', '', '01010604040600'),
('01010604040702', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP02', '玻尔理论成功地解释了氢原子光谱的实验规律。', '', '01010604040600'),
('01010604040703', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP03', '①玻尔理论的不足之处在于保留了经典粒子的观念，仍然把电子的运动看作经典力学描述下的轨道运动。', '', '01010604040600'),
('01010604040704', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP04', '实际上，根据量子力学，原子中电子的坐标没有确定的值。', '', '01010604040600'),
('01010604040705', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP05', '当原子处于不同的状态时，电子在各处出现的概率是不一样的。', '', '01010604040600'),
('01010604050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05', '粒子的波动性和量子力学的建立', '', '01010604000000'),
('01010604050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01', '粒子的波动性', '', '01010604050000'),
('01010604050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01.OP01', '1924年，法国物理学家德布罗意在对光的波粒二象性、玻尔氢原子理论以及相对论的深入研究的基础上，把波粒二象性推广到实物粒子，如电子、质子等。', '', '01010604050100'),
('01010604050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02', '物质波的实验验证', '', '01010604050000'),
('01010604050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP01', '德布罗意关于实物粒子具有波动性的假说在当时来看是难以理解的。', '', '01010604050200'),
('01010604050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP02', '1929年，德布罗意因提出物质波的假说获得了诺贝尔物理学奖。', '', '01010604050200'),
('01010604050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP03', '除了电子以外，后来还陆续证实了中子、质子以及原子、分子的波动性。', '', '01010604050200'),
('01010604050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP04', '德布罗意提出物质波的观念被实验证实，表明电子、质子、原子等粒子不但具有粒子的性质，而且具有波动的性质。', '', '01010604050200'),
('01010604050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03', '量子力学的建立', '', '01010604050000'),
('01010604050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP01', '19、20世纪之交，人们在黑体辐射、光电效应、氢原子光谱等许多类问题中，都发现了经典物理学无法解释的现象。', '', '01010604050300'),
('01010604050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP02', '普朗克黑体辐射理论、爱因斯坦光电效应理论、康普顿散射理论、玻尔氢原子理论以及德布罗意物质波假说等一系列理论在解释实验方面都取得了成功。', '', '01010604050300'),
('01010604050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP03', '1925年，德国物理学家海森堡和玻恩等人对玻尔的氢原子理论进行了推广和改造，使之可以适用于更普遍的情况。', '', '01010604050300'),
('01010604050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04', '量子力学的应用', '', '01010604050000'),
('01010604050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP01', '量子力学被应用到众多具体物理系统中，得到了与实验符合得很好的结果，获得了极大的成功。', '', '01010604050400'),
('01010604050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP02', '借助量子力学，人们深入认识了微观世界的组成、结构和属性。', '', '01010604050400'),
('01010604050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP03', '量子力学推动了核物理和粒子物理的发展。', '', '01010604050400'),
('01010604050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP04', '人们认识了原子、原子核、基本粒子等各个微观层次的物质结构。', '', '01010604050400'),
('01010604050505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP05', '而粒子物理学的发展又促进了天文学和宇宙学的研究。', '', '01010604050400'),
('01010604050506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP06', '从整个宇宙到微小的粒子，人类探索自然的视野前所未有的宽广。', '', '01010604050400'),
('01010604050507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP07', '人们惊讶地发现，世界具有奇妙的结构，最微观层次和最宏观层次的规律，竟有着紧密的联系。', '', '01010604050400'),
('01010604050508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP08', '核物理的发展，还让人们成功地认识并利用了原子核反应堆所释放的能量——核能。', '', '01010604050400'),
('01010604050509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP09', '”量子力学推动了原子、分子物理和光学的发展。', '', '01010604050400'),
('01010604050510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP10', '人们认识了原子的结构，以及原子、分子和电磁场相互作用的方式。', '', '01010604050400'),
('01010604050511', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP11', '在此基础上，发展了各式各样的对原子和电磁场进行精确操控和测量的技术，如激光、核磁共振、原子钟，等等。', '', '01010604050400'),
('01010604050512', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP12', '以利用振荡的磁场测量材料中原子的性质，因此，被广泛地用于化学、生物研究和医学诊断。', '', '01010604050400'),
('01010604050513', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP13', '原子钟利用原子为电磁波校准频率，从而实现了对时间的高精度测量。', '', '01010604050400'),
('01010604050514', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP14', '在日常生活和国家安全中发挥巨大作用的卫星定位技术，其核心部件就是原子钟。', '', '01010604050400'),
('01010604050515', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP15', '量子力学推动了固体物理的发展。', '', '01010604050400'),
('01010604050516', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP16', '人们了解了固体中电子运行的规律，并弄清了为什么固体有导体、绝缘体和半导体之分。', '', '01010604050400'),
('01010604050517', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP17', '量子力学的应用还有很多。', '', '01010604050400'),
('01010604050518', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP18', '毫不夸张地说，在过去的近一百年中，量子力学极大地推动了人类的进步。', '', '01010604050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010605000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05', '原子核', '', NULL),
('01010605010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01', '原子核的组成', '', '01010605000000'),
('01010605010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01', '天然放射现象', '', '01010605010000'),
('01010605010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP01', '受到贝克勒尔的发现的鼓舞，波兰裔法国物理学家玛丽·居里和她的丈夫皮埃尔·居里对铀和含铀的各种矿石进行了深入研究。', '', '01010605010100'),
('01010605010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP02', '放射性元素自发地发出射线的现象，叫作天然放射现象。', '', '01010605010100'),
('01010605010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02', '射线的本质', '', '01010605010000'),
('01010605010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP01', '把放射源铀、钋或镭放入用铅做成的容器中，射线只能从容器的小孔射出，成为细细的一束。', '', '01010605010200'),
('01010605010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP02', '物理学家经过多方面的研究后确认α射线、β射线和γ射线具有以下特征。', '', '01010605010200'),
('01010605010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP03', 'α粒子带正电，电荷量是电子的2倍，质量是氢原子的4倍，其组成与氦原子核相同。', '', '01010605010200'),
('01010605010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP04', '由于α粒子带电，质量又比较大，通过气体时很容易把气体分子中的电子剥离，使气体电离。', '', '01010605010200'),
('01010605010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP05', '是电子流，速度可以接近光速。', '', '01010605010200'),
('01010605010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP06', 'β射线的电离作用较弱，穿透能力较强，很容易穿透黑纸，也能穿透几毫米厚的铝板。', '', '01010605010200'),
('01010605010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP07', 'γ射线的电离作用更弱，穿透能力更强，甚至能穿透几厘米厚的铅板和几十厘米厚的混凝土。', '', '01010605010200'),
('01010605010308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP08', '放射性的强度也不受温度、外界压强的影响。', '', '01010605010200'),
('01010605010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03', '原子核的组成', '', '01010605010000'),
('01010605010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP01', '绝大多数原子核的质量与电荷量之比都大于质子的相应比值。', '', '01010605010300'),
('01010605010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP02', '质子带正电荷，电荷量与一个电子的电荷量相等。', '', '01010605010300'),
('01010605010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP03', '由于中子不带电，原子核所带的电荷等于核内质子电荷的总和。', '', '01010605010300'),
('01010605010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP04', '元素的性质与原子核外的电子有密切的关系。', '', '01010605010300'),
('01010605020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02', '放射性元素的衰变', '', '01010605000000'),
('01010605020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01', '原子核的衰变', '', '01010605020000'),
('01010605020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP01', '铀238放出一个α粒子后，质量数减少4，电荷数减少2，成为新核。', '', '01010605020100'),
('01010605020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP02', '研究还表明，在一定条件下，原子核中的两个中子和两个质子会结合起来形成α粒子，并被释放出来。', '', '01010605020100'),
('01010605020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP03', '原子核的能量也跟原子的能量一样，其变化是不连续的，也只能取一系列不连续的数值，因此，也存在着能级，同样是能级越低越稳定。', '', '01010605020100'),
('01010605020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP04', '当放射性物质连续衰变时，原子核中有的发生α衰变，有的发生β衰变，同时伴随着γ射线辐射。', '', '01010605020100'),
('01010605020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02', '半衰期', '', '01010605020000'),
('01010605020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP01', '放射性同位素衰变的快慢有一定的规律。', '', '01010605020200'),
('01010605020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP02', '不同的放射性元素，半衰期不同，甚至差别非常大。', '', '01010605020200'),
('01010605020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP03', '放射性元素衰变的快慢是由核内部自身的因素决定的，跟原子所处的化学状态和外部条件没有关系。', '', '01010605020200'),
('01010605020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03', '核反应', '', '01010605020000'),
('01010605020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP01', '衰变是原子核的自发变化，科学家更希望人工控制原子核的变化。', '', '01010605020300'),
('01010605020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP02', '与衰变过程一样，在核反应中，质量数守恒、电荷数守恒。', '', '01010605020300'),
('01010605020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04', '放射性同位素及其应用', '', '01010605020000'),
('01010605020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP01', '天然放射性同位素不过40多种，而今天通过核反应生成的人工放射性同位素已达3000多种，每种元素都有了自己的放射性同位素。', '', '01010605020400'),
('01010605020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP02', '丰富的放射性同位素资源在国民经济和科学研究的各个领域中得到了广泛的应用。', '', '01010605020400'),
('01010605020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP03', '工业部门可以使用放射性同位素发出的射线来测厚度。', '', '01010605020400'),
('01010605020504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP04', '像癌细胞那样，不断迅速繁殖的、无法控制的细胞组织，在射线照射下破坏得比健康细胞快。', '', '01010605020400'),
('01010605020505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP05', '利用γ射线照射种子，会使种子的遗传基因发生变异，经过筛选，可以培育出新品种。', '', '01010605020400'),
('01010605020506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP06', '棉花在开花、结桃的时候需要较多的磷肥，把磷肥喷在棉花叶子上，磷肥也能被吸收。', '', '01010605020400'),
('01010605020507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP07', '如果用磷的放射性同位素制成肥料喷在棉花的叶面上，然后每隔一定时间用探测器测量棉株各部位的放射性强度，上面的问题就解决了。', '', '01010605020400'),
('01010605020508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP08', '人体甲状腺的工作需要碘，碘被吸收后聚集在甲状腺内。', '', '01010605020400'),
('01010605020509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP09', '定时用探测器测量甲状腺及邻近组织的放射强度，有助于诊断甲状腺的疾病。', '', '01010605020400'),
('01010605020510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP10', '近年来，有关生物大分子的结构及其功能的研究，几乎都要借助于示踪原子。', '', '01010605020400'),
('01010605020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05', '辐射与安全', '', '01010605020000'),
('01010605020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05.OP01', '人类一直生活在放射性的环境中。', '', '01010605020500'),
('01010605030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03', '核力与结合能', '', '01010605000000'),
('01010605030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01', '核力与四种基本相互作用', '', '01010605030000'),
('01010605030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP01', '现代物理学认为，自然界存在着四种基本相互作用，即引力相互作用、电磁相互作用、强相互作用和弱相互作用。', '', '01010605030100'),
('01010605030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP02', '17世纪下半叶，人们发现了万有引力。', '', '01010605030100'),
('01010605030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP03', '引力是自然界的一种基本相互作用，地面物体所受的重力只是引力在地球表面附近的一种表现。', '', '01010605030100'),
('01010605030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP04', '20世纪，物理学家发现原子核是由若干带正电荷的质子和不带电的中子组成的。', '', '01010605030100'),
('01010605030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP05', '19世纪末20世纪初，物理学家发现了放射现象。', '', '01010605030100'),
('01010605030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP06', '①弱相互作用是引起原子核β衰变的原因，即引起中子—质子转变的原因。', '', '01010605030100'),
('01010605030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02', '结合能', '', '01010605030000'),
('01010605030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP01', '相距很远的两个物体，由于万有引力而相互接近，运动速度越来越大，引力势能转化为动能。', '', '01010605030200'),
('01010605030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP02', '最后撞在一起，一部分动能变成热并散失掉了。', '', '01010605030200'),
('01010605030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP03', '比结合能越大，原子核中核子结合得越牢固，原子核越稳定。', '', '01010605030200'),
('01010605030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03', '质量亏损', '', '01010605030000'),
('01010605030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP01', '原子核的结合能很难直接测量。', '', '01010605030300'),
('01010605030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP02', '质量亏损表明，的确存在着原子核的结合能。', '', '01010605030300'),
('01010605040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04', '核裂变与核聚变', '', '01010605000000'),
('01010605040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01', '核裂变的发现', '', '01010605040000'),
('01010605040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP01', '以铀235为例，核裂变时产生两或三个中子。', '', '01010605040100'),
('01010605040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP02', '原子核的体积非常小，原子内部的空隙很大，如果铀块不够大，中子在铀块中通过时，很有可能碰不到铀核而跑到铀块外面去，链式反应不能继续。', '', '01010605040100'),
('01010605040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP03', '只有当铀块足够大时，核裂变产生的中子才有足够大的概率打中某个铀核，使链式反应进行下去。', '', '01010605040100'),
('01010605040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP04', '铀核裂变时如果生成物不同，释放的能量也有差异。', '', '01010605040100'),
('01010605040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02', '反应堆与核电站', '', '01010605040000'),
('01010605040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP01', '原子核的链式反应也可以在人工控制下进行。', '', '01010605040200'),
('01010605040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP02', '其实，在第一个原子弹制成以前，科学家们已经实现了核能的可控释放。', '', '01010605040200'),
('01010605040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP03', '1942年，美籍意大利物理学家费米就主持建立了世界上第一个称为“核反应堆”的装置，首次通过可控制的链式反应实现了核能的释放。', '', '01010605040200'),
('01010605040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP04', '常用的慢化剂有石墨、重水和普通水（也叫轻水）。', '', '01010605040200'),
('01010605040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP05', '为了调节中子数目以控制反应速度，还需要在铀棒之间插进一些镉棒。', '', '01010605040200'),
('01010605040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP06', '核燃料发生核裂变释放的能量使反应区温度升高。', '', '01010605040200'),
('01010605040307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP07', '水或液态的金属钠等流体在反应堆内外循环流动，把反应堆内的热量传输出去，用于发电，同时也使反应堆冷却。', '', '01010605040200'),
('01010605040308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP08', '在热交换器内，第二回路中的水被加热生成高温高压蒸汽，驱动汽轮机运转。', '', '01010605040200'),
('01010605040309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP09', '建造核电站时需要特别注意防止放射线和放射性物质的泄漏，以避免射线对人体的伤害和放射性物质对水源、空气和工作场所造成放射性污染。', '', '01010605040200'),
('01010605040310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP10', '核反应堆中的核废料具有很强的放射性，需要装入特制的容器，深埋地下。', '', '01010605040200'),
('01010605040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03', '核聚变', '', '01010605040000'),
('01010605040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP01', '当物质的温度达到几百万开尔文时，剧烈的热运动使得一部分原子核具有足够的动能，可以克服库仑斥力，碰撞时十分接近，发生核聚变。', '', '01010605040300'),
('01010605040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP02', '太阳的主要成分是氢。', '', '01010605040300'),
('01010605040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP03', '现在地球上消耗的能量，追根溯源，绝大部分还是来自太阳，即太阳内部核聚变时释放的核能。', '', '01010605040300'),
('01010605040404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP04', '太阳在“核燃烧”的过程中“体重”不断减轻。', '', '01010605040300'),
('01010605040405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP05', '太阳的寿命已经有50亿年了。', '', '01010605040300'),
('01010605040406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP06', '目前，人工产生的热核反应主要用在核武器上，那就是氢弹。', '', '01010605040300'),
('01010605040407', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP07', '与核裂变相比，核聚变有很多优点。', '', '01010605040300'),
('01010605040408', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP08', '利用核聚变物质的惯性进行约束。', '', '01010605040300'),
('01010605040409', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP09', '总的来说，实现受控核聚变还有一段很长的路要走。', '', '01010605040300'),
('01010605050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05', '“基本”粒子', '', '01010605000000'),
('01010605050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01', '发现新粒子', '', '01010605050000'),
('01010605050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP01', '从20世纪30年代以来，人们在对宇宙线的研究中陆续发现了一些新的粒子。', '', '01010605050100'),
('01010605050202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP02', '1932年发明的粒子加速器，能使带电粒子加速到很高的能量。', '', '01010605050100'),
('01010605050203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP03', '利用加速器和对撞机，人们发现了更多的粒子。', '', '01010605050100'),
('01010605050204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP04', '粒子物理的研究不再单纯依赖宇宙线。', '', '01010605050100'),
('01010605050205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP05', '高能量的对撞机往往极为庞大和复杂。', '', '01010605050100'),
('01010605050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02', '粒子的分类', '', '01010605050000'),
('01010605050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP01', '现在已经发现的粒子达400多种。', '', '01010605050200'),
('01010605050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP02', '强子是参与强相互作用的粒子。', '', '01010605050200'),
('01010605050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP03', '质子和中子都是强子。', '', '01010605050200'),
('01010605050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP04', '轻子不参与强相互作用。', '', '01010605050200'),
('01010605050305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP05', '最早发现的轻子是电子，后来发现的轻子有电子中微子、μ子、μ子中微子以及τ子和τ子中微子。', '', '01010605050200'),
('01010605050306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP06', '每种轻子都有对应的反粒子。', '', '01010605050200'),
('01010605050307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP07', '现代实验还没有发现轻子的内部结构。', '', '01010605050200'),
('01010605050308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP08', '光子传递电磁相互作用，中间玻色子传递弱相互作用，胶子传递强相互作用。', '', '01010605050200'),
('01010605050309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP09', '希格斯玻色子是希格斯场的量子激发。', '', '01010605050200'),
('01010605050310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP10', '基本粒子因与希格斯场耦合而获得质量。', '', '01010605050200'),
('01010605050311', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP11', '2012年，欧洲核子研究中心利用大型强子对撞机发现了希格斯玻色子。', '', '01010605050200'),
('01010605050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03', '研究样例', '', '01010605050000'),
('01010605050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP01', '①燃气灶烧水时的能量转化和转移的情况如下。', '', '01010605050300'),
('01010605050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP02', '记录数据时室温为26℃，基本上保证了实验环境温度的恒定。', '', '01010605050300'),
('01010605050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP03', '为减小水在高温时的放热损失，应缩短水在高温时的加热时间。', '', '01010605050300'),
('01010605050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP04', '用实验检验研究结论的正确性。', '', '01010605050300'),
('01010605050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04', '参考选题', '', '01010605050000'),
('01010605050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP01', '使用热水器洗澡时，常有两种调温习惯：一种是把加热后的水温直接调节至洗澡的合适温度，如38℃左右；另一种是把热水器的加热温度调到更高，如50℃左右，再通过淋浴水龙头加兑冷水至合适水温，后者可以获得更大的适温水量。', '', '01010605050400'),
('01010605050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP02', '热水器工作前，先观察燃气表示数，测量加热前水的温度。', '', '01010605050400'),
('01010605050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP03', '观察燃气表示数，记录所用去的燃气体积。', '', '01010605050400'),
('01010605050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP04', '如果已知被加热水的体积，查找燃气的热值，还可以算出两种情况下热效率的大小。', '', '01010605050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010601000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01', '分子动理论', '', NULL),
('01010601010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01', '分子动理论的基本内容', '', '01010601000000'),
('01010601010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01', '物体是由大量分子组成的', '', '01010601010000'),
('01010601010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP01', '人们用肉眼无法直接看到分子，就是用高倍的光学显微镜也看不到。', '', '01010601010100'),
('01010601010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP02', '直至1982年，人们研制了能放大几亿倍的扫描隧道显微镜③，才观察到物质表面原子的排列。', '', '01010601010100'),
('01010601010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02', '分子热运动', '', '01010601010000'),
('01010601010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP01', '扩散现象是物质分子永不停息地做无规则运动的证据之一。', '', '01010601010200'),
('01010601010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP02', '扩散现象在科学技术中有很多应用。', '', '01010601010200'),
('01010601010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP03', '19世纪初，一些人观察到，悬浮在液体中的小颗粒总在不停地运动。', '', '01010601010200'),
('01010601010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP04', '从实验结果可以看出，小炭粒的运动是无规则的，温度越高，小炭粒的运动越明显。', '', '01010601010200'),
('01010601010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP05', '在扩散现象中，温度越高，扩散得越快。', '', '01010601010200'),
('01010601010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP06', '观察布朗运动，温度越高，悬浮微粒的运动就越明显。', '', '01010601010200'),
('01010601010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP07', '温度是分子热运动剧烈程度的标志。', '', '01010601010200'),
('01010601010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03', '分子间的作用力', '', '01010601010000'),
('01010601010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP01', '水和酒精混合后的总体积变小了。', '', '01010601010300'),
('01010601010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP02', '当用力拉伸物体时，物体内各部分之间要产生反抗拉伸的作用力，此时分子间的作用力表现为引力。', '', '01010601010300'),
('01010601010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP03', '当用力压缩物体时，物体内各部分之间会产生反抗压缩的作用力，此时分子间的作用力表现为斥力。', '', '01010601010300'),
('01010601010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP04', '原子内部有带正电的原子核和带负电的电子。', '', '01010601010300'),
('01010601010400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP04', '分子动理论', '', '01010601010000'),
('01010601020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02', '实验：用油膜法估测油酸分子的大小', '', '01010601000000'),
('01010601020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP01', '实验思路', '', '01010601020000'),
('01010601020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP02', '物理量的测量', '', '01010601020000'),
('01010601020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP03', '数据分析', '', '01010601020000'),
('01010601030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03', '分子运动速率分布规律', '', '01010601000000'),
('01010601030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01', '气体分子运动的特点', '', '01010601030000'),
('01010601030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP01', '热现象与大量分子热运动的统计规律有关。', '', '01010601030100'),
('01010601030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP02', '要研究气体的热现象，就要了解气体分子运动的特点。', '', '01010601030100'),
('01010601030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP03', '液体变为气体后，体积要增大上千倍，可见，气体分子间距离大约是分子直径的10倍。', '', '01010601030100'),
('01010601030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP04', '分子的大小相对分子间的空隙来说很小，所以，可以把气体分子视为质点。', '', '01010601030100'),
('01010601030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP05', '分子的运动杂乱无章，在某一时刻，向着任何一个方向运动的分子都有，而且向各个方向运动的分子数目几乎相等。', '', '01010601030100'),
('01010601030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02', '分子运动速率分布图像', '', '01010601030000'),
('01010601030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP01', '尽管分子做无规则运动，速率有大有小，但大量分子的速率却按一定的规律分布。', '', '01010601030200'),
('01010601030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP02', '100℃的氧气，速率大的分子比例较多，其分子的平均速率比0℃的大。', '', '01010601030200'),
('01010601030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03', '气体压强的微观解释', '', '01010601030000'),
('01010601030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP01', '器壁单位面积上受到的压力，就是气体的压强。', '', '01010601030300'),
('01010601030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP02', '从微观角度来看，气体分子的数量是巨大的。', '', '01010601030300'),
('01010601040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04', '分子动能和分子势能', '', '01010601000000'),
('01010601040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01', '分子动能', '', '01010601040000'),
('01010601040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP01', '物体中分子热运动的速率大小不一，所以各个分子的动能也有大有小，而且在不断改变。', '', '01010601040100'),
('01010601040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP02', '温度升高时，分子的热运动加剧，温度越高，分子热运动的平均动能越大。', '', '01010601040100'),
('01010601040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP03', '温度越低，分子热运动的平均动能越小。', '', '01010601040100'),
('01010601040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02', '分子势能', '', '01010601040000'),
('01010601040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP01', '分子间存在着相互作用力，可以证明分子间的作用力所做的功与路径无关，分子组成的系统具有分子势能。', '', '01010601040200'),
('01010601040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP02', '物体的体积变化时，分子间距离将发生变化，因而分子势能随之改变。', '', '01010601040200'),
('01010601040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03', '物体的内能', '', '01010601040000'),
('01010601040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP01', '任何物体都具有内能。', '', '01010601040300'),
('01010601040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP02', '分子热运动的平均动能与温度有关，分子势能与物体的体积有关。', '', '01010601040300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010602000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02', '气体、固体和液体', '', NULL),
('01010602010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01', '温度和温标', '', '01010602000000'),
('01010602010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01', '状态参量与平衡态', '', '01010602010000'),
('01010602010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01.OP01', '要定量地描述系统的状态往往很难，因为有时系统的参量会随时间变化。', '', '01010602010100'),
('01010602010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP02', '热平衡与温度', '', '01010602010000'),
('01010602010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03', '温度计与温标', '', '01010602010000'),
('01010602010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03.OP01', '现代科学中用得更多的是热力学温标。', '', '01010602010300'),
('01010602020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC02', '气体的等温变化', '', '01010602000000'),
('01010602030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03', '气体的等压变化和等容变化', '', '01010602000000'),
('01010602030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP01', '气体的等压变化', '', '01010602030000'),
('01010602030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02', '气体的等容变化', '', '01010602030000'),
('01010602030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02.OP01', '法国科学家查理在分析了实验事实后发现，当一定质量的气体体积一定时，各种气体的压强与温度之间都有线性关系。', '', '01010602030200'),
('01010602030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03', '理想气体', '', '01010602030000'),
('01010602030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP01', '当压强很大、温度很低时，由上述规律计算的结果与实际测量结果有很大的差别。', '', '01010602030300'),
('01010602030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP02', '实际气体的分子之间有相互作用力，但是作用力很小；分子也有大小，但气体分子之间的间距比分子直径大得多；气体分子与器壁碰撞几乎是弹性的，动能损失也很小。', '', '01010602030300'),
('01010602030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP03', '在温度不低于零下几十摄氏度、压强不超过大气压的几倍时，把实际气体当成理想气体来处理，误差很小。', '', '01010602030300'),
('01010602030400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04', '气体实验定律的微观解释', '', '01010602030000'),
('01010602030501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP01', '用分子动理论可以定性解释气体的实验定律。', '', '01010602030400'),
('01010602030502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP02', '一定质量的某种理想气体，温度保持不变时，分子的平均动能是一定的。', '', '01010602030400'),
('01010602030503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP03', '一定质量的某种理想气体，温度升高时，分子的平均动能增大；只有气体的体积同时增大，使分子的数密度减小，才能保持压强不变。', '', '01010602030400'),
('01010602030504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP04', '一定质量的某种理想气体，体积保持不变时，分子的数密度保持不变。', '', '01010602030400'),
('01010602040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04', '固体', '', '01010602000000'),
('01010602040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01', '晶体和非晶体', '', '01010602040000'),
('01010602040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP01', '固体可以分为晶体和非晶体两类。', '', '01010602040100'),
('01010602040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP02', '石英、云母①、明矾、食盐、硫酸铜、味精等是晶体。', '', '01010602040100'),
('01010602040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP03', '玻璃、蜂蜡、松香、沥青、橡胶等是非晶体。', '', '01010602040100'),
('01010602040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP04', '非晶体没有规则的外形。', '', '01010602040100'),
('01010602040205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP05', '除了形状是否规则外，晶体和非晶体在物理性质上也有所不同。', '', '01010602040100'),
('01010602040206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP06', '从实验结果可以看出，玻璃片上石蜡熔化区域的形状近似于圆形，表明玻璃沿各个方向的导热性能相同；云母片上石蜡熔化区域的形状呈椭圆形，表明云母沿不同方向的导热性能不同。', '', '01010602040100'),
('01010602040207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP07', '蔗糖受潮后粘在一起形成的糖块，看起来没有确定的几何形状。', '', '01010602040100'),
('01010602040208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP08', '粘在一起的糖块也是多晶体，单个的蔗糖晶体颗粒是单晶体。', '', '01010602040100'),
('01010602040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02', '晶体的微观结构', '', '01010602040000'),
('01010602040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP01', '在20世纪70年代，人们又用电子显微镜观察到了铀、钍原子的像。', '', '01010602040200'),
('01010602040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP02', '1982年，人们用扫描隧道显微镜观察到物质表面原子的排列。', '', '01010602040200'),
('01010602040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP03', '在各种晶体中，原子（或分子、离子）都是按照一定的规则排列的，具有空间上的周期性。', '', '01010602040200'),
('01010602040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP04', '石墨是层状结构，层与层之间距离较大，原子间的作用力比较弱，所以石墨质地松软，可以用来制作粉状润滑剂；金刚石中碳原子间的作用力很强，所以金刚石有很大的硬度，可以用来切割玻璃。', '', '01010602040200'),
('01010602040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP05', '同种物质也可能以晶体和非晶体两种不同的形态出现，也就是说，物质是晶体还是非晶体，并不是绝对的。', '', '01010602040200'),
('01010602040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP06', '有些非晶体在一定条件下也可以转化为晶体。', '', '01010602040200'),
('01010602050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05', '液体', '', '01010602000000'),
('01010602050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP01', '液体的表面张力', '', '01010602050000'),
('01010602050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02', '浸润和不浸润', '', '01010602050000'),
('01010602050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP01', '管的内径越小，上述现象就越明显。', '', '01010602050200'),
('01010602050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP02', '有些物体内部有许多细小的孔道，孔道起到了毛细管的作用。', '', '01010602050200'),
('01010602050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP03', '当液体和与之接触的固体的相互作用比液体分子之间的相互作用强时，液体能够浸润固体。', '', '01010602050200'),
('01010602050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP04', '如果把水银滴到干净的铅板上，水银就会附着在铅板上，很难擦去。', '', '01010602050200'),
('01010602050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03', '液晶', '', '01010602050000'),
('01010602050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP01', '液晶在现代生活中扮演着重要角色，广泛应用于手机屏幕、平板电视等显示设备中。', '', '01010602050300'),
('01010602050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP02', '液晶态既具有液体的流动性，又在一定程度上具有晶体分子的规则排列的性质。', '', '01010602050300'),
('01010602050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP03', '有些物质在特定的温度范围之内具有液晶态；另一些物质，在适当的溶剂中溶解时，在一定的浓度范围具有液晶态。', '', '01010602050300'),
('01010602050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP04', '分子取向排列的液晶具有光学各向异性，具体地说，沿分子长轴方向上的折射率不同于沿短轴的。', '', '01010602050300'),
('01010602050405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP05', '入射光的偏振方向与液晶分子长轴的方向成不同夹角时，液晶对光的折射率不同。', '', '01010602050300'),
('01010602050406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP06', '液晶的研究在生命科学中也有应用。', '', '01010602050300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010603000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03', '热力学定律', '', NULL),
('01010603010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01', '功、热和内能的改变', '', '01010603000000'),
('01010603010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01', '焦耳的实验', '', '01010603010000'),
('01010603010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP01', '在焦耳研究功与热等效性的许多实验中，有两个最具代表性。', '', '01010603010100'),
('01010603010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP02', '一个是让重物下落带动叶片搅拌容器中的水，引起水温上升；另一个是通过电流的热效应给水加热。', '', '01010603010100'),
('01010603010203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP03', '焦耳的多次实验测量表明，尽管各次悬挂重物的质量不同，下落的高度也不一样，但只要重力所做的功相同，容器内水温上升的数值都是相同的，即系统状态的变化是相同的。', '', '01010603010100'),
('01010603010204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP04', '多次实验结果表明，对于同一个系统，如果过程是绝热的，那么不管通过电阻丝的电流或大或小、通电时间或长或短，只要所做的电功相等，则系统温度上升的数值是相同的，即系统的状态就发生了同样的变化。', '', '01010603010100'),
('01010603010205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP05', '两个位置间移动时重力所做的功。', '', '01010603010100'),
('01010603010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02', '功与内能的改变', '', '01010603010000'),
('01010603010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP01', '当温度达到易燃物的燃点时，易燃物发生自燃。', '', '01010603010200'),
('01010603010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP02', '当橡胶塞跳出时，瓶内出现白雾。', '', '01010603010200'),
('01010603010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP03', '以瓶内气体为研究对象，橡胶塞跳出后，瓶内的气体迅速膨胀，系统对外做功，因此，气体的内能迅速减少，瓶内气体温度迅速下降，瓶内水蒸气液化，就会出现大量的雾状小水滴。', '', '01010603010200'),
('01010603010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03', '热与内能的改变', '', '01010603010000'),
('01010603010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03.OP01', '外界对系统做功可以改变系统的热力学状态，而单纯地对系统传热也能改变系统的热力学状态。', '', '01010603010300'),
('01010603020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02', '热力学第一定律', '', '01010603000000'),
('01010603020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01', '热力学第一定律', '', '01010603020000'),
('01010603020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01.OP01', '（1）求上述压缩过程中气体内能的变化量。', '', '01010603020100'),
('01010603020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP02', '热力学第一定律的应用', '', '01010603020000'),
('01010603030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03', '能量守恒定律', '', '01010603000000'),
('01010603030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01', '探索能量守恒的足迹', '', '01010603030000'),
('01010603030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP01', '能量的概念是人类在对物质运动规律进行长期探索中建立起来的。', '', '01010603030100'),
('01010603030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP02', '所有自然现象都涉及能量，人类的任何活动都离不开能量。', '', '01010603030100'),
('01010603030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP03', '能量具有不同的形式，有描述热运动的内能、描述机械运动的机械能、描述光辐射的光能，等等。', '', '01010603030100'),
('01010603030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP04', '不同形式的运动都可以用能量来描述。', '', '01010603030100'),
('01010603030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP05', '在认识自然的进程中，科学家慢慢知道了要用联系的观点去观察自然。', '', '01010603030100'),
('01010603030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP06', '由于能量既抽象又以各种不同的形式广泛存在，所以能量守恒思想的萌芽及产生并不是一蹴而就的。', '', '01010603030100'),
('01010603030207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP07', '从各种实验的验证，再到实验结果的被质疑，科学家们付出了大量的努力。', '', '01010603030100'),
('01010603030208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP08', '从18世纪末到19世纪中叶，不同领域的科学家从不同角度都提出过能量守恒的思想。', '', '01010603030100'),
('01010603030209', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP09', '人类对能量守恒的认识经历了一个由浅入深、由含糊到清晰的过程。', '', '01010603030100'),
('01010603030210', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP10', '俄国化学家盖斯的研究发现，任何一个化学反应，不论是一步完成，还是分几步完成，放出的总热量相同。', '', '01010603030100'),
('01010603030211', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP11', '焦耳的实验精确地测量了做功与传热之间的等价关系，从而为能量守恒定律奠定了牢固的实验基础，也为能量守恒的定量描述迈出了重要的一步。', '', '01010603030100'),
('01010603030212', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP12', '德国医生迈尔通过比对不同地区人血颜色的差异，认识到食物中化学能与内能的等效性，即生物体内能量的输入和输出是平衡的。', '', '01010603030100'),
('01010603030213', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP13', '半个多世纪人类对能量以及能量守恒研究的历史，预示着人们把各分立的领域连成一体的时刻已经到来，也就是到了建立能量转化与守恒定律的时候了。', '', '01010603030100'),
('01010603030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02', '能量守恒定律', '', '01010603030000'),
('01010603030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP01', '在热力学领域内，做功和传热可以改变系统的内能，即系统内能与系统外的能量会发生转化或转移，但能的总量不会改变。', '', '01010603030200'),
('01010603030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP02', '能量守恒是自然界的普遍规律。', '', '01010603030200'),
('01010603030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP03', '根据能量守恒定律，物理学发现和解释了很多科学现象。', '', '01010603030200'),
('01010603030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP04', '在20世纪30年代初，奥地利物理学家泡利根据能量守恒定律预言了中微子。', '', '01010603030200'),
('01010603030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP05', '能量守恒定律经受住了新的检验。', '', '01010603030200'),
('01010603030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP06', '能量守恒定律的发现是科学史上的重大事件。', '', '01010603030200'),
('01010603030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03', '永动机不可能制成', '', '01010603030000'),
('01010603030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP01', '“问题”栏目中的饮水小鸭不是永动机。', '', '01010603030300'),
('01010603030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP02', '小鸭的头和身由两个玻璃球构成，并通过细玻璃管相连。', '', '01010603030300'),
('01010603030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP03', '下球盛有一些乙醚，上球连同尖嘴被吸水毛毡包起来。', '', '01010603030300'),
('01010603030404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP04', '永动机的思想违背了能量守恒定律，所以是不可能制成的。', '', '01010603030300'),
('01010603030405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP05', '应该指出的是，制造永动机的千万次失败使人们的头脑冷静下来，开始在更深层次寻找失败的原因。', '', '01010603030300'),
('01010603030406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP06', '国科学院宣布“不再审查有关永动机的一切设计”。', '', '01010603030300'),
('01010603040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04', '热力学第二定律', '', '01010603000000'),
('01010603040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01', '热力学第二定律', '', '01010603040000'),
('01010603040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP01', '在电冰箱的实例中，热量的确从低温物体——冰箱内的食品，传到了高温物体——冰箱外的空气。', '', '01010603040100'),
('01010603040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP02', '从能量的角度看，热机的工作分为两个阶段：第一个阶段是燃烧燃料，把燃料中的化学能变成工作物质的内能；第二个阶段是工作物质对外做功，把自己的内能变成机械能。', '', '01010603040100'),
('01010603040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP03', '热力学第二定律的开尔文表述阐述了机械能与内能转化的方向性：机械能可以全部转化为内能，而内能无法全部用来做功以转化成机械能。', '', '01010603040100'),
('01010603040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP04', '可以证明，热力学第二定律的克劳修斯表述和开尔文表述是等价的。', '', '01010603040100'),
('01010603040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02', '能源是有限的', '', '01010603040000'),
('01010603040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP01', '自然界的一切变化，人类社会的所有活动，都伴随着能量的转移和转化，能量是一切物质运动的源泉，是一切生命活动的基础。', '', '01010603040200'),
('01010603040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP02', '能量在数量上虽然守恒，但其转移和转化却具有方向性。', '', '01010603040200'),
('01010603040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP03', '核能、生物能……最终都转化成内能，流散到周围的环境中。', '', '01010603040200'),
('01010603040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP04', '周围环境中的内能很难再利用，而机械能、光能、化学能、电能相对于周围环境中的内能来说，可利用的品质要高。', '', '01010603040200'),
('01010603040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP05', '能源的使用过程中虽然能的总量保持守恒，但能量的品质下降了。', '', '01010603040200'),
('01010603040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP06', '虽然能量总量不会减少，但能源会逐步减少，因此能源是有限的资源。', '', '01010603040200'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010604000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04', '原子结构和波粒二象性', '', NULL),
('01010604010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01', '普朗克黑体辐射理论', '', '01010604000000'),
('01010604010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01', '黑体与黑体辐射', '', '01010604010000'),
('01010604010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP01', '19世纪，由于冶金、星体测温等需求，人们对热辐射进行了大量的研究。', '', '01010604010100'),
('01010604010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP02', '研究表明，对于一般材料的物体，辐射电磁波的情况除了与温度有关，还与材料的种类及表面状况有关，而黑体辐射电磁波的强度按波长的分布只与黑体的温度有关。', '', '01010604010100'),
('01010604010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02', '黑体辐射的实验规律', '', '01010604010000'),
('01010604010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP01', '利用分光技术和热电偶等设备，可以测出黑体辐射电磁波的强度按波长分布的情况。', '', '01010604010200'),
('01010604010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP02', '从中可以看出，随着温度的升高，一方面，各种波长的辐射强度都有增加，另一方面，辐射强度的极大值向波长较短的方向移动。', '', '01010604010200'),
('01010604010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP03', '德国物理学家维恩在1896年、英国物理学家瑞利在1900年，分别提出了辐射强度按波长分布的理论公式。', '', '01010604010200'),
('01010604010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP04', '维恩公式在短波区与实验非常接近，在长波区则与实验偏离较大；瑞利公式在长波区与实验基本一致，但在短波区与实验严重不符。', '', '01010604010200'),
('01010604010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP05', '为了得出同全部实验相符的黑体辐射公式，德国物理学家普朗克做了多种尝试。', '', '01010604010200'),
('01010604010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03', '能量子', '', '01010604010000'),
('01010604010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP01', '弹簧振子的能量不一定是某个最小值的整数倍。', '', '01010604010300'),
('01010604010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP02', '而普朗克的假设则认为微观粒子的能量是量子化的，或者说微观粒子的能量是分立的。', '', '01010604010300'),
('01010604010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP03', '普朗克本人因此获得了1918年的诺贝尔物理学奖。', '', '01010604010300'),
('01010604020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02', '光电效应', '', '01010604000000'),
('01010604020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01', '光电效应的实验规律', '', '01010604020000'),
('01010604020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP01', '1887年，赫兹在研究电磁波的实验中偶尔发现，接收电路的间隙如果受到光照，就更容易产生电火花。', '', '01010604020100'),
('01010604020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP02', '科学家通过实验发现了以下规律。', '', '01010604020100'),
('01010604020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP03', '实验表明，不同金属的截止频率不同。', '', '01010604020100'),
('01010604020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP04', '在光照条件不变的情况下，随着所加电压的增大，光电流趋于一个饱和值。', '', '01010604020100'),
('01010604020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP05', '实验表明，在光的频率不变的情况下，入射光越强，饱和电流越大。', '', '01010604020100'),
('01010604020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02', '光电效应经典解释中的疑难', '', '01010604020000'),
('01010604020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP01', '当光照射金属表面时，电子会吸收光的能量。', '', '01010604020200'),
('01010604020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP02', '光越强，逸出的电子数越多，光电流也就越大。', '', '01010604020200'),
('01010604020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP03', '●不管光的频率如何，只要光足够强，电子都可以获得足够能量从而逸出表面，不应存在截止频率。', '', '01010604020200'),
('01010604020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03', '爱因斯坦的光电效应理论', '', '01010604020000'),
('01010604020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP01', '对于光电效应的解释，爱因斯坦是在普朗克量子假说的基础上作出的。', '', '01010604020300'),
('01010604020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP02', '从下面的讨论可以看出，爱因斯坦光电效应方程可以很好地解释光电效应实验中的各种现象。', '', '01010604020300'),
('01010604020403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP03', '●电子一次性吸收光子的全部能量，不需要积累能量的时间，光电流自然几乎是瞬时产生的。', '', '01010604020300'),
('01010604020404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP04', '爱因斯坦由于提出了光电效应理论而获得1921年的诺贝尔物理学奖。', '', '01010604020300'),
('01010604020405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP05', '金属表面的光子数较多，照射金属时产生的光电子较多，因而饱和电流较大。', '', '01010604020300'),
('01010604020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04', '康普顿效应和光子的动量', '', '01010604020000'),
('01010604020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP01', '光可以与介质中的物质微粒发生散射，改变传播方向。', '', '01010604020400'),
('01010604020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP02', '康普顿效应让人们对光子有了更深入的认识。', '', '01010604020400'),
('01010604020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP03', '康普顿因此获得了1927年的诺贝尔物理学奖。', '', '01010604020400'),
('01010604020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05', '光的波粒二象性', '', '01010604020000'),
('01010604020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP01', '众所周知，在麦克斯韦的电磁理论建立之后，人们认识到光是一种电磁波，从而光的波动说被普遍接受，人们不再认为光是由粒子组成的。', '', '01010604020500'),
('01010604020602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP02', '而爱因斯坦的光电效应理论和康普顿效应理论表明，光在某些方面确实会表现得像是由一些粒子（即一个个有确定能量和动量的“光子”）组成的。', '', '01010604020500'),
('01010604020603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP03', '人们意识到，光既具有波动性，又具有粒子性。', '', '01010604020500'),
('01010604020604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP04', '从牛顿的微粒说、惠更斯和托马斯·杨的波动说，到麦克斯韦的电磁理论，再到爱因斯坦的光子理论乃至量子电动力学，人类对光的认识构成了一部科学史诗。', '', '01010604020500'),
('01010604030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03', '原子的核式结构模型', '', '01010604000000'),
('01010604030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01', '电子的发现', '', '01010604030000'),
('01010604030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP01', '为了找到有利于自己的证据，双方都做了许多实验。', '', '01010604030100'),
('01010604030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP02', '由实验测得的阴极射线粒子的比荷是氢离子①（也就是质子）比荷的近两千倍。', '', '01010604030100'),
('01010604030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP03', '金属受热还是放射性物质的自发辐射，都能发射同样的带电粒子——电子。', '', '01010604030100'),
('01010604030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02', '原子的核式结构模型', '', '01010604030000'),
('01010604030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP01', 'α粒子是从放射性物质（如铀和镭）中发射出来的快速运动的粒子，质量为氢原子质量的4倍、电子质量的7300倍。', '', '01010604030200'),
('01010604030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP02', '被散射的α粒子打在荧光屏上会有微弱的闪光产生。', '', '01010604030200'),
('01010604030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP03', '通过显微镜观察闪光就可以记录在某一时间内向某一方向散射的α粒子数。', '', '01010604030200'),
('01010604030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP04', 'α粒子有库仑力的作用，一些α粒子的运动方向改变，也就是发生了α粒子的散射。', '', '01010604030200'),
('01010604030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP05', 'α粒子所占的比例，可以推知原子中电荷的分布情况。', '', '01010604030200'),
('01010604030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP06', '卢瑟福分析了实验数据后认为，事实应该是：占原子质量绝大部分的带正电的物质集中在很小的空间范围。', '', '01010604030200'),
('01010604030307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP07', '1911年，卢瑟福提出了自己的原子结构模型。', '', '01010604030200'),
('01010604030308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP08', '因为正电体很小，当α粒子进入原子区域后，大部分离正电体很远，受到的库仑斥力很小，运动方向几乎不改变。', '', '01010604030200'),
('01010604030309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP09', '只有极少数α粒子在穿过时距离正电体很近，因此受到很强的库仑斥力，发生大角度散射。', '', '01010604030200'),
('01010604030310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP10', '按照卢瑟福的理论，正电体的尺度是很小的，称为原子核。', '', '01010604030200'),
('01010604030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03', '原子核的电荷与尺度', '', '01010604030000'),
('01010604030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP01', '又由于原子是电中性的，可以推算出原子内含有的电子数。', '', '01010604030300'),
('01010604030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP02', '通常用核半径描述核的大小。', '', '01010604030300'),
('01010604040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04', '氢原子光谱和玻尔的原子模型', '', '01010604000000'),
('01010604040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01', '光谱', '', '01010604040000'),
('01010604040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP01', '有的光谱看起来不是一条条分立的谱线，而是连在一起的光带，叫作连续谱。', '', '01010604040100'),
('01010604040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP02', '气体中中性原子的发光光谱都是线状谱，说明原子只发出几种特定频率的光。', '', '01010604040100'),
('01010604040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02', '氢原子光谱的实验规律', '', '01010604040000'),
('01010604040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP01', '光谱的结果显示氢原子只能发出一系列特定波长的光。', '', '01010604040200'),
('01010604040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP02', '巴耳末公式以简洁的形式反映了氢原子的线状光谱的特征。', '', '01010604040200'),
('01010604040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03', '经典理论的困难', '', '01010604040000'),
('01010604040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP01', '卢瑟福的核式结构模型正确地指出了原子核的存在，很好地解释了α粒子散射实验，但跟经典电磁理论发生了矛盾。', '', '01010604040300'),
('01010604040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP02', '核外电子受到原子核的库仑引力的作用，却没有被吸引到原子核上，而是在以一定的速度绕核运动。', '', '01010604040300'),
('01010604040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP03', '随着绕核运动轨道半径的不断变化，电子运动的频率也要不断变化，因此原子辐射电磁波的频率也要不断变化。', '', '01010604040300'),
('01010604040400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04', '玻尔原子理论的基本假设', '', '01010604040000'),
('01010604040501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP01', '丹麦物理学家玻尔意识到了经典理论在解释原子结构方面的困难。', '', '01010604040400'),
('01010604040502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP02', '玻尔的原子结构假说包括以下两方面的内容。', '', '01010604040400'),
('01010604040503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP03', '玻尔认为，原子中的电子在库仑引力的作用下，绕原子核做圆周运动，服从经典力学的规律。', '', '01010604040400'),
('01010604040504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP04', '在玻尔理论中，电子的轨道半径只可能是某些分立的数值。', '', '01010604040400'),
('01010604040505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP05', '当电子在不同的轨道上运动时，原子处于不同的状态，具有不同的能量。', '', '01010604040400'),
('01010604040506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP06', '根据玻尔理论，电子只能在特定轨道上运动，因此，原子的能量也只能取一系列特定的值。', '', '01010604040400'),
('01010604040507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP07', '按照玻尔的观点，电子在一系列定态轨道上运动，不会发生电磁辐射。', '', '01010604040400'),
('01010604040500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05', '玻尔理论对氢光谱的解释', '', '01010604040000'),
('01010604040601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP01', '从玻尔的基本假设出发，运用经典电磁学和经典力学的理论，可以计算氢原子中电子的可能轨道半径及相应的能量。', '', '01010604040500'),
('01010604040602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP02', '处于基态的原子非常稳定。', '', '01010604040500'),
('01010604040603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP03', '处于激发态的原子是不稳定的，会自发地向能量较低的能级跃迁，放出光子，最终回到基态。', '', '01010604040500'),
('01010604040604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP04', '由于原子的能级是分立的，所以放出的光子的能量也是分立的。', '', '01010604040500'),
('01010604040605', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP05', '由于不同的原子具有不同的结构，能级各不相同，因此辐射（或吸收）的光子频率也不相同。', '', '01010604040500'),
('01010604040606', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP06', '各种气体原子的能级不同，跃迁时发射光子的能量各异。', '', '01010604040500'),
('01010604040607', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP07', '街道上的霓虹灯、试电笔中的氖管，都是由灯管内的气体原子从高能级向低能级跃迁而发光的。', '', '01010604040500'),
('01010604040608', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP08', '食盐被灼烧时发的光，也主要是由食盐蒸气中钠原子的能级跃迁而造成的。', '', '01010604040500'),
('01010604040600', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06', '玻尔理论的局限性', '', '01010604040000'),
('01010604040701', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP01', '玻尔的原子理论第一次将量子观念引入原子领域，提出了定态和跃迁的概念。', '', '01010604040600'),
('01010604040702', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP02', '玻尔理论成功地解释了氢原子光谱的实验规律。', '', '01010604040600'),
('01010604040703', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP03', '①玻尔理论的不足之处在于保留了经典粒子的观念，仍然把电子的运动看作经典力学描述下的轨道运动。', '', '01010604040600'),
('01010604040704', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP04', '实际上，根据量子力学，原子中电子的坐标没有确定的值。', '', '01010604040600'),
('01010604040705', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP05', '当原子处于不同的状态时，电子在各处出现的概率是不一样的。', '', '01010604040600'),
('01010604050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05', '粒子的波动性和量子力学的建立', '', '01010604000000'),
('01010604050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01', '粒子的波动性', '', '01010604050000'),
('01010604050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01.OP01', '1924年，法国物理学家德布罗意在对光的波粒二象性、玻尔氢原子理论以及相对论的深入研究的基础上，把波粒二象性推广到实物粒子，如电子、质子等。', '', '01010604050100'),
('01010604050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02', '物质波的实验验证', '', '01010604050000'),
('01010604050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP01', '德布罗意关于实物粒子具有波动性的假说在当时来看是难以理解的。', '', '01010604050200'),
('01010604050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP02', '1929年，德布罗意因提出物质波的假说获得了诺贝尔物理学奖。', '', '01010604050200'),
('01010604050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP03', '除了电子以外，后来还陆续证实了中子、质子以及原子、分子的波动性。', '', '01010604050200'),
('01010604050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP04', '德布罗意提出物质波的观念被实验证实，表明电子、质子、原子等粒子不但具有粒子的性质，而且具有波动的性质。', '', '01010604050200'),
('01010604050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03', '量子力学的建立', '', '01010604050000'),
('01010604050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP01', '19、20世纪之交，人们在黑体辐射、光电效应、氢原子光谱等许多类问题中，都发现了经典物理学无法解释的现象。', '', '01010604050300'),
('01010604050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP02', '普朗克黑体辐射理论、爱因斯坦光电效应理论、康普顿散射理论、玻尔氢原子理论以及德布罗意物质波假说等一系列理论在解释实验方面都取得了成功。', '', '01010604050300'),
('01010604050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP03', '1925年，德国物理学家海森堡和玻恩等人对玻尔的氢原子理论进行了推广和改造，使之可以适用于更普遍的情况。', '', '01010604050300'),
('01010604050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04', '量子力学的应用', '', '01010604050000'),
('01010604050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP01', '量子力学被应用到众多具体物理系统中，得到了与实验符合得很好的结果，获得了极大的成功。', '', '01010604050400'),
('01010604050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP02', '借助量子力学，人们深入认识了微观世界的组成、结构和属性。', '', '01010604050400'),
('01010604050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP03', '量子力学推动了核物理和粒子物理的发展。', '', '01010604050400'),
('01010604050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP04', '人们认识了原子、原子核、基本粒子等各个微观层次的物质结构。', '', '01010604050400'),
('01010604050505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP05', '而粒子物理学的发展又促进了天文学和宇宙学的研究。', '', '01010604050400'),
('01010604050506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP06', '从整个宇宙到微小的粒子，人类探索自然的视野前所未有的宽广。', '', '01010604050400'),
('01010604050507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP07', '人们惊讶地发现，世界具有奇妙的结构，最微观层次和最宏观层次的规律，竟有着紧密的联系。', '', '01010604050400'),
('01010604050508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP08', '核物理的发展，还让人们成功地认识并利用了原子核反应堆所释放的能量——核能。', '', '01010604050400'),
('01010604050509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP09', '”量子力学推动了原子、分子物理和光学的发展。', '', '01010604050400'),
('01010604050510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP10', '人们认识了原子的结构，以及原子、分子和电磁场相互作用的方式。', '', '01010604050400'),
('01010604050511', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP11', '在此基础上，发展了各式各样的对原子和电磁场进行精确操控和测量的技术，如激光、核磁共振、原子钟，等等。', '', '01010604050400'),
('01010604050512', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP12', '以利用振荡的磁场测量材料中原子的性质，因此，被广泛地用于化学、生物研究和医学诊断。', '', '01010604050400'),
('01010604050513', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP13', '原子钟利用原子为电磁波校准频率，从而实现了对时间的高精度测量。', '', '01010604050400'),
('01010604050514', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP14', '在日常生活和国家安全中发挥巨大作用的卫星定位技术，其核心部件就是原子钟。', '', '01010604050400'),
('01010604050515', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP15', '量子力学推动了固体物理的发展。', '', '01010604050400'),
('01010604050516', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP16', '人们了解了固体中电子运行的规律，并弄清了为什么固体有导体、绝缘体和半导体之分。', '', '01010604050400'),
('01010604050517', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP17', '量子力学的应用还有很多。', '', '01010604050400'),
('01010604050518', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP18', '毫不夸张地说，在过去的近一百年中，量子力学极大地推动了人类的进步。', '', '01010604050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010605000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05', '原子核', '', NULL),
('01010605010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01', '原子核的组成', '', '01010605000000'),
('01010605010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01', '天然放射现象', '', '01010605010000'),
('01010605010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP01', '受到贝克勒尔的发现的鼓舞，波兰裔法国物理学家玛丽·居里和她的丈夫皮埃尔·居里对铀和含铀的各种矿石进行了深入研究。', '', '01010605010100'),
('01010605010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP02', '放射性元素自发地发出射线的现象，叫作天然放射现象。', '', '01010605010100'),
('01010605010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02', '射线的本质', '', '01010605010000'),
('01010605010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP01', '把放射源铀、钋或镭放入用铅做成的容器中，射线只能从容器的小孔射出，成为细细的一束。', '', '01010605010200'),
('01010605010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP02', '物理学家经过多方面的研究后确认α射线、β射线和γ射线具有以下特征。', '', '01010605010200'),
('01010605010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP03', 'α粒子带正电，电荷量是电子的2倍，质量是氢原子的4倍，其组成与氦原子核相同。', '', '01010605010200'),
('01010605010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP04', '由于α粒子带电，质量又比较大，通过气体时很容易把气体分子中的电子剥离，使气体电离。', '', '01010605010200'),
('01010605010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP05', '是电子流，速度可以接近光速。', '', '01010605010200'),
('01010605010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP06', 'β射线的电离作用较弱，穿透能力较强，很容易穿透黑纸，也能穿透几毫米厚的铝板。', '', '01010605010200'),
('01010605010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP07', 'γ射线的电离作用更弱，穿透能力更强，甚至能穿透几厘米厚的铅板和几十厘米厚的混凝土。', '', '01010605010200'),
('01010605010308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP08', '放射性的强度也不受温度、外界压强的影响。', '', '01010605010200'),
('01010605010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03', '原子核的组成', '', '01010605010000'),
('01010605010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP01', '绝大多数原子核的质量与电荷量之比都大于质子的相应比值。', '', '01010605010300'),
('01010605010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP02', '质子带正电荷，电荷量与一个电子的电荷量相等。', '', '01010605010300'),
('01010605010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP03', '由于中子不带电，原子核所带的电荷等于核内质子电荷的总和。', '', '01010605010300'),
('01010605010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP04', '元素的性质与原子核外的电子有密切的关系。', '', '01010605010300'),
('01010605020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02', '放射性元素的衰变', '', '01010605000000'),
('01010605020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01', '原子核的衰变', '', '01010605020000'),
('01010605020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP01', '铀238放出一个α粒子后，质量数减少4，电荷数减少2，成为新核。', '', '01010605020100'),
('01010605020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP02', '研究还表明，在一定条件下，原子核中的两个中子和两个质子会结合起来形成α粒子，并被释放出来。', '', '01010605020100'),
('01010605020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP03', '原子核的能量也跟原子的能量一样，其变化是不连续的，也只能取一系列不连续的数值，因此，也存在着能级，同样是能级越低越稳定。', '', '01010605020100'),
('01010605020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP04', '当放射性物质连续衰变时，原子核中有的发生α衰变，有的发生β衰变，同时伴随着γ射线辐射。', '', '01010605020100'),
('01010605020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02', '半衰期', '', '01010605020000'),
('01010605020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP01', '放射性同位素衰变的快慢有一定的规律。', '', '01010605020200'),
('01010605020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP02', '不同的放射性元素，半衰期不同，甚至差别非常大。', '', '01010605020200'),
('01010605020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP03', '放射性元素衰变的快慢是由核内部自身的因素决定的，跟原子所处的化学状态和外部条件没有关系。', '', '01010605020200'),
('01010605020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03', '核反应', '', '01010605020000'),
('01010605020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP01', '衰变是原子核的自发变化，科学家更希望人工控制原子核的变化。', '', '01010605020300'),
('01010605020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP02', '与衰变过程一样，在核反应中，质量数守恒、电荷数守恒。', '', '01010605020300'),
('01010605020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04', '放射性同位素及其应用', '', '01010605020000'),
('01010605020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP01', '天然放射性同位素不过40多种，而今天通过核反应生成的人工放射性同位素已达3000多种，每种元素都有了自己的放射性同位素。', '', '01010605020400'),
('01010605020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP02', '丰富的放射性同位素资源在国民经济和科学研究的各个领域中得到了广泛的应用。', '', '01010605020400'),
('01010605020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP03', '工业部门可以使用放射性同位素发出的射线来测厚度。', '', '01010605020400'),
('01010605020504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP04', '像癌细胞那样，不断迅速繁殖的、无法控制的细胞组织，在射线照射下破坏得比健康细胞快。', '', '01010605020400'),
('01010605020505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP05', '利用γ射线照射种子，会使种子的遗传基因发生变异，经过筛选，可以培育出新品种。', '', '01010605020400'),
('01010605020506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP06', '棉花在开花、结桃的时候需要较多的磷肥，把磷肥喷在棉花叶子上，磷肥也能被吸收。', '', '01010605020400'),
('01010605020507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP07', '如果用磷的放射性同位素制成肥料喷在棉花的叶面上，然后每隔一定时间用探测器测量棉株各部位的放射性强度，上面的问题就解决了。', '', '01010605020400'),
('01010605020508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP08', '人体甲状腺的工作需要碘，碘被吸收后聚集在甲状腺内。', '', '01010605020400'),
('01010605020509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP09', '定时用探测器测量甲状腺及邻近组织的放射强度，有助于诊断甲状腺的疾病。', '', '01010605020400'),
('01010605020510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP10', '近年来，有关生物大分子的结构及其功能的研究，几乎都要借助于示踪原子。', '', '01010605020400'),
('01010605020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05', '辐射与安全', '', '01010605020000'),
('01010605020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05.OP01', '人类一直生活在放射性的环境中。', '', '01010605020500'),
('01010605030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03', '核力与结合能', '', '01010605000000'),
('01010605030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01', '核力与四种基本相互作用', '', '01010605030000'),
('01010605030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP01', '现代物理学认为，自然界存在着四种基本相互作用，即引力相互作用、电磁相互作用、强相互作用和弱相互作用。', '', '01010605030100'),
('01010605030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP02', '17世纪下半叶，人们发现了万有引力。', '', '01010605030100'),
('01010605030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP03', '引力是自然界的一种基本相互作用，地面物体所受的重力只是引力在地球表面附近的一种表现。', '', '01010605030100'),
('01010605030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP04', '20世纪，物理学家发现原子核是由若干带正电荷的质子和不带电的中子组成的。', '', '01010605030100'),
('01010605030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP05', '19世纪末20世纪初，物理学家发现了放射现象。', '', '01010605030100'),
('01010605030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP06', '①弱相互作用是引起原子核β衰变的原因，即引起中子—质子转变的原因。', '', '01010605030100'),
('01010605030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02', '结合能', '', '01010605030000'),
('01010605030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP01', '相距很远的两个物体，由于万有引力而相互接近，运动速度越来越大，引力势能转化为动能。', '', '01010605030200'),
('01010605030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP02', '最后撞在一起，一部分动能变成热并散失掉了。', '', '01010605030200'),
('01010605030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP03', '比结合能越大，原子核中核子结合得越牢固，原子核越稳定。', '', '01010605030200'),
('01010605030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03', '质量亏损', '', '01010605030000'),
('01010605030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP01', '原子核的结合能很难直接测量。', '', '01010605030300'),
('01010605030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP02', '质量亏损表明，的确存在着原子核的结合能。', '', '01010605030300'),
('01010605040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04', '核裂变与核聚变', '', '01010605000000'),
('01010605040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01', '核裂变的发现', '', '01010605040000'),
('01010605040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP01', '以铀235为例，核裂变时产生两或三个中子。', '', '01010605040100'),
('01010605040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP02', '原子核的体积非常小，原子内部的空隙很大，如果铀块不够大，中子在铀块中通过时，很有可能碰不到铀核而跑到铀块外面去，链式反应不能继续。', '', '01010605040100'),
('01010605040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP03', '只有当铀块足够大时，核裂变产生的中子才有足够大的概率打中某个铀核，使链式反应进行下去。', '', '01010605040100'),
('01010605040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP04', '铀核裂变时如果生成物不同，释放的能量也有差异。', '', '01010605040100'),
('01010605040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02', '反应堆与核电站', '', '01010605040000'),
('01010605040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP01', '原子核的链式反应也可以在人工控制下进行。', '', '01010605040200'),
('01010605040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP02', '其实，在第一个原子弹制成以前，科学家们已经实现了核能的可控释放。', '', '01010605040200'),
('01010605040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP03', '1942年，美籍意大利物理学家费米就主持建立了世界上第一个称为“核反应堆”的装置，首次通过可控制的链式反应实现了核能的释放。', '', '01010605040200'),
('01010605040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP04', '常用的慢化剂有石墨、重水和普通水（也叫轻水）。', '', '01010605040200'),
('01010605040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP05', '为了调节中子数目以控制反应速度，还需要在铀棒之间插进一些镉棒。', '', '01010605040200'),
('01010605040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP06', '核燃料发生核裂变释放的能量使反应区温度升高。', '', '01010605040200'),
('01010605040307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP07', '水或液态的金属钠等流体在反应堆内外循环流动，把反应堆内的热量传输出去，用于发电，同时也使反应堆冷却。', '', '01010605040200'),
('01010605040308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP08', '在热交换器内，第二回路中的水被加热生成高温高压蒸汽，驱动汽轮机运转。', '', '01010605040200'),
('01010605040309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP09', '建造核电站时需要特别注意防止放射线和放射性物质的泄漏，以避免射线对人体的伤害和放射性物质对水源、空气和工作场所造成放射性污染。', '', '01010605040200'),
('01010605040310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP10', '核反应堆中的核废料具有很强的放射性，需要装入特制的容器，深埋地下。', '', '01010605040200'),
('01010605040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03', '核聚变', '', '01010605040000'),
('01010605040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP01', '当物质的温度达到几百万开尔文时，剧烈的热运动使得一部分原子核具有足够的动能，可以克服库仑斥力，碰撞时十分接近，发生核聚变。', '', '01010605040300'),
('01010605040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP02', '太阳的主要成分是氢。', '', '01010605040300'),
('01010605040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP03', '现在地球上消耗的能量，追根溯源，绝大部分还是来自太阳，即太阳内部核聚变时释放的核能。', '', '01010605040300'),
('01010605040404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP04', '太阳在“核燃烧”的过程中“体重”不断减轻。', '', '01010605040300'),
('01010605040405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP05', '太阳的寿命已经有50亿年了。', '', '01010605040300'),
('01010605040406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP06', '目前，人工产生的热核反应主要用在核武器上，那就是氢弹。', '', '01010605040300'),
('01010605040407', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP07', '与核裂变相比，核聚变有很多优点。', '', '01010605040300'),
('01010605040408', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP08', '利用核聚变物质的惯性进行约束。', '', '01010605040300'),
('01010605040409', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP09', '总的来说，实现受控核聚变还有一段很长的路要走。', '', '01010605040300'),
('01010605050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05', '“基本”粒子', '', '01010605000000'),
('01010605050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01', '发现新粒子', '', '01010605050000'),
('01010605050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP01', '从20世纪30年代以来，人们在对宇宙线的研究中陆续发现了一些新的粒子。', '', '01010605050100'),
('01010605050202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP02', '1932年发明的粒子加速器，能使带电粒子加速到很高的能量。', '', '01010605050100'),
('01010605050203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP03', '利用加速器和对撞机，人们发现了更多的粒子。', '', '01010605050100'),
('01010605050204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP04', '粒子物理的研究不再单纯依赖宇宙线。', '', '01010605050100'),
('01010605050205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP05', '高能量的对撞机往往极为庞大和复杂。', '', '01010605050100'),
('01010605050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02', '粒子的分类', '', '01010605050000'),
('01010605050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP01', '现在已经发现的粒子达400多种。', '', '01010605050200'),
('01010605050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP02', '强子是参与强相互作用的粒子。', '', '01010605050200'),
('01010605050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP03', '质子和中子都是强子。', '', '01010605050200'),
('01010605050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP04', '轻子不参与强相互作用。', '', '01010605050200'),
('01010605050305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP05', '最早发现的轻子是电子，后来发现的轻子有电子中微子、μ子、μ子中微子以及τ子和τ子中微子。', '', '01010605050200'),
('01010605050306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP06', '每种轻子都有对应的反粒子。', '', '01010605050200'),
('01010605050307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP07', '现代实验还没有发现轻子的内部结构。', '', '01010605050200'),
('01010605050308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP08', '光子传递电磁相互作用，中间玻色子传递弱相互作用，胶子传递强相互作用。', '', '01010605050200'),
('01010605050309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP09', '希格斯玻色子是希格斯场的量子激发。', '', '01010605050200'),
('01010605050310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP10', '基本粒子因与希格斯场耦合而获得质量。', '', '01010605050200'),
('01010605050311', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP11', '2012年，欧洲核子研究中心利用大型强子对撞机发现了希格斯玻色子。', '', '01010605050200'),
('01010605050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03', '研究样例', '', '01010605050000'),
('01010605050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP01', '①燃气灶烧水时的能量转化和转移的情况如下。', '', '01010605050300'),
('01010605050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP02', '记录数据时室温为26℃，基本上保证了实验环境温度的恒定。', '', '01010605050300'),
('01010605050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP03', '为减小水在高温时的放热损失，应缩短水在高温时的加热时间。', '', '01010605050300'),
('01010605050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP04', '用实验检验研究结论的正确性。', '', '01010605050300'),
('01010605050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04', '参考选题', '', '01010605050000'),
('01010605050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP01', '使用热水器洗澡时，常有两种调温习惯：一种是把加热后的水温直接调节至洗澡的合适温度，如38℃左右；另一种是把热水器的加热温度调到更高，如50℃左右，再通过淋浴水龙头加兑冷水至合适水温，后者可以获得更大的适温水量。', '', '01010605050400'),
('01010605050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP02', '热水器工作前，先观察燃气表示数，测量加热前水的温度。', '', '01010605050400'),
('01010605050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP03', '观察燃气表示数，记录所用去的燃气体积。', '', '01010605050400'),
('01010605050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP04', '如果已知被加热水的体积，查找燃气的热值，还可以算出两种情况下热效率的大小。', '', '01010605050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010601000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01', '分子动理论', '', NULL),
('01010601010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01', '分子动理论的基本内容', '', '01010601000000'),
('01010601010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01', '物体是由大量分子组成的', '', '01010601010000'),
('01010601010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP01', '人们用肉眼无法直接看到分子，就是用高倍的光学显微镜也看不到。', '', '01010601010100'),
('01010601010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP02', '直至1982年，人们研制了能放大几亿倍的扫描隧道显微镜③，才观察到物质表面原子的排列。', '', '01010601010100'),
('01010601010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02', '分子热运动', '', '01010601010000'),
('01010601010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP01', '扩散现象是物质分子永不停息地做无规则运动的证据之一。', '', '01010601010200'),
('01010601010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP02', '扩散现象在科学技术中有很多应用。', '', '01010601010200'),
('01010601010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP03', '19世纪初，一些人观察到，悬浮在液体中的小颗粒总在不停地运动。', '', '01010601010200'),
('01010601010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP04', '从实验结果可以看出，小炭粒的运动是无规则的，温度越高，小炭粒的运动越明显。', '', '01010601010200'),
('01010601010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP05', '在扩散现象中，温度越高，扩散得越快。', '', '01010601010200'),
('01010601010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP06', '观察布朗运动，温度越高，悬浮微粒的运动就越明显。', '', '01010601010200'),
('01010601010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP07', '温度是分子热运动剧烈程度的标志。', '', '01010601010200'),
('01010601010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03', '分子间的作用力', '', '01010601010000'),
('01010601010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP01', '水和酒精混合后的总体积变小了。', '', '01010601010300'),
('01010601010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP02', '当用力拉伸物体时，物体内各部分之间要产生反抗拉伸的作用力，此时分子间的作用力表现为引力。', '', '01010601010300'),
('01010601010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP03', '当用力压缩物体时，物体内各部分之间会产生反抗压缩的作用力，此时分子间的作用力表现为斥力。', '', '01010601010300'),
('01010601010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP04', '原子内部有带正电的原子核和带负电的电子。', '', '01010601010300'),
('01010601010400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP04', '分子动理论', '', '01010601010000'),
('01010601020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02', '实验：用油膜法估测油酸分子的大小', '', '01010601000000'),
('01010601020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP01', '实验思路', '', '01010601020000'),
('01010601020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP02', '物理量的测量', '', '01010601020000'),
('01010601020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP03', '数据分析', '', '01010601020000'),
('01010601030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03', '分子运动速率分布规律', '', '01010601000000'),
('01010601030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01', '气体分子运动的特点', '', '01010601030000'),
('01010601030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP01', '热现象与大量分子热运动的统计规律有关。', '', '01010601030100'),
('01010601030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP02', '要研究气体的热现象，就要了解气体分子运动的特点。', '', '01010601030100'),
('01010601030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP03', '液体变为气体后，体积要增大上千倍，可见，气体分子间距离大约是分子直径的10倍。', '', '01010601030100'),
('01010601030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP04', '分子的大小相对分子间的空隙来说很小，所以，可以把气体分子视为质点。', '', '01010601030100'),
('01010601030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP05', '分子的运动杂乱无章，在某一时刻，向着任何一个方向运动的分子都有，而且向各个方向运动的分子数目几乎相等。', '', '01010601030100'),
('01010601030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02', '分子运动速率分布图像', '', '01010601030000'),
('01010601030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP01', '尽管分子做无规则运动，速率有大有小，但大量分子的速率却按一定的规律分布。', '', '01010601030200'),
('01010601030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP02', '100℃两种不同情况下的速率分布情况。', '', '01010601030200'),
('01010601030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP03', '100℃的氧气，速率大的分子比例较多，其分子的平均速率比0℃的大。', '', '01010601030200'),
('01010601030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03', '气体压强的微观解释', '', '01010601030000'),
('01010601030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP01', '器壁单位面积上受到的压力，就是气体的压强。', '', '01010601030300'),
('01010601030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP02', '从微观角度来看，气体分子的数量是巨大的。', '', '01010601030300'),
('01010601040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04', '分子动能和分子势能', '', '01010601000000'),
('01010601040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01', '分子动能', '', '01010601040000'),
('01010601040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP01', '物体中分子热运动的速率大小不一，所以各个分子的动能也有大有小，而且在不断改变。', '', '01010601040100'),
('01010601040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP02', '温度升高时，分子的热运动加剧，温度越高，分子热运动的平均动能越大。', '', '01010601040100'),
('01010601040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP03', '温度越低，分子热运动的平均动能越小。', '', '01010601040100'),
('01010601040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02', '分子势能', '', '01010601040000'),
('01010601040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP01', '分子间存在着相互作用力，可以证明分子间的作用力所做的功与路径无关，分子组成的系统具有分子势能。', '', '01010601040200'),
('01010601040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP02', '物体的体积变化时，分子间距离将发生变化，因而分子势能随之改变。', '', '01010601040200'),
('01010601040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03', '物体的内能', '', '01010601040000'),
('01010601040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP01', '任何物体都具有内能。', '', '01010601040300'),
('01010601040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP02', '分子热运动的平均动能与温度有关，分子势能与物体的体积有关。', '', '01010601040300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010602000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02', '气体、固体和液体', '', NULL),
('01010602010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01', '温度和温标', '', '01010602000000'),
('01010602010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01', '状态参量与平衡态', '', '01010602010000'),
('01010602010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01.OP01', '要定量地描述系统的状态往往很难，因为有时系统的参量会随时间变化。', '', '01010602010100'),
('01010602010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP02', '热平衡与温度', '', '01010602010000'),
('01010602010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03', '温度计与温标', '', '01010602010000'),
('01010602010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03.OP01', '现代科学中用得更多的是热力学温标。', '', '01010602010300'),
('01010602020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC02', '气体的等温变化', '', '01010602000000'),
('01010602030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03', '气体的等压变化和等容变化', '', '01010602000000'),
('01010602030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP01', '气体的等压变化', '', '01010602030000'),
('01010602030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02', '气体的等容变化', '', '01010602030000'),
('01010602030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02.OP01', '法国科学家查理在分析了实验事实后发现，当一定质量的气体体积一定时，各种气体的压强与温度之间都有线性关系。', '', '01010602030200'),
('01010602030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03', '理想气体', '', '01010602030000'),
('01010602030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP01', '当压强很大、温度很低时，由上述规律计算的结果与实际测量结果有很大的差别。', '', '01010602030300'),
('01010602030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP02', '实际气体的分子之间有相互作用力，但是作用力很小；分子也有大小，但气体分子之间的间距比分子直径大得多；气体分子与器壁碰撞几乎是弹性的，动能损失也很小。', '', '01010602030300'),
('01010602030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP03', '在温度不低于零下几十摄氏度、压强不超过大气压的几倍时，把实际气体当成理想气体来处理，误差很小。', '', '01010602030300'),
('01010602030400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04', '气体实验定律的微观解释', '', '01010602030000'),
('01010602030501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP01', '用分子动理论可以定性解释气体的实验定律。', '', '01010602030400'),
('01010602030502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP02', '一定质量的某种理想气体，温度保持不变时，分子的平均动能是一定的。', '', '01010602030400'),
('01010602030503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP03', '一定质量的某种理想气体，温度升高时，分子的平均动能增大；只有气体的体积同时增大，使分子的数密度减小，才能保持压强不变。', '', '01010602030400'),
('01010602030504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP04', '一定质量的某种理想气体，体积保持不变时，分子的数密度保持不变。', '', '01010602030400'),
('01010602040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04', '固体', '', '01010602000000'),
('01010602040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01', '晶体和非晶体', '', '01010602040000'),
('01010602040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP01', '固体可以分为晶体和非晶体两类。', '', '01010602040100'),
('01010602040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP02', '石英、云母①、明矾、食盐、硫酸铜、味精等是晶体。', '', '01010602040100'),
('01010602040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP03', '玻璃、蜂蜡、松香、沥青、橡胶等是非晶体。', '', '01010602040100'),
('01010602040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP04', '非晶体没有规则的外形。', '', '01010602040100'),
('01010602040205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP05', '除了形状是否规则外，晶体和非晶体在物理性质上也有所不同。', '', '01010602040100'),
('01010602040206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP06', '从实验结果可以看出，玻璃片上石蜡熔化区域的形状近似于圆形，表明玻璃沿各个方向的导热性能相同；云母片上石蜡熔化区域的形状呈椭圆形，表明云母沿不同方向的导热性能不同。', '', '01010602040100'),
('01010602040207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP07', '蔗糖受潮后粘在一起形成的糖块，看起来没有确定的几何形状。', '', '01010602040100'),
('01010602040208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP08', '粘在一起的糖块也是多晶体，单个的蔗糖晶体颗粒是单晶体。', '', '01010602040100'),
('01010602040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02', '晶体的微观结构', '', '01010602040000'),
('01010602040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP01', '在20世纪70年代，人们又用电子显微镜观察到了铀、钍原子的像。', '', '01010602040200'),
('01010602040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP02', '1982年，人们用扫描隧道显微镜观察到物质表面原子的排列。', '', '01010602040200'),
('01010602040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP03', '在各种晶体中，原子（或分子、离子）都是按照一定的规则排列的，具有空间上的周期性。', '', '01010602040200'),
('01010602040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP04', '石墨是层状结构，层与层之间距离较大，原子间的作用力比较弱，所以石墨质地松软，可以用来制作粉状润滑剂；金刚石中碳原子间的作用力很强，所以金刚石有很大的硬度，可以用来切割玻璃。', '', '01010602040200'),
('01010602040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP05', '同种物质也可能以晶体和非晶体两种不同的形态出现，也就是说，物质是晶体还是非晶体，并不是绝对的。', '', '01010602040200'),
('01010602040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP06', '有些非晶体在一定条件下也可以转化为晶体。', '', '01010602040200'),
('01010602050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05', '液体', '', '01010602000000'),
('01010602050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP01', '液体的表面张力', '', '01010602050000'),
('01010602050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02', '浸润和不浸润', '', '01010602050000'),
('01010602050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP01', '管的内径越小，上述现象就越明显。', '', '01010602050200'),
('01010602050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP02', '有些物体内部有许多细小的孔道，孔道起到了毛细管的作用。', '', '01010602050200'),
('01010602050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP03', '当液体和与之接触的固体的相互作用比液体分子之间的相互作用强时，液体能够浸润固体。', '', '01010602050200'),
('01010602050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP04', '如果把水银滴到干净的铅板上，水银就会附着在铅板上，很难擦去。', '', '01010602050200'),
('01010602050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03', '液晶', '', '01010602050000'),
('01010602050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP01', '液晶在现代生活中扮演着重要角色，广泛应用于手机屏幕、平板电视等显示设备中。', '', '01010602050300'),
('01010602050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP02', '液晶态既具有液体的流动性，又在一定程度上具有晶体分子的规则排列的性质。', '', '01010602050300'),
('01010602050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP03', '有些物质在特定的温度范围之内具有液晶态；另一些物质，在适当的溶剂中溶解时，在一定的浓度范围具有液晶态。', '', '01010602050300'),
('01010602050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP04', '分子取向排列的液晶具有光学各向异性，具体地说，沿分子长轴方向上的折射率不同于沿短轴的。', '', '01010602050300'),
('01010602050405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP05', '入射光的偏振方向与液晶分子长轴的方向成不同夹角时，液晶对光的折射率不同。', '', '01010602050300'),
('01010602050406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP06', '液晶的研究在生命科学中也有应用。', '', '01010602050300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010603000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03', '热力学定律', '', NULL),
('01010603010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01', '功、热和内能的改变', '', '01010603000000'),
('01010603010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01', '焦耳的实验', '', '01010603010000'),
('01010603010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP01', '在焦耳研究功与热等效性的许多实验中，有两个最具代表性。', '', '01010603010100'),
('01010603010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP02', '一个是让重物下落带动叶片搅拌容器中的水，引起水温上升；另一个是通过电流的热效应给水加热。', '', '01010603010100'),
('01010603010203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP03', '焦耳的多次实验测量表明，尽管各次悬挂重物的质量不同，下落的高度也不一样，但只要重力所做的功相同，容器内水温上升的数值都是相同的，即系统状态的变化是相同的。', '', '01010603010100'),
('01010603010204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP04', '多次实验结果表明，对于同一个系统，如果过程是绝热的，那么不管通过电阻丝的电流或大或小、通电时间或长或短，只要所做的电功相等，则系统温度上升的数值是相同的，即系统的状态就发生了同样的变化。', '', '01010603010100'),
('01010603010205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP05', '两个位置间移动时重力所做的功。', '', '01010603010100'),
('01010603010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02', '功与内能的改变', '', '01010603010000'),
('01010603010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP01', '当温度达到易燃物的燃点时，易燃物发生自燃。', '', '01010603010200'),
('01010603010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP02', '当橡胶塞跳出时，瓶内出现白雾。', '', '01010603010200'),
('01010603010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP03', '以瓶内气体为研究对象，橡胶塞跳出后，瓶内的气体迅速膨胀，系统对外做功，因此，气体的内能迅速减少，瓶内气体温度迅速下降，瓶内水蒸气液化，就会出现大量的雾状小水滴。', '', '01010603010200'),
('01010603010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03', '热与内能的改变', '', '01010603010000'),
('01010603010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03.OP01', '外界对系统做功可以改变系统的热力学状态，而单纯地对系统传热也能改变系统的热力学状态。', '', '01010603010300'),
('01010603020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02', '热力学第一定律', '', '01010603000000'),
('01010603020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01', '热力学第一定律', '', '01010603020000'),
('01010603020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01.OP01', '（1）求上述压缩过程中气体内能的变化量。', '', '01010603020100'),
('01010603020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP02', '热力学第一定律的应用', '', '01010603020000'),
('01010603030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03', '能量守恒定律', '', '01010603000000'),
('01010603030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01', '探索能量守恒的足迹', '', '01010603030000'),
('01010603030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP01', '能量的概念是人类在对物质运动规律进行长期探索中建立起来的。', '', '01010603030100'),
('01010603030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP02', '所有自然现象都涉及能量，人类的任何活动都离不开能量。', '', '01010603030100'),
('01010603030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP03', '能量具有不同的形式，有描述热运动的内能、描述机械运动的机械能、描述光辐射的光能，等等。', '', '01010603030100'),
('01010603030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP04', '不同形式的运动都可以用能量来描述。', '', '01010603030100'),
('01010603030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP05', '在认识自然的进程中，科学家慢慢知道了要用联系的观点去观察自然。', '', '01010603030100'),
('01010603030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP06', '由于能量既抽象又以各种不同的形式广泛存在，所以能量守恒思想的萌芽及产生并不是一蹴而就的。', '', '01010603030100'),
('01010603030207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP07', '从各种实验的验证，再到实验结果的被质疑，科学家们付出了大量的努力。', '', '01010603030100'),
('01010603030208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP08', '从18世纪末到19世纪中叶，不同领域的科学家从不同角度都提出过能量守恒的思想。', '', '01010603030100'),
('01010603030209', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP09', '人类对能量守恒的认识经历了一个由浅入深、由含糊到清晰的过程。', '', '01010603030100'),
('01010603030210', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP10', '俄国化学家盖斯的研究发现，任何一个化学反应，不论是一步完成，还是分几步完成，放出的总热量相同。', '', '01010603030100'),
('01010603030211', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP11', '焦耳的实验精确地测量了做功与传热之间的等价关系，从而为能量守恒定律奠定了牢固的实验基础，也为能量守恒的定量描述迈出了重要的一步。', '', '01010603030100'),
('01010603030212', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP12', '德国医生迈尔通过比对不同地区人血颜色的差异，认识到食物中化学能与内能的等效性，即生物体内能量的输入和输出是平衡的。', '', '01010603030100'),
('01010603030213', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP13', '半个多世纪人类对能量以及能量守恒研究的历史，预示着人们把各分立的领域连成一体的时刻已经到来，也就是到了建立能量转化与守恒定律的时候了。', '', '01010603030100'),
('01010603030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02', '能量守恒定律', '', '01010603030000'),
('01010603030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP01', '在热力学领域内，做功和传热可以改变系统的内能，即系统内能与系统外的能量会发生转化或转移，但能的总量不会改变。', '', '01010603030200'),
('01010603030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP02', '能量守恒是自然界的普遍规律。', '', '01010603030200'),
('01010603030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP03', '根据能量守恒定律，物理学发现和解释了很多科学现象。', '', '01010603030200'),
('01010603030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP04', '在20世纪30年代初，奥地利物理学家泡利根据能量守恒定律预言了中微子。', '', '01010603030200'),
('01010603030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP05', '能量守恒定律经受住了新的检验。', '', '01010603030200'),
('01010603030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP06', '能量守恒定律的发现是科学史上的重大事件。', '', '01010603030200'),
('01010603030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03', '永动机不可能制成', '', '01010603030000'),
('01010603030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP01', '“问题”栏目中的饮水小鸭不是永动机。', '', '01010603030300'),
('01010603030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP02', '小鸭的头和身由两个玻璃球构成，并通过细玻璃管相连。', '', '01010603030300'),
('01010603030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP03', '下球盛有一些乙醚，上球连同尖嘴被吸水毛毡包起来。', '', '01010603030300'),
('01010603030404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP04', '永动机的思想违背了能量守恒定律，所以是不可能制成的。', '', '01010603030300'),
('01010603030405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP05', '应该指出的是，制造永动机的千万次失败使人们的头脑冷静下来，开始在更深层次寻找失败的原因。', '', '01010603030300'),
('01010603030406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP06', '国科学院宣布“不再审查有关永动机的一切设计”。', '', '01010603030300'),
('01010603040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04', '热力学第二定律', '', '01010603000000'),
('01010603040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01', '热力学第二定律', '', '01010603040000'),
('01010603040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP01', '在电冰箱的实例中，热量的确从低温物体——冰箱内的食品，传到了高温物体——冰箱外的空气。', '', '01010603040100'),
('01010603040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP02', '从能量的角度看，热机的工作分为两个阶段：第一个阶段是燃烧燃料，把燃料中的化学能变成工作物质的内能；第二个阶段是工作物质对外做功，把自己的内能变成机械能。', '', '01010603040100'),
('01010603040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP03', '热力学第二定律的开尔文表述阐述了机械能与内能转化的方向性：机械能可以全部转化为内能，而内能无法全部用来做功以转化成机械能。', '', '01010603040100'),
('01010603040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP04', '可以证明，热力学第二定律的克劳修斯表述和开尔文表述是等价的。', '', '01010603040100'),
('01010603040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02', '能源是有限的', '', '01010603040000'),
('01010603040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP01', '自然界的一切变化，人类社会的所有活动，都伴随着能量的转移和转化，能量是一切物质运动的源泉，是一切生命活动的基础。', '', '01010603040200'),
('01010603040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP02', '能量在数量上虽然守恒，但其转移和转化却具有方向性。', '', '01010603040200'),
('01010603040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP03', '核能、生物能……最终都转化成内能，流散到周围的环境中。', '', '01010603040200'),
('01010603040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP04', '周围环境中的内能很难再利用，而机械能、光能、化学能、电能相对于周围环境中的内能来说，可利用的品质要高。', '', '01010603040200'),
('01010603040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP05', '能源的使用过程中虽然能的总量保持守恒，但能量的品质下降了。', '', '01010603040200'),
('01010603040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP06', '虽然能量总量不会减少，但能源会逐步减少，因此能源是有限的资源。', '', '01010603040200'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010604000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04', '原子结构和波粒二象性', '', NULL),
('01010604010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01', '普朗克黑体辐射理论', '', '01010604000000'),
('01010604010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01', '黑体与黑体辐射', '', '01010604010000'),
('01010604010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP01', '19世纪，由于冶金、星体测温等需求，人们对热辐射进行了大量的研究。', '', '01010604010100'),
('01010604010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP02', '研究表明，对于一般材料的物体，辐射电磁波的情况除了与温度有关，还与材料的种类及表面状况有关，而黑体辐射电磁波的强度按波长的分布只与黑体的温度有关。', '', '01010604010100'),
('01010604010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02', '黑体辐射的实验规律', '', '01010604010000'),
('01010604010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP01', '利用分光技术和热电偶等设备，可以测出黑体辐射电磁波的强度按波长分布的情况。', '', '01010604010200'),
('01010604010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP02', '从中可以看出，随着温度的升高，一方面，各种波长的辐射强度都有增加，另一方面，辐射强度的极大值向波长较短的方向移动。', '', '01010604010200'),
('01010604010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP03', '德国物理学家维恩在1896年、英国物理学家瑞利在1900年，分别提出了辐射强度按波长分布的理论公式。', '', '01010604010200'),
('01010604010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP04', '维恩公式在短波区与实验非常接近，在长波区则与实验偏离较大；瑞利公式在长波区与实验基本一致，但在短波区与实验严重不符。', '', '01010604010200'),
('01010604010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP05', '为了得出同全部实验相符的黑体辐射公式，德国物理学家普朗克做了多种尝试。', '', '01010604010200'),
('01010604010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03', '能量子', '', '01010604010000'),
('01010604010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP01', '弹簧振子的能量不一定是某个最小值的整数倍。', '', '01010604010300'),
('01010604010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP02', '而普朗克的假设则认为微观粒子的能量是量子化的，或者说微观粒子的能量是分立的。', '', '01010604010300'),
('01010604010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP03', '普朗克本人因此获得了1918年的诺贝尔物理学奖。', '', '01010604010300'),
('01010604020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02', '光电效应', '', '01010604000000'),
('01010604020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01', '光电效应的实验规律', '', '01010604020000'),
('01010604020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP01', '1887年，赫兹在研究电磁波的实验中偶尔发现，接收电路的间隙如果受到光照，就更容易产生电火花。', '', '01010604020100'),
('01010604020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP02', '科学家通过实验发现了以下规律。', '', '01010604020100'),
('01010604020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP03', '实验表明，不同金属的截止频率不同。', '', '01010604020100'),
('01010604020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP04', '在光照条件不变的情况下，随着所加电压的增大，光电流趋于一个饱和值。', '', '01010604020100'),
('01010604020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP05', '实验表明，在光的频率不变的情况下，入射光越强，饱和电流越大。', '', '01010604020100'),
('01010604020206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP06', '第四章原子结构和波粒二象性73表明产生电流的时间很快，即光电效应几乎是瞬时发生的。', '', '01010604020100'),
('01010604020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02', '光电效应经典解释中的疑难', '', '01010604020000'),
('01010604020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP01', '当光照射金属表面时，电子会吸收光的能量。', '', '01010604020200'),
('01010604020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP02', '光越强，逸出的电子数越多，光电流也就越大。', '', '01010604020200'),
('01010604020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP03', '●不管光的频率如何，只要光足够强，电子都可以获得足够能量从而逸出表面，不应存在截止频率。', '', '01010604020200'),
('01010604020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03', '爱因斯坦的光电效应理论', '', '01010604020000'),
('01010604020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP01', '对于光电效应的解释，爱因斯坦是在普朗克量子假说的基础上作出的。', '', '01010604020300'),
('01010604020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP02', '从下面的讨论可以看出，爱因斯坦光电效应方程可以很好地解释光电效应实验中的各种现象。', '', '01010604020300'),
('01010604020403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP03', '●电子一次性吸收光子的全部能量，不需要积累能量的时间，光电流自然几乎是瞬时产生的。', '', '01010604020300'),
('01010604020404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP04', '爱因斯坦由于提出了光电效应理论而获得1921年的诺贝尔物理学奖。', '', '01010604020300'),
('01010604020405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP05', '金属表面的光子数较多，照射金属时产生的光电子较多，因而饱和电流较大。', '', '01010604020300'),
('01010604020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04', '康普顿效应和光子的动量', '', '01010604020000'),
('01010604020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP01', '光可以与介质中的物质微粒发生散射，改变传播方向。', '', '01010604020400'),
('01010604020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP02', '康普顿效应让人们对光子有了更深入的认识。', '', '01010604020400'),
('01010604020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP03', '康普顿因此获得了1927年的诺贝尔物理学奖。', '', '01010604020400'),
('01010604020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05', '光的波粒二象性', '', '01010604020000'),
('01010604020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP01', '众所周知，在麦克斯韦的电磁理论建立之后，人们认识到光是一种电磁波，从而光的波动说被普遍接受，人们不再认为光是由粒子组成的。', '', '01010604020500'),
('01010604020602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP02', '而爱因斯坦的光电效应理论和康普顿效应理论表明，光在某些方面确实会表现得像是由一些粒子（即一个个有确定能量和动量的“光子”）组成的。', '', '01010604020500'),
('01010604020603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP03', '人们意识到，光既具有波动性，又具有粒子性。', '', '01010604020500'),
('01010604020604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP04', '从牛顿的微粒说、惠更斯和托马斯·杨的波动说，到麦克斯韦的电磁理论，再到爱因斯坦的光子理论乃至量子电动力学，人类对光的认识构成了一部科学史诗。', '', '01010604020500'),
('01010604030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03', '原子的核式结构模型', '', '01010604000000'),
('01010604030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01', '电子的发现', '', '01010604030000'),
('01010604030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP01', '为了找到有利于自己的证据，双方都做了许多实验。', '', '01010604030100'),
('01010604030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP02', '由实验测得的阴极射线粒子的比荷是氢离子①（也就是质子）比荷的近两千倍。', '', '01010604030100'),
('01010604030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP03', '电子电荷的精确测定是在1909〜1913年间由密立根通过著名的“油滴实验”做出的。', '', '01010604030100'),
('01010604030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP04', '金属受热还是放射性物质的自发辐射，都能发射同样的带电粒子——电子。', '', '01010604030100'),
('01010604030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02', '原子的核式结构模型', '', '01010604030000'),
('01010604030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP01', 'α粒子是从放射性物质（如铀和镭）中发射出来的快速运动的粒子，质量为氢原子质量的4倍、电子质量的7300倍。', '', '01010604030200'),
('01010604030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP02', '被散射的α粒子打在荧光屏上会有微弱的闪光产生。', '', '01010604030200'),
('01010604030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP03', '通过显微镜观察闪光就可以记录在某一时间内向某一方向散射的α粒子数。', '', '01010604030200'),
('01010604030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP04', 'α粒子有库仑力的作用，一些α粒子的运动方向改变，也就是发生了α粒子的散射。', '', '01010604030200'),
('01010604030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP05', 'α粒子所占的比例，可以推知原子中电荷的分布情况。', '', '01010604030200'),
('01010604030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP06', '卢瑟福分析了实验数据后认为，事实应该是：占原子质量绝大部分的带正电的物质集中在很小的空间范围。', '', '01010604030200'),
('01010604030307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP07', '1911年，卢瑟福提出了自己的原子结构模型。', '', '01010604030200'),
('01010604030308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP08', '因为正电体很小，当α粒子进入原子区域后，大部分离正电体很远，受到的库仑斥力很小，运动方向几乎不改变。', '', '01010604030200'),
('01010604030309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP09', '只有极少数α粒子在穿过时距离正电体很近，因此受到很强的库仑斥力，发生大角度散射。', '', '01010604030200'),
('01010604030310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP10', '按照卢瑟福的理论，正电体的尺度是很小的，称为原子核。', '', '01010604030200'),
('01010604030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03', '原子核的电荷与尺度', '', '01010604030000'),
('01010604030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP01', '又由于原子是电中性的，可以推算出原子内含有的电子数。', '', '01010604030300'),
('01010604030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP02', '通常用核半径描述核的大小。', '', '01010604030300'),
('01010604040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04', '氢原子光谱和玻尔的原子模型', '', '01010604000000'),
('01010604040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01', '光谱', '', '01010604040000'),
('01010604040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP01', '有的光谱看起来不是一条条分立的谱线，而是连在一起的光带，叫作连续谱。', '', '01010604040100'),
('01010604040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP02', '气体中中性原子的发光光谱都是线状谱，说明原子只发出几种特定频率的光。', '', '01010604040100'),
('01010604040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02', '氢原子光谱的实验规律', '', '01010604040000'),
('01010604040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP01', '光谱的结果显示氢原子只能发出一系列特定波长的光。', '', '01010604040200'),
('01010604040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP02', '巴耳末公式以简洁的形式反映了氢原子的线状光谱的特征。', '', '01010604040200'),
('01010604040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03', '经典理论的困难', '', '01010604040000'),
('01010604040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP01', '卢瑟福的核式结构模型正确地指出了原子核的存在，很好地解释了α粒子散射实验，但跟经典电磁理论发生了矛盾。', '', '01010604040300'),
('01010604040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP02', '核外电子受到原子核的库仑引力的作用，却没有被吸引到原子核上，而是在以一定的速度绕核运动。', '', '01010604040300'),
('01010604040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP03', '随着绕核运动轨道半径的不断变化，电子运动的频率也要不断变化，因此原子辐射电磁波的频率也要不断变化。', '', '01010604040300'),
('01010604040400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04', '玻尔原子理论的基本假设', '', '01010604040000'),
('01010604040501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP01', '丹麦物理学家玻尔意识到了经典理论在解释原子结构方面的困难。', '', '01010604040400'),
('01010604040502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP02', '玻尔的原子结构假说包括以下两方面的内容。', '', '01010604040400'),
('01010604040503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP03', '玻尔认为，原子中的电子在库仑引力的作用下，绕原子核做圆周运动，服从经典力学的规律。', '', '01010604040400'),
('01010604040504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP04', '在玻尔理论中，电子的轨道半径只可能是某些分立的数值。', '', '01010604040400'),
('01010604040505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP05', '当电子在不同的轨道上运动时，原子处于不同的状态，具有不同的能量。', '', '01010604040400'),
('01010604040506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP06', '根据玻尔理论，电子只能在特定轨道上运动，因此，原子的能量也只能取一系列特定的值。', '', '01010604040400'),
('01010604040507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP07', '按照玻尔的观点，电子在一系列定态轨道上运动，不会发生电磁辐射。', '', '01010604040400'),
('01010604040500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05', '玻尔理论对氢光谱的解释', '', '01010604040000'),
('01010604040601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP01', '从玻尔的基本假设出发，运用经典电磁学和经典力学的理论，可以计算氢原子中电子的可能轨道半径及相应的能量。', '', '01010604040500'),
('01010604040602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP02', '处于基态的原子非常稳定。', '', '01010604040500'),
('01010604040603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP03', '处于激发态的原子是不稳定的，会自发地向能量较低的能级跃迁，放出光子，最终回到基态。', '', '01010604040500'),
('01010604040604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP04', '由于原子的能级是分立的，所以放出的光子的能量也是分立的。', '', '01010604040500'),
('01010604040605', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP05', '由于不同的原子具有不同的结构，能级各不相同，因此辐射（或吸收）的光子频率也不相同。', '', '01010604040500'),
('01010604040606', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP06', '各种气体原子的能级不同，跃迁时发射光子的能量各异。', '', '01010604040500'),
('01010604040607', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP07', '街道上的霓虹灯、试电笔中的氖管，都是由灯管内的气体原子从高能级向低能级跃迁而发光的。', '', '01010604040500'),
('01010604040608', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP08', '食盐被灼烧时发的光，也主要是由食盐蒸气中钠原子的能级跃迁而造成的。', '', '01010604040500'),
('01010604040600', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06', '玻尔理论的局限性', '', '01010604040000'),
('01010604040701', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP01', '玻尔的原子理论第一次将量子观念引入原子领域，提出了定态和跃迁的概念。', '', '01010604040600'),
('01010604040702', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP02', '玻尔理论成功地解释了氢原子光谱的实验规律。', '', '01010604040600'),
('01010604040703', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP03', '①玻尔理论的不足之处在于保留了经典粒子的观念，仍然把电子的运动看作经典力学描述下的轨道运动。', '', '01010604040600'),
('01010604040704', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP04', '实际上，根据量子力学，原子中电子的坐标没有确定的值。', '', '01010604040600'),
('01010604040705', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP05', '当原子处于不同的状态时，电子在各处出现的概率是不一样的。', '', '01010604040600'),
('01010604050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05', '粒子的波动性和量子力学的建立', '', '01010604000000'),
('01010604050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01', '粒子的波动性', '', '01010604050000'),
('01010604050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01.OP01', '1924年，法国物理学家德布罗意在对光的波粒二象性、玻尔氢原子理论以及相对论的深入研究的基础上，把波粒二象性推广到实物粒子，如电子、质子等。', '', '01010604050100'),
('01010604050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02', '物质波的实验验证', '', '01010604050000'),
('01010604050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP01', '德布罗意关于实物粒子具有波动性的假说在当时来看是难以理解的。', '', '01010604050200'),
('01010604050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP02', '1929年，德布罗意因提出物质波的假说获得了诺贝尔物理学奖。', '', '01010604050200'),
('01010604050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP03', '除了电子以外，后来还陆续证实了中子、质子以及原子、分子的波动性。', '', '01010604050200'),
('01010604050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP04', '德布罗意提出物质波的观念被实验证实，表明电子、质子、原子等粒子不但具有粒子的性质，而且具有波动的性质。', '', '01010604050200'),
('01010604050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03', '量子力学的建立', '', '01010604050000'),
('01010604050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP01', '19、20世纪之交，人们在黑体辐射、光电效应、氢原子光谱等许多类问题中，都发现了经典物理学无法解释的现象。', '', '01010604050300'),
('01010604050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP02', '普朗克黑体辐射理论、爱因斯坦光电效应理论、康普顿散射理论、玻尔氢原子理论以及德布罗意物质波假说等一系列理论在解释实验方面都取得了成功。', '', '01010604050300'),
('01010604050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP03', '1925年，德国物理学家海森堡和玻恩等人对玻尔的氢原子理论进行了推广和改造，使之可以适用于更普遍的情况。', '', '01010604050300'),
('01010604050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04', '量子力学的应用', '', '01010604050000'),
('01010604050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP01', '量子力学被应用到众多具体物理系统中，得到了与实验符合得很好的结果，获得了极大的成功。', '', '01010604050400'),
('01010604050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP02', '借助量子力学，人们深入认识了微观世界的组成、结构和属性。', '', '01010604050400'),
('01010604050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP03', '量子力学推动了核物理和粒子物理的发展。', '', '01010604050400'),
('01010604050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP04', '人们认识了原子、原子核、基本粒子等各个微观层次的物质结构。', '', '01010604050400'),
('01010604050505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP05', '而粒子物理学的发展又促进了天文学和宇宙学的研究。', '', '01010604050400'),
('01010604050506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP06', '从整个宇宙到微小的粒子，人类探索自然的视野前所未有的宽广。', '', '01010604050400'),
('01010604050507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP07', '人们惊讶地发现，世界具有奇妙的结构，最微观层次和最宏观层次的规律，竟有着紧密的联系。', '', '01010604050400'),
('01010604050508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP08', '核物理的发展，还让人们成功地认识并利用了原子核反应堆所释放的能量——核能。', '', '01010604050400'),
('01010604050509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP09', '”量子力学推动了原子、分子物理和光学的发展。', '', '01010604050400'),
('01010604050510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP10', '人们认识了原子的结构，以及原子、分子和电磁场相互作用的方式。', '', '01010604050400'),
('01010604050511', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP11', '在此基础上，发展了各式各样的对原子和电磁场进行精确操控和测量的技术，如激光、核磁共振、原子钟，等等。', '', '01010604050400'),
('01010604050512', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP12', '以利用振荡的磁场测量材料中原子的性质，因此，被广泛地用于化学、生物研究和医学诊断。', '', '01010604050400'),
('01010604050513', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP13', '原子钟利用原子为电磁波校准频率，从而实现了对时间的高精度测量。', '', '01010604050400'),
('01010604050514', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP14', '在日常生活和国家安全中发挥巨大作用的卫星定位技术，其核心部件就是原子钟。', '', '01010604050400'),
('01010604050515', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP15', '量子力学推动了固体物理的发展。', '', '01010604050400'),
('01010604050516', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP16', '人们了解了固体中电子运行的规律，并弄清了为什么固体有导体、绝缘体和半导体之分。', '', '01010604050400'),
('01010604050517', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP17', '量子力学的应用还有很多。', '', '01010604050400'),
('01010604050518', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP18', '毫不夸张地说，在过去的近一百年中，量子力学极大地推动了人类的进步。', '', '01010604050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010605000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05', '原子核', '', NULL),
('01010605010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01', '原子核的组成', '', '01010605000000'),
('01010605010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01', '天然放射现象', '', '01010605010000'),
('01010605010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP01', '受到贝克勒尔的发现的鼓舞，波兰裔法国物理学家玛丽·居里和她的丈夫皮埃尔·居里对铀和含铀的各种矿石进行了深入研究。', '', '01010605010100'),
('01010605010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP02', '放射性元素自发地发出射线的现象，叫作天然放射现象。', '', '01010605010100'),
('01010605010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02', '射线的本质', '', '01010605010000'),
('01010605010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP01', '把放射源铀、钋或镭放入用铅做成的容器中，射线只能从容器的小孔射出，成为细细的一束。', '', '01010605010200'),
('01010605010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP02', '物理学家经过多方面的研究后确认α射线、β射线和γ射线具有以下特征。', '', '01010605010200'),
('01010605010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP03', 'α粒子带正电，电荷量是电子的2倍，质量是氢原子的4倍，其组成与氦原子核相同。', '', '01010605010200'),
('01010605010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP04', '由于α粒子带电，质量又比较大，通过气体时很容易把气体分子中的电子剥离，使气体电离。', '', '01010605010200'),
('01010605010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP05', '是电子流，速度可以接近光速。', '', '01010605010200'),
('01010605010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP06', 'β射线的电离作用较弱，穿透能力较强，很容易穿透黑纸，也能穿透几毫米厚的铝板。', '', '01010605010200'),
('01010605010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP07', 'γ射线的电离作用更弱，穿透能力更强，甚至能穿透几厘米厚的铅板和几十厘米厚的混凝土。', '', '01010605010200'),
('01010605010308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP08', '放射性的强度也不受温度、外界压强的影响。', '', '01010605010200'),
('01010605010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03', '原子核的组成', '', '01010605010000'),
('01010605010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP01', '绝大多数原子核的质量与电荷量之比都大于质子的相应比值。', '', '01010605010300'),
('01010605010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP02', '质子带正电荷，电荷量与一个电子的电荷量相等。', '', '01010605010300'),
('01010605010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP03', '由于中子不带电，原子核所带的电荷等于核内质子电荷的总和。', '', '01010605010300'),
('01010605010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP04', '元素的性质与原子核外的电子有密切的关系。', '', '01010605010300'),
('01010605020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02', '放射性元素的衰变', '', '01010605000000'),
('01010605020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01', '原子核的衰变', '', '01010605020000'),
('01010605020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP01', '铀238放出一个α粒子后，质量数减少4，电荷数减少2，成为新核。', '', '01010605020100'),
('01010605020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP02', '0、电荷数为－1，可以把电子表示为0-1。', '', '01010605020100'),
('01010605020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP03', '研究还表明，在一定条件下，原子核中的两个中子和两个质子会结合起来形成α粒子，并被释放出来。', '', '01010605020100'),
('01010605020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP04', '原子核的能量也跟原子的能量一样，其变化是不连续的，也只能取一系列不连续的数值，因此，也存在着能级，同样是能级越低越稳定。', '', '01010605020100'),
('01010605020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP05', '当放射性物质连续衰变时，原子核中有的发生α衰变，有的发生β衰变，同时伴随着γ射线辐射。', '', '01010605020100'),
('01010605020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02', '半衰期', '', '01010605020000'),
('01010605020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP01', '放射性同位素衰变的快慢有一定的规律。', '', '01010605020200'),
('01010605020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP02', '不同的放射性元素，半衰期不同，甚至差别非常大。', '', '01010605020200'),
('01010605020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP03', '放射性元素衰变的快慢是由核内部自身的因素决定的，跟原子所处的化学状态和外部条件没有关系。', '', '01010605020200'),
('01010605020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03', '核反应', '', '01010605020000'),
('01010605020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP01', '衰变是原子核的自发变化，科学家更希望人工控制原子核的变化。', '', '01010605020300'),
('01010605020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP02', '与衰变过程一样，在核反应中，质量数守恒、电荷数守恒。', '', '01010605020300'),
('01010605020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04', '放射性同位素及其应用', '', '01010605020000'),
('01010605020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP01', '天然放射性同位素不过40多种，而今天通过核反应生成的人工放射性同位素已达3000多种，每种元素都有了自己的放射性同位素。', '', '01010605020400'),
('01010605020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP02', '丰富的放射性同位素资源在国民经济和科学研究的各个领域中得到了广泛的应用。', '', '01010605020400'),
('01010605020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP03', '工业部门可以使用放射性同位素发出的射线来测厚度。', '', '01010605020400'),
('01010605020504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP04', '像癌细胞那样，不断迅速繁殖的、无法控制的细胞组织，在射线照射下破坏得比健康细胞快。', '', '01010605020400'),
('01010605020505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP05', '利用γ射线照射种子，会使种子的遗传基因发生变异，经过筛选，可以培育出新品种。', '', '01010605020400'),
('01010605020506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP06', '棉花在开花、结桃的时候需要较多的磷肥，把磷肥喷在棉花叶子上，磷肥也能被吸收。', '', '01010605020400'),
('01010605020507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP07', '如果用磷的放射性同位素制成肥料喷在棉花的叶面上，然后每隔一定时间用探测器测量棉株各部位的放射性强度，上面的问题就解决了。', '', '01010605020400'),
('01010605020508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP08', '人体甲状腺的工作需要碘，碘被吸收后聚集在甲状腺内。', '', '01010605020400'),
('01010605020509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP09', '定时用探测器测量甲状腺及邻近组织的放射强度，有助于诊断甲状腺的疾病。', '', '01010605020400'),
('01010605020510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP10', '近年来，有关生物大分子的结构及其功能的研究，几乎都要借助于示踪原子。', '', '01010605020400'),
('01010605020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05', '辐射与安全', '', '01010605020000'),
('01010605020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05.OP01', '人类一直生活在放射性的环境中。', '', '01010605020500'),
('01010605030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03', '核力与结合能', '', '01010605000000'),
('01010605030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01', '核力与四种基本相互作用', '', '01010605030000'),
('01010605030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP01', '现代物理学认为，自然界存在着四种基本相互作用，即引力相互作用、电磁相互作用、强相互作用和弱相互作用。', '', '01010605030100'),
('01010605030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP02', '17世纪下半叶，人们发现了万有引力。', '', '01010605030100'),
('01010605030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP03', '引力是自然界的一种基本相互作用，地面物体所受的重力只是引力在地球表面附近的一种表现。', '', '01010605030100'),
('01010605030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP04', '20世纪，物理学家发现原子核是由若干带正电荷的质子和不带电的中子组成的。', '', '01010605030100'),
('01010605030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP05', '19世纪末20世纪初，物理学家发现了放射现象。', '', '01010605030100'),
('01010605030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP06', '①弱相互作用是引起原子核β衰变的原因，即引起中子—质子转变的原因。', '', '01010605030100'),
('01010605030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02', '结合能', '', '01010605030000'),
('01010605030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP01', '相距很远的两个物体，由于万有引力而相互接近，运动速度越来越大，引力势能转化为动能。', '', '01010605030200'),
('01010605030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP02', '最后撞在一起，一部分动能变成热并散失掉了。', '', '01010605030200'),
('01010605030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP03', '比结合能越大，原子核中核子结合得越牢固，原子核越稳定。', '', '01010605030200'),
('01010605030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03', '质量亏损', '', '01010605030000'),
('01010605030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP01', '原子核的结合能很难直接测量。', '', '01010605030300'),
('01010605030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP02', '质量亏损表明，的确存在着原子核的结合能。', '', '01010605030300'),
('01010605040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04', '核裂变与核聚变', '', '01010605000000'),
('01010605040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01', '核裂变的发现', '', '01010605040000'),
('01010605040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP01', '以铀235为例，核裂变时产生两或三个中子。', '', '01010605040100'),
('01010605040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP02', '原子核的体积非常小，原子内部的空隙很大，如果铀块不够大，中子在铀块中通过时，很有可能碰不到铀核而跑到铀块外面去，链式反应不能继续。', '', '01010605040100'),
('01010605040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP03', '只有当铀块足够大时，核裂变产生的中子才有足够大的概率打中某个铀核，使链式反应进行下去。', '', '01010605040100'),
('01010605040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP04', '铀核裂变时如果生成物不同，释放的能量也有差异。', '', '01010605040100'),
('01010605040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02', '反应堆与核电站', '', '01010605040000'),
('01010605040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP01', '原子核的链式反应也可以在人工控制下进行。', '', '01010605040200'),
('01010605040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP02', '其实，在第一个原子弹制成以前，科学家们已经实现了核能的可控释放。', '', '01010605040200'),
('01010605040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP03', '1942年，美籍意大利物理学家费米就主持建立了世界上第一个称为“核反应堆”的装置，首次通过可控制的链式反应实现了核能的释放。', '', '01010605040200'),
('01010605040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP04', '常用的慢化剂有石墨、重水和普通水（也叫轻水）。', '', '01010605040200'),
('01010605040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP05', '为了调节中子数目以控制反应速度，还需要在铀棒之间插进一些镉棒。', '', '01010605040200'),
('01010605040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP06', '核燃料发生核裂变释放的能量使反应区温度升高。', '', '01010605040200'),
('01010605040307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP07', '水或液态的金属钠等流体在反应堆内外循环流动，把反应堆内的热量传输出去，用于发电，同时也使反应堆冷却。', '', '01010605040200'),
('01010605040308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP08', '在热交换器内，第二回路中的水被加热生成高温高压蒸汽，驱动汽轮机运转。', '', '01010605040200'),
('01010605040309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP09', '建造核电站时需要特别注意防止放射线和放射性物质的泄漏，以避免射线对人体的伤害和放射性物质对水源、空气和工作场所造成放射性污染。', '', '01010605040200'),
('01010605040310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP10', '核反应堆中的核废料具有很强的放射性，需要装入特制的容器，深埋地下。', '', '01010605040200'),
('01010605040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03', '核聚变', '', '01010605040000'),
('01010605040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP01', '当物质的温度达到几百万开尔文时，剧烈的热运动使得一部分原子核具有足够的动能，可以克服库仑斥力，碰撞时十分接近，发生核聚变。', '', '01010605040300'),
('01010605040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP02', '太阳的主要成分是氢。', '', '01010605040300'),
('01010605040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP03', '现在地球上消耗的能量，追根溯源，绝大部分还是来自太阳，即太阳内部核聚变时释放的核能。', '', '01010605040300'),
('01010605040404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP04', '太阳在“核燃烧”的过程中“体重”不断减轻。', '', '01010605040300'),
('01010605040405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP05', '太阳的寿命已经有50亿年了。', '', '01010605040300'),
('01010605040406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP06', '目前，人工产生的热核反应主要用在核武器上，那就是氢弹。', '', '01010605040300'),
('01010605040407', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP07', '与核裂变相比，核聚变有很多优点。', '', '01010605040300'),
('01010605040408', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP08', '利用核聚变物质的惯性进行约束。', '', '01010605040300'),
('01010605040409', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP09', '总的来说，实现受控核聚变还有一段很长的路要走。', '', '01010605040300'),
('01010605050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05', '“基本”粒子', '', '01010605000000'),
('01010605050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01', '发现新粒子', '', '01010605050000'),
('01010605050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP01', '从20世纪30年代以来，人们在对宇宙线的研究中陆续发现了一些新的粒子。', '', '01010605050100'),
('01010605050202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP02', '1932年发明的粒子加速器，能使带电粒子加速到很高的能量。', '', '01010605050100'),
('01010605050203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP03', '利用加速器和对撞机，人们发现了更多的粒子。', '', '01010605050100'),
('01010605050204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP04', '粒子物理的研究不再单纯依赖宇宙线。', '', '01010605050100'),
('01010605050205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP05', '高能量的对撞机往往极为庞大和复杂。', '', '01010605050100'),
('01010605050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02', '粒子的分类', '', '01010605050000'),
('01010605050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP01', '现在已经发现的粒子达400多种。', '', '01010605050200'),
('01010605050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP02', '强子是参与强相互作用的粒子。', '', '01010605050200'),
('01010605050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP03', '质子和中子都是强子。', '', '01010605050200'),
('01010605050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP04', '轻子不参与强相互作用。', '', '01010605050200'),
('01010605050305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP05', '最早发现的轻子是电子，后来发现的轻子有电子中微子、μ子、μ子中微子以及τ子和τ子中微子。', '', '01010605050200'),
('01010605050306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP06', '每种轻子都有对应的反粒子。', '', '01010605050200'),
('01010605050307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP07', '现代实验还没有发现轻子的内部结构。', '', '01010605050200'),
('01010605050308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP08', '光子传递电磁相互作用，中间玻色子传递弱相互作用，胶子传递强相互作用。', '', '01010605050200'),
('01010605050309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP09', '希格斯玻色子是希格斯场的量子激发。', '', '01010605050200'),
('01010605050310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP10', '基本粒子因与希格斯场耦合而获得质量。', '', '01010605050200'),
('01010605050311', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP11', '2012年，欧洲核子研究中心利用大型强子对撞机发现了希格斯玻色子。', '', '01010605050200'),
('01010605050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03', '研究样例', '', '01010605050000'),
('01010605050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP01', '①燃气灶烧水时的能量转化和转移的情况如下。', '', '01010605050300'),
('01010605050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP02', '记录数据时室温为26℃，基本上保证了实验环境温度的恒定。', '', '01010605050300'),
('01010605050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP03', '为减小水在高温时的放热损失，应缩短水在高温时的加热时间。', '', '01010605050300'),
('01010605050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP04', '用实验检验研究结论的正确性。', '', '01010605050300'),
('01010605050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04', '参考选题', '', '01010605050000'),
('01010605050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP01', '使用热水器洗澡时，常有两种调温习惯：一种是把加热后的水温直接调节至洗澡的合适温度，如38℃左右；另一种是把热水器的加热温度调到更高，如50℃左右，再通过淋浴水龙头加兑冷水至合适水温，后者可以获得更大的适温水量。', '', '01010605050400'),
('01010605050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP02', '热水器工作前，先观察燃气表示数，测量加热前水的温度。', '', '01010605050400'),
('01010605050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP03', '观察燃气表示数，记录所用去的燃气体积。', '', '01010605050400'),
('01010605050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP04', '如果已知被加热水的体积，查找燃气的热值，还可以算出两种情况下热效率的大小。', '', '01010605050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010601000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01', '分子动理论', '', NULL),
('01010601010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01', '分子动理论的基本内容', '', '01010601000000'),
('01010601010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01', '物体是由大量分子组成的', '', '01010601010000'),
('01010601010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP01', '人们用肉眼无法直接看到分子，就是用高倍的光学显微镜也看不到。', '', '01010601010100'),
('01010601010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP02', '直至1982年，人们研制了能放大几亿倍的扫描隧道显微镜③，才观察到物质表面原子的排列。', '', '01010601010100'),
('01010601010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02', '分子热运动', '', '01010601010000'),
('01010601010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP01', '扩散现象是物质分子永不停息地做无规则运动的证据之一。', '', '01010601010200'),
('01010601010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP02', '扩散现象在科学技术中有很多应用。', '', '01010601010200'),
('01010601010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP03', '19世纪初，一些人观察到，悬浮在液体中的小颗粒总在不停地运动。', '', '01010601010200'),
('01010601010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP04', '从实验结果可以看出，小炭粒的运动是无规则的，温度越高，小炭粒的运动越明显。', '', '01010601010200'),
('01010601010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP05', '在扩散现象中，温度越高，扩散得越快。', '', '01010601010200'),
('01010601010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP06', '观察布朗运动，温度越高，悬浮微粒的运动就越明显。', '', '01010601010200'),
('01010601010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP07', '温度是分子热运动剧烈程度的标志。', '', '01010601010200'),
('01010601010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03', '分子间的作用力', '', '01010601010000'),
('01010601010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP01', '水和酒精混合后的总体积变小了。', '', '01010601010300'),
('01010601010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP02', '当用力拉伸物体时，物体内各部分之间要产生反抗拉伸的作用力，此时分子间的作用力表现为引力。', '', '01010601010300'),
('01010601010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP03', '当用力压缩物体时，物体内各部分之间会产生反抗压缩的作用力，此时分子间的作用力表现为斥力。', '', '01010601010300'),
('01010601010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP04', '原子内部有带正电的原子核和带负电的电子。', '', '01010601010300'),
('01010601010400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP04', '分子动理论', '', '01010601010000'),
('01010601020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02', '实验：用油膜法估测油酸分子的大小', '', '01010601000000'),
('01010601020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP01', '实验思路', '', '01010601020000'),
('01010601020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP02', '物理量的测量', '', '01010601020000'),
('01010601020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP03', '数据分析', '', '01010601020000'),
('01010601030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03', '分子运动速率分布规律', '', '01010601000000'),
('01010601030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01', '气体分子运动的特点', '', '01010601030000'),
('01010601030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP01', '热现象与大量分子热运动的统计规律有关。', '', '01010601030100'),
('01010601030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP02', '要研究气体的热现象，就要了解气体分子运动的特点。', '', '01010601030100'),
('01010601030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP03', '液体变为气体后，体积要增大上千倍，可见，气体分子间距离大约是分子直径的10倍。', '', '01010601030100'),
('01010601030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP04', '分子的大小相对分子间的空隙来说很小，所以，可以把气体分子视为质点。', '', '01010601030100'),
('01010601030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP05', '分子的运动杂乱无章，在某一时刻，向着任何一个方向运动的分子都有，而且向各个方向运动的分子数目几乎相等。', '', '01010601030100'),
('01010601030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02', '分子运动速率分布图像', '', '01010601030000'),
('01010601030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP01', '尽管分子做无规则运动，速率有大有小，但大量分子的速率却按一定的规律分布。', '', '01010601030200'),
('01010601030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP02', '100℃两种不同情况下的速率分布情况。', '', '01010601030200'),
('01010601030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP03', '100℃的氧气，速率大的分子比例较多，其分子的平均速率比0℃的大。', '', '01010601030200'),
('01010601030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03', '气体压强的微观解释', '', '01010601030000'),
('01010601030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP01', '器壁单位面积上受到的压力，就是气体的压强。', '', '01010601030300'),
('01010601030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP02', '从微观角度来看，气体分子的数量是巨大的。', '', '01010601030300'),
('01010601040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04', '分子动能和分子势能', '', '01010601000000'),
('01010601040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01', '分子动能', '', '01010601040000'),
('01010601040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP01', '物体中分子热运动的速率大小不一，所以各个分子的动能也有大有小，而且在不断改变。', '', '01010601040100'),
('01010601040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP02', '温度升高时，分子的热运动加剧，温度越高，分子热运动的平均动能越大。', '', '01010601040100'),
('01010601040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP03', '温度越低，分子热运动的平均动能越小。', '', '01010601040100'),
('01010601040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02', '分子势能', '', '01010601040000'),
('01010601040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP01', '分子间存在着相互作用力，可以证明分子间的作用力所做的功与路径无关，分子组成的系统具有分子势能。', '', '01010601040200'),
('01010601040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP02', '物体的体积变化时，分子间距离将发生变化，因而分子势能随之改变。', '', '01010601040200'),
('01010601040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03', '物体的内能', '', '01010601040000'),
('01010601040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP01', '任何物体都具有内能。', '', '01010601040300'),
('01010601040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP02', '分子热运动的平均动能与温度有关，分子势能与物体的体积有关。', '', '01010601040300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010602000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02', '气体、固体和液体', '', NULL),
('01010602010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01', '温度和温标', '', '01010602000000'),
('01010602010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01', '状态参量与平衡态', '', '01010602010000'),
('01010602010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01.OP01', '要定量地描述系统的状态往往很难，因为有时系统的参量会随时间变化。', '', '01010602010100'),
('01010602010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP02', '热平衡与温度', '', '01010602010000'),
('01010602010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03', '温度计与温标', '', '01010602010000'),
('01010602010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03.OP01', '现代科学中用得更多的是热力学温标。', '', '01010602010300'),
('01010602020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC02', '气体的等温变化', '', '01010602000000'),
('01010602030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03', '气体的等压变化和等容变化', '', '01010602000000'),
('01010602030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP01', '气体的等压变化', '', '01010602030000'),
('01010602030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02', '气体的等容变化', '', '01010602030000'),
('01010602030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02.OP01', '法国科学家查理在分析了实验事实后发现，当一定质量的气体体积一定时，各种气体的压强与温度之间都有线性关系。', '', '01010602030200'),
('01010602030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03', '理想气体', '', '01010602030000'),
('01010602030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP01', '当压强很大、温度很低时，由上述规律计算的结果与实际测量结果有很大的差别。', '', '01010602030300'),
('01010602030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP02', '实际气体的分子之间有相互作用力，但是作用力很小；分子也有大小，但气体分子之间的间距比分子直径大得多；气体分子与器壁碰撞几乎是弹性的，动能损失也很小。', '', '01010602030300'),
('01010602030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP03', '在温度不低于零下几十摄氏度、压强不超过大气压的几倍时，把实际气体当成理想气体来处理，误差很小。', '', '01010602030300'),
('01010602030400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04', '气体实验定律的微观解释', '', '01010602030000'),
('01010602030501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP01', '用分子动理论可以定性解释气体的实验定律。', '', '01010602030400'),
('01010602030502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP02', '一定质量的某种理想气体，温度保持不变时，分子的平均动能是一定的。', '', '01010602030400'),
('01010602030503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP03', '一定质量的某种理想气体，温度升高时，分子的平均动能增大；只有气体的体积同时增大，使分子的数密度减小，才能保持压强不变。', '', '01010602030400'),
('01010602030504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP04', '一定质量的某种理想气体，体积保持不变时，分子的数密度保持不变。', '', '01010602030400'),
('01010602040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04', '固体', '', '01010602000000'),
('01010602040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01', '晶体和非晶体', '', '01010602040000'),
('01010602040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP01', '固体可以分为晶体和非晶体两类。', '', '01010602040100'),
('01010602040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP02', '石英、云母①、明矾、食盐、硫酸铜、味精等是晶体。', '', '01010602040100'),
('01010602040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP03', '玻璃、蜂蜡、松香、沥青、橡胶等是非晶体。', '', '01010602040100'),
('01010602040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP04', '非晶体没有规则的外形。', '', '01010602040100'),
('01010602040205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP05', '除了形状是否规则外，晶体和非晶体在物理性质上也有所不同。', '', '01010602040100'),
('01010602040206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP06', '从实验结果可以看出，玻璃片上石蜡熔化区域的形状近似于圆形，表明玻璃沿各个方向的导热性能相同；云母片上石蜡熔化区域的形状呈椭圆形，表明云母沿不同方向的导热性能不同。', '', '01010602040100'),
('01010602040207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP07', '蔗糖受潮后粘在一起形成的糖块，看起来没有确定的几何形状。', '', '01010602040100'),
('01010602040208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP08', '粘在一起的糖块也是多晶体，单个的蔗糖晶体颗粒是单晶体。', '', '01010602040100'),
('01010602040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02', '晶体的微观结构', '', '01010602040000'),
('01010602040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP01', '在20世纪70年代，人们又用电子显微镜观察到了铀、钍原子的像。', '', '01010602040200'),
('01010602040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP02', '1982年，人们用扫描隧道显微镜观察到物质表面原子的排列。', '', '01010602040200'),
('01010602040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP03', '在各种晶体中，原子（或分子、离子）都是按照一定的规则排列的，具有空间上的周期性。', '', '01010602040200'),
('01010602040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP04', '石墨是层状结构，层与层之间距离较大，原子间的作用力比较弱，所以石墨质地松软，可以用来制作粉状润滑剂；金刚石中碳原子间的作用力很强，所以金刚石有很大的硬度，可以用来切割玻璃。', '', '01010602040200'),
('01010602040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP05', '同种物质也可能以晶体和非晶体两种不同的形态出现，也就是说，物质是晶体还是非晶体，并不是绝对的。', '', '01010602040200'),
('01010602040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP06', '有些非晶体在一定条件下也可以转化为晶体。', '', '01010602040200'),
('01010602050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05', '液体', '', '01010602000000'),
('01010602050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP01', '液体的表面张力', '', '01010602050000'),
('01010602050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02', '浸润和不浸润', '', '01010602050000'),
('01010602050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP01', '管的内径越小，上述现象就越明显。', '', '01010602050200'),
('01010602050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP02', '有些物体内部有许多细小的孔道，孔道起到了毛细管的作用。', '', '01010602050200'),
('01010602050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP03', '当液体和与之接触的固体的相互作用比液体分子之间的相互作用强时，液体能够浸润固体。', '', '01010602050200'),
('01010602050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP04', '如果把水银滴到干净的铅板上，水银就会附着在铅板上，很难擦去。', '', '01010602050200'),
('01010602050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03', '液晶', '', '01010602050000'),
('01010602050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP01', '液晶在现代生活中扮演着重要角色，广泛应用于手机屏幕、平板电视等显示设备中。', '', '01010602050300'),
('01010602050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP02', '液晶态既具有液体的流动性，又在一定程度上具有晶体分子的规则排列的性质。', '', '01010602050300'),
('01010602050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP03', '有些物质在特定的温度范围之内具有液晶态；另一些物质，在适当的溶剂中溶解时，在一定的浓度范围具有液晶态。', '', '01010602050300'),
('01010602050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP04', '分子取向排列的液晶具有光学各向异性，具体地说，沿分子长轴方向上的折射率不同于沿短轴的。', '', '01010602050300'),
('01010602050405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP05', '入射光的偏振方向与液晶分子长轴的方向成不同夹角时，液晶对光的折射率不同。', '', '01010602050300'),
('01010602050406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP06', '液晶的研究在生命科学中也有应用。', '', '01010602050300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010603000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03', '热力学定律', '', NULL),
('01010603010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01', '功、热和内能的改变', '', '01010603000000'),
('01010603010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01', '焦耳的实验', '', '01010603010000'),
('01010603010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP01', '在焦耳研究功与热等效性的许多实验中，有两个最具代表性。', '', '01010603010100'),
('01010603010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP02', '一个是让重物下落带动叶片搅拌容器中的水，引起水温上升；另一个是通过电流的热效应给水加热。', '', '01010603010100'),
('01010603010203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP03', '焦耳的多次实验测量表明，尽管各次悬挂重物的质量不同，下落的高度也不一样，但只要重力所做的功相同，容器内水温上升的数值都是相同的，即系统状态的变化是相同的。', '', '01010603010100'),
('01010603010204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP04', '多次实验结果表明，对于同一个系统，如果过程是绝热的，那么不管通过电阻丝的电流或大或小、通电时间或长或短，只要所做的电功相等，则系统温度上升的数值是相同的，即系统的状态就发生了同样的变化。', '', '01010603010100'),
('01010603010205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP05', '两个位置间移动时重力所做的功。', '', '01010603010100'),
('01010603010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02', '功与内能的改变', '', '01010603010000'),
('01010603010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP01', '当温度达到易燃物的燃点时，易燃物发生自燃。', '', '01010603010200'),
('01010603010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP02', '当橡胶塞跳出时，瓶内出现白雾。', '', '01010603010200'),
('01010603010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP03', '以瓶内气体为研究对象，橡胶塞跳出后，瓶内的气体迅速膨胀，系统对外做功，因此，气体的内能迅速减少，瓶内气体温度迅速下降，瓶内水蒸气液化，就会出现大量的雾状小水滴。', '', '01010603010200'),
('01010603010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03', '热与内能的改变', '', '01010603010000'),
('01010603010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03.OP01', '外界对系统做功可以改变系统的热力学状态，而单纯地对系统传热也能改变系统的热力学状态。', '', '01010603010300'),
('01010603020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02', '热力学第一定律', '', '01010603000000'),
('01010603020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01', '热力学第一定律', '', '01010603020000'),
('01010603020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01.OP01', '（1）求上述压缩过程中气体内能的变化量。', '', '01010603020100'),
('01010603020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP02', '热力学第一定律的应用', '', '01010603020000'),
('01010603030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03', '能量守恒定律', '', '01010603000000'),
('01010603030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01', '探索能量守恒的足迹', '', '01010603030000'),
('01010603030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP01', '能量的概念是人类在对物质运动规律进行长期探索中建立起来的。', '', '01010603030100'),
('01010603030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP02', '所有自然现象都涉及能量，人类的任何活动都离不开能量。', '', '01010603030100'),
('01010603030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP03', '能量具有不同的形式，有描述热运动的内能、描述机械运动的机械能、描述光辐射的光能，等等。', '', '01010603030100'),
('01010603030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP04', '不同形式的运动都可以用能量来描述。', '', '01010603030100'),
('01010603030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP05', '在认识自然的进程中，科学家慢慢知道了要用联系的观点去观察自然。', '', '01010603030100'),
('01010603030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP06', '由于能量既抽象又以各种不同的形式广泛存在，所以能量守恒思想的萌芽及产生并不是一蹴而就的。', '', '01010603030100'),
('01010603030207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP07', '从各种实验的验证，再到实验结果的被质疑，科学家们付出了大量的努力。', '', '01010603030100'),
('01010603030208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP08', '从18世纪末到19世纪中叶，不同领域的科学家从不同角度都提出过能量守恒的思想。', '', '01010603030100'),
('01010603030209', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP09', '人类对能量守恒的认识经历了一个由浅入深、由含糊到清晰的过程。', '', '01010603030100'),
('01010603030210', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP10', '俄国化学家盖斯的研究发现，任何一个化学反应，不论是一步完成，还是分几步完成，放出的总热量相同。', '', '01010603030100'),
('01010603030211', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP11', '焦耳的实验精确地测量了做功与传热之间的等价关系，从而为能量守恒定律奠定了牢固的实验基础，也为能量守恒的定量描述迈出了重要的一步。', '', '01010603030100'),
('01010603030212', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP12', '德国医生迈尔通过比对不同地区人血颜色的差异，认识到食物中化学能与内能的等效性，即生物体内能量的输入和输出是平衡的。', '', '01010603030100'),
('01010603030213', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP13', '半个多世纪人类对能量以及能量守恒研究的历史，预示着人们把各分立的领域连成一体的时刻已经到来，也就是到了建立能量转化与守恒定律的时候了。', '', '01010603030100'),
('01010603030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02', '能量守恒定律', '', '01010603030000'),
('01010603030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP01', '在热力学领域内，做功和传热可以改变系统的内能，即系统内能与系统外的能量会发生转化或转移，但能的总量不会改变。', '', '01010603030200'),
('01010603030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP02', '能量守恒是自然界的普遍规律。', '', '01010603030200'),
('01010603030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP03', '根据能量守恒定律，物理学发现和解释了很多科学现象。', '', '01010603030200'),
('01010603030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP04', '在20世纪30年代初，奥地利物理学家泡利根据能量守恒定律预言了中微子。', '', '01010603030200'),
('01010603030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP05', '能量守恒定律经受住了新的检验。', '', '01010603030200'),
('01010603030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP06', '能量守恒定律的发现是科学史上的重大事件。', '', '01010603030200'),
('01010603030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03', '永动机不可能制成', '', '01010603030000'),
('01010603030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP01', '“问题”栏目中的饮水小鸭不是永动机。', '', '01010603030300'),
('01010603030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP02', '小鸭的头和身由两个玻璃球构成，并通过细玻璃管相连。', '', '01010603030300'),
('01010603030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP03', '下球盛有一些乙醚，上球连同尖嘴被吸水毛毡包起来。', '', '01010603030300'),
('01010603030404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP04', '永动机的思想违背了能量守恒定律，所以是不可能制成的。', '', '01010603030300'),
('01010603030405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP05', '应该指出的是，制造永动机的千万次失败使人们的头脑冷静下来，开始在更深层次寻找失败的原因。', '', '01010603030300'),
('01010603030406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP06', '国科学院宣布“不再审查有关永动机的一切设计”。', '', '01010603030300'),
('01010603040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04', '热力学第二定律', '', '01010603000000'),
('01010603040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01', '热力学第二定律', '', '01010603040000'),
('01010603040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP01', '在电冰箱的实例中，热量的确从低温物体——冰箱内的食品，传到了高温物体——冰箱外的空气。', '', '01010603040100'),
('01010603040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP02', '从能量的角度看，热机的工作分为两个阶段：第一个阶段是燃烧燃料，把燃料中的化学能变成工作物质的内能；第二个阶段是工作物质对外做功，把自己的内能变成机械能。', '', '01010603040100'),
('01010603040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP03', '热力学第二定律的开尔文表述阐述了机械能与内能转化的方向性：机械能可以全部转化为内能，而内能无法全部用来做功以转化成机械能。', '', '01010603040100'),
('01010603040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP04', '可以证明，热力学第二定律的克劳修斯表述和开尔文表述是等价的。', '', '01010603040100'),
('01010603040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02', '能源是有限的', '', '01010603040000'),
('01010603040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP01', '自然界的一切变化，人类社会的所有活动，都伴随着能量的转移和转化，能量是一切物质运动的源泉，是一切生命活动的基础。', '', '01010603040200'),
('01010603040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP02', '能量在数量上虽然守恒，但其转移和转化却具有方向性。', '', '01010603040200'),
('01010603040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP03', '核能、生物能……最终都转化成内能，流散到周围的环境中。', '', '01010603040200'),
('01010603040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP04', '周围环境中的内能很难再利用，而机械能、光能、化学能、电能相对于周围环境中的内能来说，可利用的品质要高。', '', '01010603040200'),
('01010603040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP05', '能源的使用过程中虽然能的总量保持守恒，但能量的品质下降了。', '', '01010603040200'),
('01010603040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP06', '虽然能量总量不会减少，但能源会逐步减少，因此能源是有限的资源。', '', '01010603040200'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010604000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04', '原子结构和波粒二象性', '', NULL),
('01010604010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01', '普朗克黑体辐射理论', '', '01010604000000'),
('01010604010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01', '黑体与黑体辐射', '', '01010604010000'),
('01010604010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP01', '19世纪，由于冶金、星体测温等需求，人们对热辐射进行了大量的研究。', '', '01010604010100'),
('01010604010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP02', '研究表明，对于一般材料的物体，辐射电磁波的情况除了与温度有关，还与材料的种类及表面状况有关，而黑体辐射电磁波的强度按波长的分布只与黑体的温度有关。', '', '01010604010100'),
('01010604010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02', '黑体辐射的实验规律', '', '01010604010000'),
('01010604010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP01', '利用分光技术和热电偶等设备，可以测出黑体辐射电磁波的强度按波长分布的情况。', '', '01010604010200'),
('01010604010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP02', '从中可以看出，随着温度的升高，一方面，各种波长的辐射强度都有增加，另一方面，辐射强度的极大值向波长较短的方向移动。', '', '01010604010200'),
('01010604010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP03', '德国物理学家维恩在1896年、英国物理学家瑞利在1900年，分别提出了辐射强度按波长分布的理论公式。', '', '01010604010200'),
('01010604010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP04', '维恩公式在短波区与实验非常接近，在长波区则与实验偏离较大；瑞利公式在长波区与实验基本一致，但在短波区与实验严重不符。', '', '01010604010200'),
('01010604010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP05', '为了得出同全部实验相符的黑体辐射公式，德国物理学家普朗克做了多种尝试。', '', '01010604010200'),
('01010604010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03', '能量子', '', '01010604010000'),
('01010604010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP01', '弹簧振子的能量不一定是某个最小值的整数倍。', '', '01010604010300'),
('01010604010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP02', '而普朗克的假设则认为微观粒子的能量是量子化的，或者说微观粒子的能量是分立的。', '', '01010604010300'),
('01010604010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP03', '普朗克本人因此获得了1918年的诺贝尔物理学奖。', '', '01010604010300'),
('01010604020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02', '光电效应', '', '01010604000000'),
('01010604020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01', '光电效应的实验规律', '', '01010604020000'),
('01010604020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP01', '1887年，赫兹在研究电磁波的实验中偶尔发现，接收电路的间隙如果受到光照，就更容易产生电火花。', '', '01010604020100'),
('01010604020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP02', '科学家通过实验发现了以下规律。', '', '01010604020100'),
('01010604020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP03', '实验表明，不同金属的截止频率不同。', '', '01010604020100'),
('01010604020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP04', '在光照条件不变的情况下，随着所加电压的增大，光电流趋于一个饱和值。', '', '01010604020100'),
('01010604020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP05', '实验表明，在光的频率不变的情况下，入射光越强，饱和电流越大。', '', '01010604020100'),
('01010604020206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP06', '第四章原子结构和波粒二象性73表明产生电流的时间很快，即光电效应几乎是瞬时发生的。', '', '01010604020100'),
('01010604020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02', '光电效应经典解释中的疑难', '', '01010604020000'),
('01010604020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP01', '当光照射金属表面时，电子会吸收光的能量。', '', '01010604020200'),
('01010604020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP02', '光越强，逸出的电子数越多，光电流也就越大。', '', '01010604020200'),
('01010604020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP03', '●不管光的频率如何，只要光足够强，电子都可以获得足够能量从而逸出表面，不应存在截止频率。', '', '01010604020200'),
('01010604020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03', '爱因斯坦的光电效应理论', '', '01010604020000'),
('01010604020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP01', '对于光电效应的解释，爱因斯坦是在普朗克量子假说的基础上作出的。', '', '01010604020300'),
('01010604020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP02', '从下面的讨论可以看出，爱因斯坦光电效应方程可以很好地解释光电效应实验中的各种现象。', '', '01010604020300'),
('01010604020403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP03', '●电子一次性吸收光子的全部能量，不需要积累能量的时间，光电流自然几乎是瞬时产生的。', '', '01010604020300'),
('01010604020404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP04', '爱因斯坦由于提出了光电效应理论而获得1921年的诺贝尔物理学奖。', '', '01010604020300'),
('01010604020405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP05', '金属表面的光子数较多，照射金属时产生的光电子较多，因而饱和电流较大。', '', '01010604020300'),
('01010604020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04', '康普顿效应和光子的动量', '', '01010604020000'),
('01010604020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP01', '光可以与介质中的物质微粒发生散射，改变传播方向。', '', '01010604020400'),
('01010604020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP02', '康普顿效应让人们对光子有了更深入的认识。', '', '01010604020400'),
('01010604020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP03', '康普顿因此获得了1927年的诺贝尔物理学奖。', '', '01010604020400'),
('01010604020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05', '光的波粒二象性', '', '01010604020000'),
('01010604020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP01', '众所周知，在麦克斯韦的电磁理论建立之后，人们认识到光是一种电磁波，从而光的波动说被普遍接受，人们不再认为光是由粒子组成的。', '', '01010604020500'),
('01010604020602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP02', '而爱因斯坦的光电效应理论和康普顿效应理论表明，光在某些方面确实会表现得像是由一些粒子（即一个个有确定能量和动量的“光子”）组成的。', '', '01010604020500'),
('01010604020603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP03', '人们意识到，光既具有波动性，又具有粒子性。', '', '01010604020500'),
('01010604020604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP04', '从牛顿的微粒说、惠更斯和托马斯·杨的波动说，到麦克斯韦的电磁理论，再到爱因斯坦的光子理论乃至量子电动力学，人类对光的认识构成了一部科学史诗。', '', '01010604020500'),
('01010604030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03', '原子的核式结构模型', '', '01010604000000'),
('01010604030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01', '电子的发现', '', '01010604030000'),
('01010604030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP01', '为了找到有利于自己的证据，双方都做了许多实验。', '', '01010604030100'),
('01010604030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP02', '由实验测得的阴极射线粒子的比荷是氢离子①（也就是质子）比荷的近两千倍。', '', '01010604030100'),
('01010604030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP03', '电子电荷的精确测定是在1909〜1913年间由密立根通过著名的“油滴实验”做出的。', '', '01010604030100'),
('01010604030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP04', '金属受热还是放射性物质的自发辐射，都能发射同样的带电粒子——电子。', '', '01010604030100'),
('01010604030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02', '原子的核式结构模型', '', '01010604030000'),
('01010604030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP01', 'α粒子是从放射性物质（如铀和镭）中发射出来的快速运动的粒子，质量为氢原子质量的4倍、电子质量的7300倍。', '', '01010604030200'),
('01010604030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP02', '被散射的α粒子打在荧光屏上会有微弱的闪光产生。', '', '01010604030200'),
('01010604030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP03', '通过显微镜观察闪光就可以记录在某一时间内向某一方向散射的α粒子数。', '', '01010604030200'),
('01010604030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP04', 'α粒子有库仑力的作用，一些α粒子的运动方向改变，也就是发生了α粒子的散射。', '', '01010604030200'),
('01010604030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP05', 'α粒子所占的比例，可以推知原子中电荷的分布情况。', '', '01010604030200'),
('01010604030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP06', '卢瑟福分析了实验数据后认为，事实应该是：占原子质量绝大部分的带正电的物质集中在很小的空间范围。', '', '01010604030200'),
('01010604030307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP07', '1911年，卢瑟福提出了自己的原子结构模型。', '', '01010604030200'),
('01010604030308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP08', '因为正电体很小，当α粒子进入原子区域后，大部分离正电体很远，受到的库仑斥力很小，运动方向几乎不改变。', '', '01010604030200'),
('01010604030309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP09', '只有极少数α粒子在穿过时距离正电体很近，因此受到很强的库仑斥力，发生大角度散射。', '', '01010604030200'),
('01010604030310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP10', '按照卢瑟福的理论，正电体的尺度是很小的，称为原子核。', '', '01010604030200'),
('01010604030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03', '原子核的电荷与尺度', '', '01010604030000'),
('01010604030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP01', '又由于原子是电中性的，可以推算出原子内含有的电子数。', '', '01010604030300'),
('01010604030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP02', '通常用核半径描述核的大小。', '', '01010604030300'),
('01010604040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04', '氢原子光谱和玻尔的原子模型', '', '01010604000000'),
('01010604040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01', '光谱', '', '01010604040000'),
('01010604040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP01', '有的光谱看起来不是一条条分立的谱线，而是连在一起的光带，叫作连续谱。', '', '01010604040100'),
('01010604040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP02', '气体中中性原子的发光光谱都是线状谱，说明原子只发出几种特定频率的光。', '', '01010604040100'),
('01010604040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02', '氢原子光谱的实验规律', '', '01010604040000'),
('01010604040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP01', '光谱的结果显示氢原子只能发出一系列特定波长的光。', '', '01010604040200'),
('01010604040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP02', '巴耳末公式以简洁的形式反映了氢原子的线状光谱的特征。', '', '01010604040200'),
('01010604040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03', '经典理论的困难', '', '01010604040000'),
('01010604040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP01', '卢瑟福的核式结构模型正确地指出了原子核的存在，很好地解释了α粒子散射实验，但跟经典电磁理论发生了矛盾。', '', '01010604040300'),
('01010604040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP02', '核外电子受到原子核的库仑引力的作用，却没有被吸引到原子核上，而是在以一定的速度绕核运动。', '', '01010604040300'),
('01010604040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP03', '随着绕核运动轨道半径的不断变化，电子运动的频率也要不断变化，因此原子辐射电磁波的频率也要不断变化。', '', '01010604040300'),
('01010604040400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04', '玻尔原子理论的基本假设', '', '01010604040000'),
('01010604040501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP01', '丹麦物理学家玻尔意识到了经典理论在解释原子结构方面的困难。', '', '01010604040400'),
('01010604040502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP02', '玻尔的原子结构假说包括以下两方面的内容。', '', '01010604040400'),
('01010604040503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP03', '玻尔认为，原子中的电子在库仑引力的作用下，绕原子核做圆周运动，服从经典力学的规律。', '', '01010604040400'),
('01010604040504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP04', '在玻尔理论中，电子的轨道半径只可能是某些分立的数值。', '', '01010604040400'),
('01010604040505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP05', '当电子在不同的轨道上运动时，原子处于不同的状态，具有不同的能量。', '', '01010604040400'),
('01010604040506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP06', '根据玻尔理论，电子只能在特定轨道上运动，因此，原子的能量也只能取一系列特定的值。', '', '01010604040400'),
('01010604040507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP07', '按照玻尔的观点，电子在一系列定态轨道上运动，不会发生电磁辐射。', '', '01010604040400'),
('01010604040500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05', '玻尔理论对氢光谱的解释', '', '01010604040000'),
('01010604040601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP01', '从玻尔的基本假设出发，运用经典电磁学和经典力学的理论，可以计算氢原子中电子的可能轨道半径及相应的能量。', '', '01010604040500'),
('01010604040602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP02', '处于基态的原子非常稳定。', '', '01010604040500'),
('01010604040603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP03', '处于激发态的原子是不稳定的，会自发地向能量较低的能级跃迁，放出光子，最终回到基态。', '', '01010604040500'),
('01010604040604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP04', '由于原子的能级是分立的，所以放出的光子的能量也是分立的。', '', '01010604040500'),
('01010604040605', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP05', '由于不同的原子具有不同的结构，能级各不相同，因此辐射（或吸收）的光子频率也不相同。', '', '01010604040500'),
('01010604040606', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP06', '各种气体原子的能级不同，跃迁时发射光子的能量各异。', '', '01010604040500'),
('01010604040607', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP07', '街道上的霓虹灯、试电笔中的氖管，都是由灯管内的气体原子从高能级向低能级跃迁而发光的。', '', '01010604040500'),
('01010604040608', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP08', '食盐被灼烧时发的光，也主要是由食盐蒸气中钠原子的能级跃迁而造成的。', '', '01010604040500'),
('01010604040600', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06', '玻尔理论的局限性', '', '01010604040000'),
('01010604040701', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP01', '玻尔的原子理论第一次将量子观念引入原子领域，提出了定态和跃迁的概念。', '', '01010604040600'),
('01010604040702', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP02', '玻尔理论成功地解释了氢原子光谱的实验规律。', '', '01010604040600'),
('01010604040703', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP03', '①玻尔理论的不足之处在于保留了经典粒子的观念，仍然把电子的运动看作经典力学描述下的轨道运动。', '', '01010604040600'),
('01010604040704', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP04', '实际上，根据量子力学，原子中电子的坐标没有确定的值。', '', '01010604040600'),
('01010604040705', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP05', '当原子处于不同的状态时，电子在各处出现的概率是不一样的。', '', '01010604040600'),
('01010604050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05', '粒子的波动性和量子力学的建立', '', '01010604000000'),
('01010604050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01', '粒子的波动性', '', '01010604050000'),
('01010604050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01.OP01', '1924年，法国物理学家德布罗意在对光的波粒二象性、玻尔氢原子理论以及相对论的深入研究的基础上，把波粒二象性推广到实物粒子，如电子、质子等。', '', '01010604050100'),
('01010604050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02', '物质波的实验验证', '', '01010604050000'),
('01010604050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP01', '德布罗意关于实物粒子具有波动性的假说在当时来看是难以理解的。', '', '01010604050200'),
('01010604050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP02', '1929年，德布罗意因提出物质波的假说获得了诺贝尔物理学奖。', '', '01010604050200'),
('01010604050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP03', '除了电子以外，后来还陆续证实了中子、质子以及原子、分子的波动性。', '', '01010604050200'),
('01010604050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP04', '德布罗意提出物质波的观念被实验证实，表明电子、质子、原子等粒子不但具有粒子的性质，而且具有波动的性质。', '', '01010604050200'),
('01010604050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03', '量子力学的建立', '', '01010604050000'),
('01010604050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP01', '19、20世纪之交，人们在黑体辐射、光电效应、氢原子光谱等许多类问题中，都发现了经典物理学无法解释的现象。', '', '01010604050300'),
('01010604050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP02', '普朗克黑体辐射理论、爱因斯坦光电效应理论、康普顿散射理论、玻尔氢原子理论以及德布罗意物质波假说等一系列理论在解释实验方面都取得了成功。', '', '01010604050300'),
('01010604050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP03', '1925年，德国物理学家海森堡和玻恩等人对玻尔的氢原子理论进行了推广和改造，使之可以适用于更普遍的情况。', '', '01010604050300'),
('01010604050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04', '量子力学的应用', '', '01010604050000'),
('01010604050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP01', '量子力学被应用到众多具体物理系统中，得到了与实验符合得很好的结果，获得了极大的成功。', '', '01010604050400'),
('01010604050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP02', '借助量子力学，人们深入认识了微观世界的组成、结构和属性。', '', '01010604050400'),
('01010604050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP03', '量子力学推动了核物理和粒子物理的发展。', '', '01010604050400'),
('01010604050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP04', '人们认识了原子、原子核、基本粒子等各个微观层次的物质结构。', '', '01010604050400'),
('01010604050505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP05', '而粒子物理学的发展又促进了天文学和宇宙学的研究。', '', '01010604050400'),
('01010604050506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP06', '从整个宇宙到微小的粒子，人类探索自然的视野前所未有的宽广。', '', '01010604050400'),
('01010604050507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP07', '人们惊讶地发现，世界具有奇妙的结构，最微观层次和最宏观层次的规律，竟有着紧密的联系。', '', '01010604050400'),
('01010604050508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP08', '核物理的发展，还让人们成功地认识并利用了原子核反应堆所释放的能量——核能。', '', '01010604050400'),
('01010604050509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP09', '”量子力学推动了原子、分子物理和光学的发展。', '', '01010604050400'),
('01010604050510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP10', '人们认识了原子的结构，以及原子、分子和电磁场相互作用的方式。', '', '01010604050400'),
('01010604050511', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP11', '在此基础上，发展了各式各样的对原子和电磁场进行精确操控和测量的技术，如激光、核磁共振、原子钟，等等。', '', '01010604050400'),
('01010604050512', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP12', '以利用振荡的磁场测量材料中原子的性质，因此，被广泛地用于化学、生物研究和医学诊断。', '', '01010604050400'),
('01010604050513', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP13', '原子钟利用原子为电磁波校准频率，从而实现了对时间的高精度测量。', '', '01010604050400'),
('01010604050514', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP14', '在日常生活和国家安全中发挥巨大作用的卫星定位技术，其核心部件就是原子钟。', '', '01010604050400'),
('01010604050515', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP15', '量子力学推动了固体物理的发展。', '', '01010604050400'),
('01010604050516', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP16', '人们了解了固体中电子运行的规律，并弄清了为什么固体有导体、绝缘体和半导体之分。', '', '01010604050400'),
('01010604050517', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP17', '量子力学的应用还有很多。', '', '01010604050400'),
('01010604050518', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP18', '毫不夸张地说，在过去的近一百年中，量子力学极大地推动了人类的进步。', '', '01010604050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010605000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05', '原子核', '', NULL),
('01010605010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01', '原子核的组成', '', '01010605000000'),
('01010605010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01', '天然放射现象', '', '01010605010000'),
('01010605010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP01', '受到贝克勒尔的发现的鼓舞，波兰裔法国物理学家玛丽·居里和她的丈夫皮埃尔·居里对铀和含铀的各种矿石进行了深入研究。', '', '01010605010100'),
('01010605010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP02', '放射性元素自发地发出射线的现象，叫作天然放射现象。', '', '01010605010100'),
('01010605010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02', '射线的本质', '', '01010605010000'),
('01010605010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP01', '把放射源铀、钋或镭放入用铅做成的容器中，射线只能从容器的小孔射出，成为细细的一束。', '', '01010605010200'),
('01010605010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP02', '物理学家经过多方面的研究后确认α射线、β射线和γ射线具有以下特征。', '', '01010605010200'),
('01010605010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP03', 'α粒子带正电，电荷量是电子的2倍，质量是氢原子的4倍，其组成与氦原子核相同。', '', '01010605010200'),
('01010605010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP04', '由于α粒子带电，质量又比较大，通过气体时很容易把气体分子中的电子剥离，使气体电离。', '', '01010605010200'),
('01010605010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP05', '是电子流，速度可以接近光速。', '', '01010605010200'),
('01010605010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP06', 'β射线的电离作用较弱，穿透能力较强，很容易穿透黑纸，也能穿透几毫米厚的铝板。', '', '01010605010200'),
('01010605010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP07', 'γ射线的电离作用更弱，穿透能力更强，甚至能穿透几厘米厚的铅板和几十厘米厚的混凝土。', '', '01010605010200'),
('01010605010308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP08', '放射性的强度也不受温度、外界压强的影响。', '', '01010605010200'),
('01010605010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03', '原子核的组成', '', '01010605010000'),
('01010605010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP01', '绝大多数原子核的质量与电荷量之比都大于质子的相应比值。', '', '01010605010300'),
('01010605010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP02', '质子带正电荷，电荷量与一个电子的电荷量相等。', '', '01010605010300'),
('01010605010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP03', '由于中子不带电，原子核所带的电荷等于核内质子电荷的总和。', '', '01010605010300'),
('01010605010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP04', '元素的性质与原子核外的电子有密切的关系。', '', '01010605010300'),
('01010605020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02', '放射性元素的衰变', '', '01010605000000'),
('01010605020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01', '原子核的衰变', '', '01010605020000'),
('01010605020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP01', '铀238放出一个α粒子后，质量数减少4，电荷数减少2，成为新核。', '', '01010605020100'),
('01010605020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP02', '0、电荷数为－1，可以把电子表示为0-1。', '', '01010605020100'),
('01010605020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP03', '研究还表明，在一定条件下，原子核中的两个中子和两个质子会结合起来形成α粒子，并被释放出来。', '', '01010605020100'),
('01010605020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP04', '原子核的能量也跟原子的能量一样，其变化是不连续的，也只能取一系列不连续的数值，因此，也存在着能级，同样是能级越低越稳定。', '', '01010605020100'),
('01010605020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP05', '当放射性物质连续衰变时，原子核中有的发生α衰变，有的发生β衰变，同时伴随着γ射线辐射。', '', '01010605020100'),
('01010605020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02', '半衰期', '', '01010605020000'),
('01010605020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP01', '放射性同位素衰变的快慢有一定的规律。', '', '01010605020200'),
('01010605020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP02', '不同的放射性元素，半衰期不同，甚至差别非常大。', '', '01010605020200'),
('01010605020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP03', '放射性元素衰变的快慢是由核内部自身的因素决定的，跟原子所处的化学状态和外部条件没有关系。', '', '01010605020200'),
('01010605020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03', '核反应', '', '01010605020000'),
('01010605020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP01', '衰变是原子核的自发变化，科学家更希望人工控制原子核的变化。', '', '01010605020300'),
('01010605020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP02', '与衰变过程一样，在核反应中，质量数守恒、电荷数守恒。', '', '01010605020300'),
('01010605020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04', '放射性同位素及其应用', '', '01010605020000'),
('01010605020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP01', '天然放射性同位素不过40多种，而今天通过核反应生成的人工放射性同位素已达3000多种，每种元素都有了自己的放射性同位素。', '', '01010605020400'),
('01010605020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP02', '丰富的放射性同位素资源在国民经济和科学研究的各个领域中得到了广泛的应用。', '', '01010605020400'),
('01010605020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP03', '工业部门可以使用放射性同位素发出的射线来测厚度。', '', '01010605020400'),
('01010605020504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP04', '像癌细胞那样，不断迅速繁殖的、无法控制的细胞组织，在射线照射下破坏得比健康细胞快。', '', '01010605020400'),
('01010605020505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP05', '利用γ射线照射种子，会使种子的遗传基因发生变异，经过筛选，可以培育出新品种。', '', '01010605020400'),
('01010605020506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP06', '棉花在开花、结桃的时候需要较多的磷肥，把磷肥喷在棉花叶子上，磷肥也能被吸收。', '', '01010605020400'),
('01010605020507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP07', '如果用磷的放射性同位素制成肥料喷在棉花的叶面上，然后每隔一定时间用探测器测量棉株各部位的放射性强度，上面的问题就解决了。', '', '01010605020400'),
('01010605020508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP08', '人体甲状腺的工作需要碘，碘被吸收后聚集在甲状腺内。', '', '01010605020400'),
('01010605020509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP09', '定时用探测器测量甲状腺及邻近组织的放射强度，有助于诊断甲状腺的疾病。', '', '01010605020400'),
('01010605020510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP10', '近年来，有关生物大分子的结构及其功能的研究，几乎都要借助于示踪原子。', '', '01010605020400'),
('01010605020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05', '辐射与安全', '', '01010605020000'),
('01010605020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05.OP01', '人类一直生活在放射性的环境中。', '', '01010605020500'),
('01010605030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03', '核力与结合能', '', '01010605000000'),
('01010605030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01', '核力与四种基本相互作用', '', '01010605030000'),
('01010605030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP01', '现代物理学认为，自然界存在着四种基本相互作用，即引力相互作用、电磁相互作用、强相互作用和弱相互作用。', '', '01010605030100'),
('01010605030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP02', '17世纪下半叶，人们发现了万有引力。', '', '01010605030100'),
('01010605030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP03', '引力是自然界的一种基本相互作用，地面物体所受的重力只是引力在地球表面附近的一种表现。', '', '01010605030100'),
('01010605030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP04', '20世纪，物理学家发现原子核是由若干带正电荷的质子和不带电的中子组成的。', '', '01010605030100'),
('01010605030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP05', '19世纪末20世纪初，物理学家发现了放射现象。', '', '01010605030100'),
('01010605030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP06', '①弱相互作用是引起原子核β衰变的原因，即引起中子—质子转变的原因。', '', '01010605030100'),
('01010605030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02', '结合能', '', '01010605030000'),
('01010605030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP01', '相距很远的两个物体，由于万有引力而相互接近，运动速度越来越大，引力势能转化为动能。', '', '01010605030200'),
('01010605030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP02', '最后撞在一起，一部分动能变成热并散失掉了。', '', '01010605030200'),
('01010605030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP03', '比结合能越大，原子核中核子结合得越牢固，原子核越稳定。', '', '01010605030200'),
('01010605030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03', '质量亏损', '', '01010605030000'),
('01010605030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP01', '原子核的结合能很难直接测量。', '', '01010605030300'),
('01010605030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP02', '质量亏损表明，的确存在着原子核的结合能。', '', '01010605030300'),
('01010605040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04', '核裂变与核聚变', '', '01010605000000'),
('01010605040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01', '核裂变的发现', '', '01010605040000'),
('01010605040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP01', '以铀235为例，核裂变时产生两或三个中子。', '', '01010605040100'),
('01010605040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP02', '原子核的体积非常小，原子内部的空隙很大，如果铀块不够大，中子在铀块中通过时，很有可能碰不到铀核而跑到铀块外面去，链式反应不能继续。', '', '01010605040100'),
('01010605040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP03', '只有当铀块足够大时，核裂变产生的中子才有足够大的概率打中某个铀核，使链式反应进行下去。', '', '01010605040100'),
('01010605040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP04', '铀核裂变时如果生成物不同，释放的能量也有差异。', '', '01010605040100'),
('01010605040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02', '反应堆与核电站', '', '01010605040000'),
('01010605040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP01', '原子核的链式反应也可以在人工控制下进行。', '', '01010605040200'),
('01010605040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP02', '其实，在第一个原子弹制成以前，科学家们已经实现了核能的可控释放。', '', '01010605040200'),
('01010605040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP03', '1942年，美籍意大利物理学家费米就主持建立了世界上第一个称为“核反应堆”的装置，首次通过可控制的链式反应实现了核能的释放。', '', '01010605040200'),
('01010605040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP04', '常用的慢化剂有石墨、重水和普通水（也叫轻水）。', '', '01010605040200'),
('01010605040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP05', '为了调节中子数目以控制反应速度，还需要在铀棒之间插进一些镉棒。', '', '01010605040200'),
('01010605040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP06', '核燃料发生核裂变释放的能量使反应区温度升高。', '', '01010605040200'),
('01010605040307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP07', '水或液态的金属钠等流体在反应堆内外循环流动，把反应堆内的热量传输出去，用于发电，同时也使反应堆冷却。', '', '01010605040200'),
('01010605040308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP08', '在热交换器内，第二回路中的水被加热生成高温高压蒸汽，驱动汽轮机运转。', '', '01010605040200'),
('01010605040309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP09', '建造核电站时需要特别注意防止放射线和放射性物质的泄漏，以避免射线对人体的伤害和放射性物质对水源、空气和工作场所造成放射性污染。', '', '01010605040200'),
('01010605040310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP10', '核反应堆中的核废料具有很强的放射性，需要装入特制的容器，深埋地下。', '', '01010605040200'),
('01010605040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03', '核聚变', '', '01010605040000'),
('01010605040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP01', '当物质的温度达到几百万开尔文时，剧烈的热运动使得一部分原子核具有足够的动能，可以克服库仑斥力，碰撞时十分接近，发生核聚变。', '', '01010605040300'),
('01010605040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP02', '太阳的主要成分是氢。', '', '01010605040300'),
('01010605040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP03', '现在地球上消耗的能量，追根溯源，绝大部分还是来自太阳，即太阳内部核聚变时释放的核能。', '', '01010605040300'),
('01010605040404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP04', '太阳在“核燃烧”的过程中“体重”不断减轻。', '', '01010605040300'),
('01010605040405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP05', '太阳的寿命已经有50亿年了。', '', '01010605040300'),
('01010605040406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP06', '目前，人工产生的热核反应主要用在核武器上，那就是氢弹。', '', '01010605040300'),
('01010605040407', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP07', '与核裂变相比，核聚变有很多优点。', '', '01010605040300'),
('01010605040408', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP08', '利用核聚变物质的惯性进行约束。', '', '01010605040300'),
('01010605040409', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP09', '总的来说，实现受控核聚变还有一段很长的路要走。', '', '01010605040300'),
('01010605050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05', '“基本”粒子', '', '01010605000000'),
('01010605050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01', '发现新粒子', '', '01010605050000'),
('01010605050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP01', '从20世纪30年代以来，人们在对宇宙线的研究中陆续发现了一些新的粒子。', '', '01010605050100'),
('01010605050202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP02', '1932年发明的粒子加速器，能使带电粒子加速到很高的能量。', '', '01010605050100'),
('01010605050203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP03', '利用加速器和对撞机，人们发现了更多的粒子。', '', '01010605050100'),
('01010605050204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP04', '粒子物理的研究不再单纯依赖宇宙线。', '', '01010605050100'),
('01010605050205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP05', '高能量的对撞机往往极为庞大和复杂。', '', '01010605050100'),
('01010605050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02', '粒子的分类', '', '01010605050000'),
('01010605050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP01', '现在已经发现的粒子达400多种。', '', '01010605050200'),
('01010605050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP02', '强子是参与强相互作用的粒子。', '', '01010605050200'),
('01010605050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP03', '质子和中子都是强子。', '', '01010605050200'),
('01010605050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP04', '轻子不参与强相互作用。', '', '01010605050200'),
('01010605050305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP05', '最早发现的轻子是电子，后来发现的轻子有电子中微子、μ子、μ子中微子以及τ子和τ子中微子。', '', '01010605050200'),
('01010605050306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP06', '每种轻子都有对应的反粒子。', '', '01010605050200'),
('01010605050307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP07', '现代实验还没有发现轻子的内部结构。', '', '01010605050200'),
('01010605050308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP08', '光子传递电磁相互作用，中间玻色子传递弱相互作用，胶子传递强相互作用。', '', '01010605050200'),
('01010605050309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP09', '希格斯玻色子是希格斯场的量子激发。', '', '01010605050200'),
('01010605050310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP10', '基本粒子因与希格斯场耦合而获得质量。', '', '01010605050200'),
('01010605050311', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP11', '2012年，欧洲核子研究中心利用大型强子对撞机发现了希格斯玻色子。', '', '01010605050200'),
('01010605050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03', '研究样例', '', '01010605050000'),
('01010605050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP01', '①燃气灶烧水时的能量转化和转移的情况如下。', '', '01010605050300'),
('01010605050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP02', '记录数据时室温为26℃，基本上保证了实验环境温度的恒定。', '', '01010605050300'),
('01010605050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP03', '为减小水在高温时的放热损失，应缩短水在高温时的加热时间。', '', '01010605050300'),
('01010605050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP04', '用实验检验研究结论的正确性。', '', '01010605050300'),
('01010605050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04', '参考选题', '', '01010605050000'),
('01010605050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP01', '使用热水器洗澡时，常有两种调温习惯：一种是把加热后的水温直接调节至洗澡的合适温度，如38℃左右；另一种是把热水器的加热温度调到更高，如50℃左右，再通过淋浴水龙头加兑冷水至合适水温，后者可以获得更大的适温水量。', '', '01010605050400'),
('01010605050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP02', '热水器工作前，先观察燃气表示数，测量加热前水的温度。', '', '01010605050400'),
('01010605050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP03', '观察燃气表示数，记录所用去的燃气体积。', '', '01010605050400'),
('01010605050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP04.OP04', '如果已知被加热水的体积，查找燃气的热值，还可以算出两种情况下热效率的大小。', '', '01010605050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010601000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01', '分子动理论', '', NULL),
('01010601010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01', '分子动理论的基本内容', '', '01010601000000'),
('01010601010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01', '物体是由大量分子组成的', '', '01010601010000'),
('01010601010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP01', '人们用肉眼无法直接看到分子，就是用高倍的光学显微镜也看不到。', '', '01010601010100'),
('01010601010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP01.OP02', '直至1982年，人们研制了能放大几亿倍的扫描隧道显微镜③，才观察到物质表面原子的排列。', '', '01010601010100'),
('01010601010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02', '分子热运动', '', '01010601010000'),
('01010601010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP01', '扩散现象是物质分子永不停息地做无规则运动的证据之一。', '', '01010601010200'),
('01010601010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP02', '扩散现象在科学技术中有很多应用。', '', '01010601010200'),
('01010601010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP03', '19世纪初，一些人观察到，悬浮在液体中的小颗粒总在不停地运动。', '', '01010601010200'),
('01010601010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP04', '从实验结果可以看出，小炭粒的运动是无规则的，温度越高，小炭粒的运动越明显。', '', '01010601010200'),
('01010601010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP05', '在扩散现象中，温度越高，扩散得越快。', '', '01010601010200'),
('01010601010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP06', '观察布朗运动，温度越高，悬浮微粒的运动就越明显。', '', '01010601010200'),
('01010601010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP02.OP07', '温度是分子热运动剧烈程度的标志。', '', '01010601010200'),
('01010601010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03', '分子间的作用力', '', '01010601010000'),
('01010601010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP01', '水和酒精混合后的总体积变小了。', '', '01010601010300'),
('01010601010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP02', '当用力拉伸物体时，物体内各部分之间要产生反抗拉伸的作用力，此时分子间的作用力表现为引力。', '', '01010601010300'),
('01010601010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP03', '当用力压缩物体时，物体内各部分之间会产生反抗压缩的作用力，此时分子间的作用力表现为斥力。', '', '01010601010300'),
('01010601010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP03.OP04', '原子内部有带正电的原子核和带负电的电子。', '', '01010601010300'),
('01010601010400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC01.KP04', '分子动理论', '', '01010601010000'),
('01010601020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02', '实验：用油膜法估测油酸分子的大小', '', '01010601000000'),
('01010601020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP01', '实验思路', '', '01010601020000'),
('01010601020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP02', '物理量的测量', '', '01010601020000'),
('01010601020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC02.KP03', '数据分析', '', '01010601020000'),
('01010601030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03', '分子运动速率分布规律', '', '01010601000000'),
('01010601030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01', '气体分子运动的特点', '', '01010601030000'),
('01010601030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP01', '热现象与大量分子热运动的统计规律有关。', '', '01010601030100'),
('01010601030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP02', '要研究气体的热现象，就要了解气体分子运动的特点。', '', '01010601030100'),
('01010601030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP03', '液体变为气体后，体积要增大上千倍，可见，气体分子间距离大约是分子直径的10倍。', '', '01010601030100'),
('01010601030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP04', '分子的大小相对分子间的空隙来说很小，所以，可以把气体分子视为质点。', '', '01010601030100'),
('01010601030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP01.OP05', '分子的运动杂乱无章，在某一时刻，向着任何一个方向运动的分子都有，而且向各个方向运动的分子数目几乎相等。', '', '01010601030100'),
('01010601030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02', '分子运动速率分布图像', '', '01010601030000'),
('01010601030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP01', '尽管分子做无规则运动，速率有大有小，但大量分子的速率却按一定的规律分布。', '', '01010601030200'),
('01010601030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP02', '100℃两种不同情况下的速率分布情况。', '', '01010601030200'),
('01010601030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP02.OP03', '100℃的氧气，速率大的分子比例较多，其分子的平均速率比0℃的大。', '', '01010601030200'),
('01010601030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03', '气体压强的微观解释', '', '01010601030000'),
('01010601030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP01', '器壁单位面积上受到的压力，就是气体的压强。', '', '01010601030300'),
('01010601030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC03.KP03.OP02', '从微观角度来看，气体分子的数量是巨大的。', '', '01010601030300'),
('01010601040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04', '分子动能和分子势能', '', '01010601000000'),
('01010601040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01', '分子动能', '', '01010601040000'),
('01010601040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP01', '物体中分子热运动的速率大小不一，所以各个分子的动能也有大有小，而且在不断改变。', '', '01010601040100'),
('01010601040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP02', '温度升高时，分子的热运动加剧，温度越高，分子热运动的平均动能越大。', '', '01010601040100'),
('01010601040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP01.OP03', '温度越低，分子热运动的平均动能越小。', '', '01010601040100'),
('01010601040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02', '分子势能', '', '01010601040000'),
('01010601040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP01', '分子间存在着相互作用力，可以证明分子间的作用力所做的功与路径无关，分子组成的系统具有分子势能。', '', '01010601040200'),
('01010601040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP02.OP02', '物体的体积变化时，分子间距离将发生变化，因而分子势能随之改变。', '', '01010601040200'),
('01010601040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03', '物体的内能', '', '01010601040000'),
('01010601040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP01', '任何物体都具有内能。', '', '01010601040300'),
('01010601040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH01.SEC04.KP03.OP02', '分子热运动的平均动能与温度有关，分子势能与物体的体积有关。', '', '01010601040300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010602000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02', '气体、固体和液体', '', NULL),
('01010602010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01', '温度和温标', '', '01010602000000'),
('01010602010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01', '状态参量与平衡态', '', '01010602010000'),
('01010602010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP01.OP01', '要定量地描述系统的状态往往很难，因为有时系统的参量会随时间变化。', '', '01010602010100'),
('01010602010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP02', '热平衡与温度', '', '01010602010000'),
('01010602010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03', '温度计与温标', '', '01010602010000'),
('01010602010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC01.KP03.OP01', '现代科学中用得更多的是热力学温标。', '', '01010602010300'),
('01010602020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC02', '气体的等温变化', '', '01010602000000'),
('01010602030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03', '气体的等压变化和等容变化', '', '01010602000000'),
('01010602030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP01', '气体的等压变化', '', '01010602030000'),
('01010602030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP01.OP01', '一定质量的某种气体，在压强不变时，体积随温度变化的过程叫作气体的等压变化。', '', '01010602030100'),
('01010602030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02', '气体的等容变化', '', '01010602030000'),
('01010602030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP02.OP01', '法国科学家查理在分析了实验事实后发现，当一定质量的气体体积一定时，各种气体的压强与温度之间都有线性关系。', '', '01010602030200'),
('01010602030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03', '理想气体', '', '01010602030000'),
('01010602030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP01', '当压强很大、温度很低时，由上述规律计算的结果与实际测量结果有很大的差别。', '', '01010602030300'),
('01010602030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP02', '实际气体的分子之间有相互作用力，但是作用力很小；分子也有大小，但气体分子之间的间距比分子直径大得多；气体分子与器壁碰撞几乎是弹性的，动能损失也很小。', '', '01010602030300'),
('01010602030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP03.OP03', '在温度不低于零下几十摄氏度、压强不超过大气压的几倍时，把实际气体当成理想气体来处理，误差很小。', '', '01010602030300'),
('01010602030400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04', '气体实验定律的微观解释', '', '01010602030000'),
('01010602030501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP01', '用分子动理论可以定性解释气体的实验定律。', '', '01010602030400'),
('01010602030502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP02', '一定质量的某种理想气体，温度保持不变时，分子的平均动能是一定的。', '', '01010602030400'),
('01010602030503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP03', '一定质量的某种理想气体，温度升高时，分子的平均动能增大；只有气体的体积同时增大，使分子的数密度减小，才能保持压强不变。', '', '01010602030400'),
('01010602030504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC03.KP04.OP04', '一定质量的某种理想气体，体积保持不变时，分子的数密度保持不变。', '', '01010602030400'),
('01010602040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04', '固体', '', '01010602000000'),
('01010602040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01', '晶体和非晶体', '', '01010602040000'),
('01010602040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP01', '固体可以分为晶体和非晶体两类。', '', '01010602040100'),
('01010602040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP02', '石英、云母①、明矾、食盐、硫酸铜、味精等是晶体。', '', '01010602040100'),
('01010602040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP03', '玻璃、蜂蜡、松香、沥青、橡胶等是非晶体。', '', '01010602040100'),
('01010602040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP04', '非晶体没有规则的外形。', '', '01010602040100'),
('01010602040205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP05', '除了形状是否规则外，晶体和非晶体在物理性质上也有所不同。', '', '01010602040100'),
('01010602040206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP06', '从实验结果可以看出，玻璃片上石蜡熔化区域的形状近似于圆形，表明玻璃沿各个方向的导热性能相同；云母片上石蜡熔化区域的形状呈椭圆形，表明云母沿不同方向的导热性能不同。', '', '01010602040100'),
('01010602040207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP07', '蔗糖受潮后粘在一起形成的糖块，看起来没有确定的几何形状。', '', '01010602040100'),
('01010602040208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP01.OP08', '粘在一起的糖块也是多晶体，单个的蔗糖晶体颗粒是单晶体。', '', '01010602040100'),
('01010602040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02', '晶体的微观结构', '', '01010602040000'),
('01010602040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP01', '在20世纪70年代，人们又用电子显微镜观察到了铀、钍原子的像。', '', '01010602040200'),
('01010602040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP02', '1982年，人们用扫描隧道显微镜观察到物质表面原子的排列。', '', '01010602040200'),
('01010602040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP03', '在各种晶体中，原子（或分子、离子）都是按照一定的规则排列的，具有空间上的周期性。', '', '01010602040200'),
('01010602040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP04', '石墨是层状结构，层与层之间距离较大，原子间的作用力比较弱，所以石墨质地松软，可以用来制作粉状润滑剂；金刚石中碳原子间的作用力很强，所以金刚石有很大的硬度，可以用来切割玻璃。', '', '01010602040200'),
('01010602040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP05', '同种物质也可能以晶体和非晶体两种不同的形态出现，也就是说，物质是晶体还是非晶体，并不是绝对的。', '', '01010602040200'),
('01010602040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC04.KP02.OP06', '有些非晶体在一定条件下也可以转化为晶体。', '', '01010602040200'),
('01010602050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05', '液体', '', '01010602000000'),
('01010602050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP01', '液体的表面张力', '', '01010602050000'),
('01010602050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02', '浸润和不浸润', '', '01010602050000'),
('01010602050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP01', '管的内径越小，上述现象就越明显。', '', '01010602050200'),
('01010602050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP02', '有些物体内部有许多细小的孔道，孔道起到了毛细管的作用。', '', '01010602050200'),
('01010602050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP03', '当液体和与之接触的固体的相互作用比液体分子之间的相互作用强时，液体能够浸润固体。', '', '01010602050200'),
('01010602050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP02.OP04', '如果把水银滴到干净的铅板上，水银就会附着在铅板上，很难擦去。', '', '01010602050200'),
('01010602050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03', '液晶', '', '01010602050000'),
('01010602050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP01', '液晶在现代生活中扮演着重要角色，广泛应用于手机屏幕、平板电视等显示设备中。', '', '01010602050300'),
('01010602050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP02', '液晶态既具有液体的流动性，又在一定程度上具有晶体分子的规则排列的性质。', '', '01010602050300'),
('01010602050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP03', '有些物质在特定的温度范围之内具有液晶态；另一些物质，在适当的溶剂中溶解时，在一定的浓度范围具有液晶态。', '', '01010602050300'),
('01010602050404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP04', '分子取向排列的液晶具有光学各向异性，具体地说，沿分子长轴方向上的折射率不同于沿短轴的。', '', '01010602050300'),
('01010602050405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP05', '入射光的偏振方向与液晶分子长轴的方向成不同夹角时，液晶对光的折射率不同。', '', '01010602050300'),
('01010602050406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH02.SEC05.KP03.OP06', '液晶的研究在生命科学中也有应用。', '', '01010602050300'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010603000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03', '热力学定律', '', NULL),
('01010603010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01', '功、热和内能的改变', '', '01010603000000'),
('01010603010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01', '焦耳的实验', '', '01010603010000'),
('01010603010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP01', '在焦耳研究功与热等效性的许多实验中，有两个最具代表性。', '', '01010603010100'),
('01010603010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP02', '一个是让重物下落带动叶片搅拌容器中的水，引起水温上升；另一个是通过电流的热效应给水加热。', '', '01010603010100'),
('01010603010203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP03', '焦耳的多次实验测量表明，尽管各次悬挂重物的质量不同，下落的高度也不一样，但只要重力所做的功相同，容器内水温上升的数值都是相同的，即系统状态的变化是相同的。', '', '01010603010100'),
('01010603010204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP04', '多次实验结果表明，对于同一个系统，如果过程是绝热的，那么不管通过电阻丝的电流或大或小、通电时间或长或短，只要所做的电功相等，则系统温度上升的数值是相同的，即系统的状态就发生了同样的变化。', '', '01010603010100'),
('01010603010205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP01.OP05', '两个位置间移动时重力所做的功。', '', '01010603010100'),
('01010603010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02', '功与内能的改变', '', '01010603010000'),
('01010603010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP01', '当温度达到易燃物的燃点时，易燃物发生自燃。', '', '01010603010200'),
('01010603010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP02', '当橡胶塞跳出时，瓶内出现白雾。', '', '01010603010200'),
('01010603010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP02.OP03', '以瓶内气体为研究对象，橡胶塞跳出后，瓶内的气体迅速膨胀，系统对外做功，因此，气体的内能迅速减少，瓶内气体温度迅速下降，瓶内水蒸气液化，就会出现大量的雾状小水滴。', '', '01010603010200'),
('01010603010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03', '热与内能的改变', '', '01010603010000'),
('01010603010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC01.KP03.OP01', '外界对系统做功可以改变系统的热力学状态，而单纯地对系统传热也能改变系统的热力学状态。', '', '01010603010300'),
('01010603020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02', '热力学第一定律', '', '01010603000000'),
('01010603020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01', '热力学第一定律', '', '01010603020000'),
('01010603020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP01.OP01', '（1）求上述压缩过程中气体内能的变化量。', '', '01010603020100'),
('01010603020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC02.KP02', '热力学第一定律的应用', '', '01010603020000'),
('01010603030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03', '能量守恒定律', '', '01010603000000'),
('01010603030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01', '探索能量守恒的足迹', '', '01010603030000'),
('01010603030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP01', '能量的概念是人类在对物质运动规律进行长期探索中建立起来的。', '', '01010603030100'),
('01010603030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP02', '所有自然现象都涉及能量，人类的任何活动都离不开能量。', '', '01010603030100'),
('01010603030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP03', '能量具有不同的形式，有描述热运动的内能、描述机械运动的机械能、描述光辐射的光能，等等。', '', '01010603030100'),
('01010603030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP04', '不同形式的运动都可以用能量来描述。', '', '01010603030100'),
('01010603030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP05', '在认识自然的进程中，科学家慢慢知道了要用联系的观点去观察自然。', '', '01010603030100'),
('01010603030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP06', '由于能量既抽象又以各种不同的形式广泛存在，所以能量守恒思想的萌芽及产生并不是一蹴而就的。', '', '01010603030100'),
('01010603030207', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP07', '从各种实验的验证，再到实验结果的被质疑，科学家们付出了大量的努力。', '', '01010603030100'),
('01010603030208', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP08', '从18世纪末到19世纪中叶，不同领域的科学家从不同角度都提出过能量守恒的思想。', '', '01010603030100'),
('01010603030209', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP09', '人类对能量守恒的认识经历了一个由浅入深、由含糊到清晰的过程。', '', '01010603030100'),
('01010603030210', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP10', '俄国化学家盖斯的研究发现，任何一个化学反应，不论是一步完成，还是分几步完成，放出的总热量相同。', '', '01010603030100'),
('01010603030211', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP11', '焦耳的实验精确地测量了做功与传热之间的等价关系，从而为能量守恒定律奠定了牢固的实验基础，也为能量守恒的定量描述迈出了重要的一步。', '', '01010603030100'),
('01010603030212', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP12', '德国医生迈尔通过比对不同地区人血颜色的差异，认识到食物中化学能与内能的等效性，即生物体内能量的输入和输出是平衡的。', '', '01010603030100'),
('01010603030213', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP01.OP13', '半个多世纪人类对能量以及能量守恒研究的历史，预示着人们把各分立的领域连成一体的时刻已经到来，也就是到了建立能量转化与守恒定律的时候了。', '', '01010603030100'),
('01010603030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02', '能量守恒定律', '', '01010603030000'),
('01010603030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP01', '在热力学领域内，做功和传热可以改变系统的内能，即系统内能与系统外的能量会发生转化或转移，但能的总量不会改变。', '', '01010603030200'),
('01010603030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP02', '能量守恒是自然界的普遍规律。', '', '01010603030200'),
('01010603030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP03', '根据能量守恒定律，物理学发现和解释了很多科学现象。', '', '01010603030200'),
('01010603030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP04', '在20世纪30年代初，奥地利物理学家泡利根据能量守恒定律预言了中微子。', '', '01010603030200'),
('01010603030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP05', '能量守恒定律经受住了新的检验。', '', '01010603030200'),
('01010603030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP02.OP06', '能量守恒定律的发现是科学史上的重大事件。', '', '01010603030200'),
('01010603030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03', '永动机不可能制成', '', '01010603030000'),
('01010603030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP01', '“问题”栏目中的饮水小鸭不是永动机。', '', '01010603030300'),
('01010603030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP02', '小鸭的头和身由两个玻璃球构成，并通过细玻璃管相连。', '', '01010603030300'),
('01010603030403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP03', '下球盛有一些乙醚，上球连同尖嘴被吸水毛毡包起来。', '', '01010603030300'),
('01010603030404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP04', '永动机的思想违背了能量守恒定律，所以是不可能制成的。', '', '01010603030300'),
('01010603030405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP05', '应该指出的是，制造永动机的千万次失败使人们的头脑冷静下来，开始在更深层次寻找失败的原因。', '', '01010603030300'),
('01010603030406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC03.KP03.OP06', '国科学院宣布“不再审查有关永动机的一切设计”。', '', '01010603030300'),
('01010603040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04', '热力学第二定律', '', '01010603000000'),
('01010603040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01', '热力学第二定律', '', '01010603040000'),
('01010603040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP01', '在电冰箱的实例中，热量的确从低温物体——冰箱内的食品，传到了高温物体——冰箱外的空气。', '', '01010603040100'),
('01010603040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP02', '从能量的角度看，热机的工作分为两个阶段：第一个阶段是燃烧燃料，把燃料中的化学能变成工作物质的内能；第二个阶段是工作物质对外做功，把自己的内能变成机械能。', '', '01010603040100'),
('01010603040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP03', '热力学第二定律的开尔文表述阐述了机械能与内能转化的方向性：机械能可以全部转化为内能，而内能无法全部用来做功以转化成机械能。', '', '01010603040100'),
('01010603040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP01.OP04', '可以证明，热力学第二定律的克劳修斯表述和开尔文表述是等价的。', '', '01010603040100'),
('01010603040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02', '能源是有限的', '', '01010603040000'),
('01010603040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP01', '自然界的一切变化，人类社会的所有活动，都伴随着能量的转移和转化，能量是一切物质运动的源泉，是一切生命活动的基础。', '', '01010603040200'),
('01010603040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP02', '能量在数量上虽然守恒，但其转移和转化却具有方向性。', '', '01010603040200'),
('01010603040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP03', '核能、生物能……最终都转化成内能，流散到周围的环境中。', '', '01010603040200'),
('01010603040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP04', '周围环境中的内能很难再利用，而机械能、光能、化学能、电能相对于周围环境中的内能来说，可利用的品质要高。', '', '01010603040200'),
('01010603040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP05', '能源的使用过程中虽然能的总量保持守恒，但能量的品质下降了。', '', '01010603040200'),
('01010603040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH03.SEC04.KP02.OP06', '虽然能量总量不会减少，但能源会逐步减少，因此能源是有限的资源。', '', '01010603040200'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010604000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04', '原子结构和波粒二象性', '', NULL),
('01010604010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01', '普朗克黑体辐射理论', '', '01010604000000'),
('01010604010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01', '黑体与黑体辐射', '', '01010604010000'),
('01010604010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP01', '19世纪，由于冶金、星体测温等需求，人们对热辐射进行了大量的研究。', '', '01010604010100'),
('01010604010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP01.OP02', '研究表明，对于一般材料的物体，辐射电磁波的情况除了与温度有关，还与材料的种类及表面状况有关，而黑体辐射电磁波的强度按波长的分布只与黑体的温度有关。', '', '01010604010100'),
('01010604010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02', '黑体辐射的实验规律', '', '01010604010000'),
('01010604010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP01', '利用分光技术和热电偶等设备，可以测出黑体辐射电磁波的强度按波长分布的情况。', '', '01010604010200'),
('01010604010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP02', '从中可以看出，随着温度的升高，一方面，各种波长的辐射强度都有增加，另一方面，辐射强度的极大值向波长较短的方向移动。', '', '01010604010200'),
('01010604010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP03', '德国物理学家维恩在1896年、英国物理学家瑞利在1900年，分别提出了辐射强度按波长分布的理论公式。', '', '01010604010200'),
('01010604010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP04', '维恩公式在短波区与实验非常接近，在长波区则与实验偏离较大；瑞利公式在长波区与实验基本一致，但在短波区与实验严重不符。', '', '01010604010200'),
('01010604010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP02.OP05', '为了得出同全部实验相符的黑体辐射公式，德国物理学家普朗克做了多种尝试。', '', '01010604010200'),
('01010604010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03', '能量子', '', '01010604010000'),
('01010604010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP01', '弹簧振子的能量不一定是某个最小值的整数倍。', '', '01010604010300'),
('01010604010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP02', '而普朗克的假设则认为微观粒子的能量是量子化的，或者说微观粒子的能量是分立的。', '', '01010604010300'),
('01010604010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC01.KP03.OP03', '普朗克本人因此获得了1918年的诺贝尔物理学奖。', '', '01010604010300'),
('01010604020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02', '光电效应', '', '01010604000000'),
('01010604020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01', '光电效应的实验规律', '', '01010604020000'),
('01010604020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP01', '1887年，赫兹在研究电磁波的实验中偶尔发现，接收电路的间隙如果受到光照，就更容易产生电火花。', '', '01010604020100'),
('01010604020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP02', '科学家通过实验发现了以下规律。', '', '01010604020100'),
('01010604020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP03', '实验表明，不同金属的截止频率不同。', '', '01010604020100'),
('01010604020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP04', '在光照条件不变的情况下，随着所加电压的增大，光电流趋于一个饱和值。', '', '01010604020100'),
('01010604020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP05', '实验表明，在光的频率不变的情况下，入射光越强，饱和电流越大。', '', '01010604020100'),
('01010604020206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP01.OP06', '第四章原子结构和波粒二象性73表明产生电流的时间很快，即光电效应几乎是瞬时发生的。', '', '01010604020100'),
('01010604020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02', '光电效应经典解释中的疑难', '', '01010604020000'),
('01010604020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP01', '当光照射金属表面时，电子会吸收光的能量。', '', '01010604020200'),
('01010604020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP02', '光越强，逸出的电子数越多，光电流也就越大。', '', '01010604020200'),
('01010604020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP02.OP03', '●不管光的频率如何，只要光足够强，电子都可以获得足够能量从而逸出表面，不应存在截止频率。', '', '01010604020200'),
('01010604020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03', '爱因斯坦的光电效应理论', '', '01010604020000'),
('01010604020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP01', '对于光电效应的解释，爱因斯坦是在普朗克量子假说的基础上作出的。', '', '01010604020300'),
('01010604020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP02', '从下面的讨论可以看出，爱因斯坦光电效应方程可以很好地解释光电效应实验中的各种现象。', '', '01010604020300'),
('01010604020403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP03', '●电子一次性吸收光子的全部能量，不需要积累能量的时间，光电流自然几乎是瞬时产生的。', '', '01010604020300'),
('01010604020404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP04', '爱因斯坦由于提出了光电效应理论而获得1921年的诺贝尔物理学奖。', '', '01010604020300'),
('01010604020405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP03.OP05', '金属表面的光子数较多，照射金属时产生的光电子较多，因而饱和电流较大。', '', '01010604020300'),
('01010604020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04', '康普顿效应和光子的动量', '', '01010604020000'),
('01010604020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP01', '光可以与介质中的物质微粒发生散射，改变传播方向。', '', '01010604020400'),
('01010604020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP02', '康普顿效应让人们对光子有了更深入的认识。', '', '01010604020400'),
('01010604020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP04.OP03', '康普顿因此获得了1927年的诺贝尔物理学奖。', '', '01010604020400'),
('01010604020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05', '光的波粒二象性', '', '01010604020000'),
('01010604020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP01', '众所周知，在麦克斯韦的电磁理论建立之后，人们认识到光是一种电磁波，从而光的波动说被普遍接受，人们不再认为光是由粒子组成的。', '', '01010604020500'),
('01010604020602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP02', '而爱因斯坦的光电效应理论和康普顿效应理论表明，光在某些方面确实会表现得像是由一些粒子（即一个个有确定能量和动量的“光子”）组成的。', '', '01010604020500'),
('01010604020603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP03', '人们意识到，光既具有波动性，又具有粒子性。', '', '01010604020500'),
('01010604020604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC02.KP05.OP04', '从牛顿的微粒说、惠更斯和托马斯·杨的波动说，到麦克斯韦的电磁理论，再到爱因斯坦的光子理论乃至量子电动力学，人类对光的认识构成了一部科学史诗。', '', '01010604020500'),
('01010604030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03', '原子的核式结构模型', '', '01010604000000'),
('01010604030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01', '电子的发现', '', '01010604030000'),
('01010604030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP01', '为了找到有利于自己的证据，双方都做了许多实验。', '', '01010604030100'),
('01010604030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP02', '由实验测得的阴极射线粒子的比荷是氢离子①（也就是质子）比荷的近两千倍。', '', '01010604030100'),
('01010604030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP03', '电子电荷的精确测定是在1909〜1913年间由密立根通过著名的“油滴实验”做出的。', '', '01010604030100'),
('01010604030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP01.OP04', '金属受热还是放射性物质的自发辐射，都能发射同样的带电粒子——电子。', '', '01010604030100'),
('01010604030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02', '原子的核式结构模型', '', '01010604030000'),
('01010604030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP01', 'α粒子是从放射性物质（如铀和镭）中发射出来的快速运动的粒子，质量为氢原子质量的4倍、电子质量的7300倍。', '', '01010604030200'),
('01010604030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP02', '被散射的α粒子打在荧光屏上会有微弱的闪光产生。', '', '01010604030200'),
('01010604030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP03', '通过显微镜观察闪光就可以记录在某一时间内向某一方向散射的α粒子数。', '', '01010604030200'),
('01010604030304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP04', 'α粒子有库仑力的作用，一些α粒子的运动方向改变，也就是发生了α粒子的散射。', '', '01010604030200'),
('01010604030305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP05', 'α粒子所占的比例，可以推知原子中电荷的分布情况。', '', '01010604030200'),
('01010604030306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP06', '卢瑟福分析了实验数据后认为，事实应该是：占原子质量绝大部分的带正电的物质集中在很小的空间范围。', '', '01010604030200'),
('01010604030307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP07', '1911年，卢瑟福提出了自己的原子结构模型。', '', '01010604030200'),
('01010604030308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP08', '因为正电体很小，当α粒子进入原子区域后，大部分离正电体很远，受到的库仑斥力很小，运动方向几乎不改变。', '', '01010604030200'),
('01010604030309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP09', '只有极少数α粒子在穿过时距离正电体很近，因此受到很强的库仑斥力，发生大角度散射。', '', '01010604030200'),
('01010604030310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP10', '按照卢瑟福的理论，正电体的尺度是很小的，称为原子核。', '', '01010604030200'),
('01010604030311', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP02.OP11', '卢瑟福的原子模型因而称为核式结构模型。', '', '01010604030200'),
('01010604030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03', '原子核的电荷与尺度', '', '01010604030000'),
('01010604030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP01', '又由于原子是电中性的，可以推算出原子内含有的电子数。', '', '01010604030300'),
('01010604030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC03.KP03.OP02', '通常用核半径描述核的大小。', '', '01010604030300'),
('01010604040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04', '氢原子光谱和玻尔的原子模型', '', '01010604000000'),
('01010604040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01', '光谱', '', '01010604040000'),
('01010604040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP01', '有的光谱看起来不是一条条分立的谱线，而是连在一起的光带，叫作连续谱。', '', '01010604040100'),
('01010604040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP01.OP02', '气体中中性原子的发光光谱都是线状谱，说明原子只发出几种特定频率的光。', '', '01010604040100'),
('01010604040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02', '氢原子光谱的实验规律', '', '01010604040000'),
('01010604040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP01', '光谱的结果显示氢原子只能发出一系列特定波长的光。', '', '01010604040200'),
('01010604040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP02.OP02', '巴耳末公式以简洁的形式反映了氢原子的线状光谱的特征。', '', '01010604040200'),
('01010604040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03', '经典理论的困难', '', '01010604040000'),
('01010604040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP01', '卢瑟福的核式结构模型正确地指出了原子核的存在，很好地解释了α粒子散射实验，但跟经典电磁理论发生了矛盾。', '', '01010604040300'),
('01010604040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP02', '核外电子受到原子核的库仑引力的作用，却没有被吸引到原子核上，而是在以一定的速度绕核运动。', '', '01010604040300'),
('01010604040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP03.OP03', '随着绕核运动轨道半径的不断变化，电子运动的频率也要不断变化，因此原子辐射电磁波的频率也要不断变化。', '', '01010604040300'),
('01010604040400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04', '玻尔原子理论的基本假设', '', '01010604040000'),
('01010604040501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP01', '丹麦物理学家玻尔意识到了经典理论在解释原子结构方面的困难。', '', '01010604040400'),
('01010604040502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP02', '玻尔的原子结构假说包括以下两方面的内容。', '', '01010604040400'),
('01010604040503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP03', '玻尔认为，原子中的电子在库仑引力的作用下，绕原子核做圆周运动，服从经典力学的规律。', '', '01010604040400'),
('01010604040504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP04', '在玻尔理论中，电子的轨道半径只可能是某些分立的数值。', '', '01010604040400'),
('01010604040505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP05', '当电子在不同的轨道上运动时，原子处于不同的状态，具有不同的能量。', '', '01010604040400'),
('01010604040506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP06', '根据玻尔理论，电子只能在特定轨道上运动，因此，原子的能量也只能取一系列特定的值。', '', '01010604040400'),
('01010604040507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP04.OP07', '按照玻尔的观点，电子在一系列定态轨道上运动，不会发生电磁辐射。', '', '01010604040400'),
('01010604040500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05', '玻尔理论对氢光谱的解释', '', '01010604040000'),
('01010604040601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP01', '从玻尔的基本假设出发，运用经典电磁学和经典力学的理论，可以计算氢原子中电子的可能轨道半径及相应的能量。', '', '01010604040500'),
('01010604040602', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP02', '处于基态的原子非常稳定。', '', '01010604040500'),
('01010604040603', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP03', '处于激发态的原子是不稳定的，会自发地向能量较低的能级跃迁，放出光子，最终回到基态。', '', '01010604040500'),
('01010604040604', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP04', '由于原子的能级是分立的，所以放出的光子的能量也是分立的。', '', '01010604040500'),
('01010604040605', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP05', '由于不同的原子具有不同的结构，能级各不相同，因此辐射（或吸收）的光子频率也不相同。', '', '01010604040500'),
('01010604040606', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP06', '各种气体原子的能级不同，跃迁时发射光子的能量各异。', '', '01010604040500'),
('01010604040607', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP07', '街道上的霓虹灯、试电笔中的氖管，都是由灯管内的气体原子从高能级向低能级跃迁而发光的。', '', '01010604040500'),
('01010604040608', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP05.OP08', '食盐被灼烧时发的光，也主要是由食盐蒸气中钠原子的能级跃迁而造成的。', '', '01010604040500'),
('01010604040600', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06', '玻尔理论的局限性', '', '01010604040000'),
('01010604040701', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP01', '玻尔的原子理论第一次将量子观念引入原子领域，提出了定态和跃迁的概念。', '', '01010604040600'),
('01010604040702', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP02', '玻尔理论成功地解释了氢原子光谱的实验规律。', '', '01010604040600'),
('01010604040703', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP03', '①玻尔理论的不足之处在于保留了经典粒子的观念，仍然把电子的运动看作经典力学描述下的轨道运动。', '', '01010604040600'),
('01010604040704', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP04', '实际上，根据量子力学，原子中电子的坐标没有确定的值。', '', '01010604040600'),
('01010604040705', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC04.KP06.OP05', '当原子处于不同的状态时，电子在各处出现的概率是不一样的。', '', '01010604040600'),
('01010604050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05', '粒子的波动性和量子力学的建立', '', '01010604000000'),
('01010604050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01', '粒子的波动性', '', '01010604050000'),
('01010604050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP01.OP01', '1924年，法国物理学家德布罗意在对光的波粒二象性、玻尔氢原子理论以及相对论的深入研究的基础上，把波粒二象性推广到实物粒子，如电子、质子等。', '', '01010604050100'),
('01010604050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02', '物质波的实验验证', '', '01010604050000'),
('01010604050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP01', '德布罗意关于实物粒子具有波动性的假说在当时来看是难以理解的。', '', '01010604050200'),
('01010604050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP02', '1929年，德布罗意因提出物质波的假说获得了诺贝尔物理学奖。', '', '01010604050200'),
('01010604050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP03', '除了电子以外，后来还陆续证实了中子、质子以及原子、分子的波动性。', '', '01010604050200'),
('01010604050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP02.OP04', '德布罗意提出物质波的观念被实验证实，表明电子、质子、原子等粒子不但具有粒子的性质，而且具有波动的性质。', '', '01010604050200'),
('01010604050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03', '量子力学的建立', '', '01010604050000'),
('01010604050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP01', '19、20世纪之交，人们在黑体辐射、光电效应、氢原子光谱等许多类问题中，都发现了经典物理学无法解释的现象。', '', '01010604050300'),
('01010604050402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP02', '普朗克黑体辐射理论、爱因斯坦光电效应理论、康普顿散射理论、玻尔氢原子理论以及德布罗意物质波假说等一系列理论在解释实验方面都取得了成功。', '', '01010604050300'),
('01010604050403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP03.OP03', '1925年，德国物理学家海森堡和玻恩等人对玻尔的氢原子理论进行了推广和改造，使之可以适用于更普遍的情况。', '', '01010604050300'),
('01010604050400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04', '量子力学的应用', '', '01010604050000'),
('01010604050501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP01', '量子力学被应用到众多具体物理系统中，得到了与实验符合得很好的结果，获得了极大的成功。', '', '01010604050400'),
('01010604050502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP02', '借助量子力学，人们深入认识了微观世界的组成、结构和属性。', '', '01010604050400'),
('01010604050503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP03', '量子力学推动了核物理和粒子物理的发展。', '', '01010604050400'),
('01010604050504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP04', '人们认识了原子、原子核、基本粒子等各个微观层次的物质结构。', '', '01010604050400'),
('01010604050505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP05', '而粒子物理学的发展又促进了天文学和宇宙学的研究。', '', '01010604050400'),
('01010604050506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP06', '从整个宇宙到微小的粒子，人类探索自然的视野前所未有的宽广。', '', '01010604050400'),
('01010604050507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP07', '人们惊讶地发现，世界具有奇妙的结构，最微观层次和最宏观层次的规律，竟有着紧密的联系。', '', '01010604050400'),
('01010604050508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP08', '核物理的发展，还让人们成功地认识并利用了原子核反应堆所释放的能量——核能。', '', '01010604050400'),
('01010604050509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP09', '”量子力学推动了原子、分子物理和光学的发展。', '', '01010604050400'),
('01010604050510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP10', '人们认识了原子的结构，以及原子、分子和电磁场相互作用的方式。', '', '01010604050400'),
('01010604050511', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP11', '在此基础上，发展了各式各样的对原子和电磁场进行精确操控和测量的技术，如激光、核磁共振、原子钟，等等。', '', '01010604050400'),
('01010604050512', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP12', '以利用振荡的磁场测量材料中原子的性质，因此，被广泛地用于化学、生物研究和医学诊断。', '', '01010604050400'),
('01010604050513', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP13', '原子钟利用原子为电磁波校准频率，从而实现了对时间的高精度测量。', '', '01010604050400'),
('01010604050514', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP14', '在日常生活和国家安全中发挥巨大作用的卫星定位技术，其核心部件就是原子钟。', '', '01010604050400'),
('01010604050515', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP15', '量子力学推动了固体物理的发展。', '', '01010604050400'),
('01010604050516', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP16', '人们了解了固体中电子运行的规律，并弄清了为什么固体有导体、绝缘体和半导体之分。', '', '01010604050400'),
('01010604050517', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP17', '量子力学的应用还有很多。', '', '01010604050400'),
('01010604050518', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH04.SEC05.KP04.OP18', '毫不夸张地说，在过去的近一百年中，量子力学极大地推动了人类的进步。', '', '01010604050400'),
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id) 
VALUES 
('01010605000000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05', '原子核', '', NULL),
('01010605010000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01', '原子核的组成', '', '01010605000000'),
('01010605010100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01', '天然放射现象', '', '01010605010000'),
('01010605010201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP01', '受到贝克勒尔的发现的鼓舞，波兰裔法国物理学家玛丽·居里和她的丈夫皮埃尔·居里对铀和含铀的各种矿石进行了深入研究。', '', '01010605010100'),
('01010605010202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP01.OP02', '放射性元素自发地发出射线的现象，叫作天然放射现象。', '', '01010605010100'),
('01010605010200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02', '射线的本质', '', '01010605010000'),
('01010605010301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP01', '把放射源铀、钋或镭放入用铅做成的容器中，射线只能从容器的小孔射出，成为细细的一束。', '', '01010605010200'),
('01010605010302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP02', '物理学家经过多方面的研究后确认α射线、β射线和γ射线具有以下特征。', '', '01010605010200'),
('01010605010303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP03', 'α粒子带正电，电荷量是电子的2倍，质量是氢原子的4倍，其组成与氦原子核相同。', '', '01010605010200'),
('01010605010304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP04', '由于α粒子带电，质量又比较大，通过气体时很容易把气体分子中的电子剥离，使气体电离。', '', '01010605010200'),
('01010605010305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP05', '是电子流，速度可以接近光速。', '', '01010605010200'),
('01010605010306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP06', 'β射线的电离作用较弱，穿透能力较强，很容易穿透黑纸，也能穿透几毫米厚的铝板。', '', '01010605010200'),
('01010605010307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP07', 'γ射线的电离作用更弱，穿透能力更强，甚至能穿透几厘米厚的铅板和几十厘米厚的混凝土。', '', '01010605010200'),
('01010605010308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP02.OP08', '放射性的强度也不受温度、外界压强的影响。', '', '01010605010200'),
('01010605010300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03', '原子核的组成', '', '01010605010000'),
('01010605010401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP01', '绝大多数原子核的质量与电荷量之比都大于质子的相应比值。', '', '01010605010300'),
('01010605010402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP02', '质子带正电荷，电荷量与一个电子的电荷量相等。', '', '01010605010300'),
('01010605010403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP03', '由于中子不带电，原子核所带的电荷等于核内质子电荷的总和。', '', '01010605010300'),
('01010605010404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC01.KP03.OP04', '元素的性质与原子核外的电子有密切的关系。', '', '01010605010300'),
('01010605020000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02', '放射性元素的衰变', '', '01010605000000'),
('01010605020100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01', '原子核的衰变', '', '01010605020000'),
('01010605020201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP01', '铀238放出一个α粒子后，质量数减少4，电荷数减少2，成为新核。', '', '01010605020100'),
('01010605020202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP02', '0、电荷数为－1，可以把电子表示为0-1。', '', '01010605020100'),
('01010605020203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP03', '研究还表明，在一定条件下，原子核中的两个中子和两个质子会结合起来形成α粒子，并被释放出来。', '', '01010605020100'),
('01010605020204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP04', '原子核的能量也跟原子的能量一样，其变化是不连续的，也只能取一系列不连续的数值，因此，也存在着能级，同样是能级越低越稳定。', '', '01010605020100'),
('01010605020205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP01.OP05', '当放射性物质连续衰变时，原子核中有的发生α衰变，有的发生β衰变，同时伴随着γ射线辐射。', '', '01010605020100'),
('01010605020200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02', '半衰期', '', '01010605020000'),
('01010605020301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP01', '放射性同位素衰变的快慢有一定的规律。', '', '01010605020200'),
('01010605020302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP02', '不同的放射性元素，半衰期不同，甚至差别非常大。', '', '01010605020200'),
('01010605020303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP02.OP03', '放射性元素衰变的快慢是由核内部自身的因素决定的，跟原子所处的化学状态和外部条件没有关系。', '', '01010605020200'),
('01010605020300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03', '核反应', '', '01010605020000'),
('01010605020401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP01', '衰变是原子核的自发变化，科学家更希望人工控制原子核的变化。', '', '01010605020300'),
('01010605020402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP03.OP02', '与衰变过程一样，在核反应中，质量数守恒、电荷数守恒。', '', '01010605020300'),
('01010605020400', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04', '放射性同位素及其应用', '', '01010605020000'),
('01010605020501', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP01', '天然放射性同位素不过40多种，而今天通过核反应生成的人工放射性同位素已达3000多种，每种元素都有了自己的放射性同位素。', '', '01010605020400'),
('01010605020502', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP02', '丰富的放射性同位素资源在国民经济和科学研究的各个领域中得到了广泛的应用。', '', '01010605020400'),
('01010605020503', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP03', '工业部门可以使用放射性同位素发出的射线来测厚度。', '', '01010605020400'),
('01010605020504', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP04', '像癌细胞那样，不断迅速繁殖的、无法控制的细胞组织，在射线照射下破坏得比健康细胞快。', '', '01010605020400'),
('01010605020505', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP05', '利用γ射线照射种子，会使种子的遗传基因发生变异，经过筛选，可以培育出新品种。', '', '01010605020400'),
('01010605020506', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP06', '棉花在开花、结桃的时候需要较多的磷肥，把磷肥喷在棉花叶子上，磷肥也能被吸收。', '', '01010605020400'),
('01010605020507', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP07', '如果用磷的放射性同位素制成肥料喷在棉花的叶面上，然后每隔一定时间用探测器测量棉株各部位的放射性强度，上面的问题就解决了。', '', '01010605020400'),
('01010605020508', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP08', '人体甲状腺的工作需要碘，碘被吸收后聚集在甲状腺内。', '', '01010605020400'),
('01010605020509', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP09', '定时用探测器测量甲状腺及邻近组织的放射强度，有助于诊断甲状腺的疾病。', '', '01010605020400'),
('01010605020510', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP04.OP10', '近年来，有关生物大分子的结构及其功能的研究，几乎都要借助于示踪原子。', '', '01010605020400'),
('01010605020500', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05', '辐射与安全', '', '01010605020000'),
('01010605020601', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC02.KP05.OP01', '人类一直生活在放射性的环境中。', '', '01010605020500'),
('01010605030000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03', '核力与结合能', '', '01010605000000'),
('01010605030100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01', '核力与四种基本相互作用', '', '01010605030000'),
('01010605030201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP01', '现代物理学认为，自然界存在着四种基本相互作用，即引力相互作用、电磁相互作用、强相互作用和弱相互作用。', '', '01010605030100'),
('01010605030202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP02', '17世纪下半叶，人们发现了万有引力。', '', '01010605030100'),
('01010605030203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP03', '引力是自然界的一种基本相互作用，地面物体所受的重力只是引力在地球表面附近的一种表现。', '', '01010605030100'),
('01010605030204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP04', '20世纪，物理学家发现原子核是由若干带正电荷的质子和不带电的中子组成的。', '', '01010605030100'),
('01010605030205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP05', '19世纪末20世纪初，物理学家发现了放射现象。', '', '01010605030100'),
('01010605030206', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP01.OP06', '①弱相互作用是引起原子核β衰变的原因，即引起中子—质子转变的原因。', '', '01010605030100'),
('01010605030200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02', '结合能', '', '01010605030000'),
('01010605030301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP01', '相距很远的两个物体，由于万有引力而相互接近，运动速度越来越大，引力势能转化为动能。', '', '01010605030200'),
('01010605030302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP02', '最后撞在一起，一部分动能变成热并散失掉了。', '', '01010605030200'),
('01010605030303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP02.OP03', '比结合能越大，原子核中核子结合得越牢固，原子核越稳定。', '', '01010605030200'),
('01010605030300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03', '质量亏损', '', '01010605030000'),
('01010605030401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP01', '原子核的结合能很难直接测量。', '', '01010605030300'),
('01010605030402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC03.KP03.OP02', '质量亏损表明，的确存在着原子核的结合能。', '', '01010605030300'),
('01010605040000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04', '核裂变与核聚变', '', '01010605000000'),
('01010605040100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01', '核裂变的发现', '', '01010605040000'),
('01010605040201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP01', '以铀235为例，核裂变时产生两或三个中子。', '', '01010605040100'),
('01010605040202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP02', '原子核的体积非常小，原子内部的空隙很大，如果铀块不够大，中子在铀块中通过时，很有可能碰不到铀核而跑到铀块外面去，链式反应不能继续。', '', '01010605040100'),
('01010605040203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP03', '只有当铀块足够大时，核裂变产生的中子才有足够大的概率打中某个铀核，使链式反应进行下去。', '', '01010605040100'),
('01010605040204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP01.OP04', '铀核裂变时如果生成物不同，释放的能量也有差异。', '', '01010605040100'),
('01010605040200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02', '反应堆与核电站', '', '01010605040000'),
('01010605040301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP01', '原子核的链式反应也可以在人工控制下进行。', '', '01010605040200'),
('01010605040302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP02', '其实，在第一个原子弹制成以前，科学家们已经实现了核能的可控释放。', '', '01010605040200'),
('01010605040303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP03', '1942年，美籍意大利物理学家费米就主持建立了世界上第一个称为“核反应堆”的装置，首次通过可控制的链式反应实现了核能的释放。', '', '01010605040200'),
('01010605040304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP04', '常用的慢化剂有石墨、重水和普通水（也叫轻水）。', '', '01010605040200'),
('01010605040305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP05', '为了调节中子数目以控制反应速度，还需要在铀棒之间插进一些镉棒。', '', '01010605040200'),
('01010605040306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP06', '核燃料发生核裂变释放的能量使反应区温度升高。', '', '01010605040200'),
('01010605040307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP07', '水或液态的金属钠等流体在反应堆内外循环流动，把反应堆内的热量传输出去，用于发电，同时也使反应堆冷却。', '', '01010605040200'),
('01010605040308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP08', '在热交换器内，第二回路中的水被加热生成高温高压蒸汽，驱动汽轮机运转。', '', '01010605040200'),
('01010605040309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP09', '建造核电站时需要特别注意防止放射线和放射性物质的泄漏，以避免射线对人体的伤害和放射性物质对水源、空气和工作场所造成放射性污染。', '', '01010605040200'),
('01010605040310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP02.OP10', '核反应堆中的核废料具有很强的放射性，需要装入特制的容器，深埋地下。', '', '01010605040200'),
('01010605040300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03', '核聚变', '', '01010605040000'),
('01010605040401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP01', '当物质的温度达到几百万开尔文时，剧烈的热运动使得一部分原子核具有足够的动能，可以克服库仑斥力，碰撞时十分接近，发生核聚变。', '', '01010605040300'),
('01010605040402', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP02', '太阳的主要成分是氢。', '', '01010605040300'),
('01010605040403', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP03', '现在地球上消耗的能量，追根溯源，绝大部分还是来自太阳，即太阳内部核聚变时释放的核能。', '', '01010605040300'),
('01010605040404', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP04', '太阳在“核燃烧”的过程中“体重”不断减轻。', '', '01010605040300'),
('01010605040405', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP05', '太阳的寿命已经有50亿年了。', '', '01010605040300'),
('01010605040406', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP06', '目前，人工产生的热核反应主要用在核武器上，那就是氢弹。', '', '01010605040300'),
('01010605040407', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP07', '与核裂变相比，核聚变有很多优点。', '', '01010605040300'),
('01010605040408', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP08', '利用核聚变物质的惯性进行约束。', '', '01010605040300'),
('01010605040409', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC04.KP03.OP09', '总的来说，实现受控核聚变还有一段很长的路要走。', '', '01010605040300'),
('01010605050000', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05', '“基本”粒子', '', '01010605000000'),
('01010605050100', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01', '发现新粒子', '', '01010605050000'),
('01010605050201', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP01', '从20世纪30年代以来，人们在对宇宙线的研究中陆续发现了一些新的粒子。', '', '01010605050100'),
('01010605050202', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP02', '1932年发明的粒子加速器，能使带电粒子加速到很高的能量。', '', '01010605050100'),
('01010605050203', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP03', '利用加速器和对撞机，人们发现了更多的粒子。', '', '01010605050100'),
('01010605050204', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP04', '粒子物理的研究不再单纯依赖宇宙线。', '', '01010605050100'),
('01010605050205', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP01.OP05', '高能量的对撞机往往极为庞大和复杂。', '', '01010605050100'),
('01010605050200', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02', '粒子的分类', '', '01010605050000'),
('01010605050301', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP01', '现在已经发现的粒子达400多种。', '', '01010605050200'),
('01010605050302', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP02', '强子是参与强相互作用的粒子。', '', '01010605050200'),
('01010605050303', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP03', '质子和中子都是强子。', '', '01010605050200'),
('01010605050304', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP04', '轻子不参与强相互作用。', '', '01010605050200'),
('01010605050305', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP05', '最早发现的轻子是电子，后来发现的轻子有电子中微子、μ子、μ子中微子以及τ子和τ子中微子。', '', '01010605050200'),
('01010605050306', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP06', '每种轻子都有对应的反粒子。', '', '01010605050200'),
('01010605050307', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP07', '现代实验还没有发现轻子的内部结构。', '', '01010605050200'),
('01010605050308', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP08', '光子传递电磁相互作用，中间玻色子传递弱相互作用，胶子传递强相互作用。', '', '01010605050200'),
('01010605050309', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP09', '希格斯玻色子是希格斯场的量子激发。', '', '01010605050200'),
('01010605050310', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP10', '基本粒子因与希格斯场耦合而获得质量。', '', '01010605050200'),
('01010605050311', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP02.OP11', '2012年，欧洲核子研究中心利用大型强子对撞机发现了希格斯玻色子。', '', '01010605050200'),
('01010605050300', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03', '研究样例', '', '01010605050000'),
('01010605050401', 'PHYSICS', 'SENIOR', 'TERM_6', 'CH05.SEC05.KP03.OP01', '①燃气灶烧水时的能量转化和转移的情况如下。', '', '01010605050300'),
