INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010101000000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01', '运动的描述', '', NULL),
('01010101010000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01', '质点   参考系', '', '01010101000000'),
('01010101010100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP01', '物体和质点', '', '01010101010000'),
('01010101010201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP01.OP01', '可以把物体看成一个点，什么样的问题不能把物体看成一个点。', '', '01010101010100'),
('01010101010202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP01.OP02', '人类居住的地球在绕太阳公转，同时又在自转。', '', '01010101010100'),
('01010101010203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP01.OP03', '还有一种情况，虽然不能忽略物体的大小和形状，但是，物体上各点的运动情况完全相同。', '', '01010101010100'),
('01010101010204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP01.OP04', '从前面的讨论可以看出，一个物体能否看成质点是由所要研究的问题决定的。', '', '01010101010100'),
('01010101010205', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP01.OP05', '同一个物体，由于所要研究的问题不同，有时可以看成质点，有时不能看成质点。', '', '01010101010100'),
('01010101010200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP02', '参考系', '', '01010101010000'),
('01010101010301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP02.OP01', '自然界的一切物体都处于永恒的运动中，绝对静止的物体是不存在的。', '', '01010101010200'),
('01010101010302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP02.OP02', '在描述一个物体的运动时，参考系可以任意选择。', '', '01010101010200'),
('01010101010303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP02.OP03', '参考系选取得当，会使问题的研究变得简洁、方便。', '', '01010101010200'),
('01010101010304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC01.KP02.OP04', '通常情况下，在讨论地面上物体的运动时，都以地面为参考系。', '', '01010101010200'),
('01010101020000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02', '时间   位移', '', '01010101000000'),
('01010101020100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP01', '时刻和时间间隔', '', '01010101020000'),
('01010101020201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP01.OP01', '要描述物体位置随时间的变化，首先要清楚“时间”一词的含义。', '', '01010101020100'),
('01010101020202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP01.OP02', '说到时间，不能不说时刻和时间间隔。', '', '01010101020100'),
('01010101020203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP01.OP03', '时刻和时间间隔既有联系又有区别。', '', '01010101020100'),
('01010101020204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP01.OP04', '在表示时间的数轴上，时刻用点表示，时间间隔用线段表示。', '', '01010101020100'),
('01010101020200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP02', '位置和位移', '', '01010101020000'),
('01010101020301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP02.OP01', '既可以乘火车，也可以乘飞机，还可以先乘火车到武汉，然后乘轮船沿长江而上。', '', '01010101020200'),
('01010101020300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP03', '直线运动的位移', '', '01010101020000'),
('01010101020400', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP04', '位移—时间图像', '', '01010101020000'),
('01010101020500', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP05', '位移和时间的测量', '', '01010101020000'),
('01010101020601', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP05.OP01', '学校实验室中常用打点计时器来记录时间和位移。', '', '01010101020500'),
('01010101020602', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC02.KP05.OP02', '如果把纸带和运动的物体连在一起，纸带上各点之间的距离就表示相应时间间隔中物体的位移大小。', '', '01010101020500'),
('01010101030000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03', '位置变化快慢的描述——速度', '', '01010101000000'),
('01010101030100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP01', '速度', '', '01010101030000'),
('01010101030201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP01.OP01', '不同的运动，位置变化的快慢往往不同，也就是说，运动的快慢不同。', '', '01010101030100'),
('01010101030200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP02', '平均速度和瞬时速度', '', '01010101030000'),
('01010101030301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP02.OP01', '一般来说，物体在某一段时间内，运动的快慢通常是变化的。', '', '01010101030200'),
('01010101030302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP02.OP02', '匀速直线运动是瞬时速度保持不变的运动。', '', '01010101030200'),
('01010101030303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP02.OP03', '在匀速直线运动中，平均速度与瞬时速度相等。', '', '01010101030200'),
('01010101030304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP02.OP04', '日常生活中说到的“速度”，有时是指速率，要根据上下文判断。', '', '01010101030200'),
('01010101030300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC03.KP03', '速度—时间图像', '', '01010101030000'),
('01010101040000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04', '速度变化快慢的描述——加速度', '', '01010101000000'),
('01010101040100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP01', '加速度', '', '01010101040000'),
('01010101040201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP01.OP01', '看来“速度变化的快慢”是一个不同于“速度”的概念。', '', '01010101040100'),
('01010101040202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP01.OP02', '两个物体速度变化相同，所用时间短的当然速度变化得快。', '', '01010101040100'),
('01010101040203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP01.OP03', '要比较位置变化的快慢，可以用位移除以时间。', '', '01010101040100'),
('01010101040204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP01.OP04', '同理，要比较速度变化的快慢，可以用速度的变化量除以时间。', '', '01010101040100'),
('01010101040205', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP01.OP05', '现在讨论做直线运动的物体加速度的方向。', '', '01010101040100'),
('01010101040200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP02', '加速度的方向', '', '01010101040000'),
('01010101040300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP03', '从', '', '01010101040000'),
('01010101040400', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP04', '-', '', '01010101040000'),
('01010101040500', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP05', '图像看加速度', '', '01010101040000'),
('01010101040601', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP05.OP01', '比为加速度，其比值为该直线的斜率。', '', '01010101040500'),
('01010101040602', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP05.OP02', '学习是一个不断探究、积累和总结的过程。', '', '01010101040500'),
('01010101040603', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH01.SEC04.KP05.OP03', '学习的每一阶段都应该适时地总结，把学到的知识、方法、过程和体会等及时地加以梳理，以便更好地理解和把握。', '', '01010101040500');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010102000000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02', '匀变速直线运动的研究', '', NULL),
('01010102010000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01', '实验：探究小车速度随时间变化的规律', '', '01010102000000'),
('01010102010100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP01', '实验思路', '', '01010102010000'),
('01010102010201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP01.OP01', '要研究小车速度随时间变化的规律，就要想办法测量小车在不同时刻的瞬时速度，而打点计时器具有此功能。', '', '01010102010100'),
('01010102010202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP01.OP02', '小车在槽码的牵引下运动，通过研究纸带上的信息，就可以知道小车运动的速度是怎样随时间变化的。', '', '01010102010100'),
('01010102010200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP02', '数据分析', '', '01010102010000'),
('01010102010300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP03', '进行实验', '', '01010102010000'),
('01010102010401', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP03.OP01', '把小车停在靠近打点计时器的位置。', '', '01010102010300'),
('01010102010402', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP03.OP02', '随后，立即关闭电源。', '', '01010102010300'),
('01010102010403', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP03.OP03', '增减所挂的槽码（或在小车上放置重物），更换纸带，再做两次实验。', '', '01010102010300'),
('01010102010400', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP04', '数据记录', '', '01010102010000'),
('01010102010501', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC01.KP04.OP01', '为了便于测量，舍掉纸带开头一些过于密集的点，找一个适当的点作为计时起点。', '', '01010102010400'),
('01010102020000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC02', '匀变速直线运动的速度与时间的关系', '', '01010102000000'),
('01010102020100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC02.KP01', '匀变速直线运动', '', '01010102020000'),
('01010102020200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC02.KP02', '速度与时间的关系', '', '01010102020000'),
('01010102020301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC02.KP02.OP01', '（1）汽车做匀加速直线运动。', '', '01010102020200'),
('01010102030000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03', '匀变速直线运动的位移与时间的关系', '', '01010102000000'),
('01010102030100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03.KP01', '匀变速直线运动的位移', '', '01010102030000'),
('01010102030201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03.KP01.OP01', '（2）飞机在航母上降落时，需用阻拦索使飞机迅速停下来。', '', '01010102030100'),
('01010102030202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03.KP01.OP02', '某同学乘坐动车时，通过观察里程碑和车厢内电子屏上显示的动车速度来估算动车减速进站时的加速度大小。', '', '01010102030100'),
('01010102030200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03.KP02', '速度与位移的关系', '', '01010102030000'),
('01010102030301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03.KP02.OP01', '沿动车运动方向为正方向建立一维坐标系。', '', '01010102030200'),
('01010102030302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC03.KP02.OP02', '从第2节和第3节的例题可以看到，只有建立了坐标系，速度、加速度等物理量的正负号才能确定。', '', '01010102030200'),
('01010102040000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04', '自由落体运动', '', '01010102000000'),
('01010102040100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01', '自由落体运动', '', '01010102040000'),
('01010102040201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01.OP01', '伽利略认为，根据亚里士多德“重的物体下落得快”的论断，会推出相互矛盾的结论。', '', '01010102040100'),
('01010102040202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01.OP02', '根据仔细的分析，伽利略认为物体下落的运动只有一种可能性：重的物体与轻的物体应该下落得同样快。', '', '01010102040100'),
('01010102040203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01.OP03', '由实验可以看到，将玻璃管里的空气抽出去后，没有了空气阻力的影响，轻的物体和重的物体下落得同样快。', '', '01010102040100'),
('01010102040204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01.OP04', '在现实生活中人们之所以看到物体下落的快慢不同，是因为空气阻力的影响。', '', '01010102040100'),
('01010102040205', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01.OP05', '如果没有空气阻力，所有物体下落的快慢都一样。', '', '01010102040100'),
('01010102040206', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP01.OP06', '在有空气的空间，如果空气阻力的作用比较小，可以忽略，物体的下落可以近似看作自由落体运动。', '', '01010102040100'),
('01010102040200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP02', '自由落体加速度', '', '01010102040000'),
('01010102040301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH02.SEC04.KP02.OP01', '自由落体运动是初速度为0的匀加速直线运动，所以匀变速直线运动的基本公式及其推论都适用于自由落体运动。', '', '01010102040200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010103000000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03', '相互作用——力', '', NULL),
('01010103010000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01', '重力与弹力', '', '01010103000000'),
('01010103010100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01', '重力', '', '01010103010000'),
('01010103010201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01.OP01', '重力的施力物体是地球，方向竖直向下。', '', '01010103010100'),
('01010103010202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01.OP02', '做自由落体运动的物体只受到重力的作用。', '', '01010103010100'),
('01010103010203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01.OP03', '质量均匀分布的物体，重心的位置只跟物体的形状有关。', '', '01010103010100'),
('01010103010204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01.OP04', '质量分布不均匀的物体，重心的位置除了跟物体的形状有关外，还跟物体内质量的分布有关。', '', '01010103010100'),
('01010103010205', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01.OP05', '力可以用有向线段表示。', '', '01010103010100'),
('01010103010206', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP01.OP06', '有向线段的长短表示力的大小，箭头表示力的方向，箭尾（或箭头）表示力的作用点。', '', '01010103010100'),
('01010103010200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP02', '弹力', '', '01010103010000'),
('01010103010301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP02.OP01', '有时物体的形变很小，不易被觉察。', '', '01010103010200'),
('01010103010300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP03', '胡克定律', '', '01010103010000'),
('01010103010401', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC01.KP03.OP01', '弹簧在形变时产生的弹力与弹簧的伸长量是有关系的。', '', '01010103010300'),
('01010103020000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02', '摩擦力', '', '01010103000000'),
('01010103020100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02.KP01', '滑动摩擦力', '', '01010103020000'),
('01010103020201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02.KP01.OP01', '滑动摩擦力的方向总是沿着接触面，并且跟物体相对运动的方向相反。', '', '01010103020100'),
('01010103020200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02.KP02', '静摩擦力', '', '01010103020000'),
('01010103020301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02.KP02.OP01', '静摩擦力的方向总是跟物体相对运动趋势的方向相反。', '', '01010103020200'),
('01010103020302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02.KP02.OP02', '只要沙发与地面间没有产生相对运动，静摩擦力的大小就随着推力的增大而增大，并与推力保持大小相等。', '', '01010103020200'),
('01010103020303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC02.KP02.OP03', '皮带运输机能把货物送往高处，也是静摩擦力作用的结果。', '', '01010103020200'),
('01010103030000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03', '牛顿第三定律', '', '01010103000000'),
('01010103030100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP01', '作用力和反作用力', '', '01010103030000'),
('01010103030201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP01.OP01', '力是物体对物体的作用。', '', '01010103030100'),
('01010103030202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP01.OP02', '只要谈到力，就一定存在着受力物体和施力物体。', '', '01010103030100'),
('01010103030203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP01.OP03', '观察和实验的结果表明，两个物体之间的作用总是相互的。', '', '01010103030100'),
('01010103030204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP01.OP04', '当一个物体对另一个物体施加了力，后一个物体一定同时对前一个物体也施加了力。', '', '01010103030100'),
('01010103030205', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP01.OP05', '作用力和反作用力总是互相依赖、同时存在的。', '', '01010103030100'),
('01010103030200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP02', '牛顿第三定律', '', '01010103030000'),
('01010103030301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP02.OP01', '在生活和生产中应用牛顿第三定律的例子是很多的。', '', '01010103030200'),
('01010103030302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP02.OP02', '陷在泥泞中的汽车，尽管车轮飞转，但是如果泥和车轮之间太滑，车轮得不到足够的摩擦力，车也是出不来的。', '', '01010103030200'),
('01010103030303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP02.OP03', '许多越野车可以按需要分别由前轮或后轮驱动，必要时甚至可以四轮同时驱动，以便根据车轮与地面接触的不同情况来获得足够的驱动力。', '', '01010103030200'),
('01010103030300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP03', '物体受力的初步分析', '', '01010103030000'),
('01010103030401', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC03.KP03.OP01', '分析物体的受力，有两条思路。', '', '01010103030300'),
('01010103040000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04', '力的合成和分解', '', '01010103000000'),
('01010103040100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP01', '合力和分力', '', '01010103040000'),
('01010103040200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP02', '力的合成和分解', '', '01010103040000'),
('01010103040301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP02.OP01', '由于各个力的数据都没有改变，因此，力的分解也遵从平行四边形定则。', '', '01010103040200'),
('01010103040302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP02.OP02', '一个已知力究竟应该怎样分解，要根据具体问题来确定。', '', '01010103040200'),
('01010103040300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP03', '矢量和标量', '', '01010103040000'),
('01010103040401', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP03.OP01', '力的合成，可以认为是力的相加。', '', '01010103040300'),
('01010103040402', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP03.OP02', '二力相加时，不能简单地把两个力的大小相加，而要按平行四边形定则来确定合力的大小和方向。', '', '01010103040300'),
('01010103040403', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC04.KP03.OP03', '除了力和位移以外，速度、加速度都是矢量。', '', '01010103040300'),
('01010103050000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC05', '共点力的平衡', '', '01010103000000'),
('01010103050100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC05.KP01', '共点力平衡的条件', '', '01010103050000'),
('01010103050201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC05.KP01.OP01', '桌上的书、屋顶的灯、随传送带匀速运送的物体、沿直线公路匀速前进的汽车，都处于平衡状态。', '', '01010103050100'),
('01010103050202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC05.KP01.OP02', '二力平衡时物体所受的合力为0。', '', '01010103050100'),
('01010103050203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH03.SEC05.KP01.OP03', '方法1用两个力的合力和第三个力平衡的方法求解。', '', '01010103050100');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010104000000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04', '运动和力的关系', '', NULL),
('01010104010000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01', '牛顿第一定律', '', '01010104000000'),
('01010104010100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP01', '理想实验的魅力', '', '01010104010000'),
('01010104010201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP01.OP01', '伽利略认为，将人们引入歧途的是摩擦，而物体在通常情况下运动时，摩擦又是难以避免的。', '', '01010104010100'),
('01010104010202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP01.OP02', '若没有摩擦，球将永远运动下去。', '', '01010104010100'),
('01010104010203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP01.OP03', '如果没有摩擦，小球将到达原来的高度。', '', '01010104010100'),
('01010104010204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP01.OP04', '如果第二个斜面倾角减小，小球仍将到达原来的高度，但是运动的距离更长。', '', '01010104010100'),
('01010104010200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP02', '牛顿第一定律', '', '01010104010000'),
('01010104010301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP02.OP01', '牛顿第一定律也叫作惯性定律。', '', '01010104010200'),
('01010104010302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP02.OP02', '任何物体都和周围的物体有相互作用，不受力作用的物体是不存在的。', '', '01010104010200'),
('01010104010303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP02.OP03', '牛顿第一定律揭示了运动和力的关系：力不是维持物体运动状态的原因，而是改变物体运动状态的原因。', '', '01010104010200'),
('01010104010304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP02.OP04', '牛顿第一定律为力学的发展奠定了坚实的基础。', '', '01010104010200'),
('01010104010300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP03', '惯性与质量', '', '01010104010000'),
('01010104010401', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP03.OP01', '①牛顿第一定律涉及两个重要的物理概念：力和惯性。', '', '01010104010300'),
('01010104010402', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP03.OP02', '质量大的石块需要的力大。', '', '01010104010300'),
('01010104010403', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP03.OP03', '大量事例说明，不同质量的物体，惯性的大小是不一样的。', '', '01010104010300'),
('01010104010404', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC01.KP03.OP04', '质量只有大小，没有方向，是标量。', '', '01010104010300'),
('01010104020000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02', '实验：探究加速度与力、质量的关系', '', '01010104000000'),
('01010104020100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01', '实验思路', '', '01010104020000'),
('01010104020201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01.OP01', '将小车置于水平木板上，通过滑轮与槽码相连。', '', '01010104020100'),
('01010104020202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01.OP02', '小车可以在槽码的牵引下运动。', '', '01010104020100'),
('01010104020203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01.OP03', '保持小车质量不变，通过改变槽码的个数改变小车所受的拉力。', '', '01010104020100'),
('01010104020204', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01.OP04', '小车所受的拉力可认为与槽码所受的重力相等。', '', '01010104020100'),
('01010104020205', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01.OP05', '测得不同拉力下小车运动的加速度，分析加速度与拉力的变化情况，找出二者之间的定量关系。', '', '01010104020100'),
('01010104020206', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP01.OP06', '保持小车所受的拉力不变，通过在小车上增加重物改变小车的质量。', '', '01010104020100'),
('01010104020200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP02', '物理量的测量', '', '01010104020000'),
('01010104020301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP02.OP01', '可以用天平测量质量。', '', '01010104020200'),
('01010104020302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP02.OP02', '为了改变小车的质量，可以在小车中增减砝码的数量。', '', '01010104020200'),
('01010104020303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP02.OP03', '方法2将打点计时器的纸带连在小车上，根据纸带上打出的点来测量加速度（参考第一章的实验方法）。', '', '01010104020200'),
('01010104020304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP02.OP04', '现实中，仅受一个力作用的物体几乎不存在。', '', '01010104020200'),
('01010104020300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP03', '进行实验', '', '01010104020000'),
('01010104020400', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP04', '数据分析', '', '01010104020000'),
('01010104020501', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC02.KP04.OP01', '根据各组实验数据在坐标纸上描点。', '', '01010104020400'),
('01010104030000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03', '牛顿第二定律', '', '01010104000000'),
('01010104030100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP01', '牛顿第二定律的表达式', '', '01010104030000'),
('01010104030201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP01.OP01', '牛顿第二定律不仅阐述了力、质量和加速度三者数量间的关系，还明确了加速度的方向与力的方向一致。', '', '01010104030100'),
('01010104030200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP02', '力的单位', '', '01010104030000'),
('01010104030301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP02.OP01', '假定试车过程中汽车受到的阻力不变。', '', '01010104030200'),
('01010104030302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP02.OP02', '取消动力后，汽车做匀减速直线运动。', '', '01010104030200'),
('01010104030303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP02.OP03', '在某次测定中，悬线与竖直方向的夹角为θ，求列车的加速度。', '', '01010104030200'),
('01010104030304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC03.KP02.OP04', '方法1选择小球为研究对象。', '', '01010104030200'),
('01010104040000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04', '力学单位制', '', '01010104000000'),
('01010104040100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP01', '基本单位', '', '01010104040000'),
('01010104040201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP01.OP01', '的单位（米、秒）就是基本单位。', '', '01010104040100'),
('01010104040200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP02', '国际单位制', '', '01010104040000'),
('01010104040301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP02.OP01', '如果采用不同的物理量作为基本量，或者虽然采用相同的基本量，但采用的基本单位不同，导出单位自然随之不同，从而产生不同的单位制。', '', '01010104040200'),
('01010104040302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP02.OP02', '不同的地区使用不同的单位制，不方便交流。', '', '01010104040200'),
('01010104040303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP02.OP03', '以静止的物体为研究对象。', '', '01010104040200'),
('01010104040304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC04.KP02.OP04', '既然如此，在统一已知量的单位后，计算过程中就不必写出各量后面的单位，只在数字计算式后面写出正确的单位就可以了。', '', '01010104040200'),
('01010104050000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05', '牛顿运动定律的应用', '', '01010104000000'),
('01010104050100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP01', '从受力确定运动情况', '', '01010104050000'),
('01010104050201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP01.OP01', '如果已知物体的受力情况，可以由牛顿第二定律求出物体的加速度，再通过运动学的规律确定物体的运动情况。', '', '01010104050100'),
('01010104050202', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP01.OP02', '按比赛规则，投掷冰壶运动员的队友，可以用毛刷在冰壶滑行前方来回摩擦冰面，减小冰面的动摩擦因数以调节冰壶的运动。', '', '01010104050100'),
('01010104050203', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP01.OP03', '（1）选择滑行的冰壶为研究对象。', '', '01010104050100'),
('01010104050200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP02', '从运动情况确定受力', '', '01010104050000'),
('01010104050301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP02.OP01', '如果已知物体的运动情况，可以根据运动学规律求出物体的加速度，结合受力分析，再根据牛顿第二定律求出力。', '', '01010104050200'),
('01010104050302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP02.OP02', '以滑雪者为研究对象。', '', '01010104050200'),
('01010104050303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC05.KP02.OP03', '滑雪者沿山坡向下做匀加速直线运动。', '', '01010104050200'),
('01010104060000', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06', '超重和失重', '', '01010104000000'),
('01010104060100', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP01', '重力的测量', '', '01010104060000'),
('01010104060201', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP01.OP01', '在地球表面附近，物体由于地球的吸引而受到重力。', '', '01010104060100'),
('01010104060200', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02', '超重和失重', '', '01010104060000'),
('01010104060301', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP01', '体重计的示数称为视重，反映了人对体重计的压力。', '', '01010104060200'),
('01010104060302', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP02', '设竖直向下方向为坐标轴正方向。', '', '01010104060200'),
('01010104060303', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP03', '设竖直向上方向为坐标轴正方向。', '', '01010104060200'),
('01010104060304', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP04', '实际中有许多领域涉及超重和失重现象。', '', '01010104060200'),
('01010104060305', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP05', '如果是载人航天，在火箭发射阶段，航天员要承受数倍于自身体重的压力。', '', '01010104060200'),
('01010104060306', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP06', '只有很好地研究材料、机械结构、人体自身所能承受的压力问题，才能使火箭成功发射、航天员顺利飞向太空。', '', '01010104060200'),
('01010104060307', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP07', '课堂上的学习是一种学习方式。', '', '01010104060200'),
('01010104060308', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP08', '本栏目中提供了一个“研究样例”和一个“参考选题”。', '', '01010104060200'),
('01010104060309', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP09', '通过阅读样例，大致了解应该如何进行课题研究，包括：课题研究的选题是怎样提出的，研究的证据是怎样收集的，怎样通过对证据的解释形成研究结论，如何撰写课题研究报告等。', '', '01010104060200'),
('01010104060310', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP02.OP10', '在学习样例后，同学们要自己选择一个课题进行研究，写出研究报告。', '', '01010104060200'),
('01010104060300', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03', '研究样例', '', '01010104060000'),
('01010104060401', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP01', '人们散步时并不觉得空气有阻力，但跑步时就会明显感觉迎面有风阻挡。', '', '01010104060300'),
('01010104060402', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP02', '当人站在高速行驶的敞篷车上时，会感到迎面阻挡的风力很大。', '', '01010104060300'),
('01010104060403', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP03', '鸟在飞行中受到的阻力是很小的。', '', '01010104060300'),
('01010104060404', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP04', '本课题以气球为实验对象，并保持同一气球的体积不变，探究球形物体在空气中运动时空气阻力的大小跟速率的定量关系。', '', '01010104060300'),
('01010104060405', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP05', '本实验需要测量两个物理量：气球所受空气阻力的大小和气球运动的速率。', '', '01010104060300'),
('01010104060406', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP06', '如果气球下落时的空气阻力恰好等于重力，气球将做匀速运动。', '', '01010104060300'),
('01010104060407', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP07', '让气球从空中下落，随着下落速率的增大，气球所受的空气阻力也增大，当空气阻力增大到跟重力相等时，气球便做匀速运动。', '', '01010104060300'),
('01010104060408', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP08', '如果判断气球下落一段高度后已在做匀速运动，则此时空气阻力的大小就等于气球和重物所受的总重力（因重物的体积很小，作用在重物上的空气阻力可以忽略不计）。', '', '01010104060300'),
('01010104060409', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP09', '气球的速率可以用数码相机测量。', '', '01010104060300'),
('01010104060410', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP10', '尺上的具体读数就可以计算气球的速率。', '', '01010104060300'),
('01010104060411', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP03.OP11', '之后，请一名同学在刻度尺的上方释放气球，另一名同学用数码相机在刻度尺前拍摄气球下落的视频，并得到分帧照片。', '', '01010104060300'),
('01010104060400', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP04', '参考选题', '', '01010104060000'),
('01010104060501', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP04.OP01', '橡皮筋具有类似弹簧的力学性质，日常生活中常常用橡皮筋来代替弹簧。', '', '01010104060400'),
('01010104060502', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP04.OP02', '在弹性限度内，弹簧弹力与弹簧形变量是成正比的。', '', '01010104060400'),
('01010104060503', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP04.OP03', '实验或者引发了理论的建立，或者对新建立的理论进行检验。', '', '01010104060400'),
('01010104060504', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP04.OP04', '总之，物理学离不开实验，要学好物理就要做好实验。', '', '01010104060400'),
('01010104060500', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05', '实验推动了物理学的发展', '', '01010104060000'),
('01010104060601', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP01', '在电磁学研究中，法拉第在10年中坚持实验，为电磁感应定律的建立打下了基础。', '', '01010104060500'),
('01010104060602', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP02', '赫兹用实验证明了电磁波的存在。', '', '01010104060500'),
('01010104060603', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP03', '在粒子物理学中有一个物理量叫作宇称。', '', '01010104060500'),
('01010104060604', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP04', '物理学家们曾经认为粒子经历的各种过程中宇称都是守恒的。', '', '01010104060500'),
('01010104060605', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP05', '她除了用实验证实宇称不守恒外，还用实验证实了电磁相互作用与弱相互作用的密切关系，对后来电、弱统一理论的提出起了重要作用。', '', '01010104060500'),
('01010104060606', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP06', '今天，物理学中的许多学说还在等待实验的证实或否定。', '', '01010104060500'),
('01010104060607', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP07', '物体与物体之间有万有引力。', '', '01010104060500'),
('01010104060608', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP08', '20世纪70年代初，韦伯首创用铝棒做“天线”接收天体辐射的引力波。', '', '01010104060500'),
('01010104060609', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP09', '为了提高灵敏度，“天线”重达数吨，为了排除干扰，“天线”置于－270℃左右的超低温环境中。', '', '01010104060500'),
('01010104060610', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP05.OP10', '直到2016年，科学家用激光干涉仪代替铝棒作为“天线”，建成了引力波激光干涉仪探测装置，才首次探测到了来自于双黑洞合并的引力波的信号。', '', '01010104060500'),
('01010104060600', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06', '怎样做好物理实验', '', '01010104060000'),
('01010104060701', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP01', '下面是做实验大致要涉及的几个方面。', '', '01010104060600'),
('01010104060702', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP02', '做实验有利于透彻理解物理知识。', '', '01010104060600'),
('01010104060703', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP03', '不论做哪一类物理实验，都要搞清楚实验所根据的物理知识。', '', '01010104060600'),
('01010104060704', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP04', '同时，实验器材的工作原理也需要了解。', '', '01010104060600'),
('01010104060705', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP05', '首先，要保证人身安全。', '', '01010104060600'),
('01010104060706', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP06', '凡涉及用电的器材要小心处理。', '', '01010104060600'),
('01010104060707', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP07', '要注意保护眼睛，凡在高速转动、可能出现爆炸、可能有液体溅出的场合，最好戴上护目眼镜。', '', '01010104060600'),
('01010104060708', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP08', '其次，要保证器材的安全。', '', '01010104060600'),
('01010104060709', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP09', '各种仪器都必须按照要求使用。', '', '01010104060600'),
('01010104060710', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP10', '……最后，操作要谨慎。', '', '01010104060600'),
('01010104060711', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP11', '实验的每一阶段之后都要对其进行评估，也就是回想刚才的操作有没有什么问题，可能引起什么误差，等等。', '', '01010104060600'),
('01010104060712', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP12', '实验来不得半点粗心和虚假。', '', '01010104060600'),
('01010104060713', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP13', '实验数据从来不以人的意志而改变。', '', '01010104060600'),
('01010104060714', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP14', '要获得正确的实验结果，离不开严谨、求真的态度。', '', '01010104060600'),
('01010104060715', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP15', '不能随意更改记录去“凑数”。', '', '01010104060600'),
('01010104060716', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP16', '实验后要仔细分析数据，从中得出结论。', '', '01010104060600'),
('01010104060717', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP06.OP17', '把自己的实验成果告诉别人，听取别人的批评和质疑，同时，从别人的成果中受到启发。', '', '01010104060600'),
('01010104060700', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07', '误差和有效数字', '', '01010104060000'),
('01010104060801', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP01', '任何测量结果都不可能绝对准确。', '', '01010104060700'),
('01010104060802', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP02', '由仪器直接读出的数据叫作直接测量结果。', '', '01010104060700'),
('01010104060803', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP03', '直接测量结果的误差来源于两方面。', '', '01010104060700'),
('01010104060804', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP04', '又如，中学实验室中温度计的分度值为1℃，靠目测只能估计到分度值的101，肯定会有误差。', '', '01010104060700'),
('01010104060805', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP05', '通过改进仪器和谨慎操作，可以减小误差，但任何仪器的测量都不能完全消灭误差。', '', '01010104060700'),
('01010104060806', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP06', '人读仪表时，眼睛的位置可能稍向左偏或稍向右偏，电表指针由于轴上的摩擦，停下来的位置可能一次偏大、一次偏小。', '', '01010104060700'),
('01010104060807', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP07', '偶然误差的特点是，当多次重复测量时，偏大和偏小的机会比较接近，可以用取平均值的方法来减小偶然误差。', '', '01010104060700'),
('01010104060808', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP08', '系统误差的特点是，多次重复测量的结果总是大于（或小于）被测量的真实值，呈现单一倾向。', '', '01010104060700'),
('01010104060809', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP09', '限于刻度尺的精确度，测量有一定的误差。', '', '01010104060700'),
('01010104060810', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP10', '①相对误差是一个比值，没有单位，通常用百分数表示。', '', '01010104060700'),
('01010104060811', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP11', '小数中最前一位非零数字前面的0是表示小数点的位置的，不是有效数字。', '', '01010104060700'),
('01010104060812', 'PHYSICS', 'SENIOR', 'TERM_1', 'CH04.SEC06.KP07.OP12', '实验的结果往往是由若干直接测量值经过运算得到的，每一个直接测量值的误差都对最后结果产生影响，应当按一定的数学方法来确定运算结果的有效数字。', '', '01010104060700');
