INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010301000000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01', '静电场及其应用', '', NULL),
('01010301010000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01', '电荷', '', '01010301000000'),
('01010301010100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01', '电荷', '', '01010301010000'),
('01010301010201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP01', '公元前600年左右，古希腊学者泰勒斯就发现摩擦过的琥珀吸引轻小物体的现象。', '', '01010301010100'),
('01010301010202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP02', '此语意为摩擦过的琥珀能吸引像草芥一类的轻小物体。', '', '01010301010100'),
('01010301010203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP03', '自然界的电荷只有两种。', '', '01010301010100'),
('01010301010204', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP04', '正电荷的电荷量为正值，负电荷的电荷量为负值。', '', '01010301010100'),
('01010301010205', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP05', '每个原子中质子的正电荷数量与电子的负电荷数量一样多，所以整个原子对外界表现为电中性。', '', '01010301010100'),
('01010301010206', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP06', '原子内部的质子和中子被紧密地束缚在一起构成原子核，原子核的结构一般是很稳定的。', '', '01010301010100'),
('01010301010207', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP07', '通常离原子核较远的电子受到的束缚较弱，容易受到外界的作用而脱离原子。', '', '01010301010100'),
('01010301010208', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP08', '当两种物质组成的物体互相摩擦时，一些受束缚较弱的电子会转移到另一个物体上。', '', '01010301010100'),
('01010301010209', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP09', '不同物质的微观结构不同，由于原子或分子间的相互作用，原子中电子的多少和运动状况也不相同。', '', '01010301010100'),
('01010301010210', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP01.OP10', '绝缘体中几乎不存在能自由移动的电荷。', '', '01010301010100'),
('01010301010200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP02', '静电感应', '', '01010301010000'),
('01010301010301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP02.OP01', '当一个带电体靠近导体时，由于电荷间相互吸引或排斥，导体中的自由电荷便会趋向或远离带电体，使导体靠近带电体的一端带异种电荷，远离带电体的一端带同种电荷。', '', '01010301010200'),
('01010301010302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP02.OP02', '利用静电感应使金属导体带电的过程叫作感应起电。', '', '01010301010200'),
('01010301010300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP03', '电荷守恒定律', '', '01010301010000'),
('01010301010401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP03.OP01', '静电感应过程中导体中的自由电荷只是从导体的一部分转移到另一部分。', '', '01010301010300'),
('01010301010402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP03.OP02', '近代物理实验发现，在一定条件下，带电粒子可以产生或湮没。', '', '01010301010300'),
('01010301010403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP03.OP03', '不带电，所以电荷的代数和仍然不变。', '', '01010301010300'),
('01010301010400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP04', '元电荷', '', '01010301010000'),
('01010301010501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP04.OP01', '迄今为止，实验发现的最小电荷量就是电子所带的电荷量。', '', '01010301010400'),
('01010301010502', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP04.OP02', '在密立根实验之后，人们又做了许多测量。', '', '01010301010400'),
('01010301010503', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC01.KP04.OP03', '比荷也是一个重要的物理量。', '', '01010301010400'),
('01010301020000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02', '库仑定律', '', '01010301000000'),
('01010301020100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP01', '电荷之间的作用力', '', '01010301020000'),
('01010301020201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP01.OP01', '通过上面的实验可以看到，电荷之间的作用力随着电荷量的增大而增大，随着距离的增大而减小。', '', '01010301020100'),
('01010301020202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP01.OP02', '事实上，电荷之间的作用力与万有引力是否相似的问题早已引起当年一些研究者的注意，英国科学家卡文迪什和普里斯特利等人都确信“平方反比”规律适用于电荷间的力。', '', '01010301020100'),
('01010301020203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP01.OP03', '任何带电体都有形状和大小。', '', '01010301020100'),
('01010301020200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP02', '库仑的实验', '', '01010301020000'),
('01010301020301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP02.OP01', '库仑做实验用的装置叫作库仑扭秤。', '', '01010301020200'),
('01010301020300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03', '静电力计算', '', '01010301020000'),
('01010301020401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03.OP01', '差不多相当于一百万吨的物体所受的重力！', '', '01010301020300'),
('01010301020402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03.OP02', '通常，一把梳子和衣袖摩擦后所带的电荷量不到百万分之一库仑，但天空中发生闪电之前，巨大的云层中积累的电荷量可达几百库仑。', '', '01010301020300'),
('01010301020403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03.OP03', '试比较氢原子核与电子之间的静电力和万有引力。', '', '01010301020300'),
('01010301020404', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03.OP04', '库仑定律描述的是两个点电荷之间的作用力。', '', '01010301020300'),
('01010301020405', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03.OP05', '库仑定律是电磁学的基本定律之一。', '', '01010301020300'),
('01010301020406', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC02.KP03.OP06', '库仑定律给出的虽然是点电荷之间的静电力，但是任何一个带电体都可以看成是由许多点电荷组成的。', '', '01010301020300'),
('01010301030000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03', '电场   电场强度', '', '01010301000000'),
('01010301030100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP01', '电场', '', '01010301030000'),
('01010301030201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP01.OP01', '电场以及磁场已被证明是一种客观存在。', '', '01010301030100'),
('01010301030202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP01.OP02', '场像分子、原子等实物粒子一样具有能量，因而场也是物质存在的一种形式。', '', '01010301030100'),
('01010301030203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP01.OP03', '应该指出，只有在研究运动的电荷，特别是运动状态迅速变化的电荷时，上述场的物质性才突显出来。', '', '01010301030100'),
('01010301030200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP02', '电场强度', '', '01010301030000'),
('01010301030301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP02.OP01', '电场是在与电荷的相互作用中表现出自己的特性的。', '', '01010301030200'),
('01010301030302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP02.OP02', '物理学中规定，电场中某点的电场强度的方向与正电荷在该点所受的静电力的方向相同。', '', '01010301030200'),
('01010301030300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP03', '点电荷的电场    电场强度的叠加', '', '01010301030000'),
('01010301030400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP04', '电场线', '', '01010301030000'),
('01010301030501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP04.OP01', '除了用数学公式描述电场外，形象地了解和描述电场中各点电场强度的大小和方向也很重要。', '', '01010301030400'),
('01010301030500', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP05', '匀强电场', '', '01010301030000'),
('01010301030601', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC03.KP05.OP01', '由于方向相同，匀强电场中的电场线应该是平行的；又由于电场强度大小相等，电场线的疏密程度应该是相同的。', '', '01010301030500'),
('01010301040000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04', '静电的防止与利用', '', '01010301000000'),
('01010301040100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP01', '静电平衡', '', '01010301040000'),
('01010301040201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP01.OP01', '处于静电平衡状态的导体，其内部的电场强度处处为0。', '', '01010301040100'),
('01010301040200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02', '尖端放电', '', '01010301040000'),
('01010301040301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02.OP01', '静电平衡时，导体内部没有净剩电荷，电荷只分布在导体的外表面。', '', '01010301040200'),
('01010301040302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02.OP02', '并且在导体外表面，越尖锐的位置，电荷的密度（单位面积的电荷量）越大，周围的电场强度越大。', '', '01010301040200'),
('01010301040303', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02.OP03', '第九章静电场及其应用19在一定条件下，导体尖端周围的强电场足以使空气中残留的带电粒子发生剧烈运动，并与空气分子碰撞从而使空气分子中的正负电荷分离。', '', '01010301040200'),
('01010301040304', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02.OP04', '中性的分子电离后变成带负电的自由电子和失去电子而带正电的离子。', '', '01010301040200'),
('01010301040305', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02.OP05', '当带电的雷雨云接近建筑物时，由于静电感应，金属棒出现与云层相反的电荷。', '', '01010301040200'),
('01010301040306', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP02.OP06', '尖端放电会导致高压设备上电能的损失，所以高压设备中导体的表面应该尽量光滑。', '', '01010301040200'),
('01010301040300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP03', '静电屏蔽', '', '01010301040000'),
('01010301040401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP03.OP01', '处于静电平衡状态的导体内部没有电荷，电荷只分布在导体的外表面。', '', '01010301040300'),
('01010301040402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP03.OP02', '实现静电屏蔽不一定要用密封的金属容器，金属网也能起到屏蔽作用。', '', '01010301040300'),
('01010301040403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP03.OP03', '静电的危害可能随时发生。', '', '01010301040300'),
('01010301040400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP04', '静电吸附', '', '01010301040000'),
('01010301040501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP04.OP01', '静电虽然会有危害，但也可以利用。', '', '01010301040400'),
('01010301040502', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP04.OP02', '在电场中，带电粒子受到静电力的作用，向着电极运动，最后会被吸附在电极上。', '', '01010301040400'),
('01010301040503', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP04.OP03', '静电除尘用于粉尘较多的各种场所，除去有害的微粒，或者回收物资，如回收水泥粉尘。', '', '01010301040400'),
('01010301040504', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH01.SEC04.KP04.OP04', '复印机也应用了静电吸附。', '', '01010301040400');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010302000000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02', '静电场中的能量', '', NULL),
('01010302010000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01', '电势能和电势', '', '01010302000000'),
('01010302010100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP01', '静电力做功的特点', '', '01010302010000'),
('01010302010201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP01.OP01', '节前“问题”中电荷所减少的能量，必定跟静电力做的功相关。', '', '01010302010100'),
('01010302010202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP01.OP02', '静电力做功具有跟重力做功一样的特点，即静电力做功的多少与路径无关，只与电荷在电场中的始、末位置有关。', '', '01010302010100'),
('01010302010200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP02', '电势能', '', '01010302010000'),
('01010302010301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP02.OP01', '应该注意，静电力做的功只能决定电势能的变化量，而不能决定电荷在电场中某点电势能的数值。', '', '01010302010200'),
('01010302010302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP02.OP02', '通常把电荷在离场源电荷无限远处的电势能规定为0，或把电荷在大地表面的电势能规定为0。', '', '01010302010200'),
('01010302010303', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP02.OP03', '重力势能属于地球和物体组成的系统，是地球和物体相互作用产生的。', '', '01010302010200'),
('01010302010300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP03', '电势', '', '01010302010000'),
('01010302010401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP03.OP01', '在规定了零电势点之后，电场中各点的电势可以是正值，也可以是负值。', '', '01010302010300'),
('01010302010402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC01.KP03.OP02', '电势只有大小，没有方向，是个标量。', '', '01010302010300'),
('01010302020000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC02', '电势差', '', '01010302000000'),
('01010302020100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC02.KP01', '电势差', '', '01010302020000'),
('01010302020201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC02.KP01.OP01', '选择不同的位置作为零电势点，电场中某点电势的数值也会改变，但电场中某两点之间电势的差值却保持不变。', '', '01010302020100'),
('01010302020200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC02.KP02', '等势面', '', '01010302020000'),
('01010302020301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC02.KP02.OP01', '与电场线的功能相似，等势面也是用来形象地描绘电场的。', '', '01010302020200'),
('01010302020302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC02.KP02.OP02', '在同一个等势面上，任何两点的电势都相等。', '', '01010302020200'),
('01010302030000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC03', '电势差与电场强度的关系', '', '01010302000000'),
('01010302040000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04', '电容器的电容', '', '01010302000000'),
('01010302040100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP01', '电容器', '', '01010302040000'),
('01010302040201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP01.OP01', '在两个相距很近的平行金属板中间夹上一层绝缘物质——电介质（空气也是一种电介质），就组成一个最简单的电容器，叫作平行板电容器。', '', '01010302040100'),
('01010302040202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP01.OP02', '实际上，任何两个彼此绝缘又相距很近的导体，都可以看成一个电容器。', '', '01010302040100'),
('01010302040200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP02', '电容', '', '01010302040000'),
('01010302040301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP02.OP01', '以上实验表明，电容器的电荷量变为原来的一半时，其两极板间的电势差也变为原来的一半。', '', '01010302040200'),
('01010302040300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03', '常用电容器', '', '01010302040000'),
('01010302040401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP01', '常用的电容器，从构造上看，可以分为固定电容器和可变电容器两类。', '', '01010302040300'),
('01010302040402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP02', '固定电容器的电容是固定不变的。', '', '01010302040300'),
('01010302040403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP03', '常用的有聚苯乙烯电容器和电解电容器。', '', '01010302040300'),
('01010302040404', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP04', '改变铝箔的面积和薄膜的厚度，可以制成不同电容的聚苯乙烯电容器。', '', '01010302040300'),
('01010302040405', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP05', '以陶瓷为电介质的固定电容器也很多。', '', '01010302040300'),
('01010302040406', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP06', '由于氧化膜很薄，所以电容较大。', '', '01010302040300'),
('01010302040407', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP07', '固定的一组铝片叫作定片，可以转动的一组铝片叫作动片。', '', '01010302040300'),
('01010302040408', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP08', '转动动片，使两组铝片的正对面积发生变化，电容就随着改变。', '', '01010302040300'),
('01010302040409', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC04.KP03.OP09', '超级电容器的充电时间短，储存电能多，放电功率大，使用寿命长。', '', '01010302040300'),
('01010302050000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05', '带电粒子在电场中的运动', '', '01010302000000'),
('01010302050100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01', '带电粒子在电场中的加速', '', '01010302050000'),
('01010302050201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01.OP01', '在现代科学实验和技术设备中，常常利用电场来改变或控制带电粒子的运动。', '', '01010302050100'),
('01010302050202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01.OP02', '利用电场使带电粒子加速，就是其中一种简单的情况。', '', '01010302050100'),
('01010302050203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01.OP03', '分析带电粒子加速的问题，常常有两种思路：一种是利用牛顿第二定律结合匀变速直线运动公式来分析；另一种是利用静电力做功结合动能定理来分析。', '', '01010302050100'),
('01010302050204', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01.OP04', '当解决的问题属于匀强电场且涉及运动时间等描述运动过程的物理量时，适合运用前一种思路分析；当问题只涉及位移、速率等动能定理公式中的物理量或非匀强电场情景时，适合运用后一种思路分析。', '', '01010302050100'),
('01010302050205', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01.OP05', '第十章静电场中的能量45对偶数圆筒的电势差为正值，此时位于和偶数圆筒相连的金属圆板（序号为0）中央的一个电子，在圆板和圆筒1之间的电场中由静止开始加速，沿中心轴线冲进圆筒1。', '', '01010302050100'),
('01010302050206', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP01.OP06', '为使电子运动到圆筒与圆筒之间各个间隙中都能恰好使静电力的方向跟运动方向相同而不断加速，圆筒长度的设计必须遵照一定的规律。', '', '01010302050100'),
('01010302050200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP02', '带电粒子在电场中的偏转', '', '01010302050000'),
('01010302050301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH02.SEC05.KP02.OP01', '在匀强电场中，带电粒子的运动轨迹是一条抛物线，类似平抛运动的轨迹。', '', '01010302050200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010303000000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03', '电路及其应用', '', NULL),
('01010303010000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC01', '电源和电流', '', '01010303000000'),
('01010303010100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC01.KP01', '电源', '', '01010303010000'),
('01010303010201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC01.KP01.OP01', '手电筒中的小灯泡能持续发光，是因为电路中有电源，使得电路中的电流能够持续存在。', '', '01010303010100'),
('01010303010200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC01.KP02', '恒定电流', '', '01010303010000'),
('01010303010301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC01.KP02.OP01', '在恒定电场的作用下，导体中的自由电荷做定向运动，在运动过程中与导体内不动的粒子不断碰撞，碰撞阻碍了自由电荷的定向运动，结果是大量自由电荷定向运动的平均速率不随时间变化。', '', '01010303010200'),
('01010303010302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC01.KP02.OP02', '单位时间内通过导体横截面的电荷量越多，电流就越大。', '', '01010303010200'),
('01010303020000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02', '导体的电阻', '', '01010303000000'),
('01010303020100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP01', '电阻', '', '01010303020000'),
('01010303020201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP01.OP01', '选取一个导体，研究导体两端的电压随导体中的电流的变化情况。', '', '01010303020100'),
('01010303020200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP02', '影响导体电阻的因素', '', '01010303020000'),
('01010303020300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03', '导体的电阻率', '', '01010303020000'),
('01010303020401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP01', '从表中可以看出，纯金属的电阻率较小，合金的电阻率较大。', '', '01010303020300'),
('01010303020402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP02', '连接电路的导线一般用电阻率小的铜来制作，必要时可在导线表面镀银。', '', '01010303020300'),
('01010303020403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP03', '由于用电器的电阻通常远大于导线的电阻，一般情况下，可以认为导线电阻为0。', '', '01010303020300'),
('01010303020404', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP04', '有些合金，如锰铜合金和镍铜合金，电阻率几乎不受温度变化的影响，常用来制作标准电阻。', '', '01010303020300'),
('01010303020405', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP05', '从上述关系式可以看出，在长度、横截面积一定的条件下，ρ越大，导体的电阻越大。', '', '01010303020300'),
('01010303020406', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP06', '金属的电阻率随温度的升高而增大。', '', '01010303020300'),
('01010303020407', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP07', '精密的电阻温度计是用铂做的。', '', '01010303020300'),
('01010303020408', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP08', '已知铂丝的电阻随温度的变化情况，测出铂丝的电阻就可以知道温度。', '', '01010303020300'),
('01010303020409', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP09', '当温度降低时，导体的电阻率将会减小。', '', '01010303020300'),
('01010303020410', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC02.KP03.OP10', '若用超导材料形成回路，一旦回路中有了电流，电流就将无损耗地持续下去。', '', '01010303020300'),
('01010303030000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03', '实验：导体电阻率的测量', '', '01010303000000'),
('01010303030100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP01', '实验1   长度的测量及测量工具的选用', '', '01010303030000'),
('01010303030201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP01.OP01', '长度是物理学中基本的物理量，长度的测量是最基本的测量。', '', '01010303030100'),
('01010303030202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP01.OP02', '根据测量要求的不同，可以选用不同的测量工具。', '', '01010303030100'),
('01010303030200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP02', '游标卡尺', '', '01010303030000'),
('01010303030301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP02.OP01', '当外（内）测量爪一侧的两个刃接触时，游标尺上的零刻度线与主尺上的零刻度线正好对齐。', '', '01010303030200'),
('01010303030302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP02.OP02', '合起来，就是被测物体的长度。', '', '01010303030200'),
('01010303030303', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP02.OP03', '请练习使用游标卡尺测量以下物体的尺寸。', '', '01010303030200'),
('01010303030304', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP02.OP04', '（1）用游标卡尺的外测量爪测量小钢球的直径、小圆管的外径、教科书的厚度等。', '', '01010303030200'),
('01010303030305', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP02.OP05', '（2）用游标卡尺的内测量爪测量小圆管的内径、槽的宽度等。', '', '01010303030200'),
('01010303030300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP03', '螺旋测微器', '', '01010303030000'),
('01010303030400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP04', '实验2 金属丝电阻率的测量', '', '01010303030000'),
('01010303030500', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP05', '实验思路', '', '01010303030000'),
('01010303030600', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP06', '物理量的测量', '', '01010303030000'),
('01010303030701', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP06.OP01', '电阻丝长度的测量工具应选用刻度尺。', '', '01010303030600'),
('01010303030702', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP06.OP02', '使用螺旋测微器测量以下物体的尺寸。', '', '01010303030600'),
('01010303030703', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP06.OP03', '（2）测量头发丝的直径。', '', '01010303030600'),
('01010303030704', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP06.OP04', '因为电阻丝比较细，所以直接用刻度尺测量就会产生比较大的误差。', '', '01010303030600'),
('01010303030705', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP06.OP05', '下面提供了几种测量直径的方案供大家选用。', '', '01010303030600'),
('01010303030700', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP07', '数据分析', '', '01010303030000'),
('01010303030801', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC03.KP07.OP01', '将三个测量值代入上述公式，计算得出导体的电阻率。', '', '01010303030700'),
('01010303040000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04', '串联电路和并联电路', '', '01010303000000'),
('01010303040100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP01', '串、并联电路中的电流', '', '01010303040000'),
('01010303040200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP02', '串、并联电路中的电压', '', '01010303040000'),
('01010303040301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP02.OP01', '同样，几个电阻右边的电势也相同。', '', '01010303040200'),
('01010303040300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP03', '串、并联电路中的电阻', '', '01010303040000'),
('01010303040400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP04', '电压表和电流表的电路结构', '', '01010303040000'),
('01010303040501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP04.OP01', '常用的电压表和电流表都是由小量程的电流表（表头）改装而成的。', '', '01010303040400'),
('01010303040502', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP04.OP02', '表头的工作原理涉及磁场对通电导线的作用。', '', '01010303040400'),
('01010303040503', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP04.OP03', '从电路的角度看，表头就是一个电阻，同样遵从欧姆定律。', '', '01010303040400'),
('01010303040504', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC04.KP04.OP04', '电流表可以看作一个电流可读的“小电阻”。', '', '01010303040400'),
('01010303050000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05', '实验：练习使用多用电表', '', '01010303000000'),
('01010303050100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP01', '认识多用电表', '', '01010303050000'),
('01010303050201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP01.OP01', '表的上半部分为表盘，下半部分是选择开关，开关周围标有测量功能的区域及量程。', '', '01010303050100'),
('01010303050200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02', '使用多用电表', '', '01010303050000'),
('01010303050301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP01', '使用前应该调整指针定位螺丝，使指针指到零刻度。', '', '01010303050200'),
('01010303050302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP02', '使用时，应先将选择开关旋转到与被测物理量对应的位置上并选择合适的量程。', '', '01010303050200'),
('01010303050303', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP03', '将多用电表的选择开关旋至直流电压挡，注意选择大于小灯泡两端电压估计值的量程。', '', '01010303050200'),
('01010303050304', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP04', '根据表盘上相应量程的直流电压刻度进行读数，记录小灯泡两端的电压值。', '', '01010303050200'),
('01010303050305', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP05', '将多用电表的选择开关旋至直流电流挡，注意选择大于通过小灯泡电流估计值的量程。', '', '01010303050200'),
('01010303050306', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP06', '闭合开关，根据表盘上相应量程的直流电流刻度进行读数，记录通过小灯泡的电流值。', '', '01010303050200'),
('01010303050307', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP07', '使用多用电表的欧姆挡测电阻时，如果指针偏转过大、过小，读数误差都会比较大。', '', '01010303050200'),
('01010303050308', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP08', '如果不能估计未知电阻的大小，可以先用中等倍率的某个欧姆挡试测，然后根据读数的大小选择合适的挡位再次测量，电阻值为读数乘倍率。', '', '01010303050200'),
('01010303050309', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH03.SEC05.KP02.OP09', '数字式多用电表的测量值以数字形式直接显示，使用方便。', '', '01010303050200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010304000000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04', '电能  能量守恒定律', '', NULL),
('01010304010000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01', '电路中的能量转化', '', '01010304000000'),
('01010304010100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP01', '电功和电功率', '', '01010304010000'),
('01010304010201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP01.OP01', '电流做功的实质是，导体中的恒定电场对自由电荷的静电力在做功。', '', '01010304010100'),
('01010304010200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP02', '焦耳定律', '', '01010304010000'),
('01010304010301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP02.OP01', '电流做功，究竟电能会转化为哪种形式的能量，要看电路中有哪种类型的用电器。', '', '01010304010200'),
('01010304010302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP02.OP02', '是指电流通过导体发热的功率。', '', '01010304010200'),
('01010304010300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP03', '电路中的能量转化', '', '01010304010000'),
('01010304010401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP03.OP01', '从能量转化与守恒的角度看，电动机从电源获得能量，一部分转化为机械能，还有一部分转化为内能。', '', '01010304010300'),
('01010304010402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP03.OP02', '电动机的转子与轴承均有摩擦，另外还有空气阻力。', '', '01010304010300'),
('01010304010403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC01.KP03.OP03', '同样，对于正在充电的电池，电能除了转化为化学能之外，还有一部分转化为内能。', '', '01010304010300'),
('01010304020000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02', '闭合电路的欧姆定律', '', '01010304000000'),
('01010304020100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP01', '电动势', '', '01010304020000'),
('01010304020201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP01.OP01', '在金属导体中，能够自由移动的电荷是自由电子。', '', '01010304020100'),
('01010304020202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP01.OP02', '在电源内部，存在着由正极指向负极的电场。', '', '01010304020100'),
('01010304020203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP01.OP03', '在电源内部，电源移动电荷，增加电荷的电势能。', '', '01010304020100'),
('01010304020204', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP01.OP04', '电动势由电源中非静电力的特性决定，跟外电路无关。', '', '01010304020100'),
('01010304020205', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP01.OP05', '对于常用的干电池来说，电动势跟电池的体积无关。', '', '01010304020100'),
('01010304020200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP02', '闭合电路欧姆定律及其能量分析', '', '01010304020000'),
('01010304020300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP03', '路端电压与负载的关系', '', '01010304020000'),
('01010304020401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP03.OP01', '负载变化时，电路中的电流就会变化，路端电压也随之变化。', '', '01010304020300'),
('01010304020402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC02.KP03.OP02', '电流过大，会导致温度过高，烧坏电源，甚至引起火灾。', '', '01010304020300'),
('01010304030000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03', '实验：电池电动势和内阻的测量', '', '01010304000000'),
('01010304030100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP01', '实验思路', '', '01010304030000'),
('01010304030201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP01.OP01', '电动势和内阻都是电源的重要参数。', '', '01010304030100'),
('01010304030202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP01.OP02', '根据闭合电路欧姆定律，有多种方法可以测定电池的电动势和内阻。', '', '01010304030100'),
('01010304030200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP02', '物理量的测量', '', '01010304030000'),
('01010304030301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP02.OP01', '只有多次测量，并对数据进行处理，才能减小误差。', '', '01010304030200'),
('01010304030300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP03', '数据分析', '', '01010304030000'),
('01010304030401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC03.KP03.OP01', '采用另外一种方法，也能减小误差，而且更简便、直观。', '', '01010304030300'),
('01010304040000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04', '能源与可持续发展', '', '01010304000000'),
('01010304040100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP01', '能量转移或转化的方向性', '', '01010304040000'),
('01010304040201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP01.OP01', '科学家研究发现，一切与热现象有关的宏观自然过程都是不可逆的。', '', '01010304040100'),
('01010304040202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP01.OP02', '虽然能量是守恒的，但是，在自然界中，能量的转化过程有些是可以自然发生的，有些则不能。', '', '01010304040100'),
('01010304040203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP01.OP03', '旦把热量释放出去，就不会再次自动聚集起来供人类重新利用。', '', '01010304040100'),
('01010304040204', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP01.OP04', '能量的耗散表明，在能源的利用过程中，能量在数量上虽未减少，但在可利用的品质上降低了，从便于利用的能源变成不便于利用的能源。', '', '01010304040100'),
('01010304040200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02', '能源的分类与应用', '', '01010304040000'),
('01010304040301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP01', '太阳能的利用对环境的影响很小。', '', '01010304040200'),
('01010304040302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP02', '利用太阳能最有前途的领域是，通过太阳能电池将太阳能直接转化成电能。', '', '01010304040200'),
('01010304040303', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP03', '水能是可再生能源，水电站是利用水能的重要形式。', '', '01010304040200'),
('01010304040304', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP04', '水电对环境的影响比火电小，发电成本低。', '', '01010304040200'),
('01010304040305', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP05', '为了增加风力发电的功率，通常把很多风车建在一起，成为“风车田”。', '', '01010304040200'),
('01010304040306', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP06', '一些质量较大的原子核在裂变成较轻的原子核时，会释放出惊人的能量。', '', '01010304040200'),
('01010304040307', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP07', '同样质量的化学燃料与核燃料相比，如果都进行了完全反应，核燃料释放出的能量要远远大于化学燃料释放出的能量。', '', '01010304040200'),
('01010304040308', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP08', '可控制的裂变反应所释放出的能量，可以很好地被人们所利用。', '', '01010304040200'),
('01010304040309', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP09', '核电站就是利用可控核裂变来发电的。', '', '01010304040200'),
('01010304040310', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP10', '人们预计21世纪中叶，核聚变会有重大的技术突破。', '', '01010304040200'),
('01010304040311', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP02.OP11', '由于核电站一旦发生核泄漏事故，就可能发生严重的危害，同时核废料仍然具有放射性，因此科学家制定了严格的安全措施和安全标准。', '', '01010304040200'),
('01010304040300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03', '能源与社会发展', '', '01010304040000'),
('01010304040401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP01', '自工业革命以来，煤和石油成为人类的主要能源。', '', '01010304040300'),
('01010304040402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP02', '18世纪末发明和逐步完善的蒸汽机，以对化石能源的大规模利用为特征，开始了人类文明的新纪元。', '', '01010304040300'),
('01010304040403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP03', '内燃机的发展则强有力地推动了19世纪末、20世纪初开始的机械化与电气化进程。', '', '01010304040300'),
('01010304040404', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP04', '到了20世纪50年代，世界石油和天然气的消耗量超过了煤炭。', '', '01010304040300'),
('01010304040405', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP05', '能源科技的每一次突破，都带来了生产力的巨大飞跃和社会的进步。', '', '01010304040300'),
('01010304040406', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP06', '能源是人类社会活动的物质基础。', '', '01010304040300'),
('01010304040407', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP07', '内燃机工作时的高温使空气和燃料中的多种物质发生化学反应，产生氮的氧化物和碳氢化合物。', '', '01010304040300'),
('01010304040408', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP08', '燃烧时产生的浮尘也是主要的污染物。', '', '01010304040300'),
('01010304040409', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP09', '随着人口迅速增长、经济快速发展以及工业化程度的提高，能源短缺和过度使用化石能源带来的环境恶化已经成为关系到人类社会能否持续发展的大问题。', '', '01010304040300'),
('01010304040410', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP10', '人类的生存与发展需要能源，能源的开发与使用又会对环境造成影响。', '', '01010304040300'),
('01010304040411', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP11', '可持续发展的核心是追求发展与资源、环境的平衡：既满足当代人的需求，又不损害子孙后代的需求。', '', '01010304040300'),
('01010304040412', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH04.SEC04.KP03.OP12', '立新的能源安全观，并转变能源的供需模式。', '', '01010304040300');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010305000000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05', '电磁感应与电磁波初步', '', NULL),
('01010305010000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01', '磁场 磁感线', '', '01010305000000'),
('01010305010100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP01', '电和磁的联系', '', '01010305010000'),
('01010305010201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP01.OP01', '自然界中的磁体总存在着两个磁极，自然界中同样存在着两种电荷。', '', '01010305010100'),
('01010305010202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP01.OP02', '不仅如此，磁极之间的相互作用，与电荷之间的相互作用具有相似的特征：同名磁极或同种电荷相互排斥，异名磁极或异种电荷相互吸引。', '', '01010305010100'),
('01010305010203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP01.OP03', '丹麦物理学家奥斯特相信，电和磁之间应该存在某种联系，并开始了不懈的探索。', '', '01010305010100'),
('01010305010200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP02', '磁场', '', '01010305010000'),
('01010305010301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP02.OP01', '”自奥斯特实验之后，安培等人又做了很多实验研究。', '', '01010305010200'),
('01010305010302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP02.OP02', '正像在电场中可以用电场线来描述电场一样，利用磁感线可以形象地描述磁场。', '', '01010305010200'),
('01010305010300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP03', '安培定则', '', '01010305010000'),
('01010305010401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP03.OP01', '实验表明，改变电流的方向，各点的磁场方向都变成相反的方向。', '', '01010305010300'),
('01010305010400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP04', '磁感线', '', '01010305010000'),
('01010305010501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP04.OP01', '通电螺线管可以看作许多匝环形电流串联而成。', '', '01010305010400'),
('01010305010502', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP04.OP02', '与天然磁体的磁场相比，电流磁场的强弱容易控制，因而在实际中有很多重要的应用。', '', '01010305010400'),
('01010305010503', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP04.OP03', '电磁起重机、电动机、发电机，以及在自动控制中普遍应用的电磁继电器等，都离不开电流的磁场。', '', '01010305010400'),
('01010305010504', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP04.OP04', '近些年来，随着超导、新材料等技术的运用，人们可以较方便地获得大电流和强磁场。', '', '01010305010400'),
('01010305010505', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC01.KP04.OP05', '利用磁场与电流之间的相互作用，人们发明了磁浮列车、电磁弹射装置等。', '', '01010305010400'),
('01010305020000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02', '磁感应强度 磁通量', '', '01010305000000'),
('01010305020100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02.KP01', '磁感应强度', '', '01010305020000'),
('01010305020201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02.KP01.OP01', '若以通电导线作为磁场的检验物体，则既可以知道导线中电流的大小，又能测量导线的长度，从而可以进行定量的研究。', '', '01010305020100'),
('01010305020200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02.KP02', '匀强磁场', '', '01010305020000'),
('01010305020301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02.KP02.OP01', '匀强磁场的磁感线用一些间隔相等的平行直线表示。', '', '01010305020200'),
('01010305020300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02.KP03', '磁通量', '', '01010305020000'),
('01010305020401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC02.KP03.OP01', '磁感线的疏密程度表示了磁场的强弱。', '', '01010305020300'),
('01010305030000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03', '电磁感应现象及应用', '', '01010305000000'),
('01010305030100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01', '划时代的发现', '', '01010305030000'),
('01010305030201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01.OP01', '有关电与磁关系的崭新研究领域洞开在人们面前，激发了科学家们的探索热情。', '', '01010305030100'),
('01010305030202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01.OP02', '人们早就认识了磁化现象，知道磁体能使附近的铁棒产生磁性，带电体能在导体上感应出电荷。', '', '01010305030100'),
('01010305030203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01.OP03', '最初，法拉第认为，很强的磁体或很强的电流可能会在邻近的闭合导线中感应出电流。', '', '01010305030100'),
('01010305030204', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01.OP04', '当给一个线圈通电或断电的瞬间，在另一个线圈上出现了电流。', '', '01010305030100'),
('01010305030205', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01.OP05', '法拉第从中领悟到，“磁生电”是一种在变化、运动的过程中才能出现的效应。', '', '01010305030100'),
('01010305030206', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP01.OP06', '电磁感应的发现使人们对电与磁内在联系的认识更加深入，宣告了电磁学作为一门统一学科的诞生。', '', '01010305030100'),
('01010305030200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP02', '产生感应电流的条件', '', '01010305030000'),
('01010305030300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP03', '电磁感应现象的应用', '', '01010305030000'),
('01010305030401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP03.OP01', '1831年圣诞节前夕，一次科学报告会上，法拉第当众表演了一个实验。', '', '01010305030300'),
('01010305030402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP03.OP02', '一个铜盘的轴和铜盘的边缘分别连在“电流表”的两端。', '', '01010305030300'),
('01010305030403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP03.OP03', '法拉第摇动手柄使铜盘在磁极之间旋转，“电流表”的指针随之摆动。', '', '01010305030300'),
('01010305030404', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC03.KP03.OP04', '生产、生活中广泛使用的变压器、电磁炉等也是根据电磁感应制造的。', '', '01010305030300'),
('01010305040000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04', '电磁波的发现及应用', '', '01010305000000'),
('01010305040100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP01', '电磁场', '', '01010305040000'),
('01010305040201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP01.OP01', '在变化的磁场中放入一个闭合电路，电路里会产生感应电流。', '', '01010305040100'),
('01010305040202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP01.OP02', '麦克斯韦确信自然规律的统一性与和谐性，相信电场与磁场的对称之美。', '', '01010305040100'),
('01010305040200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02', '电磁波', '', '01010305040000'),
('01010305040301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02.OP01', '空气、水是声波和水波传播的介质。', '', '01010305040200'),
('01010305040302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02.OP02', '水波和声波的传播都离不开介质。', '', '01010305040200'),
('01010305040303', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02.OP03', '麦克斯韦推算出一个出人意料的答案：电磁波的速度等于光速！', '', '01010305040200'),
('01010305040304', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02.OP04', '遗憾的是，麦克斯韦英年早逝，没有看到科学实验对电磁场理论的证实。', '', '01010305040200'),
('01010305040305', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02.OP05', '把天才的预言变成世人公认的真理的人，是德国科学家赫兹。', '', '01010305040200'),
('01010305040306', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP02.OP06', '1886年，赫兹通过实验捕捉到了电磁波。', '', '01010305040200'),
('01010305040300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP03', '电磁波谱', '', '01010305040000'),
('01010305040401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP03.OP01', '波速、波长、频率三者之间的关系是波速＝波长×频率对于电磁波，有同样的关系。', '', '01010305040300'),
('01010305040402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP03.OP02', '不同电磁波由于具有不同的波长（或频率），因此具有不同的特性。', '', '01010305040300'),
('01010305040400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP04', '电磁波的能量', '', '01010305040000'),
('01010305040501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP04.OP01', '法拉第用“力线”形象地描述了电磁场，麦克斯韦用数学语言表述了电磁场。', '', '01010305040400'),
('01010305040502', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP04.OP02', '食物中的水分子在微波的作用下热运动加剧，内能增加，温度升高。', '', '01010305040400'),
('01010305040503', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP04.OP03', '因为电台发射的电磁波在收音机的天线里感应出了电流，有电流就有能量。', '', '01010305040400'),
('01010305040500', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP05', '电磁波通信', '', '01010305040000'),
('01010305040601', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP05.OP01', '随着计算机网络的发展和智能手机的出现，负责通话的电信网、广播电视网和互联网相互渗透、相互兼容，逐步整合成为统一的信息通信网络。', '', '01010305040500'),
('01010305040602', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP05.OP02', '移动电话的主要业务也已经发展成为以互联网为基础的信息服务。', '', '01010305040500'),
('01010305040603', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP05.OP03', '人们可以通过接入互联网的手机看电影、聊天、购物、查阅资料、视频通话。', '', '01010305040500'),
('01010305040604', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC04.KP05.OP04', '构建卫星宽带通信网络，可以使人们无论身处万米高空的飞机，还是茫茫大海的轮船，甚至没有人烟的荒漠、深山、海岛，都能高速浏览网页，与家人视频通话等。', '', '01010305040500'),
('01010305050000', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05', '能量量子化', '', '01010305000000'),
('01010305050100', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01', '热辐射', '', '01010305050000'),
('01010305050201', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01.OP01', '物体在室温时，热辐射的主要成分是波长较长的电磁波，不能引起人的视觉。', '', '01010305050100'),
('01010305050202', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01.OP02', '当温度升高时，热辐射中波长较短的成分越来越强。', '', '01010305050100'),
('01010305050203', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01.OP03', '大量实验结果表明，辐射强度按波长的分布情况随物体的温度而有所不同。', '', '01010305050100'),
('01010305050204', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01.OP04', '除了热辐射外，物体表面还会吸收和反射外界射来的电磁波。', '', '01010305050100'),
('01010305050205', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01.OP05', '黑体虽然不反射电磁波，但是却可以向外辐射电磁波。', '', '01010305050100'),
('01010305050206', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP01.OP06', '物体中存在着不停运动的带电微粒，带电微粒的振动会产生变化的电磁场，从而产生电磁辐射。', '', '01010305050100'),
('01010305050200', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP02', '能量子', '', '01010305050000'),
('01010305050301', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP02.OP01', '为了得出同实验相符的黑体辐射公式，德国物理学家普朗克进行了多种尝试，进行了激烈的思想斗争。', '', '01010305050200'),
('01010305050302', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP02.OP02', '而普朗克的假设则认为微观粒子的能量是量子化的，或者说微观粒子的能量是不连续（分立）的。', '', '01010305050200'),
('01010305050300', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP03', '能级', '', '01010305050000'),
('01010305050401', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP03.OP01', '微观世界中能量取分立值的观念也适用于原子系统，原子的能量是量子化的。', '', '01010305050300'),
('01010305050402', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP03.OP02', '气体放电管中的原子受到高速运动的电子的撞击，有可能跃迁到较高的能量状态。', '', '01010305050300'),
('01010305050403', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP03.OP03', '原子从高能态向低能态跃迁时放出的光子的能量，等于前后两个能级之差。', '', '01010305050300'),
('01010305050404', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP03.OP04', '核能的利用，计算机和智能手机的制造，激光技术等的应用都离不开量子力学。', '', '01010305050300'),
('01010305050400', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04', '研究样例', '', '01010305050000'),
('01010305050501', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP01', '随着智能手机耗电的增加，充电宝成了为手机及时充电的一种重要选择。', '', '01010305050400'),
('01010305050502', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP02', '在人们心目中，充电宝就是跟蓄电池和干电池一样的可移动直流电源。', '', '01010305050400'),
('01010305050503', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP03', '如果是一条直线，表明其电路特性跟普通电池一样，具有一定电动势和内阻。', '', '01010305050400'),
('01010305050504', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP04', '剥开充电宝连接线的外绝缘层，里面有四根导线，红导线为充电宝的正极，黑导线为负极，其余两根导线空置不用。', '', '01010305050400'),
('01010305050505', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP05', '将滑动变阻器电阻调至最大。', '', '01010305050400'),
('01010305050506', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP06', '不管是充电宝还是锂电池，其内阻跟储电量没有明显的相关。', '', '01010305050400'),
('01010305050507', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP07', '4.本实验结论具有一定的现实意义。', '', '01010305050400'),
('01010305050508', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP08', '手机锂电池随着储电量的减少，电动势逐步降低，其电源性能的变化主要是电动势的变化。', '', '01010305050400'),
('01010305050509', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP09', '充电宝的内阻很小，包括电路中被测量的导线在内，其阻值总计也只有0.2Ω左右，因此，充电宝的内电路对输出电压的影响是非常小的。', '', '01010305050400'),
('01010305050510', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP04.OP10', '研究表明，如果使用所测试的充电宝供电，不必考虑充电宝的电量百分比以及电流大小对输出电压的影响。', '', '01010305050400'),
('01010305050500', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP05', '参考选题', '', '01010305050000'),
('01010305050601', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP05.OP01', '研究内容：智能手机功能强大，带来方便的同时，耗电太快已经成为大众普遍关注的问题。', '', '01010305050500'),
('01010305050602', 'PHYSICS', 'SENIOR', 'TERM_3', 'CH05.SEC05.KP05.OP02', '屏幕亮度、音量等功能的设置，上网浏览、网络聊天、主叫电话、视频播放、录音重放、网络游戏等功能的应用，都是影响手机耗电的因素。', '', '01010305050500');
