INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010201000000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01', '安培力与洛伦兹力', '', NULL),
('01010201010000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01', '磁场对通电导线的作用力', '', '01010201000000'),
('01010201010100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP01', '安培力的方向', '', '01010201010000'),
('01010201010201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP01.OP01', '1.1-1所示，把一段导体棒悬挂在蹄形磁体的两极间，通以电流。', '', '01010201010100'),
('01010201010202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP01.OP02', '研究安培力的方向与哪些因素有关。', '', '01010201010100'),
('01010201010203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP01.OP03', '实验现象表明，通电导体棒受力方向与磁场方向、电流方向有关。', '', '01010201010100'),
('01010201010204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP01.OP04', '研究库仑力时，受力的物体是点电荷，点电荷受力的方向与电场的方向相同或相反；但在研究安培力时，受力的物体是通电导线，通电导线受力的方向与磁场的方向、电流的方向不但不在一条直线上，而且不在一个平面里。', '', '01010201010100'),
('01010201010200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP02', '安培力的大小', '', '01010201010000'),
('01010201010300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03', '磁电式电流表', '', '01010201010000'),
('01010201010401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP01', '磁电式电流表最基本的组成部分是磁体和放在磁体两极之间的线圈。', '', '01010201010300'),
('01010201010402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP02', '当电流通过线圈时，导线受到安培力的作用。', '', '01010201010300'),
('01010201010403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP03', '由左手定则可以判定，线圈左右两边所受的安培力的方向相反，于是安装在轴上的线圈就要转动。', '', '01010201010300'),
('01010201010404', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP04', '电流越大，安培力就越大，螺旋弹簧的形变也就越大，线圈偏转的角度也越大，达到新的平衡。', '', '01010201010300'),
('01010201010405', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP05', '从前面的分析可知，安培力总与磁感应强度的方向垂直。', '', '01010201010300'),
('01010201010406', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP06', '电流表的两磁极装有极靴，极靴中间还有一个用软铁制成的圆柱。', '', '01010201010300'),
('01010201010407', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP07', '线圈中的电流方向改变时，安培力的方向随着改变，指针的偏转方向也随着改变。', '', '01010201010300'),
('01010201010408', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC01.KP03.OP08', '磁电式电流表的优点是灵敏度高，可以测出很弱的电流；缺点是线圈的导线很细，允许通过的电流很弱（几十微安到几毫安）。', '', '01010201010300'),
('01010201020000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02', '磁场对运动电荷的作用力', '', '01010201000000'),
('01010201020100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02.KP01', '洛伦兹力的方向', '', '01010201020000'),
('01010201020201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02.KP01.OP01', '负电荷受力的方向与正电荷受力的方向相反。', '', '01010201020100'),
('01010201020200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02.KP02', '洛伦兹力的大小', '', '01010201020000'),
('01010201020300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02.KP03', '电子束的磁偏转', '', '01010201020000'),
('01010201020401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02.KP03.OP01', '撞击荧光屏，就能发光。', '', '01010201020300'),
('01010201020402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC02.KP03.OP02', '可是，很细的一束电子打在荧光屏上只能使一个点发光，要使整个荧光屏发光，就要靠磁场来使电子束偏转了。', '', '01010201020300'),
('01010201030000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03', '带电粒子在匀强磁场中的运动', '', '01010201000000'),
('01010201030100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP01', '带电粒子在匀强磁场中的运动', '', '01010201030000'),
('01010201030201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP01.OP01', '要分析上述问题中带电粒子的运动，就需要分析粒子的受力情况。', '', '01010201030100'),
('01010201030202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP01.OP02', '洛伦兹力总是与粒子的运动方向垂直，只改变粒子速度的方向，不改变粒子速度的大小。', '', '01010201030100'),
('01010201030203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP01.OP03', '由于粒子速度的大小不变，粒子在匀强磁场中所受洛伦兹力的大小也不改变，洛伦兹力对粒子起到了向心力的作用。', '', '01010201030100'),
('01010201030200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP02', '带电粒子在磁场中做圆周运动的半径和周期', '', '01010201030000'),
('01010201030301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP02.OP01', '然后就可以分别判断粒子的速度和磁场的强弱对圆半径的影响。', '', '01010201030200'),
('01010201030302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP02.OP02', '（1）求粒子所受的重力和洛伦兹力的大小之比。', '', '01010201030200'),
('01010201030303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP02.OP03', '（2）求粒子在磁场中运动的轨道半径。', '', '01010201030200'),
('01010201030304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC03.KP02.OP04', '（3）求粒子做匀速圆周运动的周期。', '', '01010201030200'),
('01010201040000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04', '质谱仪与回旋加速器', '', '01010201000000'),
('01010201040100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP01', '质谱仪', '', '01010201040000'),
('01010201040201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP01.OP01', '可以利用电场和磁场来控制带电粒子的运动。', '', '01010201040100'),
('01010201040202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP01.OP02', '利用电场让带电粒子获得一定的速度，利用磁场让粒子做圆周运动。', '', '01010201040100'),
('01010201040200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02', '回旋加速器', '', '01010201040000'),
('01010201040301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP01', '要认识原子核内部的情况，必须把核“打开”进行“观察”。', '', '01010201040200'),
('01010201040302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP02', '由于库仑力可以对带电粒子做功，从而增加粒子的动能，因此，人们首先想到加速器中一定要用到电场。', '', '01010201040200'),
('01010201040303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP03', '加速电压越高，粒子获得的动能就越高。', '', '01010201040200'),
('01010201040304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP04', '由于粒子在加速过程中的径迹为直线，要得到较高动能的粒子，其加速装置要很长。', '', '01010201040200'),
('01010201040305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP05', '而磁场正好能使带电粒子“转圈圈”！', '', '01010201040200'),
('01010201040306', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP06', '两个半圆盒处于与盒面垂直的匀强磁场中，所以粒子在磁场中做匀速圆周运动。', '', '01010201040200'),
('01010201040307', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP07', '如此，粒子在做圆周运动的过程中一次一次地经过盒缝，而两盒间的电势差一次一次地改变正负，粒子的速度就能够增加到很大。', '', '01010201040200'),
('01010201040308', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH01.SEC04.KP02.OP08', '原因是，按照狭义相对论，粒子的质量随着速度增加而增大，而质量的变化会导致其回转周期的变化，从而破坏了与电场变化周期的同步。', '', '01010201040200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010202000000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02', '电磁感应', '', NULL),
('01010202010000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01', '楞次定律', '', '01010202000000'),
('01010202010100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP01', '影响感应电流方向的因素', '', '01010202010000'),
('01010202010200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02', '楞次定律', '', '01010202010000'),
('01010202010301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02.OP01', '感应电流沿着楞次定律所述的方向，是能量守恒定律的必然结果。', '', '01010202010200'),
('01010202010302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02.OP02', '在上述实验中，把磁极插入线圈或从线圈内抽出时，推力或拉力都必须做机械功，做功过程中消耗的机械能转化成感应电流的电能。', '', '01010202010200'),
('01010202010303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02.OP03', '用楞次定律可以判定感应电流的方向。', '', '01010202010200'),
('01010202010304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02.OP04', '已知距离载流直导线较近的位置磁场较强。', '', '01010202010200'),
('01010202010305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02.OP05', '根据楞次定律，感应电流的磁场应该是阻碍穿过线圈的磁通量变化的。', '', '01010202010200'),
('01010202010306', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP02.OP06', '现在已经判明感应电流的磁场从纸面内向外指向读者，是跟原来磁场的方向相反的。', '', '01010202010200'),
('01010202010300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC01.KP03', '右手定则', '', '01010202010000'),
('01010202020000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02', '法拉第电磁感应定律', '', '01010202000000'),
('01010202020100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP01', '电磁感应定律', '', '01010202020000'),
('01010202020201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP01.OP01', '电路中有感应电流，就一定有电动势。', '', '01010202020100'),
('01010202020202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP01.OP02', '产生感应电动势的那部分导体就相当于电源。', '', '01010202020100'),
('01010202020200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP02', '导线切割磁感线时的感应电动势', '', '01010202020000'),
('01010202020301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP02.OP01', '后者不切割磁感线，不产生感应电动势。', '', '01010202020200'),
('01010202020302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP02.OP02', '可以判断，安培力的方向与推动导体棒运动的力的方向是相反的。', '', '01010202020200'),
('01010202020303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC02.KP02.OP03', '如果没有推力的作用，导体棒将克服安培力做功而消耗本身的机械能。', '', '01010202020200'),
('01010202030000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03', '涡流、电磁阻尼和电磁驱动', '', '01010202000000'),
('01010202030100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP01', '电磁感应现象中的感生电场', '', '01010202030000'),
('01010202030201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP01.OP01', '麦克斯韦认为，磁场变化时会在空间激发一种电场。', '', '01010202030100'),
('01010202030202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP01.OP02', '如果此刻空间存在闭合导体，导体中的自由电荷就会在感生电场的作用下做定向运动，产生感应电流，也就是说导体中产生了感应电动势。', '', '01010202030100'),
('01010202030203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP01.OP03', '现代科学研究中常要用到高速电子，电子感应加速器就是利用感生电场使电子加速的设备。', '', '01010202030100'),
('01010202030204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP01.OP04', '电磁铁线圈中电流的大小、方向可以变化，产生的感生电场使电子加速。', '', '01010202030100'),
('01010202030205', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP01.OP05', '根据楞次定律，为使真空室中产生顺时针方向的感生电场，磁场应该由弱变强。', '', '01010202030100'),
('01010202030200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02', '涡流', '', '01010202030000'),
('01010202030301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP01', '当某线圈中的电流随时间变化时，由于电磁感应，附近的另一个线圈中可能会产生感应电流。', '', '01010202030200'),
('01010202030302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP02', '金属块中的涡流会产生热量。', '', '01010202030200'),
('01010202030303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP03', '涡流产生的热量使金属熔化。', '', '01010202030200'),
('01010202030304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP04', '利用涡流冶炼金属的优点是，整个过程可以在真空中进行，能防止空气中的杂质进入金属，可以冶炼高质量的合金。', '', '01010202030200'),
('01010202030305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP05', '电动机、变压器的线圈都绕在铁芯上。', '', '01010202030200'),
('01010202030306', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP06', '线圈中流过变化的电流，在铁芯中产生的涡流使铁芯发热，浪费了能量，还可能损坏电器。', '', '01010202030200'),
('01010202030307', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP02.OP07', '一种探测地雷的探雷器是利用涡流工作的。', '', '01010202030200'),
('01010202030300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP03', '电磁阻尼', '', '01010202030000'),
('01010202030401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP03.OP01', '如果地下埋着金属物品，金属中会感应出涡流，涡流的磁场反过来影响线圈中的电流，使仪器报警。', '', '01010202030300'),
('01010202030402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP03.OP02', '机场、车站和重要活动场所的安检门可以探测人身携带的金属物品，道理是一样的。', '', '01010202030300'),
('01010202030403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP03.OP03', '交流感应电动机就是利用电磁驱动的原理工作的。', '', '01010202030300'),
('01010202030400', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC03.KP04', '电磁驱动', '', '01010202030000'),
('01010202040000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04', '互感和自感', '', '01010202000000'),
('01010202040100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP01', '互感现象', '', '01010202040000'),
('01010202040201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP01.OP01', '利用互感现象可以把能量由一个线圈传递到另一个线圈，因此互感在电工技术和电子技术中有广泛的应用。', '', '01010202040100'),
('01010202040202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP01.OP02', '变压器就是利用互感现象制成的。', '', '01010202040100'),
('01010202040200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP02', '自感现象', '', '01010202040000'),
('01010202040301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP02.OP01', '变压器、电动机等设备中有匝数很多的线圈，当电路中的开关断开时会产生很大的自感电动势，使得开关中的金属片之间产生电火花，烧蚀接触点，甚至会引起人身伤害。', '', '01010202040200'),
('01010202040300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP03', '自感系数', '', '01010202040000'),
('01010202040400', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP04', '磁场的能量', '', '01010202040000'),
('01010202040501', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP04.OP01', '开关断开以后，线圈中的电流并未立即消失，线圈中有电流，有电流就有磁场，能量储存在磁场中。', '', '01010202040400'),
('01010202040502', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH02.SEC04.KP04.OP02', '当线圈刚刚接通电源的时候，自感电动势阻碍线圈中电流的增加；当电源断开的时候，自感电动势又阻碍线圈中电流的减小。', '', '01010202040400');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010203000000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03', '交变电流', '', NULL),
('01010203010000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01', '交变电流', '', '01010203000000'),
('01010203010100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP01', '交变电流', '', '01010203010000'),
('01010203010201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP01.OP01', '在恒定电流的电路中，电源的电动势不随时间变化，电路中的电流、电压也不随时间变化。', '', '01010203010100'),
('01010203010202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP01.OP02', '电池供给的电流方向不随时间变化，所以属于直流。', '', '01010203010100'),
('01010203010203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP01.OP03', '日常使用的各种充电器能把交变电流变成低压直流。', '', '01010203010100'),
('01010203010200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP02', '交变电流的产生', '', '01010203010000'),
('01010203010301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP02.OP01', '教学用发电机产生的电流，大小和方向都在不断地变化，是一种交变电流。', '', '01010203010200'),
('01010203010300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP03', '交变电流的变化规律', '', '01010203010000'),
('01010203010401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP03.OP01', '正弦式电流是最简单、最基本的交变电流。', '', '01010203010300'),
('01010203010400', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP04', '交流发电机', '', '01010203010000'),
('01010203010501', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP04.OP01', '电枢转动，磁极不动的发电机，叫作旋转电枢式发电机。', '', '01010203010400'),
('01010203010502', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP04.OP02', '不论哪种发电机，转动的部分都叫转子，不动的部分都叫定子。', '', '01010203010400'),
('01010203010503', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP04.OP03', '同时，转动的电枢无法做得很大，线圈匝数也不可能很多，所以产生的感应电动势也不能很高。', '', '01010203010400'),
('01010203010504', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP04.OP04', '旋转磁极式发电机克服了上述缺点，能够产生几千伏到几万伏的电压，输出功率可达几百兆瓦。', '', '01010203010400'),
('01010203010505', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC01.KP04.OP05', '蒸汽轮机、水轮机等将机械能传递给发电机，发电机将机械能转化为电能，输送给外电路。', '', '01010203010400'),
('01010203020000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02', '交变电流的描述', '', '01010203000000'),
('01010203020100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP01', '周期和频率', '', '01010203020000'),
('01010203020201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP01.OP01', '交变电流与振动和波一样具有周期性。', '', '01010203020100'),
('01010203020202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP01.OP02', '与任何周期性过程一样，交变电流也可以用周期或频率表示其变化的快慢。', '', '01010203020100'),
('01010203020203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP01.OP03', '使用交流的电气设备上，标出的额定电压和额定电流都是有效值，一般交流电压表测量的数值也是有效值。', '', '01010203020100'),
('01010203020200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP02', '峰值和有效值', '', '01010203020000'),
('01010203020301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP02.OP01', '电容器所能承受的电压要高于交流电压的峰值，否则电容器就可能被击穿。', '', '01010203020200'),
('01010203020300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC02.KP03', '正弦式交变电流的公式和图像', '', '01010203020000'),
('01010203030000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03', '变压器', '', '01010203000000'),
('01010203030100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01', '变压器的原理', '', '01010203030000'),
('01010203030201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01.OP01', '互感现象是变压器工作的基础。', '', '01010203030100'),
('01010203030202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01.OP02', '电流通过原线圈时在铁芯中激发磁场，由于电流的大小、方向在不断变化，铁芯中的磁场也在不断变化。', '', '01010203030100'),
('01010203030203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01.OP03', '变化的磁场在副线圈中产生感应电动势，所以尽管两个线圈之间没有导线相连，副线圈也能够输出电流。', '', '01010203030100'),
('01010203030204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01.OP04', '在输入的交流电压一定时，原线圈、副线圈取不同的匝数，副线圈输出的电压也不一样，变压器由此得名。', '', '01010203030100'),
('01010203030205', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01.OP05', '其实，变压器线圈通过电流时会发热；铁芯在交变磁场的作用下也会发热；此外，交变电流产生的磁场也不可能完全局限在铁芯内。', '', '01010203030100'),
('01010203030206', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP01.OP06', '理想变压器也是一个理想化模型。', '', '01010203030100'),
('01010203030200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP02', '电压与匝数的关系', '', '01010203030000'),
('01010203030301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP02.OP01', '实际上变压器的效率都是比较高的，特别是电力设备中的巨大变压器，在满负荷工作时效率可以超过95%。', '', '01010203030200'),
('01010203030302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP02.OP02', '实际应用中需要改变交流电压的情况是很多的。', '', '01010203030200'),
('01010203030303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP02.OP03', '大型发电机发出的交流电压为几万伏，而远距离输电却需要几十万伏以上的电压。', '', '01010203030200'),
('01010203030304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP02.OP04', '各种用电设备所需的电压也不相同。', '', '01010203030200'),
('01010203030305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC03.KP02.OP05', '变压器能输送电能是利用了电磁感应。', '', '01010203030200'),
('01010203040000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04', '电能的输送', '', '01010203000000'),
('01010203040100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01', '降低输电损耗的两个途径', '', '01010203040000'),
('01010203040201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP01', '一个途径是减小输电线的电阻。', '', '01010203040100'),
('01010203040202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP02', '过粗的导线会耗费太多的金属材料，而且输电线太重、太粗也给铺设工程带来困难。', '', '01010203040100'),
('01010203040203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP03', '远距离输电时，为了降低输电线路中的损耗，就要减小输电电流；为了减小输电电流，同时又要保证向用户提供一定的电功率，就要提高输电电压。', '', '01010203040100'),
('01010203040204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP04', '现代远距离输电的电压都很高。', '', '01010203040100'),
('01010203040205', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP05', '输电电压也不是越高越好。', '', '01010203040100'),
('01010203040206', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP06', '电压越高，对输电线路绝缘性能的要求就越高，线路修建费用就会增多。', '', '01010203040100'),
('01010203040207', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP07', '输电电压越高，变压器上的电压也越高，对变压器的要求也相应提高。', '', '01010203040100'),
('01010203040208', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP01.OP08', '实际输送电能时，要综合考虑各种因素，如输送功率的大小、距离的远近、技术和经济要求等，依照不同情况选择合适的输电电压。', '', '01010203040100'),
('01010203040200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP02', '电网供电', '', '01010203040000'),
('01010203040301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP02.OP01', '采用电网送电，是输电技术的重要发展。', '', '01010203040200'),
('01010203040302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH03.SEC04.KP02.OP02', '电网可以减小断电的风险，调剂不同地区电力供需的平衡。', '', '01010203040200');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010204000000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04', '电磁振荡与电磁波', '', NULL),
('01010204010000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01', '电磁振荡', '', '01010204000000'),
('01010204010100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP01', '电磁振荡的产生', '', '01010204010000'),
('01010204010201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP01.OP01', '在前面的实验中，电路的电压发生周期性的变化，电路中的电流也发生周期性的变化。', '', '01010204010100'),
('01010204010202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP01.OP02', '会立即减小为0，而要保持原来的方向继续流动，并逐渐减小。', '', '01010204010100'),
('01010204010203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP01.OP03', '由于电流继续流动，电容器充电，电容器两极板带上与原来相反的电荷，并且电荷逐渐增多。', '', '01010204010100'),
('01010204010200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP02', '电磁振荡中的能量变化', '', '01010204010000'),
('01010204010301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP02.OP01', '从能量的观点来看，电容器刚要放电时，电容器里的电场最强，电路里的能量全部储存在电容器的电场中；电容器开始放电后，电容器里的电场逐渐减弱，线圈的磁场逐渐增强，电场能逐渐转化为磁场能；在放电完毕的瞬间，电场能全部转化为磁场能；之后，线圈的磁场逐渐减弱，电容器里的电场逐渐增强，磁场能逐渐转化为电场能；到反方向充电完毕的瞬间，磁场能全部转化为电场能。', '', '01010204010200'),
('01010204010302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP02.OP02', '如果没有能量损耗，振荡可以永远持续下去，振荡电流的振幅保持不变。', '', '01010204010200'),
('01010204010303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP02.OP03', '也可以用可调电容器或可调电感的线圈组成电路，改变电容器的电容或线圈的电感，振荡电路的周期和频率就会随着改变。', '', '01010204010200'),
('01010204010304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP02.OP04', '会有一部分能量会转化为内能。', '', '01010204010200'),
('01010204010300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP03', '电磁振荡的周期和频率', '', '01010204010000'),
('01010204010401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP03.OP01', '电磁振荡完成一次周期性变化需要的时间叫作周期。', '', '01010204010300'),
('01010204010402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC01.KP03.OP02', '求振荡电路的频率范围。', '', '01010204010300'),
('01010204020000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02', '电磁场与电磁波', '', '01010204000000'),
('01010204020100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP01', '电磁场', '', '01010204020000'),
('01010204020201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP01.OP01', '在变化的磁场中放一个闭合电路，电路里就会产生感应电流。', '', '01010204020100'),
('01010204020202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP01.OP02', '当电荷从静止到运动时，电场就发生变化，即空间各点的电场强度将随时间变化。', '', '01010204020100'),
('01010204020203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP01.OP03', '运动的电荷在空间要产生磁场。', '', '01010204020100'),
('01010204020204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP01.OP04', '如果在空间某区域有周期性变化的电场，就会在周围引起变化的磁场，变化的电场和磁场又会在较远的空间引起新的变化的电场和磁场。', '', '01010204020100'),
('01010204020200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02', '电磁波', '', '01010204020000'),
('01010204020301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP01', '因为电磁波的传播，靠的是电和磁的相互“感应”，而不是靠介质的机械传递。', '', '01010204020200'),
('01010204020302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP02', '1886年，赫兹在做放电实验时发现附近未闭合的线圈也出现了电火花。', '', '01010204020200'),
('01010204020303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP03', '仪器中有一对抛光的金属小球，两球之间有很小的空气间隙。', '', '01010204020200'),
('01010204020304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP04', '将两个球连接到产生高电压的感应圈的两端时，两球之间就出现了火花放电。', '', '01010204020200'),
('01010204020305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP05', '仪器的另一部分是弯成环状的导线，导线两端也安装两个金属小球，小球之间也有空隙。', '', '01010204020200'),
('01010204020306', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP06', '电磁波从发射器到达了接收器。', '', '01010204020200'),
('01010204020307', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP07', '当与感应圈相连的两个金属球间产生电火花时，周围空间出现了迅速变化的电磁场。', '', '01010204020200'),
('01010204020308', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP08', '在以后的一系列实验中，赫兹观察到了电磁波的反射、折射、干涉、偏振和衍射等现象。', '', '01010204020200'),
('01010204020309', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP09', '根据电磁场理论，电磁场的转换就是电场能量和磁场能量的转换，因而电磁波的发射过程就是辐射能量的过程，传播过程就是能量传播的过程。', '', '01010204020200'),
('01010204020310', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP10', '能量是电磁场的物质性最有说服力的证据之一。', '', '01010204020200'),
('01010204020311', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC02.KP02.OP11', '由此，人们认识到物质存在两种形式，一种是由原子和分子构成的实物，另一种则是以电磁场为代表的场。', '', '01010204020200'),
('01010204030000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03', '无线电波的发射和接收', '', '01010204000000'),
('01010204030100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP01', '无线电波的发射', '', '01010204030000'),
('01010204030201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP01.OP01', '研究表明，要有效地发射电磁波，振荡电路必须具有如下两个特点。', '', '01010204030100'),
('01010204030202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP01.OP02', '振荡电路向外界辐射能量的本领，与振荡频率密切相关。', '', '01010204030100'),
('01010204030203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP01.OP03', '频率越高，发射电磁波的本领就越大。', '', '01010204030100'),
('01010204030204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP01.OP04', '板间的距离，减小极板间的正对面积，从而使电场和磁场扩展到电容器的外部。', '', '01010204030100'),
('01010204030200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP02', '无线电波的接收', '', '01010204030000'),
('01010204030301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP02.OP01', '电磁波在传播时如果遇到导体，会使导体中产生感应电流。', '', '01010204030200'),
('01010204030302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP02.OP02', '调幅波的解调也叫检波。', '', '01010204030200'),
('01010204030300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP03', '电视广播的发射和接收', '', '01010204030000'),
('01010204030401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP03.OP01', '电视广播信号也是一种无线电信号。', '', '01010204030300'),
('01010204030402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP03.OP02', '在现代电视发射系统中，首先通过摄像机的感光器件将景物的光信号转变为电信号。', '', '01010204030300'),
('01010204030403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP03.OP03', '目前，高频电视信号的传播方式主要有三种，即地面无线电传输、有线网络传输以及卫星传输。', '', '01010204030300'),
('01010204030404', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP03.OP04', '不同的传播方式使用不同频率范围的电磁波，采取不同的调制方式。', '', '01010204030300'),
('01010204030405', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC03.KP03.OP05', '伴音信号经解调后送到扬声器。', '', '01010204030300'),
('01010204040000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04', '电磁波谱', '', '01010204000000'),
('01010204040100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP01', '无线电波', '', '01010204040000'),
('01010204040201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP01.OP01', '波长短的电磁波，由于衍射现象不明显，传播的方向性好，有利于用电磁波定位，因此雷达用的是微波。', '', '01010204040100'),
('01010204040202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP01.OP02', '移动电话也应用了无线电波。', '', '01010204040100'),
('01010204040203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP01.OP03', '许多自然过程也辐射无线电波。', '', '01010204040100'),
('01010204040200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP02', '红外线', '', '01010204040000'),
('01010204040301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP02.OP01', '所有物体都发射红外线。', '', '01010204040200'),
('01010204040302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP02.OP02', '热物体的红外辐射比冷物体的红外辐射强。', '', '01010204040200'),
('01010204040300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP03', '可见光', '', '01010204040000'),
('01010204040401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP03.OP01', '能使人的眼睛产生视觉效应的电磁波，称为可见光。', '', '01010204040300'),
('01010204040402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP03.OP02', '用灵敏的红外探测器接收远处物体发出的红外线，然后用电子电路对信号进行处理，可以得知被测对象的形状及温度、湿度等参数。', '', '01010204040300'),
('01010204040403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP03.OP03', '利用红外遥感技术可以在飞机或人造地球卫星上勘测地热、寻找水源、监视森林火情、预报风暴和寒潮。', '', '01010204040300'),
('01010204040404', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP03.OP04', '红外遥感技术在军事上的应用也十分重要。', '', '01010204040300'),
('01010204040405', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP03.OP05', '人体也在发射红外线，体温越高，发射的红外线越强。', '', '01010204040300'),
('01010204040400', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP04', '紫外线', '', '01010204040000'),
('01010204040501', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP04.OP01', '人眼看不到比紫光波长更短的电磁波。', '', '01010204040400'),
('01010204040502', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP04.OP02', '紫外线具有较高的能量，足以破坏细胞核中的物质。', '', '01010204040400'),
('01010204040503', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP04.OP03', '太阳光中有许多紫外线，人体接受适量的紫外线照射，能促进钙的吸收，改善身体健康状况。', '', '01010204040400'),
('01010204040500', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP05', 'X射线和', '', '01010204040000'),
('01010204040600', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP06', '射线', '', '01010204040000'),
('01010204040701', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP06.OP01', 'γ射线能破坏生命物质。', '', '01010204040600'),
('01010204040702', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH04.SEC04.KP06.OP02', 'γ射线的穿透能力很强，也可用于探测金属构件内部的缺陷。', '', '01010204040600');
INSERT INTO knowledge_point (kp_id, subject_code, category1_code, category2_code, code, name, description, parent_id)
VALUES
('01010205000000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05', '传感器', '', NULL),
('01010205010000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01', '认识传感器', '', '01010205000000'),
('01010205010100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP01', '神奇的传感器', '', '01010205010000'),
('01010205010201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP01.OP01', '才会接通，灯才会亮。', '', '01010205010100'),
('01010205010202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP01.OP02', '实际上，有些传感器还具有“超人”的本领。', '', '01010205010100'),
('01010205010203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP01.OP03', '通常转换成的可用信号是电压、电流等电学量，或转换为电路的通断。', '', '01010205010100'),
('01010205010204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP01.OP04', '把非电学量转换为电学量，可以很方便地进行测量、传输、处理和控制。', '', '01010205010100'),
('01010205010200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP02', '传感器的种类', '', '01010205010000'),
('01010205010301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP02.OP01', '在林林总总的传感器世界中，大多数传感器是以物理原理为基础制作的。', '', '01010205010200'),
('01010205010302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP02.OP02', '根据被测量的不同，可分为声、光、压力、位移、加速度、温度等传感器；根据具体工作原理的不同，可分为电阻式、电容式、电感式、光电式、热电式、压电式、磁电式等传感器。', '', '01010205010200'),
('01010205010300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP03', '传感器的组成与应用模式', '', '01010205010000'),
('01010205010401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP03.OP01', '传感器种类繁多，工作原理各异，外观和结构也千差万别，但是，传感器的基本部分一般由敏感元件、转换元件组成。', '', '01010205010300'),
('01010205010402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP03.OP02', '敏感元件是指能直接感受或响应外界被测非电学量的部分；转换元件是指能将敏感元件输出的信号直接转换成电信号的部分。', '', '01010205010300'),
('01010205010403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP03.OP03', '应该指出的是，并不是所有传感器都能够明显地区分敏感元件和转换元件。', '', '01010205010300'),
('01010205010404', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC01.KP03.OP04', '声波使振动膜片发生振动，振动膜片作为敏感元件，感受声音信号的变化，与固定电极组成的电容器相当于转换元件，将声音信号的强弱转换成电容器的电容变化。', '', '01010205010300'),
('01010205020000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02', '常见传感器的工作原理及应用', '', '01010205000000'),
('01010205020100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP01', '光敏电阻', '', '01010205020000'),
('01010205020201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP01.OP01', '硫化镉表面受到的光照强度不同时，两个电极间的电阻也不一样。', '', '01010205020100'),
('01010205020202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP01.OP02', '光敏电阻是光电传感器中常见的光敏元件。', '', '01010205020100'),
('01010205020203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP01.OP03', '光敏电阻在被光照射时电阻发生变化，原因是：硫化镉是一种半导体材料，无光照时，载流子极少，导电性能差；随着光照的增强，载流子增多，导电性变好。', '', '01010205020100'),
('01010205020204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP01.OP04', '光敏电阻有许多应用。', '', '01010205020100'),
('01010205020200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02', '金属热电阻和热敏电阻', '', '01010205020000'),
('01010205020301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP01', '除了光照以外，温度也能明显地影响金属导体和半导体材料的导电性能。', '', '01010205020200'),
('01010205020302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP02', '金属热电阻和热敏电阻就是传感器中常见的感知温度的敏感元件。', '', '01010205020200'),
('01010205020303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP03', '用金属丝可以制作温度传感器，称为热电阻。', '', '01010205020200'),
('01010205020304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP04', '常用的一种热电阻是用铂制作的，可用来做电阻温度计。', '', '01010205020200'),
('01010205020305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP05', '与热敏电阻相比，金属热电阻的化学稳定性好，测温范围大，但灵敏度较差。', '', '01010205020200'),
('01010205020306', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP06', '若给热敏电阻通以一定的电流，热敏电阻会发热。', '', '01010205020200'),
('01010205020307', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP02.OP07', '通过判断热敏电阻的阻值变化，就可以知道液面是否低于设定值。', '', '01010205020200'),
('01010205020300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP03', '电阻应变片', '', '01010205020000'),
('01010205020401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP03.OP01', '当金属丝受到拉力时，长度变长、横截面积变小，导致电阻变大；当金属丝受到压力时，长度变短、横截面积变大，导致电阻变小。', '', '01010205020300'),
('01010205020402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP03.OP02', '通过前面的学习可以知道，光敏电阻、热敏电阻、电阻应变片等电阻式传感器的工作共性是通过测量电阻的变化来确定外界非电学量的变化。', '', '01010205020300'),
('01010205020403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC02.KP03.OP03', '电容式传感器有非常广泛的应用。', '', '01010205020300'),
('01010205030000', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03', '利用传感器制作简单的自动控制装置', '', '01010205000000'),
('01010205030100', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01', '实验1 门窗防盗报警装置', '', '01010205030000'),
('01010205030201', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP01', '门窗防盗报警装置具有自动提示报警的功能。', '', '01010205030100'),
('01010205030202', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP02', '在睡觉前连接好电路，启动防盗报警装置。', '', '01010205030100'),
('01010205030203', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP03', '当门窗紧闭时，蜂鸣器不响，指示灯亮；当门窗被打开时，蜂鸣器发出声音警报，指示灯灭。', '', '01010205030100'),
('01010205030204', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP04', '利用干簧管在磁场中的特性是不错的选择。', '', '01010205030100'),
('01010205030205', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP05', '继电器（虚线方框部分）作为执行装置。', '', '01010205030100'),
('01010205030206', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP06', '当门窗开启时，磁体离开干簧管，干簧管失磁断开，继电器被断电。', '', '01010205030100'),
('01010205030207', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP07', '干簧管在电路中起传感器和控制开关的作用，继电器则相当于一个自动的双向开关。', '', '01010205030100'),
('01010205030208', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP08', '实验操作（1）连接电路前，要先判断一下干簧管是否可以正常工作。', '', '01010205030100'),
('01010205030209', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP09', '用磁体直接靠近干簧管，观察簧片能否正常动作。', '', '01010205030100'),
('01010205030210', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP01.OP10', '（3）接通电源后，将磁体靠近和离开干簧管，分别观察实验现象。', '', '01010205030100'),
('01010205030200', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02', '实验2 光控开关', '', '01010205030000'),
('01010205030301', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP01', '光控路灯可以根据光照的变化自动开启或关闭。', '', '01010205030200'),
('01010205030302', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP02', '种模拟的光控开关电路，用发光二极管或小灯泡代表路灯。', '', '01010205030200'),
('01010205030303', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP03', '在控制电路中，由于光敏电阻的工作电流很微弱，不能直接驱动执行机构工作，因此一般都需要配置一定的放大电路实现控制。', '', '01010205030200'),
('01010205030304', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP04', '晶体三极管是半导体基本元件之一，具有电流放大作用，在控制电路中常用作电子开关。', '', '01010205030200'),
('01010205030305', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP05', '本实验采用三极管配合光敏电阻完成光控开关的任务。', '', '01010205030200'),
('01010205030306', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP06', '三极管的一个重要特性是，从基极输入一个较小的电流，就会在集电极获得较大的电流。', '', '01010205030200'),
('01010205030307', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP02.OP07', '如果实验现象不明显，可用手电筒加强光照，或遮盖光敏电阻，再进行观察。', '', '01010205030200'),
('01010205030300', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03', '研究样例', '', '01010205030000'),
('01010205030401', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP01', '压阻效应是指当半导体材料沿某一方向受到外力作用时，其电阻率发生变化的现象。', '', '01010205030300'),
('01010205030402', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP02', '为使应变片受力均匀，在圆柱体与应变片之间垫一个面积相当于有效区域的聚氨酯橡胶垫片。', '', '01010205030300'),
('01010205030403', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP03', '传感器的两根引线跟数字多用电表两表笔相连。', '', '01010205030300'),
('01010205030404', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP04', '数字多用电表选用电阻挡，可以直接测量传感器的电阻。', '', '01010205030300'),
('01010205030405', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP05', '改变钩码的数量，就改变了传感器所受的压力。', '', '01010205030300'),
('01010205030406', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP06', '该装置可以测量不同压力下传感器的电阻值。', '', '01010205030300'),
('01010205030407', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP07', '要使传感器比较灵敏，就应使之工作在灵敏度较大的区间。', '', '01010205030300'),
('01010205030408', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP08', '该装置把大小不同的苹果，按一定质量标准自动分拣为大苹果和小苹果。', '', '01010205030300'),
('01010205030409', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP03.OP09', '该放大电路中包含保持电路，能够确保大苹果在衔铁上运动时电磁铁始终保持吸动状态。', '', '01010205030300'),
('01010205030400', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP04', '参考选题', '', '01010205030000'),
('01010205030501', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP04.OP01', '热敏电阻价格低廉，为研究热敏电阻的特性提供了良好的器材条件。', '', '01010205030400'),
('01010205030502', 'PHYSICS', 'SENIOR', 'TERM_5', 'CH05.SEC03.KP04.OP02', '通过加兑热水改变水温，测量不同温度下热敏电阻的阻值，绘制电阻－温度曲线，描述热敏电阻的阻值随温度变化的规律。', '', '01010205030400');
